// Helpers
const toBinary = (n, bits) => n.toString(2).padStart(bits, '0');
const asciiToBinary = (str) => {
  let out = '';
  for (let i = 0; i < str.length; i++) {
    out += toBinary(str.charCodeAt(i) & 0xFF, 8);
  }
  return out;
};
const padRight = (s, len, ch='0') => s.length >= len ? s : s + ch.repeat(len - s.length);
const chunkString = (s, size) => {
  const arr = [];
  for (let i = 0; i < s.length; i += size) arr.push(s.slice(i, i + size));
  return arr;
};
const permute = (bits, table) => table.map(i => bits[i-1]).join('');
const leftRotate = (bits, shift) => bits.slice(shift) + bits.slice(0, shift);
const xorBits = (a, b) => {
  let out = '';
  for (let i = 0; i < a.length; i++) out += (a[i] === b[i]) ? '0' : '1';
  return out;
};
const sboxTransform = (bits48) => {
  let out = '';
  for (let i = 0; i < 8; i++) {
    const block = bits48.slice(i*6, i*6 + 6);
    const row = parseInt(block[0] + block[5], 2);
    const col = parseInt(block.slice(1, 5), 2);
    const val = SBOXES[i][row][col];
    out += toBinary(val, 4);
  }
  return out;
};
const binToHex = (bin) => {
  let out = '';
  for (let i = 0; i < bin.length; i += 4) {
    const nibble = bin.slice(i, i + 4);
    out += parseInt(nibble, 2).toString(16);
  }
  return out;
};

// Key schedule: generate 16 subkeys (48-bit each)
function generateSubkeys(key64) {
  const key56 = permute(key64, PC1);
  let C = key56.slice(0, 28);
  let D = key56.slice(28, 56);
  const subkeys = [];
  for (let i = 0; i < 16; i++) {
    C = leftRotate(C, SHIFTS[i]);
    D = leftRotate(D, SHIFTS[i]);
    subkeys.push(permute(C + D, PC2));
  }
  return subkeys;
}

// DES f-function
function fFunction(R32, K48) {
  const expanded = permute(R32, E);
  const mixed = xorBits(expanded, K48);
  const substituted = sboxTransform(mixed);
  return permute(substituted, P);
}

// Encrypt a single 64-bit block for a given number of rounds (2..16)
function desEncryptBlock(block64, subkeys, rounds) {
  const ip = permute(block64, IP);
  let L = ip.slice(0, 32);
  let R = ip.slice(32, 64);
  for (let i = 0; i < rounds; i++) {
    const newL = R;
    const newR = xorBits(L, fFunction(R, subkeys[i]));
    L = newL;
    R = newR;
  }
  const preoutput = R + L; // final swap
  return permute(preoutput, FP);
}

/* =========================
   UI logic
   ========================= */

function updateDisplay() {
  const msg = document.getElementById('input-message').value;
  const key = document.getElementById('input-key').value;
  const steps = parseInt(document.querySelector('input[name="steps"]:checked').value, 10);

  // Convert message to binary, pad to 64-bit multiples
  let msgBits = asciiToBinary(msg);
  if (msgBits.length % 64 !== 0) {
    msgBits = padRight(msgBits, Math.ceil(msgBits.length / 64) * 64, '0');
  }
  const blocks = msgBits.length ? chunkString(msgBits, 64) : [];

  // Key processing to exact 64 bits (truncate or pad right with zeros)
  let keyBits = asciiToBinary(key);
  keyBits = keyBits.length >= 64 ? keyBits.slice(0, 64) : padRight(keyBits, 64, '0');

  // Precompute subkeys
  const subkeys = generateSubkeys(keyBits);

  const lines = [];
  for (let s = 2; s <= steps; s += 2) {
    let currentHex = '';
    if (blocks.length === 0) {
      currentHex = '';
    } else {
      for (const block of blocks) {
        const encBits = desEncryptBlock(block, subkeys, s);
        currentHex += binToHex(encBits).toUpperCase();
      }
    }
    const stepStr = String(s).padStart(2, ' ');
    lines.push(`${stepStr}      ${currentHex}`);
  }
  document.getElementById('output-results').textContent = lines.join('\n');
}

function attachListeners() {
  document.getElementById('input-message').addEventListener('input', updateDisplay);
  document.getElementById('input-key').addEventListener('input', updateDisplay);
  document.querySelectorAll('input[name="steps"]').forEach(r => {
    r.addEventListener('change', updateDisplay);
  });
}

// Initialize with defaults as specified
document.addEventListener('DOMContentLoaded', () => {
  // Defaults are already set in HTML: message="hello", key="test", steps=16
  attachListeners();
  updateDisplay();
});
</script>
</body>
