  <script>
    // Global app state
    const state = {
      currentAlgorithm: 'shellsort',
      numElements: 10,
      sortHistory: [], // history[0] is initial array, history[1] after first transformation, etc.
      currentStep: 1,  // 1-based index into history (maps to history[currentStep])
      isPlaying: false,
      playTimer: null
    };

    // DOM elements
    const elElementsSlider = document.getElementById('slider-elements');
    const elElementsValue  = document.getElementById('elements-value');
    const elStepSlider     = document.getElementById('slider-step');
    const elStepValue      = document.getElementById('step-value');
    const btnPlayPause     = document.getElementById('btn-play-pause');
    const btnStepBackward  = document.getElementById('btn-step-backward');
    const btnStepForward   = document.getElementById('btn-step-forward');
    const btnResetStep     = document.getElementById('btn-reset-step');
    const btnRandomize     = document.getElementById('btn-randomize');
    const btnShell         = document.getElementById('btn-shellsort');
    const btnBubble        = document.getElementById('btn-bubblesort');
    const btnInsertion     = document.getElementById('btn-insertionsort');
    const btnCocktail      = document.getElementById('btn-cocktail');
    const btnBogo          = document.getElementById('btn-bogosort');
    const sortButtons      = [btnShell, btnBubble, btnInsertion, btnCocktail, btnBogo];

    // Utility: Shuffle array in-place (Fisher-Yates)
    function shuffleInPlace(arr) {
      for (let i = arr.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
      }
      return arr;
    }

    // Utility: generate initial array [1..N] and shuffle
    function generateRandomArray(n) {
      const arr = new Array(n);
      for (let i = 0; i < n; i++) arr[i] = i + 1;
      return shuffleInPlace(arr);
    }

    // Sorting algorithms producing history (including initial state as history[0])
    function bubbleSortHistory(input) {
      const arr = input.slice();
      const history = [arr.slice()];
      const n = arr.length;
      for (let i = 0; i < n - 1; i++) {
        let swapped = false;
        for (let j = 0; j < n - 1 - i; j++) {
          if (arr[j] > arr[j + 1]) {
            [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
            history.push(arr.slice());
            swapped = true;
          }
        }
        if (!swapped) break;
      }
      return history;
    }

    function insertionSortHistory(input) {
      const arr = input.slice();
      const history = [arr.slice()];
      const n = arr.length;
      for (let i = 1; i < n; i++) {
        const key = arr[i];
        let j = i - 1;
        while (j >= 0 && arr[j] > key) {
          arr[j + 1] = arr[j];
          history.push(arr.slice());
          j--;
        }
        arr[j + 1] = key;
        history.push(arr.slice());
      }
      return history;
    }

    function shellSortHistory(input) {
      const arr = input.slice();
      const history = [arr.slice()];
      const n = arr.length;
      // Simple Shell gap sequence: n/2 halving
      for (let gap = Math.floor(n / 2); gap > 0; gap = Math.floor(gap / 2)) {
        for (let i = gap; i < n; i++) {
          const temp = arr[i];
          let j = i;
          while (j >= gap && arr[j - gap] > temp) {
            arr[j] = arr[j - gap];
            history.push(arr.slice());
            j -= gap;
          }
          if (arr[j] !== temp) {
            arr[j] = temp;
            history.push(arr.slice());
          }
        }
      }
      return history;
    }

    function cocktailSortHistory(input) {
      const arr = input.slice();
      const history = [arr.slice()];
      let start = 0;
      let end = arr.length - 1;
      let swapped = true;
      while (swapped) {
        swapped = false;
        for (let i = start; i < end; i++) {
          if (arr[i] > arr[i + 1]) {
            [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
            history.push(arr.slice());
            swapped = true;
          }
        }
        if (!swapped) break;
        swapped = false;
        end--;
        for (let i = end - 1; i >= start; i--) {
          if (arr[i] > arr[i + 1]) {
            [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
            history.push(arr.slice());
            swapped = true;
          }
        }
        start++;
      }
      return history;
    }

    function isSorted(arr) {
      for (let i = 1; i < arr.length; i++) {
        if (arr[i - 1] > arr[i]) return false;
      }
      return true;
    }

    function bogoSortHistory(input) {
      const arr = input.slice();
      const history = [arr.slice()];
      // Bogosort: shuffle until sorted or we hit a cap to keep demo responsive
      const maxShuffles = 400; // cap to prevent runaway
      let count = 0;
      while (!isSorted(arr) && count < maxShuffles) {
        shuffleInPlace(arr);
        history.push(arr.slice());
        count++;
      }
      return history;
    }

    function computeHistoryForAlgorithm(algorithm, array) {
      switch (algorithm) {
        case 'shellsort':     return shellSortHistory(array);
        case 'bubblesort':    return bubbleSortHistory(array);
        case 'insertionsort': return insertionSortHistory(array);
        case 'cocktail':      return cocktailSortHistory(array);
        case 'bogosort':      return bogoSortHistory(array);
        default:              return shellSortHistory(array);
      }
    }

    // Randomize and compute steps (core function)
    function randomizeAndCompute() {
      // Read number of elements
      let N = parseInt(elElementsSlider.value, 10);
      if (state.currentAlgorithm === 'bogosort') {
        N = 7; // enforce
        elElementsSlider.value = 7;
        elElementsValue.textContent = '7';
      }
      state.numElements = N;

      // Create and shuffle array
      const initialArray = generateRandomArray(N);

      // Compute history for current algorithm
      const history = computeHistoryForAlgorithm(state.currentAlgorithm, initialArray);

      // Update state
      state.sortHistory = history;
      const stepsCount = Math.max(1, history.length - 1); // steps excluding initial (step 0)
      elStepSlider.max = String(stepsCount);
      elStepSlider.value = '1';
      state.currentStep = 1;
      elStepValue.textContent = '1';

      // Stop animation if running
      stopPlaying();

      // Redraw
      requestRedraw();
    }

    // Sort button handling
    function setActiveAlgorithm(algo) {
      state.currentAlgorithm = algo;
      sortButtons.forEach(btn => btn.classList.remove('active'));
      const map = {
        shellsort: btnShell,
        bubblesort: btnBubble,
        insertionsort: btnInsertion,
        cocktail: btnCocktail,
        bogosort: btnBogo
      };
      map[algo].classList.add('active');

      if (algo === 'bogosort') {
        elElementsSlider.disabled = true;
        elElementsSlider.value = '7';
        elElementsValue.textContent = '7';
      } else {
        elElementsSlider.disabled = false;
      }

      // Automatically randomize and compute for the selected algorithm
      randomizeAndCompute();
    }

    // Event listeners
    elElementsSlider.addEventListener('input', () => {
      elElementsValue.textContent = elElementsSlider.value;
    });

    btnRandomize.addEventListener('click', () => {
      randomizeAndCompute();
    });

    btnShell.addEventListener('click', () => setActiveAlgorithm('shellsort'));
    btnBubble.addEventListener('click', () => setActiveAlgorithm('bubblesort'));
    btnInsertion.addEventListener('click', () => setActiveAlgorithm('insertionsort'));
    btnCocktail.addEventListener('click', () => setActiveAlgorithm('cocktail'));
    btnBogo.addEventListener('click', () => setActiveAlgorithm('bogosort'));

    elStepSlider.addEventListener('input', () => {
      state.currentStep = parseInt(elStepSlider.value, 10);
      elStepValue.textContent = String(state.currentStep);
      requestRedraw();
    });

    btnPlayPause.addEventListener('click', () => {
      if (state.isPlaying) {
        stopPlaying();
      } else {
        startPlaying();
      }
    });

    btnStepBackward.addEventListener('click', () => {
      const current = parseInt(elStepSlider.value, 10);
      if (current > 1) {
        elStepSlider.value = String(current - 1);
        state.currentStep = current - 1;
        elStepValue.textContent = String(state.currentStep);
        requestRedraw();
      }
    });

    btnStepForward.addEventListener('click', () => {
      const current = parseInt(elStepSlider.value, 10);
      const max = parseInt(elStepSlider.max, 10);
      if (current < max) {
        elStepSlider.value = String(current + 1);
        state.currentStep = current + 1;
        elStepValue.textContent = String(state.currentStep);
        requestRedraw();
      }
    });

    btnResetStep.addEventListener('click', () => {
      elStepSlider.value = '1';
      state.currentStep = 1;
      elStepValue.textContent = '1';
      requestRedraw();
    });

    function startPlaying() {
      if (state.isPlaying) return;
      state.isPlaying = true;
      btnPlayPause.textContent = '❚❚';
      const tick = () => {
        const current = parseInt(elStepSlider.value, 10);
        const max = parseInt(elStepSlider.max, 10);
        if (current < max) {
          elStepSlider.value = String(current + 1);
          state.currentStep = current + 1;
          elStepValue.textContent = String(state.currentStep);
          requestRedraw();
        } else {
          stopPlaying();
        }
      };
      state.playTimer = setInterval(tick, 200);
    }

    function stopPlaying() {
      if (state.playTimer) {
        clearInterval(state.playTimer);
        state.playTimer = null;
      }
      state.isPlaying = false;
      btnPlayPause.textContent = '▶';
    }

    // p5.js handling
    let p5Canvas;
    const vizArea = document.getElementById('visualization-area');

    // Drawing config
    const margins = { left: 50, right: 20, top: 20, bottom: 30 };
    let canvasW = 800;
    let canvasH = 420;

    function computeCanvasSize() {
      const rect = vizArea.getBoundingClientRect();
      const w = Math.max(300, Math.floor(rect.width) - 0);
      const h = 420; // fixed height for this demo
      canvasW = w;
      canvasH = h;
    }

    function requestRedraw() {
      if (typeof window.redraw === 'function') {
        window.redraw();
      }
    }

    new p5((p) => {
      p.setup = () => {
        // Remove/rename placeholder canvas with the same id to avoid duplicate ids
        const placeholder = document.getElementById('bar-chart-canvas');
        if (placeholder) {
          placeholder.id = 'bar-chart-canvas-placeholder';
          placeholder.style.display = 'none';
        }
        computeCanvasSize();
        p5Canvas = p.createCanvas(canvasW, canvasH);
        // Assign the required id to the p5 canvas element
        p5Canvas.elt.id = 'bar-chart-canvas';
        p.pixelDensity(1);
        p.colorMode(p.HSB, 360, 100, 100);
        p.noLoop();

        // Initialization: default array with 10 elements and shellsort
        setActiveAlgorithm('shellsort');
      };

      p.windowResized = () => {
        computeCanvasSize();
        p.resizeCanvas(canvasW, canvasH);
        requestRedraw();
      };

      p.draw = () => {
        p.background(0, 0, 100); // white background in HSB

        // Draw axes
        const chartX = margins.left;
        const chartY = margins.top;
        const chartW = canvasW - margins.left - margins.right;
        const chartH = canvasH - margins.top - margins.bottom;

        // Axis lines
        p.stroke(0, 0, 20);
        p.strokeWeight(1);
        // Y-axis
        p.line(chartX, chartY, chartX, chartY + chartH);
        // X-axis
        p.line(chartX, chartY + chartH, chartX + chartW, chartY + chartH);

        // Y-axis ticks and labels (fixed at 0..20)
        p.fill(0, 0, 20);
        p.noStroke();
        p.textSize(12);
        p.textAlign(p.RIGHT, p.CENTER);
        const maxAxis = 20;
        const tickStep = 5;
        for (let v = 0; v <= maxAxis; v += tickStep) {
          const ty = p.map(v, 0, maxAxis, chartY + chartH, chartY);
          p.stroke(0, 0, 60);
          p.line(chartX - 4, ty, chartX, ty);
          p.noStroke();
          p.text(String(v), chartX - 8, ty);
        }

        // Draw bars from current history step
        const hist = state.sortHistory;
        if (!hist || hist.length === 0) return;

        const stepIndex = Math.min(state.currentStep, Math.max(0, hist.length - 1)); // clamp
        const arr = hist[stepIndex] || hist[hist.length - 1];
        const N = arr.length;

        if (N <= 0) return;

        const barGap = 2;
        const barW = Math.max(1, (chartW - (N - 1) * barGap) / N);

        p.stroke(0, 0, 0);
        p.strokeWeight(1);

        for (let i = 0; i < N; i++) {
          const v = arr[i];
          const h = p.map(v, 1, N, 0, chartH);
          const x = chartX + i * (barW + barGap);
          const y = chartY + chartH - h;

          // Stable color by value: hue from 240 (blue) to 0 (red)
          const hue = p.map(v, 1, N, 240, 0);
          p.fill(hue, 80, 90);

          p.rect(x, y, barW, h);
        }

        // Title / info
        p.noStroke();
        p.fill(0, 0, 20);
        p.textAlign(p.LEFT, p.TOP);
        p.textSize(12);
        const algoName = state.currentAlgorithm;
        const stepsTotal = Math.max(1, hist.length - 1);
        const info = `algorithm: ${algoName}   elements: ${state.numElements}   step: ${state.currentStep}/${stepsTotal}`;
        p.text(info, chartX, 4);
      };
    });

    // Ensure initial elements value matches slider (in case)
    elElementsValue.textContent = elElementsSlider.value;
  </script>
</body>
</html>
