<!-- CDN for Plotly.js -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script>
(function() {
    // Element references
    const sliders = {
        x1: document.getElementById('slider-x1'),
        v1: document.getElementById('slider-v1'),
        x2: document.getElementById('slider-x2'),
        v2: document.getElementById('slider-v2')
    };
    const values = {
        x1: document.getElementById('value-x1'),
        v1: document.getElementById('value-v1'),
        x2: document.getElementById('value-x2'),
        v2: document.getElementById('value-v2')
    };
    const btnReset = document.getElementById('btn-reset');
    const resultText = document.getElementById('result-text');
    const plotDiv = document.getElementById('plot-container');

    // Defaults from spec
    const DEFAULTS = {
        x1: 500,
        v1: -10,
        x2: 100,
        v2: 5
    };

    let hasPlotted = false;

    // Utility: number formatting
    const fmt2 = (n) => (Number.isFinite(n) ? n.toFixed(2) : '—');

    function computeIntersection(x1, v1, x2, v2) {
        const EPS = 1e-12;
        const denom = (v2 - v1);
        if (Math.abs(denom) < EPS) {
            return { intersects: false, T: null, X: null };
        }
        const T = (x1 - x2) / denom;
        const X = x1 + v1 * T;
        const inWindow = (T >= 0 && T <= 1000);
        return { intersects: inWindow, T, X };
    }

    function updateDisplays(x1, v1, x2, v2) {
        values.x1.textContent = String(Math.round(x1));
        values.v1.textContent = fmt2(v1);
        values.x2.textContent = String(Math.round(x2));
        values.v2.textContent = fmt2(v2);
    }

    function plot(x1, v1, x2, v2, info) {
        const t0 = 0, t1 = 1000;

        const trace1 = {
            type: 'scatter',
            mode: 'lines',
            name: 'Runner 1',
            x: [t0, t1],
            y: [x1, x1 + v1 * t1],
            line: { color: 'rgb(70,130,180)', width: 3 }
        };
        const trace2 = {
            type: 'scatter',
            mode: 'lines',
            name: 'Runner 2',
            x: [t0, t1],
            y: [x2, x2 + v2 * t1],
            line: { color: 'rgb(255,165,0)', width: 3 }
        };

        const traces = [trace1, trace2];

        if (info.intersects) {
            traces.push({
                type: 'scatter',
                mode: 'markers',
                name: 'Intersection',
                x: [info.T],
                y: [info.X],
                marker: {
                    color: 'rgb(34,139,34)',
                    size: 10,
                    symbol: 'circle',
                    line: { color: 'rgb(17,99,17)', width: 1.5 }
                },
                hovertemplate: 't=%{x:.2f}s<br>x=%{y:.2f}m<extra></extra>'
            });
        }

        const layout = {
            xaxis: {
                title: 't',
                range: [0, 1000],
                showgrid: true,
                gridcolor: '#e5e7eb',
                zeroline: false
            },
            yaxis: {
                title: 'distance',
                autorange: true,
                showgrid: true,
                gridcolor: '#e5e7eb',
                zeroline: true,
                zerolinecolor: '#6b7280',
                zerolinewidth: 1.5
            },
            showlegend: false,
            margin: { t: 20, r: 20, b: 40, l: 60 }
        };

        if (!hasPlotted) {
            Plotly.newPlot(plotDiv, traces, layout, { responsive: true, displayModeBar: false });
            hasPlotted = true;
        } else {
            Plotly.react(plotDiv, traces, layout, { responsive: true, displayModeBar: false });
        }
    }

    function update() {
        const x1 = parseFloat(sliders.x1.value);
        const v1 = parseFloat(sliders.v1.value);
        const x2 = parseFloat(sliders.x2.value);
        const v2 = parseFloat(sliders.v2.value);

        updateDisplays(x1, v1, x2, v2);

        const info = computeIntersection(x1, v1, x2, v2);

        if (info.intersects) {
            resultText.textContent = `T = ${fmt2(info.T)} s | X = ${fmt2(info.X)} m`;
        } else {
            resultText.textContent = "The runners' paths never intersect";
        }

        plot(x1, v1, x2, v2, info);
    }

    function attachListeners() {
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', update);
            slider.addEventListener('change', update);
        });
        btnReset.addEventListener('click', () => {
            sliders.x1.value = DEFAULTS.x1;
            sliders.v1.value = DEFAULTS.v1;
            sliders.x2.value = DEFAULTS.x2;
            sliders.v2.value = DEFAULTS.v2;
            update();
        });
        window.addEventListener('resize', () => {
            if (hasPlotted) Plotly.Plots.resize(plotDiv);
        });
    }

    // Initialize
    function init() {
        // Ensure default displays before first plot
        sliders.x1.value = DEFAULTS.x1;
        sliders.v1.value = DEFAULTS.v1;
        sliders.x2.value = DEFAULTS.x2;
        sliders.v2.value = DEFAULTS.v2;
        attachListeners();
        update();
    }

    // Start after DOM is ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
    } else {
        init();
    }
})();
</script>
</body>
</html>
