<body>
<div id="app">
  <div class="control-panel">
    <label for="slider-phase">phosphorus cycle</label>
    <input type="range" id="slider-phase" min="0" max="8" step="1" value="0">
    <span>Phase:</span>
    <span id="slider-value">0</span>
    <button id="btn-reset" title="Reset to phase 0">↻ Reset</button>
  </div>
  <div id="canvas-container"></div>
</div>

<!-- p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

<script>
  // DOM elements
  const slider = document.getElementById('slider-phase');
  const sliderValueEl = document.getElementById('slider-value');
  const btnReset = document.getElementById('btn-reset');

  function updateSliderDisplay() {
    sliderValueEl.textContent = slider.value;
  }

  slider.addEventListener('input', updateSliderDisplay);
  btnReset.addEventListener('click', () => {
    slider.value = 0;
    updateSliderDisplay();
  });

  updateSliderDisplay(); // initialize

  // Tooltip content dictionary
  const TOOLTIP = {
    'Clouds': 'Water vapor condensed in the atmosphere.',
    'Rocks': 'A primary reservoir of phosphorus in the form of phosphate.',
    'Animals': 'Obtain phosphorus by eating plants or other animals.',
    'Plants': 'Absorb phosphate from the soil to build organic molecules.',
    'Water': 'Phosphorus is carried into water bodies by runoff and leaching.',
    'Erosion': 'Weathering of rocks by wind and water releases phosphate.',
    'Runoff': 'Phosphate from fertilizers and rocks is washed into rivers and oceans.',
    'Soil': 'Phosphorus in the soil is available for plants.',
    'Leaching': 'Phosphates in the soil are dissolved in water and carried away.',
    'Plant uptake': 'Plants absorb inorganic phosphate from the soil through their roots.',
    'Animals eat plants': 'Transfers phosphorus from plants to animals along the food chain.',
    'Decomposition': 'Bacteria break down dead organic matter, returning phosphate to the soil and water.',
    'Geologic uplift': 'Over millions of years, ocean sediments containing phosphate are lifted up to form new land.'
  };

  // p5.js sketch
  let labelBoxes = []; // {x, y, w, h, text, tooltip}
  let hoveredLabel = null;

  const CANVAS_W = 600;
  const CANVAS_H = 500;
  const SKY_H = 250;
  const GROUND_Y = SKY_H;

  function setup() {
    const cnv = createCanvas(CANVAS_W, CANVAS_H);
    cnv.parent('canvas-container');
    textFont('Arial, Helvetica, sans-serif');
    frameRate(30);
  }

  function draw() {
    const phase = parseInt(slider.value, 10);
    labelBoxes = [];
    hoveredLabel = null;

    drawBackground();
    drawSun();
    drawCloudsAndRain();
    drawRocks();
    drawAnimal();
    drawPlants();
    drawWater();

    drawStaticLabels(); // Clouds, Rocks, Animals, Plants, Water

    // Dynamic process arrows and labels (cumulative)
    stroke(0);
    strokeWeight(2);
    fill(0);

    if (phase >= 1) {
      drawArrow(120, 130, 220, 310, 'Erosion', 1);
    }
    if (phase >= 2) {
      drawArrow(220, 340, 120, 410, 'Runoff', -1);
    }
    if (phase >= 3) {
      drawArrow(130, 420, 300, 430, 'Soil', 1);
    }
    if (phase >= 4) {
      drawArrow(240, 360, 150, 395, 'Leaching', -1);
    }
    if (phase >= 5) {
      drawArrow(340, 430, 470, 360, 'Plant uptake', 1);
    }
    if (phase >= 6) {
      drawArrow(490, 340, 360, 320, 'Animals eat plants', 1);
    }
    if (phase >= 7) {
      drawArrow(340, 350, 360, 430, 'Decomposition', -1);
    }
    if (phase >= 8) {
      drawCurvedArrow(
        {x: 90, y: 450},
        {x: 130, y: 500},
        {x: 200, y: 400},
        {x: 210, y: 320},
        'Geologic uplift'
      );
    }

    // Hover detection
    hoveredLabel = hitTestLabels(mouseX, mouseY);

    if (hoveredLabel) {
      cursor('pointer');
      showTooltip(mouseX, mouseY, hoveredLabel.tooltip);
    } else {
      cursor('default');
    }
  }

  function drawBackground() {
    noStroke();
    // Sky
    fill('#CAE9FF');
    rect(0, 0, width, SKY_H);
    // Ground
    fill('#9B7653');
    rect(0, GROUND_Y, width, height - GROUND_Y);
  }

  function drawSun() {
    // Sun
    push();
    translate(540, 70);
    noStroke();
    fill('#FFDD00');
    ellipse(0, 0, 80, 80);
    stroke('#FFDD00');
    strokeWeight(3);
    for (let a = 0; a < TWO_PI; a += PI / 8) {
      const r1 = 50;
      const r2 = 75;
      const x1 = r1 * cos(a);
      const y1 = r1 * sin(a);
      const x2 = r2 * cos(a);
      const y2 = r2 * sin(a);
      line(x1, y1, x2, y2);
    }
    pop();
  }

  function drawCloudsAndRain() {
    // Clouds
    noStroke();
    fill('#E0E0E0');
    ellipse(90, 90, 90, 50);
    ellipse(120, 80, 100, 60);
    ellipse(145, 95, 90, 50);
    ellipse(110, 105, 110, 50);
    ellipse(75, 85, 70, 40);

    // Rain (slanted)
    stroke('#0000FF');
    strokeWeight(2);
    for (let i = 70; i <= 160; i += 14) {
      line(i, 120, i - 8, 138);
      line(i + 5, 130, i - 3, 148);
    }
  }

  function drawRocks() {
    // Rocks cluster
    noStroke();
    fill('#5A5A5A');
    ellipse(210, 330, 90, 60);
    ellipse(245, 335, 70, 50);
    ellipse(230, 315, 60, 40);
    // subtle highlights
    fill(255, 30);
    ellipse(200, 320, 20, 10);
    ellipse(240, 330, 16, 8);
  }

  function drawAnimal() {
    // Stylized cow-like animal
    push();
    stroke(0);
    strokeWeight(2);
    fill('#FFFFFF');
    // Body
    ellipse(320, 320, 120, 60);
    // Head
    ellipse(380, 310, 40, 30);
    // Legs
    fill('#000000');
    rect(290, 350, 8, 20, 2);
    rect(310, 350, 8, 20, 2);
    rect(330, 350, 8, 20, 2);
    rect(350, 350, 8, 20, 2);
    // Spots
    fill('#000000');
    ellipse(300, 315, 18, 12);
    ellipse(325, 330, 14, 10);
    ellipse(340, 310, 16, 12);
    // Eye
    fill('#1E90FF');
    ellipse(390, 305, 6, 6);
    pop();
  }

  function drawPlants() {
    // Three small light-green triangles (spruce-like)
    fill('#90EE90');
    stroke('#006400');
    strokeWeight(1.5);
    // left plant
    triangle(460, 350, 450, 350, 455, 320);
    // middle plant
    triangle(480, 360, 468, 360, 474, 315);
    // right plant
    triangle(505, 350, 493, 350, 499, 318);
  }

  function drawWater() {
    noStroke();
    fill(0, 0, 255, 150);
    ellipse(80, 430, 120, 60);
    ellipse(125, 440, 140, 70);
  }

  function drawStaticLabels() {
    // Clouds label inside the cloud
    addLabel(95, 90, 'Clouds', '#505050', TOOLTIP['Clouds'], 14);

    // Rocks label below rocks
    addLabel(200, 372, 'Rocks', '#000000', TOOLTIP['Rocks'], 14);

    // Animals label below animal
    addLabel(300, 370, 'Animals', '#000000', TOOLTIP['Animals'], 14);

    // Plants label next to plants
    addLabel(515, 350, 'Plants', '#006400', TOOLTIP['Plants'], 14);

    // Water label inside water
    addLabel(65, 415, 'Water', '#00008B', TOOLTIP['Water'], 14);
  }

  // Helper: draw straight arrow with triangular head and its label
  function drawArrow(x1, y1, x2, y2, labelText, labelSide = 1) {
    push();
    stroke(0);
    strokeWeight(2);
    fill(0);

    // vector and angle
    const dx = x2 - x1;
    const dy = y2 - y1;
    const angle = atan2(dy, dx);

    // arrow head size
    const head = 10;
    const len = dist(x1, y1, x2, y2);
    const bx = x2 - cos(angle) * head * 1.2;
    const by = y2 - sin(angle) * head * 1.2;

    // line
    line(x1, y1, bx, by);

    // head (triangle)
    push();
    translate(x2, y2);
    rotate(angle);
    triangle(-head, -6, -head, 6, 0, 0);
    pop();

    // Label placement near the middle, offset perpendicular to line
    const mx = (x1 + x2) / 2;
    const my = (y1 + y2) / 2;
    const nx = -sin(angle); // normal vector
    const ny = cos(angle);
    const offset = 14 * (labelSide >= 0 ? 1 : -1);
    const lx = mx + nx * offset;
    const ly = my + ny * offset;

    addLabel(lx, ly, labelText, '#000000', TOOLTIP[labelText], 13);
    pop();
  }

  // Helper: draw curved arrow using Bezier and place label along it
  function drawCurvedArrow(p0, p1, p2, p3, labelText) {
    push();
    noFill();
    stroke(0);
    strokeWeight(2);
    // curve
    bezier(p0.x, p0.y, p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);

    // Arrowhead at end, direction = tangent at t ~ 1
    const t = 0.99;
    const tx = bezierTangent(p0.x, p1.x, p2.x, p3.x, t);
    const ty = bezierTangent(p0.y, p1.y, p2.y, p3.y, t);
    const ang = atan2(ty, tx);
    const head = 10;

    push();
    translate(p3.x, p3.y);
    rotate(ang);
    fill(0);
    noStroke();
    triangle(-head, -6, -head, 6, 0, 0);
    pop();

    // Label near t=0.6, offset by normal
    const tl = 0.6;
    const lx = bezierPoint(p0.x, p1.x, p2.x, p3.x, tl);
    const ly = bezierPoint(p0.y, p1.y, p2.y, p3.y, tl);
    const tx2 = bezierTangent(p0.x, p1.x, p2.x, p3.x, tl);
    const ty2 = bezierTangent(p0.y, p1.y, p2.y, p3.y, tl);
    const nLen = sqrt(tx2 * tx2 + ty2 * ty2) || 1;
    const nx = -ty2 / nLen;
    const ny = tx2 / nLen;
    addLabel(lx + nx * 14, ly + ny * 14, labelText, '#000000', TOOLTIP[labelText], 13);
    pop();
  }

  // Add a text label to canvas and register its hover bounding box
  function addLabel(x, y, textStr, colorStr, tooltipText, sizePx = 14) {
    push();
    textSize(sizePx);
    textAlign(LEFT, BASELINE);
    fill(colorStr);
    noStroke();
    text(textStr, x, y);

    const w = textWidth(textStr);
    const asc = textAscent();
    const desc = textDescent();
    const box = {
      x: x,
      yTop: y - asc,
      yBottom: y + desc,
      w: w,
      h: asc + desc,
      text: textStr,
      tooltip: tooltipText
    };
    labelBoxes.push(box);
    pop();
  }

  // Hit test mouse against label boxes
  function hitTestLabels(mx, my) {
    for (let i = labelBoxes.length - 1; i >= 0; i--) {
      const b = labelBoxes[i];
      if (mx >= b.x && mx <= b.x + b.w && my >= b.yTop && my <= b.yBottom) {
        return b;
      }
    }
    return null;
  }

  // Tooltip rendering with word wrap
  function showTooltip(mx, my, textStr) {
    push();
    const padX = 10;
    const padY = 8;
    const maxW = 260;
    textSize(12);
    textLeading(16);
    textAlign(LEFT, TOP);
    const lines = wrapText(textStr, maxW);
    let boxW = 0;
    for (const line of lines) {
      boxW = max(boxW, textWidth(line));
    }
    const lineH = textAscent() + textDescent() + 2;
    const boxH = lines.length * lineH;

    let tx = mx + 12;
    let ty = my + 12;

    if (tx + boxW + padX * 2 > width) {
      tx = mx - (boxW + padX * 2) - 12;
    }
    if (ty + boxH + padY * 2 > height) {
      ty = height - (boxH + padY * 2) - 6;
    }

    // background
    noStroke();
    fill(255, 255, 255, 235);
    rect(tx, ty, boxW + padX * 2, boxH + padY * 2, 8);
    // border
    noFill();
    stroke(30, 30, 30, 220);
    rect(tx, ty, boxW + padX * 2, boxH + padY * 2, 8);

    // text
    noStroke();
    fill(30);
    let yOffset = ty + padY;
    for (const line of lines) {
      text(line, tx + padX, yOffset);
      yOffset += lineH;
    }
    pop();
  }

  function wrapText(str, maxWidth) {
    const words = str.split(' ');
    const lines = [];
    let current = '';
    for (let i = 0; i < words.length; i++) {
      const test = current ? current + ' ' + words[i] : words[i];
      if (textWidth(test) <= maxWidth) {
        current = test;
      } else {
        if (current) lines.push(current);
        // if a single word longer than maxWidth, hard-split
        if (textWidth(words[i]) > maxWidth) {
          let partial = '';
          for (const ch of words[i]) {
            const test2 = partial + ch;
            if (textWidth(test2) <= maxWidth) {
              partial = test2;
            } else {
              if (partial) lines.push(partial);
              partial = ch;
            }
          }
          current = partial;
        } else {
          current = words[i];
        }
      }
    }
    if (current) lines.push(current);
    return lines;
  }
</script>
