    // Utility: build a circular layout of N vertices within given canvas size
    function buildCircularVertices(n, cx, cy, radius, startAngleDeg = -90) {
      const verts = [];
      for (let i = 0; i < n; i++) {
        const angle = (startAngleDeg + i * (360 / n)) * Math.PI / 180;
        const x = cx + radius * Math.cos(angle);
        const y = cy + radius * Math.sin(angle);
        verts.push({ x, y });
      }
      return verts;
    }

    // Build a rich set of edges for a circular graph:
    // - cycle edges (i, i+1)
    // - skip-2 edges (i, i+2)
    // - opposite edges (i, i+N/2) for i < N/2 (N even)
    function buildCircularEdges(n) {
      const edges = [];
      const addEdge = (u, v) => {
        const a = Math.min(u, v), b = Math.max(u, v);
        // de-duplicate
        if (!edges.some(e => (e.u === a && e.v === b))) {
          edges.push({ u: a, v: b });
        }
      };
      // cycle
      for (let i = 0; i < n; i++) addEdge(i, (i + 1) % n);
      // skip-2
      for (let i = 0; i < n; i++) addEdge(i, (i + 2) % n);
      // opposite chords (n is 28 here -> 14 opposites)
      for (let i = 0; i < n / 2; i++) addEdge(i, (i + n / 2) % n);
      return edges;
    }

    // Predefine matching index arrays for convenience
    function evenCycleEdgeIndices(n) {
      // cycle edges are indices 0..n-1 in our construction
      const res = [];
      for (let i = 0; i < n - 2; i += 2) res.push(i); // up to n-4 => 0..24 for n=28
      return res;
    }
    function oddCycleEdgeIndices(n) {
      const res = [];
      for (let i = 1; i < n - 1; i += 2) res.push(i); // 1..25 for n=28
      return res;
    }

    // Build Example Data
    function buildExampleData() {
      const N = 28;
      const canvasW = 600, canvasH = 450;
      const cx = canvasW / 2, cy = canvasH / 2;
      const radius = Math.min(canvasW, canvasH) * 0.38;

      // Shared geometry for both examples
      const vertices = buildCircularVertices(N, cx, cy, radius, -90);
      const edges = buildCircularEdges(N); // includes cycle, skip-2, and opposite chords

      // Helper to clone an array
      const clone = (arr) => JSON.parse(JSON.stringify(arr));

      // Example 1
      const e1_step1_matching = evenCycleEdgeIndices(N); // 13 red edges: 0,2,...,24
      const e1_step11_matching = [...e1_step1_matching, 26]; // add (26,27) -> 14 blue edges

      const example1Steps = [
