      appState.currentExample = exampleId;

      // Update example button active states
      setActiveButton([exampleBtn1, exampleBtn2], exampleId === "1" ? exampleBtn1 : exampleBtn2);

      // Reset to step 1
      appState.currentStep = 1;
      setActiveButton(stepButtons, stepButtons[0]);
      updateDescription();
      if (p5Instance) p5Instance.redraw();
    }

    function setStep(stepNum) {
      if (stepNum < 1 || stepNum > 11) return;
      appState.currentStep = stepNum;
      setActiveButton(stepButtons, stepButtons[stepNum - 1]);
      updateDescription();
      if (p5Instance) p5Instance.redraw();
    }

    exampleBtn1.addEventListener("click", () => setExample("1"));
    exampleBtn2.addEventListener("click", () => setExample("2"));

    stepButtons.forEach((btn, idx) => {
