        const cnv = p.createCanvas(W, H);
        cnv.parent("canvas-container");
        p.noLoop();
        p.pixelDensity(1);
      };

      function drawEdge(u, v, col, sw) {
        const verts = demoData[appState.currentExample].vertices;
        const a = verts[u], b = verts[v];
        p.stroke(col);
        p.strokeWeight(sw);
        p.line(a.x, a.y, b.x, b.y);
