      function drawBlossom(blossom) {
        if (!blossom) return;
        const verts = demoData[appState.currentExample].vertices;
        p.push();
        p.fill(0, 0, 255, 76); // ~0.3 alpha
        p.stroke(0);
        p.strokeWeight(1);
        p.beginShape();
        blossom.vertices.forEach(idx => {
          const v = verts[idx];
          p.vertex(v.x, v.y);
        });
        p.endShape(p.CLOSE);

        // Draw super-vertex at topVertex
        const top = verts[blossom.topVertex];
        p.stroke(0);
        p.strokeWeight(1);
        p.fill("#ffff00");
        p.circle(top.x, top.y, 16);
        p.pop();
