        p.push();
        edges.forEach((e, idx) => {
          if (!matchedIndexSet.has(idx)) {
            drawEdge(e.u, e.v, "#cccccc", 2);
          }
        });
        p.pop();

        // 2) Draw matched edges (thick red or blue)
        const colorMatched = stepData.isFinal ? "#4d4dff" : "#ff4d4d";
        p.push();
        stepData.matching.forEach(mi => {
          const e = edges[mi];
          drawEdge(e.u, e.v, colorMatched, 6);
        });
        p.pop();

        // 3) Draw blossom if any
        drawBlossom(stepData.blossom);

        // 4) Draw vertices (on top)
        p.push();
        p.noStroke();
        p.fill(0);
        example.vertices.forEach(v => {
          p.circle(v.x, v.y, 8);
        });
        p.pop();
      };
    };

    // Initialize description and p5
    updateDescription();
    p5Instance = new p5(sketch);

    // Ensure initial state matches requirements
    // Example 1 selected, Step 1 selected, description already set above.
  </script>
