  /* Control Panel */
  #control-panel {
    width: var(--panel-width);
    min-width: var(--panel-width);
    max-width: var(--panel-width);
    border-right: 1px solid var(--border-color);
    padding: 14px 12px;
    box-sizing: border-box;
    overflow-y: auto;
