<script type="module">
  import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
  import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
  import { Brush, Evaluator, SUBTRACTION } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

  // DOM Elements
  const canvas = document.getElementById('main-canvas');
  const vizContainer = document.getElementById('visualization-container');

  const sliders = {
    cylinderRadius: document.getElementById('slider-cylinder-radius'),
    opacity: document.getElementById('slider-opacity'),
    drillRadius: document.getElementById('slider-drill-radius'),
    inclination: document.getElementById('slider-inclination'),
    rotation: document.getElementById('slider-rotation'),
    offset: document.getElementById('slider-offset')
  };
  const labels = {
    cylinderRadius: document.getElementById('label-cylinder-radius'),
    opacity: document.getElementById('label-opacity'),
    drillRadius: document.getElementById('label-drill-radius'),
    inclination: document.getElementById('label-inclination'),
    rotation: document.getElementById('label-rotation'),
    offset: document.getElementById('label-offset')
  };
  const checkboxBoundaryOnly = document.getElementById('checkbox-boundary-only');

  // Three.js setup
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xE0E0E0);

  const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
  camera.position.set(10, 8, 12);
  camera.lookAt(0, 0, 0);

  const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

  const controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.enablePan = true;
  controls.minDistance = 3;
  controls.maxDistance = 60;

  // Lights
  scene.add(new THREE.AmbientLight(0xffffff, 0.6));
  const dir1 = new THREE.DirectionalLight(0xffffff, 0.8);
  dir1.position.set(5, 8, 5);
  scene.add(dir1);
  const dir2 = new THREE.DirectionalLight(0xffffff, 0.5);
  dir2.position.set(-6, -4, -3);
  scene.add(dir2);

  // Resize handling
  function resizeRendererToDisplaySize() {
    const w = vizContainer.clientWidth;
    const h = vizContainer.clientHeight;
    const needsResize = canvas.width !== w || canvas.height !== h;
    if (needsResize) {
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
    }
    return needsResize;
  }
  window.addEventListener('resize', resizeRendererToDisplaySize);
  resizeRendererToDisplaySize();

  // Common materials/colors
  const tanColor = new THREE.Color('#D2B48C');
  const orangeColor = new THREE.Color('#FFA500');
  const blueColor = new THREE.Color('#4169E1');

  // Evaluator for CSG
  const evaluator = new Evaluator();

  // Holder for current objects to clear on update
  let objectsGroup = new THREE.Group();
  scene.add(objectsGroup);

  // Helpers to dispose
  function disposeObject3D(obj) {
    obj.traverse(child => {
      if (child.geometry) child.geometry.dispose();
      if (child.material) {
        if (Array.isArray(child.material)) child.material.forEach(m => m.dispose && m.dispose());
        else child.material.dispose && child.material.dispose();
      }
    });
  }

  // UI label updates
  function to2(v) {
    const n = Number(v);
    return (Math.round(n * 100) / 100).toFixed(2);
  }
  function updateLabels() {
    labels.cylinderRadius.textContent = to2(sliders.cylinderRadius.value);
    labels.opacity.textContent = to2(sliders.opacity.value);
    labels.drillRadius.textContent = to2(sliders.drillRadius.value);
    labels.inclination.textContent = to2(sliders.inclination.value);
    labels.rotation.textContent = to2(sliders.rotation.value);
    labels.offset.textContent = to2(sliders.offset.value);
  }

  function getParams() {
    return {
      R: parseFloat(sliders.cylinderRadius.value),
      alpha: parseFloat(sliders.opacity.value),
      r: parseFloat(sliders.drillRadius.value),
      inc: parseFloat(sliders.inclination.value),
      rotY: parseFloat(sliders.rotation.value),
      offY: parseFloat(sliders.offset.value),
      boundaryOnly: checkboxBoundaryOnly.checked
    };
  }

  // Main updateScene function
  function updateScene() {
    // Clear previous objects
    scene.remove(objectsGroup);
    disposeObject3D(objectsGroup);
    objectsGroup = new THREE.Group();
    scene.add(objectsGroup);

    updateLabels();
    const { R, alpha, r, inc, rotY, offY, boundaryOnly } = getParams();

    const H = 8;           // main cylinder height
    const drillH = 10;     // drill cylinder height (long enough to pass through)

    // Main cylinder (for CSG and also for optional ghost)
    const mainGeom = new THREE.CylinderGeometry(R, R, H, 128, 1, false);
    const baseMat = new THREE.MeshStandardMaterial({
      color: tanColor,
      roughness: 0.65,
      metalness: 0.0,
      transparent: true,
      opacity: boundaryOnly ? 0.15 : alpha
    });

    // Brush A: main cylinder
    const mainBrush = new Brush(mainGeom, baseMat);
    mainBrush.updateMatrixWorld(true);

    // Drill cylinder (axis along X after Z-rotation)
    const drillGeom = new THREE.CylinderGeometry(r, r, drillH, 96, 1, true);
    const drillMat = new THREE.MeshStandardMaterial({
      color: 0x888888,
      transparent: true,
      opacity: 0.2
    });
    const drillBrush = new Brush(drillGeom, drillMat);
    drillBrush.rotation.z = Math.PI / 2;   // make it horizontal (axis from Y -> X)
    drillBrush.rotation.x = inc;           // inclination around X
    drillBrush.rotation.y = rotY;          // rotation around vertical (world Y)
    drillBrush.position.y = offY;          // vertical offset
    drillBrush.updateMatrixWorld(true);

    // CSG: main - drill
    let resultMesh = null;
    try {
      resultMesh = evaluator.evaluate(mainBrush, drillBrush, SUBTRACTION);
    } catch (e) {
      // In case of any temporary CSG error, skip gracefully
      resultMesh = null;
      console.warn('CSG evaluation failed:', e);
    }

    if (resultMesh) {
      resultMesh.material = new THREE.MeshStandardMaterial({
        color: tanColor,
        roughness: 0.65,
        metalness: 0.0,
        transparent: true,
        opacity: boundaryOnly ? 0.0 : alpha
      });
      resultMesh.castShadow = false;
      resultMesh.receiveShadow = false;
      resultMesh.visible = !boundaryOnly;
      objectsGroup.add(resultMesh);
    }

    // Ghost main cylinder (visible when boundary-only)
    const ghostMat = new THREE.MeshStandardMaterial({
      color: boundaryOnly ? new THREE.Color('#f0f0f0') : new THREE.Color('#000000'),
      transparent: true,
      opacity: boundaryOnly ? 0.15 : 0.0,
      roughness: 0.5,
      metalness: 0.0
    });
    const ghostMesh = new THREE.Mesh(mainGeom.clone(), ghostMat);
    ghostMesh.visible = boundaryOnly;
    objectsGroup.add(ghostMesh);

    // Boundary curve creation
    // We compute the intersection curve of the drill cylinder surface with the main cylinder surface
    // Param: for each t in [0, 2π), consider the generator line of the drill cylinder surface and
    // intersect with the main cylinder x^2 + z^2 = R^2.

    // Build orthonormal basis from drillBrush world transform
    const q = new THREE.Quaternion();
    drillBrush.getWorldQuaternion(q);
    const c = new THREE.Vector3();
    drillBrush.getWorldPosition(c);

    // Axis (direction) is local X in world
    const d = new THREE.Vector3(1, 0, 0).applyQuaternion(q).normalize();
    // u and v span the cross-section plane (local Y and Z in world)
    const u = new THREE.Vector3(0, 1, 0).applyQuaternion(q).normalize();
    const v = new THREE.Vector3(0, 0, 1).applyQuaternion(q).normalize();

    const frontPts = [];
    const backPts = [];

    const N = 256;
    for (let i = 0; i < N; i++) {
      const t = i / N * Math.PI * 2;
      const cosT = Math.cos(t);
      const sinT = Math.sin(t);

      // Point on the surface line (at offset s along axis)
      const p0 = new THREE.Vector3().copy(c)
        .addScaledVector(u, r * cosT)
        .addScaledVector(v, r * sinT);

      // Solve (p0 + s d) projected onto XZ satisfies x^2 + z^2 = R^2
      const dx = d.x, dz = d.z;
      const px = p0.x, pz = p0.z;

      const A = dx * dx + dz * dz;
      const B = 2 * (px * dx + pz * dz);
      const C = px * px + pz * pz - R * R;

      if (A < 1e-10) continue; // axis nearly parallel to Y, skip to avoid numerical issues

      const disc = B * B - 4 * A * C;
      if (disc < 0) continue; // no intersection for this t

      const sqrtD = Math.sqrt(Math.max(0, disc));
      const s1 = (-B + sqrtD) / (2 * A);
      const s2 = (-B - sqrtD) / (2 * A);

      const P1 = new THREE.Vector3().copy(p0).addScaledVector(d, s1);
      const P2 = new THREE.Vector3().copy(p0).addScaledVector(d, s2);

      // Split into two curves based on sign of local sin(t) (local Z around drill axis)
      if (sinT >= 0) {
        frontPts.push(P1);
        backPts.push(P2);
      } else {
        frontPts.push(P2);
        backPts.push(P1);
      }
    }

    // Create tube meshes for the two boundary curves if there are enough points
    const tubes = [];
    if (frontPts.length > 2) {
      const frontCurve = new THREE.CatmullRomCurve3(frontPts, true, 'centripetal', 0.5);
      const frontTubeGeo = new THREE.TubeGeometry(frontCurve, 800, 0.05, 12, true);
      const frontMat = new THREE.MeshStandardMaterial({ color: orangeColor, roughness: 0.4, metalness: 0.0, emissive: 0x552200, emissiveIntensity: 0.15 });
      const frontTube = new THREE.Mesh(frontTubeGeo, frontMat);
      tubes.push(frontTube);
    }
    if (backPts.length > 2) {
      const backCurve = new THREE.CatmullRomCurve3(backPts, true, 'centripetal', 0.5);
      const backTubeGeo = new THREE.TubeGeometry(backCurve, 800, 0.05, 12, true);
      const backMat = new THREE.MeshStandardMaterial({ color: blueColor, roughness: 0.4, metalness: 0.0, emissive: 0x001133, emissiveIntensity: 0.15 });
      const backTube = new THREE.Mesh(backTubeGeo, backMat);
      tubes.push(backTube);
    }

    tubes.forEach(t => objectsGroup.add(t));
  }

  // Event listeners
  Object.values(sliders).forEach(sl => {
    sl.addEventListener('input', () => {
      updateScene();
    });
  });
  checkboxBoundaryOnly.addEventListener('change', () => {
    updateScene();
  });

  // Initial paint
  updateLabels();
  updateScene();

  // Animate
  function animate() {
    requestAnimationFrame(animate);
    resizeRendererToDisplaySize();
    controls.update();
    renderer.render(scene, camera);
  }
  animate();
</script>
</body>
</html>
