<script>
(function(){
  // DOM refs
  const sliderHue = document.getElementById('slider-hue');
  const sliderSat = document.getElementById('slider-saturation');
  const sliderBri = document.getElementById('slider-brightness');

  const valueHue = document.getElementById('value-hue');
  const valueSat = document.getElementById('value-saturation');
  const valueBri = document.getElementById('value-brightness');

  const btnReset = document.getElementById('btn-reset');

  const arrowHue = document.getElementById('arrow-hue');
  const arrowSat = document.getElementById('arrow-saturation');
  const arrowBri = document.getElementById('arrow-brightness');

  const parentHue = document.getElementById('canvas-hue-parent');
  const parentSat = document.getElementById('canvas-saturation-parent');
  const parentBri = document.getElementById('canvas-brightness-parent');
  const parentRes = document.getElementById('canvas-result-parent');

  // Defaults
  const DEFAULTS = { h: 0, s: 0.5, b: 1 };

  // p5 instances
  let pHue, pSat, pBri, pRes;
  let readyCount = 0;

  // Utils
  function fmt(val){
    // Keep concise, trim trailing zeros while preserving "0", "0.5", etc.
    const s = (+val).toFixed(3);
    return s.replace(/\.?0+$/,'');
  }

  function getValues(){
    return {
      h: parseFloat(sliderHue.value),
      s: parseFloat(sliderSat.value),
      b: parseFloat(sliderBri.value)
    };
  }

  function setDisplays(){
    valueHue.textContent = fmt(sliderHue.value);
    valueSat.textContent = fmt(sliderSat.value);
    valueBri.textContent = fmt(sliderBri.value);
  }

  function clamp(v, min, max){ return Math.max(min, Math.min(max, v)); }

  function positionArrow(arrowEl, value01, wrapperEl){
    const w = wrapperEl.clientWidth;
    const aw = arrowEl.getBoundingClientRect().width || 16;
    const left = clamp(value01 * w - aw/2, 0, Math.max(0, w - aw));
    arrowEl.style.left = left + 'px';
  }

  // p5 drawing helpers (shared)
  function withRoundedClip(p, drawContent){
    const ctx = p.drawingContext;
    const w = p.width, h = p.height;
    const r = h/2;
    ctx.save();
    ctx.beginPath();
    // Rounded rectangle path (manual for broad support)
    ctx.moveTo(r, 0);
    ctx.lineTo(w - r, 0);
    ctx.quadraticCurveTo(w, 0, w, r);
    ctx.lineTo(w, h - r);
    ctx.quadraticCurveTo(w, h, w - r, h);
    ctx.lineTo(r, h);
    ctx.quadraticCurveTo(0, h, 0, h - r);
    ctx.lineTo(0, r);
    ctx.quadraticCurveTo(0, 0, r, 0);
    ctx.closePath();
    ctx.clip();
    drawContent();
    ctx.restore();
  }

  function drawHueBar(p){
    p.clear();
    p.colorMode(p.HSB, 1, 1, 1);
    withRoundedClip(p, () => {
      p.strokeWeight(1);
      for(let x=0; x<p.width; x++){
        const t = p.width <= 1 ? 0 : x / (p.width - 1);
        p.stroke(t, 1, 1);
        p.line(x, 0, x, p.height);
      }
    });
  }

  function drawSaturationBar(p, h, b){
    p.clear();
    p.colorMode(p.HSB, 1, 1, 1);
    withRoundedClip(p, () => {
      p.strokeWeight(1);
      for(let x=0; x<p.width; x++){
        const s = p.width <= 1 ? 0 : x / (p.width - 1);
        p.stroke(h, s, b);
        p.line(x, 0, x, p.height);
      }
    });
  }

  function drawBrightnessBar(p, h, s){
    p.clear();
    p.colorMode(p.HSB, 1, 1, 1);
    withRoundedClip(p, () => {
      p.strokeWeight(1);
      for(let x=0; x<p.width; x++){
        const b = p.width <= 1 ? 0 : x / (p.width - 1);
        p.stroke(h, s, b);
        p.line(x, 0, x, p.height);
      }
    });
  }

  function drawResultBar(p, h, s, b){
    p.clear();
    p.colorMode(p.HSB, 1, 1, 1);
    p.noStroke();
    // Use p5's rounded rect directly for the solid bar
    const r = p.height/2;
    p.fill(h, s, b);
    p.rect(0, 0, p.width, p.height, r);
  }

  function resizeSketchToParent(p, parentEl){
    const targetW = Math.max(10, parentEl.clientWidth);
    const targetH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-height')) || 36;
    p.resizeCanvas(targetW, targetH);
  }

  // Create p5 instances (one per bar)
  function createHueSketch(){
    pHue = new p5((p) => {
      p.setup = function(){
        const w = parentHue.clientWidth || 600;
        const h = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-height')) || 36;
        const cnv = p.createCanvas(w, h);
        cnv.parent(parentHue);
        p.pixelDensity(1);
        p.noLoop();
        p.colorMode(p.HSB, 1, 1, 1);
        drawHueBar(p);
        onSketchReady();
      };
      p.redrawBar = function(){ drawHueBar(p); };
      p.handleResize = function(){
        resizeSketchToParent(p, parentHue);
        drawHueBar(p);
      };
    });
  }

  function createSaturationSketch(){
    pSat = new p5((p) => {
      p.setup = function(){
        const w = parentSat.clientWidth || 600;
        const h = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-height')) || 36;
        const cnv = p.createCanvas(w, h);
        cnv.parent(parentSat);
        p.pixelDensity(1);
        p.noLoop();
        p.colorMode(p.HSB, 1, 1, 1);
        onSketchReady();
      };
      p.redrawBar = function(h, b){ drawSaturationBar(p, h, b); };
      p.handleResize = function(h, b){
        resizeSketchToParent(p, parentSat);
        drawSaturationBar(p, h, b);
      };
    });
  }

  function createBrightnessSketch(){
    pBri = new p5((p) => {
      p.setup = function(){
        const w = parentBri.clientWidth || 600;
        const h = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-height')) || 36;
        const cnv = p.createCanvas(w, h);
        cnv.parent(parentBri);
        p.pixelDensity(1);
        p.noLoop();
        p.colorMode(p.HSB, 1, 1, 1);
        onSketchReady();
      };
      p.redrawBar = function(h, s){ drawBrightnessBar(p, h, s); };
      p.handleResize = function(h, s){
        resizeSketchToParent(p, parentBri);
        drawBrightnessBar(p, h, s);
      };
    });
  }

  function createResultSketch(){
    pRes = new p5((p) => {
      p.setup = function(){
        const w = parentRes.clientWidth || 600;
        const h = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-height')) || 36;
        const cnv = p.createCanvas(w, h);
        cnv.parent(parentRes);
        p.pixelDensity(1);
        p.noLoop();
        p.colorMode(p.HSB, 1, 1, 1);
        onSketchReady();
      };
      p.redrawBar = function(h, s, b){ drawResultBar(p, h, s, b); };
      p.handleResize = function(h, s, b){
        resizeSketchToParent(p, parentRes);
        drawResultBar(p, h, s, b);
      };
    });
  }

  function onSketchReady(){
    readyCount++;
    if(readyCount === 4){
      // Initial load logic per spec
      setDisplays();
      pHue.redrawBar(); // Hue bar is static and drawn once here
      const v = getValues();
      pSat.redrawBar(v.h, v.b);
      pBri.redrawBar(v.h, v.s);
      pRes.redrawBar(v.h, v.s, v.b);
      // Position arrows
      positionArrow(arrowHue, v.h, parentHue.parentElement); // wrapper is the parent of the canvas parent
      positionArrow(arrowSat, v.s, parentSat.parentElement);
      positionArrow(arrowBri, v.b, parentBri.parentElement);
    }
  }

  // Interaction handlers following the specified logic
  function onHueChange(){
    const v = getValues();
    valueHue.textContent = fmt(v.h);
    positionArrow(arrowHue, v.h, parentHue.parentElement);
    // Update dependent bars
    pSat.redrawBar(v.h, v.b);
    pBri.redrawBar(v.h, v.s);
    pRes.redrawBar(v.h, v.s, v.b);
  }

  function onSatChange(){
    const v = getValues();
    valueSat.textContent = fmt(v.s);
    positionArrow(arrowSat, v.s, parentSat.parentElement);
    // Update dependent bars
    pBri.redrawBar(v.h, v.s);
    pRes.redrawBar(v.h, v.s, v.b);
  }

  function onBriChange(){
    const v = getValues();
    valueBri.textContent = fmt(v.b);
    positionArrow(arrowBri, v.b, parentBri.parentElement);
    // Update dependent bars
    pSat.redrawBar(v.h, v.b);
    pRes.redrawBar(v.h, v.s, v.b);
  }

  function resetAll(){
    sliderHue.value = DEFAULTS.h;
    sliderSat.value = DEFAULTS.s;
    sliderBri.value = DEFAULTS.b;
    setDisplays();
    // Full redraw after reset
    const v = getValues();
    pHue.redrawBar(); // static, but safe
    pSat.redrawBar(v.h, v.b);
    pBri.redrawBar(v.h, v.s);
    pRes.redrawBar(v.h, v.s, v.b);
    positionArrow(arrowHue, v.h, parentHue.parentElement);
    positionArrow(arrowSat, v.s, parentSat.parentElement);
    positionArrow(arrowBri, v.b, parentBri.parentElement);
  }

  // Resize handling
  function onResize(){
    const v = getValues();
    // Resize canvases to their parent widths and redraw
    pHue.handleResize();
    pSat.handleResize(v.h, v.b);
    pBri.handleResize(v.h, v.s);
    pRes.handleResize(v.h, v.s, v.b);
    // Reposition arrows according to new widths
    positionArrow(arrowHue, v.h, parentHue.parentElement);
    positionArrow(arrowSat, v.s, parentSat.parentElement);
    positionArrow(arrowBri, v.b, parentBri.parentElement);
  }

  // Wire events
  sliderHue.addEventListener('input', onHueChange);
  sliderSat.addEventListener('input', onSatChange);
  sliderBri.addEventListener('input', onBriChange);
  btnReset.addEventListener('click', resetAll);
  window.addEventListener('resize', onResize);

  // Create sketches
  createHueSketch();
  createSaturationSketch();
  createBrightnessSketch();
  createResultSketch();
})();
</script>
</body>
</html>
