          <span class="control-value" id="value-alpha">0.05</span>
        </div>
        <!-- Row: beta -->
        <div class="control-row">
          <label class="control-label" for="slider-beta">β</label>
          <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5" />
          <span class="control-value" id="value-beta">5</span>
        </div>
        <!-- Row: gamma -->
        <div class="control-row">
          <label class="control-label" for="slider-gamma">γ</label>
          <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500" />
          <span class="control-value" id="value-gamma">500</span>
        </div>
      </div>
    </div>

    <!-- Plot Area Section -->
    <div id="plot-container"></div>
  </div>

  <script>
    // Grab DOM elements
    const sliderAlpha = document.getElementById('slider-alpha');
    const sliderBeta  = document.getElementById('slider-beta');
    const sliderGamma = document.getElementById('slider-gamma');

    const valueAlpha = document.getElementById('value-alpha');
    const valueBeta  = document.getElementById('value-beta');
    const valueGamma = document.getElementById('value-gamma');

    // Formatting helpers
    function fmtAlpha(v) { return Number(v).toFixed(4); }
    function fmtBeta(v)  { return Number(v).toFixed(2); }
    function fmtGamma(v) { return Math.round(Number(v)).toString(); }

    // ODE system:
    // y[0] = ΔT, y[1] = C_X
    // d(ΔT)/dt = γ * C_X * exp(ΔT / (1 + ΔT/α)) - β * ΔT
    // d(C_X)/dt = 1 - C_X * exp(ΔT / (1 + ΔT/α))
    function rhs(t, y, alpha, beta, gamma) {
      const dT = y[0];
      const Cx = y[1];

      // Guard against denominator approaching zero
      let denom = 1 + dT / alpha;
      if (Math.abs(denom) < 1e-9) {
        denom = (denom >= 0 ? 1 : -1) * 1e-9;
      }
      const expTerm = Math.exp(dT / denom);

      const dTdt = gamma * Cx * expTerm - beta * dT;
      const dCxdt = 1 - Cx * expTerm;

      return [dTdt, dCxdt];
    }

    // 4th-order Runge-Kutta solver for the system
    function rk4Solve(alpha, beta, gamma, t0 = 0, tEnd = 20, h = 0.05) {
      const nSteps = Math.round((tEnd - t0) / h);
      const tArr = new Array(nSteps + 1);
      const dTArr = new Array(nSteps + 1);
      const CxArr = new Array(nSteps + 1);

      // Initial conditions
      let t = t0;
      let y = [0, 0]; // [ΔT, C_X]

      for (let i = 0; i <= nSteps; i++) {
        tArr[i] = t;
        dTArr[i] = y[0];
        CxArr[i] = y[1];

        if (i === nSteps) break;

        const k1 = rhs(t, y, alpha, beta, gamma);
        const yk2 = [ y[0] + 0.5 * h * k1[0], y[1] + 0.5 * h * k1[1] ];
        const k2 = rhs(t + 0.5 * h, yk2, alpha, beta, gamma);
        const yk3 = [ y[0] + 0.5 * h * k2[0], y[1] + 0.5 * h * k2[1] ];
        const k3 = rhs(t + 0.5 * h, yk3, alpha, beta, gamma);
        const yk4 = [ y[0] + h * k3[0], y[1] + h * k3[1] ];
        const k4 = rhs(t + h, yk4, alpha, beta, gamma);

        y = [
          y[0] + (h / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]),
          y[1] + (h / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1])
        ];
        t += h;
      }

      return { t: tArr, dT: dTArr, Cx: CxArr };
    }

    // Plotting function using Plotly.react for efficiency
    function computeAndPlot() {
      const alpha = parseFloat(sliderAlpha.value);
      const beta  = parseFloat(sliderBeta.value);
      const gamma = parseFloat(sliderGamma.value);

      // Update display values with required formatting
      valueAlpha.textContent = fmtAlpha(alpha);
      valueBeta.textContent  = fmtBeta(beta);
      valueGamma.textContent = fmtGamma(gamma);

      // Solve ODEs
      const { t, dT, Cx } = rk4Solve(alpha, beta, gamma, 0, 20, 0.05);

      // Scale concentration by 1000 for plotting
      const CxScaled = Cx.map(v => 1000 * v);

      // Prepare traces
      const traces = [
        {
          x: t,
          y: dT,
          mode: 'lines',
          line: { color: 'orange', width: 2 },
          hoverinfo: 'x+y',
          name: 'ΔT'
        },
        {
          x: t,
          y: CxScaled,
          mode: 'lines',
          line: { color: 'steelblue', width: 2 },
          hoverinfo: 'x+y',
          name: '1000 Cx'
        }
      ];

      const layout = {
        xaxis: { title: '', range: [0, 20], zeroline: true, gridcolor: '#e6e6e6' },
        yaxis: { title: '', range: [0, 100], zeroline: true, gridcolor: '#e6e6e6' },
        showlegend: false,
        margin: { l: 50, r: 20, b: 30, t: 20 },
        plot_bgcolor: '#f0f0f0',
        paper_bgcolor: 'white',
        annotations: [
          {
            text: 'ΔT',
            x: -0.05, y: 100,
            xref: 'paper', yref: 'y',
            xanchor: 'right', yanchor: 'top',
            showarrow: false,
            font: { color: 'purple', size: 12 }
          },
          {
            text: '1000 C<sub>X</sub>',
            x: -0.05, y: 95,
            xref: 'paper', yref: 'y',
            xanchor: 'right', yanchor: 'top',
            showarrow: false,
            font: { color: 'purple', size: 12 }
          }
        ]
      };

      const config = { responsive: true, displayModeBar: false };

      Plotly.react('plot-container', traces, layout, config);
    }

    // Initialize
    window.addEventListener('DOMContentLoaded', () => {
      // Set initial display values as specified
      valueAlpha.textContent = '0.05';
      valueBeta.textContent  = '5';
      valueGamma.textContent = '500';

      computeAndPlot();

      // Real-time updates on slider movement
      const onInput = () => computeAndPlot();
      sliderAlpha.addEventListener('input', onInput);
      sliderBeta.addEventListener('input', onInput);
      sliderGamma.addEventListener('input', onInput);
    });
  </script>
</body>
</html>
