  <script>
    // p5.js sketch and UI integration

    // Canvas size
    const CANVAS_W = 700;
    const CANVAS_H = 450;

    // Grid definition
    const COLS = 10;
    const ROWS = 6;

    // Visual scaling for amplitudes (pixels)
    const AMP_SCALE = 20;

    // Margins for the grid inside the canvas
    const MARGIN = { left: 60, right: 60, top: 60, bottom: 60 };

    // Global state
    let grid = []; // stores {x0, y0, row, col}
    let timeVal = 7.0;
    let wavelength = 4.0;
    let ampLong = 2.0; // longitudinal amplitude
    let ampLat = 1.0;  // latitudinal amplitude
    const dt = 0.03;
    const timeMin = 0;
    const timeMax = 25;

    // DOM elements
    let sliderTime, sliderWavelength, sliderAmpLong, sliderAmpLat;
    let valueTime, valueWavelength, valueAmpLong, valueAmpLat;

    // Interaction flags for time scrubbing
    let draggingTime = false;

    function format2(n) {
      return parseFloat(n).toFixed(2);
    }

    function syncSpan(spanEl, val) {
      spanEl.textContent = format2(val);
    }

    function buildGrid() {
      grid = [];
      const usableW = CANVAS_W - (MARGIN.left + MARGIN.right);
      const usableH = CANVAS_H - (MARGIN.top + MARGIN.bottom);

      const dx = COLS > 1 ? usableW / (COLS - 1) : 0;
      const dy = ROWS > 1 ? usableH / (ROWS - 1) : 0;

      for (let r = 0; r < ROWS; r++) {
        for (let c = 0; c < COLS; c++) {
          const x0 = MARGIN.left + c * dx;
          const y0 = MARGIN.top + r * dy;
          grid.push({ x0, y0, row: r, col: c });
        }
      }
    }

    function setup() {
      const cnv = createCanvas(CANVAS_W, CANVAS_H);
      cnv.parent('canvas-container');
      buildGrid();

      // Link sliders & spans
      sliderTime = document.getElementById('slider-time');
      sliderWavelength = document.getElementById('slider-wavelength');
      sliderAmpLong = document.getElementById('slider-longitudinal-amplitude');
      sliderAmpLat = document.getElementById('slider-latitudinal-amplitude');

      valueTime = document.getElementById('value-time');
      valueWavelength = document.getElementById('value-wavelength');
      valueAmpLong = document.getElementById('value-longitudinal-amplitude');
      valueAmpLat = document.getElementById('value-latitudinal-amplitude');

      // Initialize UI displays
      syncSpan(valueTime, sliderTime.value);
      syncSpan(valueWavelength, sliderWavelength.value);
      syncSpan(valueAmpLong, sliderAmpLong.value);
      syncSpan(valueAmpLat, sliderAmpLat.value);

      // Slider event handlers
      sliderWavelength.addEventListener('input', () => {
        wavelength = parseFloat(sliderWavelength.value);
        syncSpan(valueWavelength, wavelength);
      });

      sliderAmpLong.addEventListener('input', () => {
        ampLong = parseFloat(sliderAmpLong.value);
        syncSpan(valueAmpLong, ampLong);
      });

      sliderAmpLat.addEventListener('input', () => {
        ampLat = parseFloat(sliderAmpLat.value);
        syncSpan(valueAmpLat, ampLat);
      });

      // Time slider: scrubbing logic
      const startDrag = () => {
        draggingTime = true;
      };
      const endDrag = () => {
        if (draggingTime) {
          draggingTime = false; // resume animation from the chosen time
        }
      };
      sliderTime.addEventListener('mousedown', startDrag);
      sliderTime.addEventListener('touchstart', startDrag, { passive: true });
      window.addEventListener('mouseup', endDrag);
      window.addEventListener('touchend', endDrag);
      window.addEventListener('touchcancel', endDrag);

      // While dragging or changing input, override timeVal
      sliderTime.addEventListener('input', () => {
        timeVal = parseFloat(sliderTime.value);
        syncSpan(valueTime, timeVal);
      });

      // Seed state from the initial slider values
      wavelength = parseFloat(sliderWavelength.value);
      ampLong = parseFloat(sliderAmpLong.value);
      ampLat = parseFloat(sliderAmpLat.value);
      timeVal = parseFloat(sliderTime.value);
    }

    function draw() {
      // 1) Update State
      if (!draggingTime) {
        timeVal += dt;
        if (timeVal > timeMax) timeVal = timeMin + (timeVal - timeMax);
        // Reflect animated time in UI
        sliderTime.value = timeVal;
        syncSpan(valueTime, timeVal);
      } else {
        // While scrubbing, keep timeVal tied to the slider
        timeVal = parseFloat(sliderTime.value);
        syncSpan(valueTime, timeVal);
      }

      // Always read current values (in case user changes them)
      wavelength = parseFloat(sliderWavelength.value);
      ampLong = parseFloat(sliderAmpLong.value);
      ampLat = parseFloat(sliderAmpLat.value);

      // 2) Clear Canvas
      background('#E0F0FF');

      // 3) Particle Grid Logic
      const k = TWO_PI / wavelength;

      // To give wavelength an intuitive scale across the canvas width,
      // normalize x to span 0..8 "units" across the grid width.
      const gridLeft = MARGIN.left;
      const gridRight = width - MARGIN.right;
      const gridWidthPx = gridRight - gridLeft;
      const maxUnitSpan = 8; // maps canvas width to 8 units

      // Store current positions per row for dashed connection lines
      const rowPositions = Array.from({ length: ROWS }, () => []);

      // Draw ellipses and compute particle positions
      noFill();
      stroke('#8F998F');
      strokeWeight(2);

      for (let i = 0; i < grid.length; i++) {
        const { x0, y0, row, col } = grid[i];

        // Amplitude decay with depth
        const ampDecay = Math.exp(- (row / (ROWS - 1)) * 1.5);
        const ampX = ampLong * ampDecay * AMP_SCALE;
        const ampY = ampLat * ampDecay * AMP_SCALE;

        // Phase: use normalized x across width to maintain intuitive wavelength control
        const xUnit = ((x0 - gridLeft) / (gridWidthPx)) * maxUnitSpan;
        const phase = k * xUnit - timeVal;

        // Elliptical path
        ellipse(x0, y0, 2 * ampX, 2 * ampY);

        // Retrograde (counter-clockwise for rightward traveling wave)
        const dx = ampX * Math.cos(phase);
        const dy = ampY * Math.sin(phase);
        const x = x0 + dx;
        const y = y0 + dy;

        rowPositions[row].push({ x, y });
      }

      // 4) Draw connecting lines and particles
      // Dashed blue lines per row
      stroke('#0000FF');
      strokeWeight(2);
      noFill();
      drawingContext.setLineDash([5, 5]);
      for (let r = 0; r < ROWS; r++) {
        beginShape();
        for (let c = 0; c < COLS; c++) {
          const p = rowPositions[r][c];
          vertex(p.x, p.y);
        }
        endShape();
      }
      drawingContext.setLineDash([]);

      // Red particles
      noStroke();
      fill('#FF0000');
      const d = 8;
      for (let r = 0; r < ROWS; r++) {
        for (let c = 0; c < COLS; c++) {
          const p = rowPositions[r][c];
          circle(p.x, p.y, d);
        }
      }
    }

    // Ensure canvas is crisp on HiDPI
    function windowResized() {
      // Fixed-size canvas per spec; no responsive resize needed.
    }
  </script>
</body>
</html>
