  <script>
    // Helper: format span values consistently with slider resolution
    function formatValue(id, val){
      if(id === 'value-c') return (+val).toFixed(3);
      if(id === 'value-b') return (+val).toFixed(2);
      if(id === 'value-a') return Math.round(+val).toString();
      if(id === 'value-n') return Math.round(+val).toString();
      if(id === 'value-tmin' || id === 'value-tmax' || id === 'value-tref') return Math.round(+val).toString();
      return String(val);
    }

    // Simple evenly spaced array
    function linspace(a, b, n){
      const arr = [];
      if(n === 1){ arr.push(a); return arr; }
      const step = (b - a) / (n - 1);
      for(let i=0;i<n;i++) arr.push(a + i*step);
      return arr;
    }

    // Linear regression: returns {slope, intercept, r2}
    function linearRegression(points){
      const n = points.length;
      let sumX=0, sumY=0, sumXX=0, sumXY=0;
      for(const [x,y] of points){
        sumX += x; sumY += y; sumXX += x*x; sumXY += x*y;
      }
      const meanX = sumX / n;
      const meanY = sumY / n;
      const denom = (n * sumXX - sumX * sumX);
      const slope = denom === 0 ? 0 : (n * sumXY - sumX * sumY) / denom;
      const intercept = meanY - slope * meanX;
      // R^2
      let ssRes=0, ssTot=0;
      for(const [x,y] of points){
        const yhat = slope * x + intercept;
        ssRes += (y - yhat) * (y - yhat);
        ssTot += (y - meanY) * (y - meanY);
      }
      const r2 = ssTot === 0 ? 1 : (1 - ssRes/ssTot);
      return {slope, intercept, r2};
    }

    // Get current UI state
    function getState(){
      return {
        model: document.getElementById('btn-arrhenius').classList.contains('active-button') ? 'Arrhenius' : 'exponential',
        n: +document.getElementById('slider-n').value,
        Tmin: +document.getElementById('slider-tmin').value,
        Tmax: +document.getElementById('slider-tmax').value,
        Tref: +document.getElementById('slider-tref').value,
        c: +document.getElementById('slider-c').value,
        b: +document.getElementById('slider-b').value,
        a: +document.getElementById('slider-a').value
      };
    }

    // Generate data and fit with the other model
    function compute(){
      const st = getState();

      // Temperatures
      const T = linspace(st.Tmin, st.Tmax, st.n);

      // Generate normalized y = k(T)/k(Tref)
      let y = [];
      if(st.model === 'Arrhenius'){
        // y = exp(-a/(T+b)) / exp(-a/(Tref+b))
        const denom = Math.exp(-st.a / (st.Tref + st.b));
        y = T.map(t => Math.exp(-st.a / (t + st.b)) / denom);
      }else{
        // exponential: y = exp(c*(T - Tref))
        y = T.map(t => Math.exp(st.c * (t - st.Tref)));
      }

      // Fit with the other model using linearization
      let fitInfo = {};
      if(st.model === 'Arrhenius'){
        // Fit exponential: x = T - Tref, yfit = log(y) ~ c*x
        const xFit = T.map(t => t - st.Tref);
        const yFit = y.map(v => Math.log(v));
        const points = xFit.map((x,i)=>[x, yFit[i]]);
        const reg = linearRegression(points);
        const cFit = reg.slope; // intercept ~ 0
        fitInfo = {
          type: 'expFitToArr',
          xFit, yFit,
          slope: cFit,
          intercept: reg.intercept,
          r2: reg.r2
        };
      }else{
        // Fit Arrhenius: x = 1/(T+b), yfit = log(y) ~ -a*x + const
        const xFit = T.map(t => 1/(t + st.b));
        const yFit = y.map(v => Math.log(v));
        const points = xFit.map((x,i)=>[x, yFit[i]]);
        const reg = linearRegression(points);
        const aFit = -reg.slope;
        fitInfo = {
          type: 'arrFitToExp',
          xFit, yFit,
          slope: reg.slope, // equals -a
          intercept: reg.intercept,
          aFit,
          r2: reg.r2
        };
      }

      return {T, y, st, fitInfo};
    }

    // Draw both plots
    function draw(result){
      const {T, y, st, fitInfo} = result;

      // Prepare top plot traces
      const dataTrace = {
        x: T, y: y,
        type: 'scatter', mode: 'markers',
        name: 'data',
        marker: {color: '#d62728', size: 8, opacity: 0.9}
      };

      // Smooth line for fitted curve across T range
      const Tf = linspace(st.Tmin, st.Tmax, 200);
      let yFitTop = [];
      let topTitle = '';
      if(fitInfo.type === 'expFitToArr'){
        yFitTop = Tf.map(t => Math.exp(fitInfo.slope * (t - st.Tref)));
        topTitle = `exponential model fit to Arrhenius data<br>c = ${fitInfo.slope.toFixed(4)}, R² = ${fitInfo.r2.toFixed(5)}`;
      }else{
        const aFit = fitInfo.aFit;
        const normRef = Math.exp(-aFit / (st.Tref + st.b));
        yFitTop = Tf.map(t => Math.exp(-aFit / (t + st.b)) / normRef);
        topTitle = `Arrhenius model fit to exponential data<br>a = ${aFit.toFixed(2)}, R² = ${fitInfo.r2.toFixed(5)}`;
      }
      const fitTrace = {
        x: Tf, y: yFitTop,
        type: 'scatter', mode: 'lines',
        name: 'fit',
        line: {color: '#d62728', width: 3}
      };

      const layoutTop = {
        title: {text: topTitle, font:{size:14}},
        margin: {t:48, r:18, b:48, l:60},
        xaxis: {title: 'T (°C)', zeroline: false},
        yaxis: {title: 'k(T)/k(T_ref)', rangemode: 'tozero'},
        legend: {orientation:'h', x:0, y:1.12}
      };

      Plotly.react('plot-top', [dataTrace, fitTrace], layoutTop, {responsive:true, displayModeBar:false});

      // Bottom plot (linearized view)
      let bottomTitle = '';
      let bottomXLabel = '';
      let bottomColor = '';
      let xData = [];
      let yData = [];
      let xLine = [];
      let yLine = [];
      let shapes = [
        // x = 0 vertical
        {type:'line', xref:'x', yref:'paper', x0:0, x1:0, y0:0, y1:1, line:{color:'#999', width:1, dash:'dot'}},
        // y = 0 horizontal
        {type:'line', xref:'paper', yref:'y', x0:0, x1:1, y0:0, y1:0, line:{color:'#999', width:1, dash:'dot'}}
      ];

      if(fitInfo.type === 'expFitToArr'){
        // Exponential linearization: x = T - Tref, y = log(y) ~ c*x
        xData = fitInfo.xFit;
        yData = fitInfo.yFit;
        bottomTitle = `linear fit of the exponential model<br>R² = ${fitInfo.r2.toFixed(5)}`;
        bottomXLabel = 'T - T_ref';
        bottomColor = '#2ca02c';
        const xmin = Math.min(...xData), xmax = Math.max(...xData);
        xLine = [xmin, xmax];
        // Plot y = c*x (intercept forced to zero as specified)
        yLine = xLine.map(x => fitInfo.slope * x);
      }else{
        // Arrhenius linearization: x = 1/(T + b), y = log(y) ~ -a*x + intercept
        xData = fitInfo.xFit;
        yData = fitInfo.yFit;
        bottomTitle = `linear fit of the Arrhenius model<br>R² = ${fitInfo.r2.toFixed(5)}`;
        bottomXLabel = '1/(T + b)';
        bottomColor = '#1f77b4';
        const xmin = Math.min(...xData), xmax = Math.max(...xData);
        xLine = [xmin, xmax];
        yLine = xLine.map(x => fitInfo.slope * x + fitInfo.intercept);
      }

      const bottomData = [
        {
          x: xData, y: yData,
          type:'scatter', mode:'markers',
          name: 'linearized data',
          marker:{color: bottomColor, size: 8, opacity:.9}
        },
        {
          x: xLine, y: yLine,
          type:'scatter', mode:'lines',
          name: 'linear fit',
          line:{color: bottomColor, width: 3}
        }
      ];

      const layoutBottom = {
        title: {text: bottomTitle, font:{size:14}},
        margin: {t:48, r:18, b:48, l:60},
        xaxis: {title: bottomXLabel, zeroline:false},
        yaxis: {title: 'log(k(T)/k(T_ref))', zeroline:false},
        showlegend:false,
        shapes
      };

      Plotly.react('plot-bottom', bottomData, layoutBottom, {responsive:true, displayModeBar:false});
    }

    // Update display spans
    function updateDisplays(){
      const mappings = [
        ['slider-n', 'value-n'],
        ['slider-tmin', 'value-tmin'],
        ['slider-tmax', 'value-tmax'],
        ['slider-tref', 'value-tref'],
        ['slider-c', 'value-c'],
        ['slider-b', 'value-b'],
        ['slider-a', 'value-a']
      ];
      for(const [sid, vid] of mappings){
        const el = document.getElementById(sid);
        document.getElementById(vid).textContent = formatValue(vid, el.value);
      }
    }

    // Enforce Tmin/Tmax constraint: Tmax > Tmin
    function enforceTmaxConstraint(){
      const tminEl = document.getElementById('slider-tmin');
      const tmaxEl = document.getElementById('slider-tmax');
      const tmin = +tminEl.value;
      const currentMinTmax = Math.max(+tmaxEl.min, tmin + 1);
      if(+tmaxEl.min !== currentMinTmax) tmaxEl.min = String(currentMinTmax);
      if(+tmaxEl.value <= tmin){
        tmaxEl.value = String(tmin + 1);
      }
      document.getElementById('value-tmax').textContent = formatValue('value-tmax', tmaxEl.value);
    }

    // Main recompute + redraw
    function recompute(){
      enforceTmaxConstraint();
      updateDisplays();
      const res = compute();
      draw(res);
    }

    // Initialize UI
    function init(){
      // Buttons
      const btnArr = document.getElementById('btn-arrhenius');
      const btnExp = document.getElementById('btn-exponential');

      btnArr.addEventListener('click', () => {
        btnArr.classList.add('active-button');
        btnExp.classList.remove('active-button');
        recompute();
      });
      btnExp.addEventListener('click', () => {
        btnExp.classList.add('active-button');
        btnArr.classList.remove('active-button');
        recompute();
      });

      // Sliders
      const sliders = [
        'slider-n', 'slider-tmin', 'slider-tmax',
        'slider-tref', 'slider-c', 'slider-b', 'slider-a'
      ];
      sliders.forEach(id => {
        document.getElementById(id).addEventListener('input', (e) => {
          if(id === 'slider-tmin' || id === 'slider-tmax'){
            enforceTmaxConstraint();
          }
          document.getElementById('value-' + id.split('-')[1]).textContent =
            formatValue('value-' + id.split('-')[1], e.target.value);
          recompute();
        });
      });

      // Initial render
      recompute();
    }

    // Start
    window.addEventListener('DOMContentLoaded', init);
  </script>
</body>
</html>
