      for (let i = 0; i < n; i++) {
        const tau = taus[i];
        let p = calculatePressureDrop(tau, lambda, omega);
        if (mode === 'difference') {
          p = p - 0.5 * (Math.log(tau) + 0.8091);
        }
        // guard finite
        if (!isFinite(p)) p = 0;
        y[i] = p;
      }
      return y;
    }

    function readParamsAndUpdateDisplays() {
      const expLambda = parseFloat(elLambda.value);
      const expOmega = parseFloat(elOmega.value);
      const lambda = Math.pow(10, expLambda);
      const omega = Math.pow(10, expOmega);
      valueLambda.textContent = formatLambda(lambda);
      valueOmega.textContent = formatOmega(omega);
      return { lambda, omega };
    }

    function updatePlot() {
      const { lambda, omega } = readParamsAndUpdateDisplays();
      const y = generateData(lambda, omega, currentMode);
      const trace = {
        x: taus,
        y: y,
        mode: 'lines',
        type: 'scatter',
        line: { color: 'red', width: 2 }
      };
      // For slider updates, maintain layout/view using Plotly.react
      Plotly.react(plotDiv, [trace], getLayout(currentMode), {responsive: true});
    }

    function newPlotFull() {
      const { lambda, omega } = readParamsAndUpdateDisplays();
      const y = generateData(lambda, omega, currentMode);
      const trace = {
        x: taus,
        y: y,
        mode: 'lines',
        type: 'scatter',
        line: { color: 'red', width: 2 }
      };
      Plotly.newPlot(plotDiv, [trace], getLayout(currentMode), {responsive: true});
    }

    function setActiveButton(btn) {
      btnPressure.classList.remove('active');
      btnDiff.classList.remove('active');
      btn.classList.add('active');
    }

    function debounce(fn, delay = 150) {
      return function(...args) {
        clearTimeout(debounceTimer);
        debounceTimer = setTimeout(() => fn.apply(this, args), delay);
      };
    }

    // Initialize
    function init() {
      taus = logspace(0, 10, 200); // τ from 1 to 1e10

      // Initial state
      valueLambda.textContent = "0.00001";
      valueOmega.textContent = "0.01";
      currentMode = 'pressure';
      newPlotFull();

      // Events
      elLambda.addEventListener('input', debounce(updatePlot, 150));
      elOmega.addEventListener('input', debounce(updatePlot, 150));

      btnPressure.addEventListener('click', () => {
        if (currentMode !== 'pressure') {
          currentMode = 'pressure';
          setActiveButton(btnPressure);
          newPlotFull(); // layout changes
        }
      });
      btnDiff.addEventListener('click', () => {
        if (currentMode !== 'difference') {
          currentMode = 'difference';
          setActiveButton(btnDiff);
          newPlotFull(); // layout changes
        }
      });
    }

    // Run
    window.addEventListener('DOMContentLoaded', init);
  </script>
</body>
</html>
