          <th>age</th>
          <th>volume</th>
        </tr>
      </thead>
      <tbody id="data-table-body">
        <!-- Populated dynamically -->
      </tbody>
    </table>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
  <script>
    // Data/state
    let dataPoints = [
      { age: 30, volume: 289 },
      { age: 35, volume: 445 },
      { age: 40, volume: 565 },
      { age: 45, volume: 704 },
      { age: 50, volume: 833 },
      { age: 55, volume: 924 }
    ];

    // Model exponent a = 4/5
    const a = 0.8;

    // Initial parameter estimates per plan (also used to draw initial curve)
    let pEstimate = 58.2729;
    let uEstimate = 22.7319;

    // Plot settings
    const xMin = 20, xMax = 80;
    const yMin = 0,  yMax = 1500;

    // Canvas and plot area
    let canvas;
    const CANVAS_W = 500;
    const CANVAS_H = 500;
    const margin = { left: 75, right: 30, top: 55, bottom: 60 };
    let plotLeft, plotRight, plotTop, plotBottom;

    // Interaction
    let draggingIndex = -1;
    let hoveredIndex = -1;
    const pointRadiusPx = 6;

    function setup() {
      canvas = createCanvas(CANVAS_W, CANVAS_H);
      canvas.parent('canvas-container');
      textFont('sans-serif');
      plotLeft   = margin.left;
      plotRight  = width - margin.right;
      plotTop    = margin.top;
      plotBottom = height - margin.bottom;

      noLoop(); // We will redraw on demand

      // Populate table initially
      updateTable();
      // Ensure UI text shows initial values to match plan
      updateResultsUI(false); // do not overwrite the pre-filled exact text, but ensure formatting later

      redraw(); // Draw once
    }

    function draw() {
      background('#FFFFE0');
      drawGridAndAxes();
      drawTitleAndLabels();
      drawFittedCurve();
      drawPoints();
      drawHoverHighlight();
    }

    // Mapping between data space and pixel space
    function dataToPixelX(x) {
      return map(x, xMin, xMax, plotLeft, plotRight);
    }
    function dataToPixelY(y) {
      return map(y, yMin, yMax, plotBottom, plotTop);
    }
    function pixelToDataX(px) {
      return map(px, plotLeft, plotRight, xMin, xMax);
    }
    function pixelToDataY(py) {
      return map(py, plotBottom, plotTop, yMin, yMax);
    }

    function drawDashedLine(x1, y1, x2, y2, segLen = 6, gapLen = 6) {
      const dx = x2 - x1;
      const dy = y2 - y1;
      const dist = Math.hypot(dx, dy);
      const steps = Math.floor(dist / (segLen + gapLen));
      const ux = dx / dist;
      const uy = dy / dist;
      push();
      stroke(200);
      for (let i = 0; i < steps; i++) {
        const sx = x1 + (i * (segLen + gapLen)) * ux;
        const sy = y1 + (i * (segLen + gapLen)) * uy;
        const ex = sx + segLen * ux;
        const ey = sy + segLen * uy;
        line(sx, sy, ex, ey);
      }
      pop();
    }

    function drawGridAndAxes() {
      // Grid
      strokeWeight(1);

      // Vertical grid and ticks for age 20..80 step 10
      for (let xTick = 20; xTick <= 80; xTick += 10) {
        const px = dataToPixelX(xTick);
        // dashed vertical grid
        drawDashedLine(px, plotTop, px, plotBottom);
      }
      // Horizontal grid and ticks for volume 0..1500 step 200
      for (let yTick = 0; yTick <= 1500; yTick += 200) {
        const py = dataToPixelY(yTick);
        drawDashedLine(plotLeft, py, plotRight, py);
      }

      // Axes
      push();
      stroke(0);
      strokeWeight(2);
      // X-axis
      line(plotLeft, plotBottom, plotRight, plotBottom);
      // Y-axis
      line(plotLeft, plotBottom, plotLeft, plotTop);
      pop();

      // Tick marks and labels
      fill(0);
      noStroke();
      textSize(12);
      textAlign(CENTER, TOP);
      for (let xTick = 20; xTick <= 80; xTick += 10) {
        const px = dataToPixelX(xTick);
        stroke(0);
        line(px, plotBottom - 4, px, plotBottom + 4);
        noStroke();
        text(xTick, px, plotBottom + 8);
      }
      textAlign(RIGHT, CENTER);
      for (let yTick = 0; yTick <= 1500; yTick += 200) {
        const py = dataToPixelY(yTick);
        stroke(0);
        line(plotLeft - 4, py, plotLeft + 4, py);
        noStroke();
        text(yTick, plotLeft - 8, py);
      }
    }

    function drawTitleAndLabels() {
      // Title
      textAlign(CENTER, CENTER);
      textSize(16);
      fill(0);
      text('Forest Growth Curve', (plotLeft + plotRight) / 2, margin.top / 2);

      // X-axis label
      textSize(13);
      text('Stand age, yr', (plotLeft + plotRight) / 2, plotBottom + 35);

      // Y-axis label
      push();
      translate(margin.left / 3, (plotTop + plotBottom) / 2);
      rotate(-HALF_PI);
      text('Wood volume, m³ ha⁻¹', 0, 0);
      pop();

      // "4/5 law" label rotated -90° on the right side
      push();
      translate(plotRight + 20, (plotTop + plotBottom) / 2);
      rotate(-HALF_PI);
      text('4/5 law', 0, 0);
      pop();
    }

    function drawPoints() {
      stroke(0);
      strokeWeight(1);
      for (let i = 0; i < dataPoints.length; i++) {
        const pt = dataPoints[i];
        const px = dataToPixelX(pt.age);
        const py = dataToPixelY(pt.volume);
        fill('#0000CD');
        ellipse(px, py, pointRadiusPx * 2, pointRadiusPx * 2);
      }
    }

    function drawHoverHighlight() {
      if (hoveredIndex >= 0 && hoveredIndex < dataPoints.length) {
        const pt = dataPoints[hoveredIndex];
        const px = dataToPixelX(pt.age);
        const py = dataToPixelY(pt.volume);
        noFill();
        stroke('#1e90ff');
        strokeWeight(2);
        ellipse(px, py, (pointRadiusPx + 4) * 2, (pointRadiusPx + 4) * 2);
      }
      if (draggingIndex >= 0) {
        const pt = dataPoints[draggingIndex];
        const px = dataToPixelX(pt.age);
        const py = dataToPixelY(pt.volume);
        noFill();
        stroke('#ff7f0e');
        strokeWeight(2);
        ellipse(px, py, (pointRadiusPx + 6) * 2, (pointRadiusPx + 6) * 2);
      }
    }

    function drawFittedCurve() {
      // Draw y = p * (x - u)^a for x in [max(xMin, u), xMax]
      const startX = Math.max(xMin, uEstimate);
      const endX = xMax;
      if (endX <= startX) return;

      const steps = 200;
      const dx = (endX - startX) / steps;

      noFill();
      stroke('#0000CD');
      strokeWeight(2);
      beginShape();
      for (let i = 0; i <= steps; i++) {
        const x = startX + i * dx;
        const y = pEstimate * Math.pow(Math.max(1e-9, x - uEstimate), a);
        const px = dataToPixelX(x);
        const py = dataToPixelY(y);
        vertex(px, py);
      }
      endShape();
    }

    function updateResultsUI(forceFormat = true) {
      const uSpan = document.getElementById('results-u');
      const pSpan = document.getElementById('results-p');
      // If forceFormat, overwrite with current estimates formatted to 4 decimals
      if (forceFormat) {
        uSpan.textContent = (uEstimate).toFixed(4);
        pSpan.textContent = (pEstimate).toFixed(4);
      }
      // else leave initial values as provided in HTML
    }

    function updateTable() {
      const tbody = document.getElementById('data-table-body');
      tbody.innerHTML = '';
      for (const pt of dataPoints) {
        const tr = document.createElement('tr');
        const tdAge = document.createElement('td');
        const tdVol = document.createElement('td');
        tdAge.textContent = Number(pt.age).toFixed(2);
        tdVol.textContent = Number(pt.volume).toFixed(2);
        tr.appendChild(tdAge);
        tr.appendChild(tdVol);
        tbody.appendChild(tr);
      }
    }

    // Fitting by gradient descent
    function fitParameters(iterations = 1000) {
      if (dataPoints.length < 2) return; // not enough data to fit
      const lr_p = 1e-4;   // learning rate for p
      const lr_u = 1e-5;   // learning rate for u
      const eps = 1e-6;
      const minAge = Math.min(...dataPoints.map(d => d.age));
      // Use current estimates to keep continuity during interaction
      let p = pEstimate;
      let u = Math.min(uEstimate, minAge - 1e-3);

      let prevE = Infinity;
      for (let it = 0; it < iterations; it++) {
        let dE_dp = 0;
        let dE_du = 0;
        let E = 0;
        for (const pt of dataPoints) {
          const dx = Math.max(eps, pt.age - u);
          const pow_a = Math.pow(dx, a);
          const pred = p * pow_a;
          const r = pt.volume - pred;
          E += r * r;
          dE_dp += -2 * r * pow_a;
          dE_du +=  2 * r * p * a * Math.pow(dx, a - 1);
        }
        // Update parameters
        p -= lr_p * dE_dp;
        u -= lr_u * dE_du;
        // Constraints
        if (p < 0) p = 0;
        u = Math.min(u, minAge - 1e-6);

        // Early stop
        if (Math.abs(prevE - E) < 1e-6) break;
        prevE = E;
      }
      pEstimate = p;
      uEstimate = u;
    }

    function recalc(iterations = 1000) {
      fitParameters(iterations);
      updateResultsUI(true);
      updateTable();
      redraw();
    }

    function isInsidePlot(px, py) {
      return px >= plotLeft && px <= plotRight && py >= plotTop && py <= plotBottom;
    }

    function pointAtMouse(mx, my) {
      // return index of point near mouse, else -1
      for (let i = dataPoints.length - 1; i >= 0; i--) {
        const pt = dataPoints[i];
        const px = dataToPixelX(pt.age);
        const py = dataToPixelY(pt.volume);
        if (dist(mx, my, px, py) <= pointRadiusPx + 3) {
          return i;
        }
      }
      return -1;
    }

    function mouseMoved() {
      if (!isInsidePlot(mouseX, mouseY)) {
        hoveredIndex = -1;
        canvas.elt.style.cursor = 'default';
        redraw();
        return;
      }
      const idx = pointAtMouse(mouseX, mouseY);
      hoveredIndex = idx;
      if (idx >= 0) {
        canvas.elt.style.cursor = 'grab';
      } else {
        canvas.elt.style.cursor = 'crosshair';
      }
      redraw();
    }

    function mousePressed() {
      if (!isInsidePlot(mouseX, mouseY)) return;
      const idx = pointAtMouse(mouseX, mouseY);
      if (idx >= 0) {
        draggingIndex = idx;
        canvas.elt.style.cursor = 'grabbing';
      } else {
        // Add a point at clicked location
        const age = constrain(pixelToDataX(mouseX), xMin, xMax);
        const volume = constrain(pixelToDataY(mouseY), yMin, yMax);
        dataPoints.push({ age, volume });
        recalc(600);
      }
    }

    function mouseDragged() {
      if (draggingIndex >= 0) {
        // Update point position to follow mouse within plot
        const clampedX = constrain(mouseX, plotLeft, plotRight);
        const clampedY = constrain(mouseY, plotTop, plotBottom);
        const age = constrain(pixelToDataX(clampedX), xMin, xMax);
        const volume = constrain(pixelToDataY(clampedY), yMin, yMax);
        dataPoints[draggingIndex].age = age;
        dataPoints[draggingIndex].volume = volume;
        // Recalculate with fewer iterations for responsiveness
        recalc(250);
      }
    }

    function mouseReleased() {
      if (draggingIndex >= 0) {
        draggingIndex = -1;
        canvas.elt.style.cursor = 'default';
        // Finalize with a more thorough fit
        recalc(1000);
      }
    }

    function doubleClicked() {
      if (!isInsidePlot(mouseX, mouseY)) return;
      const idx = pointAtMouse(mouseX, mouseY);
      if (idx >= 0) {
        dataPoints.splice(idx, 1);
        draggingIndex = -1;
        hoveredIndex = -1;
        recalc(800);
      }
    }
  </script>
