      <div class="left-group">
        <label for="angle-slider">vertices sorted by angle</label>
        <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
        <span id="angle-value">3</span>
      </div>
      <button id="reset-button" title="Reset">+</button>
    </div>
    <div id="canvas-container"></div>
  </div>

  <script>
    // Geometry definition (outer + holes) as per plan
    const outerPolygon = [
      [50, 50], [550, 50], [550, 550], [50, 550]
    ];

    const holes = [
      [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
      [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
      [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
      [[300, 400], [450, 400], [450, 500], [300, 500]],
      [[300, 300], [350, 300], [350, 350], [300, 350]]
    ];

    // According to the plan, "total vertices is 38" but the provided coordinates sum to 30.
    // We honor the slider max=38 per requirement and gracefully handle the mismatch.

    // State
    const state = {
      source: { x: 275, y: 275 }, // id: source-point (conceptual)
      dragging: false,
      dragOffset: { x: 0, y: 0 },
      angleSlider: null,
      angleValueEl: null,
      resetButton: null,
      sliderMax: 38,
      canvasSize: 600,
      computed: null // cache of per-frame computations
    };

    // Utility math
    const EPS = 1e-9;
    function toVec(a, b) { return { x: b.x - a.x, y: b.y - a.y }; }
    function dot(a, b) { return a.x * b.x + a.y * b.y; }
    function cross(a, b) { return a.x * b.y - a.y * b.x; }
    function length(v) { return Math.hypot(v.x, v.y); }
    function normalize(v) {
      const len = length(v);
      return len < EPS ? { x: 0, y: 0 } : { x: v.x / len, y: v.y / len };
    }
    function angleOf(a, b) { // angle from a to b
      return Math.atan2(b.y - a.y, b.x - a.x);
    }
    function normAngle(a) {
      let t = a % (Math.PI * 2);
      return t < 0 ? t + Math.PI * 2 : t;
    }

    function pointInPolygon(pt, poly) {
      // Ray casting algorithm
      let x = pt.x, y = pt.y;
      let inside = false;
      for (let i = 0, j = poly.length - 1; i < poly.length; j = i++) {
        const xi = poly[i][0], yi = poly[i][1];
        const xj = poly[j][0], yj = poly[j][1];
        const intersect = ((yi > y) !== (yj > y)) &&
          (x < (xj - xi) * (y - yi) / ((yj - yi) || EPS) + xi);
        if (intersect) inside = !inside;
      }
      return inside;
    }

    function pointOnSegment(p, a, b, tol = 1e-6) {
      const ap = { x: p.x - a.x, y: p.y - a.y };
      const ab = { x: b.x - a.x, y: b.y - a.y };
      const ab2 = dot(ab, ab);
      const t = dot(ap, ab) / (ab2 || EPS);
      if (t < -tol || t > 1 + tol) return false;
      const closest = { x: a.x + t * ab.x, y: a.y + t * ab.y };
      const dist = Math.hypot(closest.x - p.x, closest.y - p.y);
      return dist <= tol;
    }

    function isPointValidInEnvironment(p) {
      // Inside outer && not in any hole, and not on walls
      if (!pointInPolygon(p, outerPolygon)) return false;
      for (const h of holes) {
        if (pointInPolygon(p, h)) return false;
      }
      // Avoid letting the point sit exactly on edges
      const edges = allEdges();
      for (const e of edges) {
        if (pointOnSegment(p, {x:e.a[0], y:e.a[1]}, {x:e.b[0], y:e.b[1]})) return false;
      }
      return true;
    }

    function allVertices() {
      const verts = [];
      for (const v of outerPolygon) verts.push({ x: v[0], y: v[1] });
      for (const h of holes) {
        for (const v of h) verts.push({ x: v[0], y: v[1] });
      }
      return verts;
    }

    function allEdges() {
      const edges = [];
      function pushEdges(poly) {
        for (let i = 0; i < poly.length; i++) {
          const a = poly[i], b = poly[(i + 1) % poly.length];
          edges.push({ a, b });
        }
      }
      pushEdges(outerPolygon);
      for (const h of holes) pushEdges(h);
      return edges;
    }

    function raySegmentIntersection(S, dir, A, B) {
      // Ray: S + t*dir, t >= 0
      // Segment: A + u*(B-A), u in [0,1]
      const r = dir;
      const s = { x: B.x - A.x, y: B.y - A.y };
      const rxs = r.x * s.y - r.y * s.x;
      const q_p = { x: A.x - S.x, y: A.y - S.y };
      const qpxr = q_p.x * r.y - q_p.y * r.x;

      if (Math.abs(rxs) < EPS) {
        // Parallel
        if (Math.abs(qpxr) < EPS) {
          // Colinear: overlap on ray
          const r2 = dot(r, r);
          const t0 = dot({ x: A.x - S.x, y: A.y - S.y }, r) / (r2 || EPS);
          const t1 = dot({ x: B.x - S.x, y: B.y - S.y }, r) / (r2 || EPS);
          const tmin = Math.min(t0, t1);
          const tmax = Math.max(t0, t1);
          if (tmax < EPS) return null; // entirely behind
          const t = tmin >= EPS ? tmin : tmax; // first non-negative
          if (t < EPS) return null;
          return { x: S.x + t * r.x, y: S.y + t * r.y, t };
        }
        return null;
      }
      const t = (q_p.x * s.y - q_p.y * s.x) / rxs;
      const u = (q_p.x * r.y - q_p.y * r.x) / rxs;
      if (t >= EPS && u >= -EPS && u <= 1 + EPS) {
        return { x: S.x + t * r.x, y: S.y + t * r.y, t, u };
      }
      return null;
    }

    function computeSweep(source) {
      const verts = allVertices();
      const edges = allEdges();

      // Sort vertices by angle around source in [0, 2π)
      const angList = verts.map((v, idx) => {
        const ang = normAngle(angleOf(source, v));
        return { idx, v, ang };
      }).sort((a, b) => a.ang - b.ang);

      // Cast rays through each angle to find nearest wall intersection
      const hits = [];
      const hitEdges = [];
      for (let i = 0; i < angList.length; i++) {
        const v = angList[i].v;
        const dir = normalize({ x: v.x - source.x, y: v.y - source.y });
        let best = null;
        let bestEdgeIdx = -1;
        for (let e = 0; e < edges.length; e++) {
          const A = { x: edges[e].a[0], y: edges[e].a[1] };
          const B = { x: edges[e].b[0], y: edges[e].b[1] };
          const inter = raySegmentIntersection(source, dir, A, B);
          if (!inter) continue;
          if (!best || inter.t < best.t) {
            best = inter;
            bestEdgeIdx = e;
          }
        }
        if (best) {
          hits.push({ x: best.x, y: best.y, ang: angList[i].ang });
          hitEdges.push(bestEdgeIdx);
        } else {
          // Should not happen in a closed environment; as fallback project far away
          const far = { x: source.x + dir.x * 2000, y: source.y + dir.y * 2000 };
          hits.push({ x: far.x, y: far.y, ang: angList[i].ang });
          hitEdges.push(-1);
        }
      }
      return { vertsSorted: angList, hits, hitEdges };
    }

    // p5 Sketch
    let cnv;

    function setup() {
      const container = document.getElementById('canvas-container');
      cnv = createCanvas(state.canvasSize, state.canvasSize);
      cnv.parent(container);
      angleMode(RADIANS);
      noLoop(); // We'll redraw on demand

      // DOM elements
      state.angleSlider = document.getElementById('angle-slider');
      state.angleValueEl = document.getElementById('angle-value');
      state.resetButton = document.getElementById('reset-button');

      state.angleSlider.addEventListener('input', () => {
        state.angleValueEl.textContent = state.angleSlider.value;
        redraw();
      });

      state.resetButton.addEventListener('click', () => {
        state.source = { x: 275, y: 275 };
        state.angleSlider.value = "3";
        state.angleValueEl.textContent = "3";
        redraw();
      });

      // Initial draw
      redraw();
    }

    function mousePressed() {
      // Check if the click is near the source point
      const mx = mouseX, my = mouseY;
      if (mx < 0 || my < 0 || mx > width || my > height) return;
      const d = dist(mx, my, state.source.x, state.source.y);
      if (d <= 12) {
        state.dragging = true;
        state.dragOffset.x = state.source.x - mx;
        state.dragOffset.y = state.source.y - my;
      }
    }

    function mouseDragged() {
      if (!state.dragging) return;
      const proposed = {
        x: constrain(mouseX + state.dragOffset.x, 0, width),
        y: constrain(mouseY + state.dragOffset.y, 0, height)
      };
      if (isPointValidInEnvironment(proposed)) {
        state.source = proposed;
        redraw();
      }
    }

    function mouseReleased() {
      state.dragging = false;
    }

    function drawEnvironment() {
      // Draw outer with holes using beginContour; holes must be reversed
      stroke(170,170,170);
      strokeWeight(1);
      fill(255);

      beginShape();
      // Outer
      for (const v of outerPolygon) vertex(v[0], v[1]);
      // Holes as contours (reverse orientation)
      for (const h of holes) {
        beginContour();
        for (let i = h.length - 1; i >= 0; i--) {
          vertex(h[i][0], h[i][1]);
        }
        endContour();
      }
      endShape(CLOSE);

      // Draw vertices (small red dots)
      noStroke();
      fill('#ff0000');
      function drawVerts(poly) {
        for (const v of poly) ellipse(v[0], v[1], 4, 4);
      }
      drawVerts(outerPolygon);
      for (const h of holes) drawVerts(h);
    }

    function drawSourcePoint() {
      stroke('#0066ff');
      strokeWeight(1);
      fill('#0066ff');
      ellipse(state.source.x, state.source.y, 8, 8);
      noFill();
      ellipse(state.source.x, state.source.y, 12, 12);
    }

    function drawHelperRayAndArc(sweep, N) {
      if (N <= 0 || sweep.vertsSorted.length === 0) return;
      const idx = Math.min(N - 1, sweep.vertsSorted.length - 1);

      // Helper Ray to N-th vertex (not to intersection point)
      const vN = sweep.vertsSorted[idx].v;
      stroke('#0066ff');
      strokeWeight(1.5);
      line(state.source.x, state.source.y, vN.x, vN.y);

      // Sweep Arc from first angle to N-th angle
      const startAng = sweep.vertsSorted[0].ang;
      const stopAng = sweep.vertsSorted[idx].ang;
      noFill();
      stroke('#0066ff');
      strokeWeight(2);
      const r = 26;
      // Ensure we draw in increasing angular direction
      push();
      translate(state.source.x, state.source.y);
      arc(0, 0, r * 2, r * 2, startAng, stopAng);
      pop();
    }

    function drawActiveEdge(sweep, N) {
      if (N <= 0 || sweep.hits.length === 0) return;
      if (N - 1 >= sweep.hits.length) return;
      const edgeIdx = sweep.hitEdges[N - 1];
      if (edgeIdx == null || edgeIdx < 0) return;
      const edges = allEdges();
      const e = edges[edgeIdx];
      stroke('#00cc00');
      strokeWeight(3);
      line(e.a[0], e.a[1], e.b[0], e.b[1]);
    }

    function drawVisibility(sweep, N) {
      const total = sweep.hits.length;
      const sliderMax = state.sliderMax;
      const completed = Number(state.angleSlider.value) == sliderMax;

      const strokeCol = color(170, 170, 170, 128);
      stroke(strokeCol);
      strokeWeight(1);

      if (completed) {
        // Draw full visibility polygon in orange-yellow
        fill('#ffcc66');
        beginShape();
        for (let i = 0; i < total; i++) {
          vertex(sweep.hits[i].x, sweep.hits[i].y);
        }
        endShape(CLOSE);
        return;
      }

      // Partial: committed part up to vertex N-1 (i.e., indices [0, N-2])
      const K = Math.max(0, Math.min(N - 1, total - 1));
      if (K > 0) {
        fill('#ffff99');
        beginShape();
        // Build a "fan" from source: S -> hit[0] -> ... -> hit[K-1]
        vertex(state.source.x, state.source.y);
        for (let i = 0; i < K; i++) {
          vertex(sweep.hits[i].x, sweep.hits[i].y);
        }
        endShape(CLOSE);
      }

      // Active triangle with vertex N (connect S, hit[N-2], hit[N-1])
      if (N >= 2 && total >= 2) {
        const i2 = Math.min(N - 2, total - 1);
        const i1 = Math.min(N - 1, total - 1);
        if (i1 !== i2) {
          fill('#ffcc66');
          beginShape();
          vertex(state.source.x, state.source.y);
          vertex(sweep.hits[i2].x, sweep.hits[i2].y);
          vertex(sweep.hits[i1].x, sweep.hits[i1].y);
          endShape(CLOSE);
        }
      }
    }

    function draw() {
      background('#ffffff');

      // Compute sweep every frame (fast for small N)
      const sweep = computeSweep(state.source);

      // Draw static environment
      drawEnvironment();

      // Determine slider position
      const N = parseInt(state.angleSlider.value, 10);
      const isComplete = (N === state.sliderMax);

      // Draw visibility before helpers so helpers sit on top
      drawVisibility(sweep, N);

      // Helpers only when not complete AND the requested step is within actual available steps
      if (!isComplete && N > 0 && N <= sweep.hits.length) {
        drawHelperRayAndArc(sweep, N);
        drawActiveEdge(sweep, N);
      }

      // Draw source point on top
      drawSourcePoint();

      // Optional legend
      drawLegend();
    }

    function drawLegend() {
      // Simple legend implemented using p5 overlay text
      // (kept minimal; not a requirement, but helpful)
      push();
      const pad = 8;
      const x = 12, y = height - 88, w = 210, h = 76;
      noStroke();
      fill(255, 255, 255, 230);
      rect(x, y, w, h, 6);
      stroke(220);
      noFill();
      rect(x, y, w, h, 6);
      noStroke();
      fill(68);
      textSize(12);
      const lineH = 15;
      let ty = y + pad + 12;
      text('Legend:', x + pad, ty); ty += lineH;
      // vertices
      fill('#ff0000');
      ellipse(x + pad + 5, ty - 5, 8, 8);
      fill(68);
      text('Polygon vertices', x + pad + 16, ty); ty += lineH;
      // source
      fill('#0066ff');
      ellipse(x + pad + 5, ty - 5, 8, 8);
      fill(68);
      text('Source point', x + pad + 16, ty); ty += lineH;
      // visibility committed
      fill('#ffff99');
      stroke(170,170,170,128);
      rect(x + pad + 2, ty - 12, 12, 10);
      noStroke();
      fill(68);
      text('Committed visibility', x + pad + 20, ty); ty += lineH;
      // active triangle
      fill('#ffcc66');
      stroke(170,170,170,128);
      rect(x + pad + 2, ty - 12, 12, 10);
      noStroke();
      fill(68);
      text('Active triangle', x + pad + 20, ty);
      pop();
    }

    // Initial validation of source point (ensure inside)
    window.addEventListener('load', () => {
      // If initial source is invalid (e.g. exactly on an edge), nudge slightly
      if (!isPointValidInEnvironment(state.source)) {
        const dirs = [
          {x: 1, y: 0},{x:-1,y:0},{x:0,y:1},{x:0,y:-1},
          {x: 1, y: 1},{x:-1,y:1},{x:1,y:-1},{x:-1,y:-1}
        ];
        for (const d of dirs) {
          const p = { x: state.source.x + d.x * 2, y: state.source.y + d.y * 2 };
          if (isPointValidInEnvironment(p)) { state.source = p; break; }
        }
      }
    });
  </script>
</body>
