  <script>
    const orderSlider = document.getElementById('slider-order');
    const aSlider = document.getElementById('slider-a');
    const bSlider = document.getElementById('slider-b');

    const orderValue = document.getElementById('value-order');
    const aValue = document.getElementById('value-a');
    const bValue = document.getElementById('value-b');

    function factorial(n) {
      let r = 1;
      for (let i = 2; i <= n; i++) r *= i;
      return r;
    }

    function formatCoeff(num) {
      // Round to 5 decimals then trim trailing zeros
      const rounded = Number(num.toFixed(5));
      let s = rounded.toFixed(5);
      s = s.replace(/\.?0+$/, ''); // trim trailing zeros and trailing dot
      if (s === '-0') s = '0';
      return s;
    }

    function buildPolynomialString(coeffs, order) {
      // coeffs are c_k for k=0..5
      let parts = [];
      for (let k = 0; k <= order; k++) {
        const c = coeffs[k];
        if (Math.abs(c) < 1e-12) continue; // omit zero terms
        const absC = Math.abs(c);
        const coeffStr = formatCoeff(absC);
        let term = '';
        if (k === 0) {
          term = coeffStr;
        } else if (k === 1) {
          term = coeffStr + ' x';
        } else {
          term = coeffStr + ' x^' + k;
        }
        if (parts.length === 0) {
          term = (c < 0 ? '- ' : '') + term;
        } else {
          term = (c < 0 ? ' - ' : ' + ') + term;
        }
        parts.push(term);
      }
      if (parts.length === 0) parts.push('0');
      return 'f(x) ≈ ' + parts.join('');
    }

    function rk4Integrate(xs, a, b) {
      // System:
      // y1' = y2
      // y2' = -(y2 + 1.5*y1) / y1
      const y = [];
      let y1 = a;
      let y2 = b;
      y.push(y1);

      function derivs(x, y1, y2) {
        let denom = y1;
        if (Math.abs(denom) < 1e-12) denom = (denom >= 0 ? 1e-12 : -1e-12);
        const dy1 = y2;
        const dy2 = -(y2 + 1.5 * y1) / denom;
        return [dy1, dy2];
      }

      for (let i = 1; i < xs.length; i++) {
        const x = xs[i - 1];
        const h = xs[i] - xs[i - 1];

        const [k1_1, k1_2] = derivs(x, y1, y2);
        const [k2_1, k2_2] = derivs(x + 0.5 * h, y1 + 0.5 * h * k1_1, y2 + 0.5 * h * k1_2);
        const [k3_1, k3_2] = derivs(x + 0.5 * h, y1 + 0.5 * h * k2_1, y2 + 0.5 * h * k2_2);
        const [k4_1, k4_2] = derivs(x + h,     y1 + h * k3_1,       y2 + h * k3_2);

        y1 = y1 + (h / 6) * (k1_1 + 2 * k2_1 + 2 * k3_1 + k4_1);
        y2 = y2 + (h / 6) * (k1_2 + 2 * k2_2 + 2 * k3_2 + k4_2);

        y.push(y1);
      }
      return y;
    }

    function updatePlot() {
      // 1. Read inputs
      const order = parseInt(orderSlider.value);
      const a = parseFloat(aSlider.value);
      const b = parseFloat(bSlider.value);

      // 2. Update displays
      orderValue.textContent = String(order);
      aValue.textContent = a.toFixed(1);
      bValue.textContent = b.toFixed(1);

      // 3. Calculate derivatives at x=0 up to 5th
      const f_derivs = [];
      f_derivs[0] = a;
      f_derivs[1] = b;
      f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];
      f_derivs[3] = -(f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]) / f_derivs[0];
      f_derivs[4] = -(2 * f_derivs[3] * f_derivs[1] + f_derivs[2] ** 2 + f_derivs[3] + 1.5 * f_derivs[2]) / f_derivs[0];
      f_derivs[5] = -(3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]) / f_derivs[0];

      const coeffs = [];
      for (let k = 0; k <= 5; k++) coeffs[k] = f_derivs[k] / factorial(k);

      // 4. Generate power series data
      const xMax = 1.5;
      const N = 200;
      const xs = [];
      const yApprox = [];
      for (let i = 0; i < N; i++) {
        const x = (xMax * i) / (N - 1);
        xs.push(x);
        let s = 0;
        for (let k = 0; k <= order; k++) {
          s += coeffs[k] * Math.pow(x, k);
        }
        yApprox.push(s);
      }

      // 5. Numerical solution via RK4
      const yNum = rk4Integrate(xs, a, b);

      // 6. Agreement region x_limit where |diff| >= 0.01
      let x_limit = xMax;
      for (let i = 0; i < xs.length; i++) {
        if (Math.abs(yApprox[i] - yNum[i]) >= 0.01) {
          x_limit = xs[i];
          break;
        }
      }

      // 7. Update plot title with polynomial string
      const titleStr = buildPolynomialString(coeffs, order);

      // 8. Redraw plot
      const traceRed = {
        type: 'scatter',
        mode: 'lines',
        x: xs,
        y: yApprox,
        line: { color: 'red', width: 2 },
        name: 'Power series'
      };
      const traceGreen = {
        type: 'scatter',
        mode: 'lines',
        x: xs,
        y: yNum,
        line: { color: 'green', width: 3 },
        name: 'Numerical (RK4)'
      };
      const layout = {
        title: { text: titleStr, font: { size: 14 } },
        xaxis: { title: 'x', range: [0, 1.5], zeroline: false, gridcolor: '#e5e7eb' },
        yaxis: { title: 'f(x)', range: [0, 4], zeroline: false, gridcolor: '#e5e7eb' },
        margin: { l: 50, r: 20, t: 50, b: 40 },
        shapes: [
          {
            type: 'rect',
            xref: 'x',
            yref: 'paper',
            x0: 0,
            y0: 0,
            x1: x_limit,
            y1: 1,
            fillcolor: 'lightblue',
            opacity: 0.4,
            layer: 'below',
            line: { width: 0 }
          }
        ]
      };
      Plotly.react('plot-container', [traceRed, traceGreen], layout, { displayModeBar: false });
    }

    // Initialize
    orderSlider.addEventListener('input', updatePlot);
    aSlider.addEventListener('input', updatePlot);
    bSlider.addEventListener('input', updatePlot);

    // Initial render
    window.addEventListener('DOMContentLoaded', updatePlot);
  </script>
</body>
