<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script>
(function(){
  // DOM elements
  const selectText = document.getElementById('select-text');
  const btn2D = document.getElementById('btn-plot-2d');
  const btn3D = document.getElementById('btn-plot-3d');
  const btnQwerty = document.getElementById('btn-keyboard-qwerty');
  const btnDvorak = document.getElementById('btn-keyboard-dvorak');
  const canvas = document.getElementById('visualization-canvas');
  const container = document.getElementById('canvas-container');

  // State
  const state = {
    currentText: selectText.value,
    currentView: '2d', // '2d' or '3d'
    keyboardLayout: 'qwerty', // 'qwerty' or 'dvorak'
    freqMap: null
  };

  // Three.js globals
  let renderer, scene, activeCamera, orthoCamera, perspCamera;
  let contentGroup, axesGroup, lightsGroup;
  let animationFrameId;

  init();
  setupEvents();
  initialRender();

  function init(){
    renderer = new THREE.WebGLRenderer({
      canvas: canvas,
      antialias: true
    });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    // Groups for organization
    contentGroup = new THREE.Group();
    axesGroup = new THREE.Group();
    lightsGroup = new THREE.Group();
    scene.add(axesGroup);
    scene.add(contentGroup);
    scene.add(lightsGroup);

    // Lights
    const amb = new THREE.AmbientLight(0xffffff, 0.6);
    const dir = new THREE.DirectionalLight(0xffffff, 0.9);
    dir.position.set(5, 10, 8);
    dir.castShadow = false;
    lightsGroup.add(amb);
    lightsGroup.add(dir);

    // Cameras
    createCameras();
    onResize();
    window.addEventListener('resize', onResize);
    animate();
  }

  function createCameras(){
    // Orthographic for 2D chart
    const width = container.clientWidth || 800;
    const height = container.clientHeight || 600;
    const aspect = width / height;
    const frustumHeight = 20; // world units
    const frustumWidth = frustumHeight * aspect;
    orthoCamera = new THREE.OrthographicCamera(
      -frustumWidth/2, frustumWidth/2,
      frustumHeight/2, -frustumHeight/2,
      -100, 1000
    );
    orthoCamera.position.set(0, 10, 50);
    orthoCamera.lookAt(new THREE.Vector3(0, 10, 0));

    // Perspective for 3D keyboard
    perspCamera = new THREE.PerspectiveCamera(45, aspect, 0.1, 2000);
    perspCamera.position.set(0, 12, 18);
    perspCamera.lookAt(new THREE.Vector3(0, 0, 0));

    activeCamera = orthoCamera;
  }

  function onResize(){
    const width = container.clientWidth;
    const height = container.clientHeight;
    renderer.setSize(width, height, false);

    const aspect = width / height;
    // Update ortho camera
    const frustumHeight = 20;
    const frustumWidth = frustumHeight * aspect;
    orthoCamera.left = -frustumWidth/2;
    orthoCamera.right = frustumWidth/2;
    orthoCamera.top = frustumHeight/2;
    orthoCamera.bottom = -frustumHeight/2;
    orthoCamera.updateProjectionMatrix();

    // Update perspective camera
    perspCamera.aspect = aspect;
    perspCamera.updateProjectionMatrix();
  }

  function animate(){
    animationFrameId = requestAnimationFrame(animate);
    renderer.render(scene, activeCamera);
  }

  function setupEvents(){
    selectText.addEventListener('change', () => {
      state.currentText = selectText.value;
      state.freqMap = calculateFrequencies(state.currentText);
      redraw();
    });

    btn2D.addEventListener('click', () => {
      setActive(btn2D, true);
      setActive(btn3D, false);
      state.currentView = '2d';
      redraw();
    });

    btn3D.addEventListener('click', () => {
      setActive(btn3D, true);
      setActive(btn2D, false);
      state.currentView = '3d';
      redraw();
    });

    btnQwerty.addEventListener('click', () => {
      setActive(btnQwerty, true);
      setActive(btnDvorak, false);
      state.keyboardLayout = 'qwerty';
      if(state.currentView === '3d') redraw();
    });

    btnDvorak.addEventListener('click', () => {
      setActive(btnDvorak, true);
      setActive(btnQwerty, false);
      state.keyboardLayout = 'dvorak';
      if(state.currentView === '3d') redraw();
    });
  }

  function setActive(button, isActive){
    if(isActive) button.classList.add('active');
    else button.classList.remove('active');
  }

  function initialRender(){
    // 1) Calculate default frequencies
    state.freqMap = calculateFrequencies(state.currentText);
    // 2) Render default 2D view, alpha-sorted A-Z (done inherently)
    // 3) Buttons already styled via initial markup
    redraw();
  }

  function redraw(){
    clearGroup(contentGroup);
    clearGroup(axesGroup);

    if(state.currentView === '2d'){
      activeCamera = orthoCamera;
      draw2DDistribution(state.freqMap);
    } else {
      activeCamera = perspCamera;
      draw3DKeyboard(state.freqMap, state.keyboardLayout);
    }
  }

  // Frequency calculation (A-Z only, case-insensitive)
  function calculateFrequencies(str){
    const freq = {};
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    for(const L of letters) freq[L] = 0;
    for(const ch of (str || '').toUpperCase()){
      if(ch >= 'A' && ch <= 'Z'){
        freq[ch] = (freq[ch] || 0) + 1;
      }
    }
    return freq;
  }

  // Utility: colormap from blue -> purple -> orange -> red
  function colorMap(t){
    // Clamp
    t = Math.max(0, Math.min(1, t));
    // Gradient stops
    const stops = [
      {t: 0.0, c: new THREE.Color(0x1E88E5)}, // blue
      {t: 0.33, c: new THREE.Color(0x7B1FA2)}, // purple
      {t: 0.66, c: new THREE.Color(0xFB8C00)}, // orange
      {t: 1.0, c: new THREE.Color(0xE53935)}  // red
    ];
    // Find segment
    for(let i=0;i<stops.length-1;i++){
      const a = stops[i], b = stops[i+1];
      if(t >= a.t && t <= b.t){
        const f = (t - a.t) / (b.t - a.t);
        return a.c.clone().lerp(b.c, f);
      }
    }
    return stops[stops.length-1].c.clone();
  }

  // 2D Distribution
  function draw2DDistribution(freq){
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    const counts = letters.map(L => freq[L] || 0);
    const maxCount = Math.max(1, ...counts);
    const yStep = 50;
    let yMax = Math.ceil(maxCount / yStep) * yStep;
    yMax = Math.max(yStep, yMax);
    const chartHeight = 12; // world units
    const yScale = chartHeight / yMax;

    const barW = 0.8, gap = 0.32, barD = 0.6;
    const n = letters.length;
    const chartWidth = n * (barW + gap) - gap;

    // Axes and grid
    drawAxesAndGrid2D(-chartWidth/2, chartWidth/2, chartHeight, yMax, yStep);

    // Bars
    const matParams = { metalness: 0.1, roughness: 0.8 };
    const group = new THREE.Group();
    contentGroup.add(group);

    const startX = -chartWidth / 2 + barW/2;
    for(let i=0;i<n;i++){
      const L = letters[i];
      const c = freq[L] || 0;
      const h = Math.max(0, c) * yScale;
      const normalized = c / yMax;

      const geom = new THREE.BoxBufferGeometry(barW, Math.max(0.05, h), barD);
      const color = colorMap(normalized);
      const mat = new THREE.MeshStandardMaterial({ color, ...matParams });
      const mesh = new THREE.Mesh(geom, mat);
      mesh.position.set(startX + i*(barW+gap), Math.max(0.05, h)/2, 0);
      group.add(mesh);

      // Letter label sprite above each bar
      const label = makeTextSprite(L, {
        fontSize: 64, fontFamily: '600 64px UI, Arial, sans-serif', fillStyle: '#111827'
      });
      label.position.set(mesh.position.x, Math.max(0.05, h) + 0.5, 0);
      // scale in world units
      label.scale.set(0.6, 0.3, 1);
      group.add(label);
    }

    // Adjust camera to fit
    fitOrthoCameraToRect(orthoCamera, chartWidth, chartHeight, { margin: 2, centerY: chartHeight/2 });
  }

  function drawAxesAndGrid2D(xMin, xMax, chartHeight, yMax, yStep){
    const axisColor = new THREE.Color(0x9ca3af);
    const gridColor = new THREE.Color(0xe5e7eb);
    const lines = [];
    const matGrid = new THREE.LineBasicMaterial({ color: gridColor, transparent: true, opacity: 1.0 });
    const matAxis = new THREE.LineBasicMaterial({ color: axisColor });

    // Horizontal grid lines at each tick
    for(let v=0; v<=yMax; v+=yStep){
      const y = v * (chartHeight / yMax);
      lines.push([xMin, y, 0, xMax, y, 0]);
    }
    // Baseline at y=0 already included; Y-axis line at x=xMin
    const axisGeom = new THREE.BufferGeometry();
    const axisVerts = new Float32Array([
      xMin, 0, 0, xMin, chartHeight, 0, // Y axis
      xMin, 0, 0, xMax, 0, 0           // X axis
    ]);
    axisGeom.setAttribute('position', new THREE.BufferAttribute(axisVerts, 3));
    const axisLines = new THREE.LineSegments(axisGeom, matAxis);
    axesGroup.add(axisLines);

    // Grid geometry
    const gridVerts = new Float32Array(lines.length * 6);
    for(let i=0;i<lines.length;i++){
      const [x1,y1,z1,x2,y2,z2] = lines[i];
      gridVerts.set([x1,y1,z1,x2,y2,z2], i*6);
    }
    const gridGeom = new THREE.BufferGeometry();
    gridGeom.setAttribute('position', new THREE.BufferAttribute(gridVerts, 3));
    const gridLines = new THREE.LineSegments(gridGeom, matGrid);
    axesGroup.add(gridLines);

    // Y tick labels
    for(let v=0; v<=yMax; v+=yStep){
      const y = v * (chartHeight / yMax);
      const sprite = makeTextSprite(String(v), {
        fontSize: 56, fontFamily: '500 56px UI, Arial, sans-serif', fillStyle: '#374151'
      });
      sprite.scale.set(1.0, 0.5, 1);
      sprite.position.set(xMin - 0.7, y, 0);
      axesGroup.add(sprite);
    }

    // Axis titles (optional)
    const yTitle = makeTextSprite("Frequency", {
      fontSize: 56, fontFamily: '600 56px UI, Arial, sans-serif', fillStyle: '#111827'
    });
    yTitle.scale.set(1.6, 0.8, 1);
    yTitle.position.set(xMin - 1.2, chartHeight + 0.5, 0);
    axesGroup.add(yTitle);
  }

  function fitOrthoCameraToRect(camera, width, height, opts){
    const margin = (opts && opts.margin) || 0;
    const centerY = (opts && opts.centerY) || height/2;
    camera.position.set(0, centerY, 50);
    camera.lookAt(new THREE.Vector3(0, centerY, 0));

    const aspect = (container.clientWidth || 800) / (container.clientHeight || 600);
    const h = height + margin*2;
    const w = width + margin*2;
    let viewH = h;
    let viewW = h * aspect;
    if(viewW < w){
      viewW = w;
      viewH = w / aspect;
    }
    camera.left = -viewW/2;
    camera.right = viewW/2;
    camera.top = viewH/2;
    camera.bottom = -viewH/2;
    camera.updateProjectionMatrix();
  }

  // 3D Keyboard
  function draw3DKeyboard(freq, layout){
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    const counts = letters.map(L => freq[L] || 0);
    const maxCount = Math.max(1, ...counts);
    const yStep = 50;
    let yMax = Math.ceil(maxCount / yStep) * yStep;
    yMax = Math.max(yStep, yMax);
    const chartHeight = 8; // world units for scaling keys
    const yScale = chartHeight / yMax;

    const mappingInfo = getKeyboardLayoutPositions(layout);
    const positions = mappingInfo.positions;
    const extent = mappingInfo.extent;

    // Axes & floor grid
    drawAxesAndFloorGrid3D(extent, chartHeight, yMax, yStep);

    // Row colors
    const rowColors = [
      new THREE.Color(0x9E9D24), // top row - olive/yellow-green
      new THREE.Color(0x26C6DA), // home row - blue/cyan
      new THREE.Color(0x8E24AA)  // bottom row - purple
    ];

    const group = new THREE.Group();
    contentGroup.add(group);

    const cylGeom = new THREE.CylinderBufferGeometry(0.55, 0.55, 1, 32);
    const matParams = { metalness: 0.3, roughness: 0.6 };

    for(const L of letters){
      const p = positions[L];
      if(!p) continue;
      const c = freq[L] || 0;
      const height = Math.max(0.12, c * yScale + 0.15);
      const mat = new THREE.MeshStandardMaterial({ color: rowColors[p.row].clone(), ...matParams });
      const cyl = new THREE.Mesh(cylGeom, mat);
      cyl.position.set(p.x, height/2, p.z);
      cyl.scale.set(1, height, 1);
      group.add(cyl);

      // Circular label on top
      const circle = makeCircleLabel(L, 128);
      circle.position.set(p.x, height + 0.01, p.z);
      circle.rotation.x = -Math.PI/2; // lay flat
      group.add(circle);
    }

    // Camera position/target to fit the keyboard nicely
    const centerX = (extent.minX + extent.maxX) / 2;
    const centerZ = (extent.minZ + extent.maxZ) / 2;
    const spanX = extent.maxX - extent.minX;
    const spanZ = extent.maxZ - extent.minZ;

    const diag = Math.sqrt(spanX*spanX + spanZ*spanZ);
    const camDist = Math.max(18, diag * 2.2);
    perspCamera.position.set(centerX, chartHeight + 6, centerZ + camDist * 0.25);
    perspCamera.lookAt(new THREE.Vector3(centerX, chartHeight * 0.35, centerZ));
    perspCamera.updateProjectionMatrix();
  }

  function drawAxesAndFloorGrid3D(extent, chartHeight, yMax, yStep){
    // Floor grid in XZ plane
    const gridColor = new THREE.Color(0xe5e7eb);
    const axisColor = new THREE.Color(0x9ca3af);
    const xMin = extent.minX - 1.0, xMax = extent.maxX + 1.0;
    const zMin = extent.minZ - 1.0, zMax = extent.maxZ + 1.0;

    const lines = [];
    const gridStep = 1.0;
    // Vertical grid lines along Z
    for(let x = Math.ceil(xMin); x <= Math.floor(xMax); x += gridStep){
      lines.push([x, 0, zMin, x, 0, zMax]);
    }
    // Horizontal grid lines along X
    for(let z = Math.ceil(zMin); z <= Math.floor(zMax); z += gridStep){
      lines.push([xMin, 0, z, xMax, 0, z]);
    }

    const gridVerts = new Float32Array(lines.length * 6);
    for(let i=0;i<lines.length;i++){
      const [x1,y1,z1,x2,y2,z2] = lines[i];
      gridVerts.set([x1,y1,z1,x2,y2,z2], i*6);
    }
    const gridGeom = new THREE.BufferGeometry();
    gridGeom.setAttribute('position', new THREE.BufferAttribute(gridVerts, 3));
    const matGrid = new THREE.LineBasicMaterial({ color: gridColor });
    const gridLines = new THREE.LineSegments(gridGeom, matGrid);
    axesGroup.add(gridLines);

    // Y-axis at the left edge
    const axisVerts = new Float32Array([
      xMin, 0, zMin, xMin, chartHeight, zMin
    ]);
    const axisGeom = new THREE.BufferGeometry();
    axisGeom.setAttribute('position', new THREE.BufferAttribute(axisVerts, 3));
    const matAxis = new THREE.LineBasicMaterial({ color: axisColor });
    const yAxis = new THREE.LineSegments(axisGeom, matAxis);
    axesGroup.add(yAxis);

    // Y tick labels (as Sprites facing camera)
    for(let v=0; v<=yMax; v+=yStep){
      const y = v * (chartHeight / yMax);
      const sprite = makeTextSprite(String(v), {
        fontSize: 56, fontFamily: '500 56px UI, Arial, sans-serif', fillStyle: '#374151'
      });
      sprite.scale.set(1.0, 0.6, 1);
      sprite.position.set(xMin - 0.6, y, zMin);
      axesGroup.add(sprite);
    }

    const yTitle = makeTextSprite("Frequency", {
      fontSize: 56, fontFamily: '600 56px UI, Arial, sans-serif', fillStyle: '#111827'
    });
    yTitle.scale.set(1.6, 0.8, 1);
    yTitle.position.set(xMin - 1.0, chartHeight + 0.6, zMin);
    axesGroup.add(yTitle);
  }

  function getKeyboardLayoutPositions(layout){
    let rows;
    if(layout === 'dvorak'){
      // as given: 3 rows
      rows = [
        "PYFGCRL",       // top
        "AOEUIDHTNS",    // home
        "QJKXBMWVZ"      // bottom
      ];
    } else {
      // qwerty
      rows = [
        "QWERTYUIOP",    // top
        "ASDFGHJKL",     // home
        "ZXCVBNM"        // bottom
      ];
    }

    const xSpacing = 1.3;
    const zSpacing = 1.6;
    const positions = {};
    let minX = Infinity, maxX = -Infinity, minZ = Infinity, maxZ = -Infinity;

    for(let rowIndex=0; rowIndex<rows.length; rowIndex++){
      const row = rows[rowIndex];
      const n = row.length;
      for(let i=0; i<n; i++){
        const ch = row[i];
        const x = (i - (n - 1)/2) * xSpacing;
        const z = -rowIndex * zSpacing;
        positions[ch] = { x, z, row: rowIndex };
        minX = Math.min(minX, x);
        maxX = Math.max(maxX, x);
        minZ = Math.min(minZ, z);
        maxZ = Math.max(maxZ, z);
      }
    }

    // For letters not on those 3 rows (should be none), leave undefined
    // Ensure we have extents
    return {
      positions,
      extent: { minX, maxX, minZ, maxZ }
    };
  }

  // Text sprite (for axis ticks and bar labels)
  function makeTextSprite(text, options){
    const opts = Object.assign({
      fontSize: 64,
      fontFamily: '64px Arial',
      fillStyle: '#111',
      padding: 16
    }, options || {});
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Measure text
    ctx.font = opts.fontFamily || (opts.fontSize + 'px Arial');
    const metrics = ctx.measureText(text);
    const textWidth = Math.ceil(metrics.width);
    const textHeight = Math.ceil(opts.fontSize * 1.2);
    canvas.width = nextPow2(textWidth + 2*opts.padding);
    canvas.height = nextPow2(textHeight + 2*opts.padding);

    // Re-apply font after resize
    ctx.font = opts.fontFamily || (opts.fontSize + 'px Arial');
    ctx.textAlign = 'left';
    ctx.textBaseline = 'middle';
    ctx.fillStyle = opts.fillStyle;
    ctx.clearRect(0,0,canvas.width, canvas.height);
    ctx.fillText(text, opts.padding, canvas.height/2);

    const texture = new THREE.CanvasTexture(canvas);
    texture.minFilter = THREE.LinearFilter;
    const material = new THREE.SpriteMaterial({ map: texture, transparent: true });
    const sprite = new THREE.Sprite(material);
    // World size scaling will be set by caller via sprite.scale
    sprite.userData.dispose = () => {
      texture.dispose();
      material.dispose();
    };
    return sprite;
  }

  // Circle label for key top
  function makeCircleLabel(letter, size){
    const s = size || 128;
    const canvas = document.createElement('canvas');
    canvas.width = s;
    canvas.height = s;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0,0,s,s);

    // Draw white disc with subtle border
    ctx.beginPath();
    ctx.arc(s/2, s/2, s*0.42, 0, Math.PI*2);
    ctx.fillStyle = '#ffffff';
    ctx.fill();
    ctx.lineWidth = Math.max(2, s*0.02);
    ctx.strokeStyle = '#d1d5db';
    ctx.stroke();

    // Draw letter
    ctx.fillStyle = '#111827';
    ctx.font = `bold ${Math.floor(s*0.45)}px Arial`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(letter, s/2, s/2);

    const tex = new THREE.CanvasTexture(canvas);
    const mat = new THREE.MeshBasicMaterial({ map: tex, transparent: true });
    const geom = new THREE.CircleBufferGeometry(0.5, 48);
    const mesh = new THREE.Mesh(geom, mat);
    mesh.userData.dispose = () => {
      tex.dispose();
      mat.dispose();
      geom.dispose();
    };
    return mesh;
  }

  function nextPow2(v){
    return Math.pow(2, Math.ceil(Math.log2(Math.max(2, v))));
  }

  // Clear group and dispose geometries/materials/textures
  function clearGroup(group){
    while(group.children.length){
      const obj = group.children.pop();
      disposeObject(obj);
    }
  }

  function disposeObject(obj){
    if(obj.userData && typeof obj.userData.dispose === 'function'){
      obj.userData.dispose();
    }
    if(obj.geometry){
      obj.geometry.dispose();
    }
    if(obj.material){
      if(Array.isArray(obj.material)){
        obj.material.forEach(m => {
          if(m.map) m.map.dispose();
          m.dispose();
        });
      } else {
        if(obj.material.map) obj.material.map.dispose();
        obj.material.dispose();
      }
    }
    if(obj.children){
      for(const c of obj.children){
        disposeObject(c);
      }
    }
    if(obj.parent){
      obj.parent.remove(obj);
    }
  }
})();
</script>
</body>
</html>
