<script>
(function(){
  // DOM references
  const sliderTime   = document.getElementById('slider-time');
  const sliderTrail  = document.getElementById('slider-trail');
  const sliderPoints = document.getElementById('slider-points');
  const sliderPAmp   = document.getElementById('slider-p-amp');
  const sliderPWl    = document.getElementById('slider-p-wl');
  const sliderSAmp   = document.getElementById('slider-s-amp');
  const sliderSWl    = document.getElementById('slider-s-wl');

  const labelTime   = document.getElementById('label-time');
  const labelTrail  = document.getElementById('label-trail');
  const labelPoints = document.getElementById('label-points');
  const labelPAmp   = document.getElementById('label-p-amp');
  const labelPWl    = document.getElementById('label-p-wl');
  const labelSAmp   = document.getElementById('label-s-amp');
  const labelSWl    = document.getElementById('label-s-wl');

  const btnPlayPause = document.getElementById('btn-play-pause');
  const btnReset     = document.getElementById('btn-reset');

  const defaults = {
    time: 0,
    trail: 5,
    points: 50,
    pAmp: 0.2,
    pWl: 3,
    sAmp: 0.1,
    sWl: 1
  };

  let isPlaying = false;

  // Utilities
  const fmt = (val, digits=3) => {
    // Display small numbers with up to 'digits' decimals, trim trailing zeros
    const s = Number(val).toFixed(digits);
    return s.replace(/(\.\d*?[1-9])0+$|\.0+$/,'$1');
  };

  function updateLabels() {
    labelTime.textContent   = fmt(sliderTime.value, 2);
    labelTrail.textContent  = sliderTrail.value;
    labelPoints.textContent = sliderPoints.value;
    labelPAmp.textContent   = fmt(sliderPAmp.value, 3);
    labelPWl.textContent    = fmt(sliderPWl.value, 2);
    labelSAmp.textContent   = fmt(sliderSAmp.value, 3);
    labelSWl.textContent    = fmt(sliderSWl.value, 2);
  }

  function resetControls() {
    sliderTime.value   = defaults.time;
    sliderTrail.value  = defaults.trail;
    sliderPoints.value = defaults.points;
    sliderPAmp.value   = defaults.pAmp;
    sliderPWl.value    = defaults.pWl;
    sliderSAmp.value   = defaults.sAmp;
    sliderSWl.value    = defaults.sWl;
    updateLabels();
    isPlaying = false;
    btnPlayPause.textContent = 'Play';
    // Also reset state in sketch if instantiated
    if (window._seismicSketch && window._seismicSketch.resetSimulation) {
      window._seismicSketch.resetSimulation();
    }
  }

  // Prepare p5 Sketch
  const seismicSketch = (p) => {
    // Logical coords
    const XMIN = 0, XMAX = 5;
    const YMIN = -0.5, YMAX = 0.5;

    // Plot margins
    let margin = {left: 60, right: 20, top: 20, bottom: 40};

    let particles = [];
    let time = defaults.time;

    // Canvas
    let cnv;

    function containerSize() {
      const cont = document.getElementById('canvas-container');
      const w = Math.max(320, cont.clientWidth);
      // Maintain a reasonable aspect ratio
      const h = Math.round(Math.min(560, Math.max(320, w*0.55)));
      return {w, h};
    }

    function initParticles() {
      const N = parseInt(sliderPoints.value, 10);
      particles = [];
      for (let i = 0; i < N; i++) {
        const x0 = (N === 1) ? (XMIN + XMAX)/2 : (i/(N-1))*(XMAX - XMIN) + XMIN;
        particles.push({ x0, history: [] });
      }
    }

    function trimHistory() {
      const maxLen = parseInt(sliderTrail.value, 10);
      for (const pt of particles) {
        if (pt.history.length > maxLen) {
          pt.history.splice(0, pt.history.length - maxLen);
        }
      }
    }

    // Map logical to pixel
    function xPixel(x) {
      return p.map(x, XMIN, XMAX, margin.left, p.width - margin.right);
    }
    function yPixel(y) {
      return p.map(y, YMAX, YMIN, margin.top, p.height - margin.bottom);
    }

    function drawAxes() {
      p.push();
      p.stroke(200);
      p.strokeWeight(1);
      p.fill(80);
      p.textSize(12);

      // Frame
      const x0 = margin.left, x1 = p.width - margin.right;
      const y0 = margin.top,  y1 = p.height - margin.bottom;

      // Border
      p.noFill();
      p.stroke(230);
      p.rect(x0, y0, x1 - x0, y1 - y0);

      // Grid and ticks: X 0..5
      p.stroke(220);
      for (let xi = 0; xi <= 5; xi++) {
        const xx = xPixel(xi);
        p.line(xx, y0, xx, y1);
      }
      // Y ticks: -0.4 to 0.4 every 0.2
      for (let yi = -0.4; yi <= 0.4001; yi += 0.2) {
        const yy = yPixel(yi);
        p.line(x0, yy, x1, yy);
      }

      // X axis labels
      p.fill(50);
      p.noStroke();
      p.textAlign(p.CENTER, p.TOP);
      for (let xi = 0; xi <= 5; xi++) {
        const xx = xPixel(xi);
        p.text(`${xi}`, xx, y1 + 6);
      }

      // Y axis labels
      p.textAlign(p.RIGHT, p.CENTER);
      for (let yi = -0.4; yi <= 0.4001; yi += 0.2) {
        const yy = yPixel(yi);
        p.text(fmt(yi,1), x0 - 6, yy);
      }

      // Axis titles
      p.textAlign(p.CENTER, p.TOP);
      p.text('x (units)', (x0 + x1)/2, y1 + 22);
      p.push();
      p.translate(x0 - 36, (y0 + y1)/2);
      p.rotate(-p.HALF_PI);
      p.text('displacement (units)', 0, 0);
      p.pop();

      // Zero line
      p.stroke(200);
      p.line(x0, yPixel(0), x1, yPixel(0));

      p.pop();
    }

    function stepTime() {
      // Increment time if playing
      if (isPlaying) {
        // Advance roughly 0.02 per second scaled by frame delta
        const dt = 0.5 * (p.deltaTime / 1000); // 0.5 units of time per second
        time += dt;
        const tmax = parseFloat(sliderTime.max);
        if (time > tmax) time = 0;
        sliderTime.value = time;
        labelTime.textContent = fmt(time, 2);
      } else {
        // Sync time with slider when paused
        time = parseFloat(sliderTime.value);
      }
    }

    p.setup = function() {
      // Remove placeholder canvas and create p5 canvas with same id
      const placeholder = document.getElementById('p5-canvas');
      if (placeholder && placeholder.parentNode) {
        placeholder.parentNode.removeChild(placeholder);
      }
      const {w, h} = containerSize();
      cnv = p.createCanvas(w, h);
      cnv.elt.id = 'p5-canvas';
      cnv.parent('canvas-container');
      p.pixelDensity(1.5);

      initParticles();
      updateLabels();

      // UI events
      sliderTime.addEventListener('input', () => {
        labelTime.textContent = fmt(sliderTime.value, 2);
      });
      sliderTrail.addEventListener('input', () => {
        labelTrail.textContent = sliderTrail.value;
        trimHistory();
      });
      sliderPoints.addEventListener('input', () => {
        labelPoints.textContent = sliderPoints.value;
        initParticles();
      });

      sliderPAmp.addEventListener('input', () => labelPAmp.textContent = fmt(sliderPAmp.value,3));
      sliderPWl.addEventListener('input', () => labelPWl.textContent = fmt(sliderPWl.value,2));
      sliderSAmp.addEventListener('input', () => labelSAmp.textContent = fmt(sliderSAmp.value,3));
      sliderSWl.addEventListener('input', () => labelSWl.textContent = fmt(sliderSWl.value,2));

      btnPlayPause.addEventListener('click', () => {
        isPlaying = !isPlaying;
        btnPlayPause.textContent = isPlaying ? 'Pause' : 'Play';
      });

      btnReset.addEventListener('click', resetControls);

      // Expose reset function for controls
      window._seismicSketch = {
        resetSimulation: () => {
          time = defaults.time;
          initParticles();
          // Clear histories
          for (const pt of particles) pt.history = [];
        }
      };
    };

    p.windowResized = function() {
      const {w, h} = containerSize();
      p.resizeCanvas(w, h);
    };

    p.draw = function() {
      p.background(255);

      // Read current slider values
      stepTime();
      const pAmp = parseFloat(sliderPAmp.value);
      const pWl  = Math.max(0.0001, parseFloat(sliderPWl.value));
      const sAmp = parseFloat(sliderSAmp.value);
      const sWl  = Math.max(0.0001, parseFloat(sliderSWl.value));
      const trailLen = parseInt(sliderTrail.value, 10);

      drawAxes();

      // Plot area bounds
      const xLeft = margin.left, xRight = p.width - margin.right;
      const yTop  = margin.top,  yBot   = p.height - margin.bottom;

      // Draw vertical compression lines and particles
      p.push();
      // Vertical lines (compression visualization)
      p.strokeWeight(1);
      p.stroke('#D3D3D3');

      for (const pt of particles) {
        const phaseP = 2 * Math.PI * ((pt.x0 / pWl) - time);
        const phaseS = 2 * Math.PI * ((pt.x0 / sWl) - time);
        const dx = pAmp * Math.sin(phaseP);
        const dy = sAmp * Math.sin(phaseS);
        const xf = pt.x0 + dx;
        const yf = dy;

        // Draw vertical line at x_final
        const xx = xPixel(xf);
        p.line(xx, yTop, xx, yBot);

        // Update history
        pt.history.push({x: xf, y: yf});
        if (pt.history.length > trailLen) {
          pt.history.splice(0, pt.history.length - trailLen);
        }
      }

      // Trails
      if (trailLen > 0) {
        p.noFill();
        p.stroke(106, 90, 205, 90); // slateblue with alpha
        p.strokeWeight(3);
        for (const pt of particles) {
          // Draw small semi-transparent points for each history entry
          p.push();
          p.stroke(106, 90, 205, 70);
          p.strokeWeight(2);
          for (let i = 0; i < pt.history.length; i++) {
            const h = pt.history[i];
            const px = xPixel(h.x);
            const py = yPixel(h.y);
            p.point(px, py);
          }
          p.pop();
        }
      }

      // Particles (current)
      p.noStroke();
      p.fill('#6A5ACD');
      for (const pt of particles) {
        const last = pt.history[pt.history.length - 1];
        const px = xPixel(last ? last.x : pt.x0);
        const py = yPixel(last ? last.y : 0);
        p.circle(px, py, 6);
      }

      p.pop();
    };
  };

  // Instantiate p5
  new p5(seismicSketch);

  // Initialize labels
  updateLabels();

  // Ensure Reset applies immediately on load if needed
  // (no-op here because defaults already set)
})();
</script>
</body>
</html>
