      </div>

      <div class="control-group component">
        <span class="group-label">displacement component</span>
        <div id="btn-group-component" class="btn-group" role="group" aria-label="Displacement component">
          <button id="btn-component-x" title="X component">X</button>
          <button id="btn-component-y" title="Y component">Y</button>
          <button id="btn-component-z" title="Z component" class="active">Z</button>
        </div>
      </div>

      <div class="control-group fault-type">
        <span class="group-label">fault type</span>
        <div id="btn-group-fault-type" class="btn-group" role="group" aria-label="Fault type">
          <button id="btn-fault-tensile" title="Tensile opening" class="active">tensile</button>
          <button id="btn-fault-strike-slip" title="Strike-slip shear">strike-slip</button>
          <button id="btn-fault-normal" title="Normal fault">normal</button>
        </div>
      </div>
    </div>

    <div id="plot-container">
      <div id="plot"></div>
    </div>
  </div>

  <script>
    // Physical constants
    const nu = 0.25;   // Poisson's ratio
    const b = 1.0;     // Dislocation magnitude (meters); used as a scale factor

    // UI state
    let selectedComponent = 'Z';          // 'X' | 'Y' | 'Z'
    let selectedFaultType = 'tensile';    // 'tensile' | 'strike-slip' | 'normal'

    // Utility: Manage active class within a button group
    function setActiveInGroup(groupEl, clickedBtn) {
      const current = groupEl.querySelector('.active');
      if (current && current !== clickedBtn) current.classList.remove('active');
      clickedBtn.classList.add('active');
    }

    // Core calculation: Displacements at surface point (x, y) due to a buried point source
    // This implements standard forms and heuristics:
    // - Tensile: Mogi (1958) source (standard, isotropic volume change-like decay)
    // - Strike-slip: tangential (rotational) shear pattern in horizontal plane (double-couple-like heuristic)
    // - Normal: down-dip slip-oriented field modulated by inverse-cubic decay (Okada-inspired heuristic for direction)
    function calculateDisplacements(x, y, dipRad, depth, faultType, nu) {
      const eps = 1e-12;
      const d = Math.max(depth, eps);
      const r2 = x*x + y*y;
      const r = Math.sqrt(r2);
      const R = Math.sqrt(r2 + d*d);
      const C = (1 - nu) * b / Math.PI; // common scale factor

      let ux = 0, uy = 0, uz = 0;

      if (faultType === 'tensile') {
        // Mogi source (isotropic opening) - standard, simple, widely used
        const invR3 = 1.0 / Math.pow(R + eps, 3);
        const ur = C * r * invR3; // radial horizontal
        const uz_mogi = C * d * invR3;
        if (r > 0) {
          ux = ur * (x / r);
          uy = ur * (y / r);
        } else {
          ux = 0;
          uy = 0;
        }
        uz = uz_mogi;
      } else if (faultType === 'strike-slip') {
        // Horizontal tangential (rotational) shear around source, small vertical component
        // Decay ~ 1/R^3 for smoothness; scale with depth to set amplitude
        const invR3 = 1.0 / Math.pow(R + eps, 3);
        const ut = C * d * invR3; // tangential magnitude
        if (r > 0) {
          // e_theta = (-y/r, x/r, 0)
          ux = ut * (-y / r);
          uy = ut * (x / r);
        } else {
          ux = 0; uy = 0;
        }
        // Small vertical modulation with dip
        uz = 0.15 * ut * Math.sin(dipRad);
      } else if (faultType === 'normal') {
        // Slip along down-dip direction in the x-z plane; add small radial component
        const sx = Math.cos(dipRad);
        const sy = 0.0;
        const sz = -Math.sin(dipRad); // down-dip has negative vertical
        const vx = x, vy = y, vz = d; // vector from source to surface point
        const R3inv = 1.0 / Math.pow(R + eps, 3);
        // Project position onto slip direction to get signed amplitude
        const proj = vx * sx + vy * sy + vz * sz;
        const U = C * proj * R3inv;
        ux = U * sx;
        uy = U * sy;
        uz = U * sz;
        // Add a small symmetric radial contribution to mimic near-field spread
        const ur2 = 0.2 * C * r * R3inv;
        if (r > 0) {
          ux += ur2 * (x / r);
          uy += ur2 * (y / r);
        }
      }
      return { ux, uy, uz };
    }

    function generateGrid(limit, n) {
      const arr = new Array(n);
      for (let i = 0; i < n; i++) {
        arr[i] = -limit + (2 * limit) * (i / (n - 1));
      }
      return arr;
    }

    async function updatePlot() {
      const dipDeg = parseFloat(document.getElementById('slider-dip').value);
      const depth = parseFloat(document.getElementById('slider-depth').value);
      const limit = parseFloat(document.getElementById('slider-limit').value);

      const dipRad = dipDeg * Math.PI / 180.0;
      const n = 50;

      const xCoords = generateGrid(limit, n);
      const yCoords = generateGrid(limit, n);

      // z_values is 2D: rows correspond to y, columns correspond to x
      const z_values = new Array(yCoords.length);
      for (let j = 0; j < yCoords.length; j++) {
        const y = yCoords[j];
        const row = new Array(xCoords.length);
        for (let i = 0; i < xCoords.length; i++) {
          const x = xCoords[i];
          const disp = calculateDisplacements(x, y, dipRad, depth, selectedFaultType, nu);
          let val = 0;
          if (selectedComponent === 'X') val = disp.ux;
          else if (selectedComponent === 'Y') val = disp.uy;
          else val = disp.uz; // 'Z'
          row[i] = val * 1000.0; // meters -> millimeters
        }
        z_values[j] = row;
      }

      const data = [{
        type: 'surface',
        x: xCoords,
        y: yCoords,
        z: z_values,
        colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
        showscale: false,
        contours: {
          x: { show: true, color: 'black', width: 0.5 },
          y: { show: true, color: 'black', width: 0.5 },
          z: { show: false }
        },
        lighting: {
          ambient: 0.8,
          diffuse: 0.8,
          specular: 0.2
        }
      }];

      const layout = {
        title: '',
        scene: {
          xaxis: { title: 'x (m)', range: [-limit, limit], gridcolor: '#243042', zerolinecolor: '#243042' },
          yaxis: { title: 'y (m)', range: [-limit, limit], gridcolor: '#243042', zerolinecolor: '#243042' },
          zaxis: { title: 'z (mm)', autorange: true, gridcolor: '#243042', zerolinecolor: '#243042' },
          camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } },
          bgcolor: '#0b0f16'
        },
        paper_bgcolor: '#0b0f16',
        margin: { l: 20, r: 20, b: 20, t: 20 }
      };

      const config = { displayModeBar: true, responsive: true };
      Plotly.react('plot', data, layout, config);
    }

    function initUI() {
      // Initialize value displays
      const dipEl = document.getElementById('slider-dip');
      const depthEl = document.getElementById('slider-depth');
      const limitEl = document.getElementById('slider-limit');

      document.getElementById('value-dip').textContent = dipEl.value;
      document.getElementById('value-depth').textContent = depthEl.value;
      document.getElementById('value-limit').textContent = limitEl.value;

      // Slider events (real-time updates)
      dipEl.addEventListener('input', () => {
        document.getElementById('value-dip').textContent = dipEl.value;
        updatePlot();
      });
      depthEl.addEventListener('input', () => {
        document.getElementById('value-depth').textContent = depthEl.value;
        updatePlot();
      });
      limitEl.addEventListener('input', () => {
        document.getElementById('value-limit').textContent = limitEl.value;
        updatePlot();
      });

      // Displacement component buttons
      const compGroup = document.getElementById('btn-group-component');
      document.getElementById('btn-component-x').addEventListener('click', (e) => {
        setActiveInGroup(compGroup, e.currentTarget);
        selectedComponent = 'X';
        updatePlot();
      });
      document.getElementById('btn-component-y').addEventListener('click', (e) => {
        setActiveInGroup(compGroup, e.currentTarget);
        selectedComponent = 'Y';
        updatePlot();
      });
      document.getElementById('btn-component-z').addEventListener('click', (e) => {
        setActiveInGroup(compGroup, e.currentTarget);
        selectedComponent = 'Z';
        updatePlot();
      });

      // Fault type buttons
      const faultGroup = document.getElementById('btn-group-fault-type');
      document.getElementById('btn-fault-tensile').addEventListener('click', (e) => {
        setActiveInGroup(faultGroup, e.currentTarget);
        selectedFaultType = 'tensile';
        updatePlot();
      });
      document.getElementById('btn-fault-strike-slip').addEventListener('click', (e) => {
        setActiveInGroup(faultGroup, e.currentTarget);
        selectedFaultType = 'strike-slip';
        updatePlot();
      });
      document.getElementById('btn-fault-normal').addEventListener('click', (e) => {
        setActiveInGroup(faultGroup, e.currentTarget);
        selectedFaultType = 'normal';
        updatePlot();
      });

      // Ensure defaults are visually active
      document.getElementById('btn-component-z').classList.add('active');
      document.getElementById('btn-fault-tensile').classList.add('active');
    }

    window.addEventListener('DOMContentLoaded', () => {
      initUI();
      updatePlot(); // initial render with defaults
    });
  </script>
</body>
</html>
