<body>
  <div id="app-container">
    <div id="control-panel">
      <h2>Control Panel</h2>

      <div class="control">
        <label for="slider-t">t <span class="value" id="t-value-display">0.10</span></label>
        <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1" />
        <div class="note">Time parameter controlling phase: cos(ω<sub>k</sub> · t)</div>
      </div>

      <div class="control">
        <label for="slider-k">k <span class="value" id="k-value-display">2</span></label>
        <input type="range" id="slider-k" min="1" max="10" step="1" value="2" />
        <div class="note">Mode index (uses k-th zero of J<sub>0</sub>)</div>
      </div>

      <div class="control">
        <div class="checkbox-row">
          <input type="checkbox" id="checkbox-axes" checked />
          <label for="checkbox-axes" style="margin: 0;">axes</label>
        </div>
        <div class="note">Toggle axes with tick marks and labels</div>
      </div>
    </div>

    <div id="visualization-area">
      <div id="formula-display"></div>
      <div id="canvas-container"></div>
    </div>
  </div>

  <!-- p5.js for drawing -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // Precomputed first 10 zeros of J0(x)
    const J0_ZEROS = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

    // Bessel function of the first kind, order zero, J0(x)
    // Approximation adapted from Cephes library
    function besselJ0(x) {
      const ax = Math.abs(x);
      let y, z, xx, ans, p, q;

      if (ax < 8.0) {
        y = x * x;
        const num = 57568490574.0
          + y * (-13362590354.0
          + y * (651619640.7
          + y * (-11214424.18
          + y * (77392.33017
          + y * (-184.9052456)))));
        const den = 57568490411.0
          + y * (1029532985.0
          + y * (9494680.718
          + y * (59272.64853
          + y * (267.8532712
          + y)))));
        return num / den;
      } else {
        z = 8.0 / ax;
        y = z * z;
        xx = ax - 0.785398164; // pi/4
        p = 1.0 + y * (-0.001098628627 + y * (0.00002734510407 + y * (-0.000002073370639 + y * 0.0000002093887211)));
        q = -0.01562499995 + y * (0.0001430488765 + y * (-0.000006911147651 + y * (0.0000007621095161 + y * (-0.0000000934945152))));
        ans = Math.sqrt(0.636619772 / ax) * (Math.cos(xx) * p - z * Math.sin(xx) * q);
        return ans;
      }
    }

    // UI elements
    const sliderT = document.getElementById('slider-t');
    const sliderK = document.getElementById('slider-k');
    const checkboxAxes = document.getElementById('checkbox-axes');
    const tValueDisplay = document.getElementById('t-value-display');
    const kValueDisplay = document.getElementById('k-value-display');
    const formulaDisplay = document.getElementById('formula-display');

    // State
    let t = parseFloat(sliderT.value);
    let k = parseInt(sliderK.value, 10);

    function updateDisplays() {
      tValueDisplay.textContent = t.toFixed(2);
      kValueDisplay.textContent = String(k);
    }

    function omegaForK(kVal) {
      const z_k = J0_ZEROS[kVal - 1];
      return z_k / 2.0;
    }

    function updateFormula() {
      const omega_k = omegaForK(k);
      // LaTeX string for MathJax
      const tex = `\\(\\omega_{${k}} = ${omega_k.toFixed(2)}\\,\\sqrt{g/l}\\)`;
      formulaDisplay.innerHTML = tex;
      if (window.MathJax && window.MathJax.typesetPromise) {
        MathJax.typesetPromise([formulaDisplay]).catch(() => {});
      }
    }

    // Event listeners
    sliderT.addEventListener('input', () => {
      t = parseFloat(sliderT.value);
      tValueDisplay.textContent = t.toFixed(2);
    });
    sliderK.addEventListener('input', () => {
      k = parseInt(sliderK.value, 10);
      kValueDisplay.textContent = String(k);
      updateFormula();
    });
    checkboxAxes.addEventListener('change', () => { /* draw() reads this state */ });

    // p5.js sketch
    let cnv;

    function setup() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      cnv = createCanvas(w, h);
      cnv.parent(container);
      pixelDensity(1);
      t = parseFloat(sliderT.value);
      k = parseInt(sliderK.value, 10);
      updateDisplays();
      updateFormula();
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      resizeCanvas(container.clientWidth, container.clientHeight);
    }

    // Logical coordinate system:
    // x in [-2, 2], y in [0, -1.2]
    const X_MIN = -2.0, X_MAX = 2.0;
    const Y_TOP = 0.0, Y_BOTTOM = -1.2;

    function xToPx(x) {
      return map(x, X_MIN, X_MAX, 0, width);
    }
    function yToPx(y) {
      return map(y, Y_TOP, Y_BOTTOM, 0, height);
    }

    function drawAxes() {
      const ctx = drawingContext;
      push();
      stroke(120);
      strokeWeight(1);

      // Dashed main axes
      ctx.save();
      ctx.setLineDash([6, 6]);
      // Horizontal axis at y = 0 (top)
      const y0 = yToPx(0);
      line(xToPx(X_MIN), y0, xToPx(X_MAX), y0);
      // Vertical axis at x = 0 (center)
      const x0 = xToPx(0);
      line(x0, yToPx(Y_TOP), x0, yToPx(Y_BOTTOM));
      ctx.restore();

      // Ticks and labels
      noStroke();
      fill(90);
      textSize(12);
      textAlign(CENTER, TOP);

      // X ticks: -2, -1, 0, 1, 2
      stroke(120);
      for (let xv = -2; xv <= 2; xv += 1) {
        const xp = xToPx(xv);
        // tick on the horizontal axis (slightly below it)
        line(xp, y0 - 4, xp, y0 + 4);
        if (xv !== 0) {
          noStroke();
          fill(90);
          text(xv.toString(), xp, y0 + 6);
          stroke(120);
        } else {
          // label 0 a bit lower to avoid overlap with tick
          noStroke();
          fill(90);
          text('0', xp, y0 + 6);
          stroke(120);
        }
      }

      // Y ticks: -0.2, -0.4, ..., -1.0
      textAlign(RIGHT, CENTER);
      for (let yv = -0.2; yv >= -1.0; yv -= 0.2) {
        const yp = yToPx(yv);
        // tick on vertical axis
        line(x0 - 4, yp, x0 + 4, yp);
        noStroke();
        fill(90);
        text(yv.toFixed(1), x0 - 8, yp);
        stroke(120);
      }
      pop();
    }

    function drawString() {
      const z_k = J0_ZEROS[k - 1];
      const omega_k = z_k / 2.0;
      const timeFactor = Math.cos(omega_k * t);
      const amplitude = 0.8;

      noFill();
      stroke('#2b6cb0');
      strokeWeight(3);
      beginShape();
      // y_norm from 0 (top) to 1 (bottom)
      for (let i = 0; i <= 100; i++) {
        const y_norm = i / 100;
        const bessel_arg = z_k * Math.sqrt(y_norm);
        const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
        const y_pos = -y_norm;
        const xp = xToPx(x_pos);
        const yp = yToPx(y_pos);
        vertex(xp, yp);
      }
      endShape();
    }

    function draw() {
      background('#f0f0f0');

      if (checkboxAxes.checked) {
        drawAxes();
      }

      drawString();

      // Attach point marker at top-center
      const x0 = xToPx(0);
      const y0 = yToPx(0);
      noStroke();
      fill('#2b6cb0');
      circle(x0, y0, 6);
    }
  </script>
</body>
</html>
