</head>
<body>
  <div id="app-container">
    <button id="btn-reset" title="Reset to defaults">Reset</button>

    <div id="top-controls">
      <div class="control-group">
        <label for="slider-opacity1">opacity 1</label>
        <input type="range" id="slider-opacity1" min="0" max="1" step="0.01" value="0.7"/>
        <span class="plus">+</span>
      </div>
      <div class="control-group">
        <label for="slider-opacity2">opacity 2</label>
        <input type="range" id="slider-opacity2" min="0" max="1" step="0.01" value="0.7"/>
        <span class="plus">+</span>
      </div>
      <div class="control-group">
        <label for="slider-opacity3">opacity 3</label>
        <input type="range" id="slider-opacity3" min="0" max="1" step="0.01" value="0.7"/>
        <span class="plus">+</span>
      </div>
      <div class="control-group">
        <label for="slider-opacity4">opacity 4</label>
        <input type="range" id="slider-opacity4" min="0" max="1" step="0.01" value="0.7"/>
        <span class="plus">+</span>
      </div>

      <div class="layering-group">
        <label>layering (bottom to top)</label>
        <span id="layering-display">1, 2, 3, 4</span>
        <div class="layer-buttons">
          <button id="btn-layer-prev" title="Previous order">▾</button>
          <button id="btn-layer-next" title="Next order">▴</button>
        </div>
      </div>
