    <div id="main-content">
      <div id="color-controls">
        <div class="color-group">
          <label for="color-picker1">color 1</label>
          <input type="color" id="color-picker1" value="#00ff00"/>
        </div>
        <div class="color-group">
          <label for="color-picker2">color 2</label>
          <input type="color" id="color-picker2" value="#0000ff"/>
        </div>
        <div class="color-group">
          <label for="color-picker3">color 3</label>
          <input type="color" id="color-picker3" value="#ff0000"/>
        </div>
        <div class="color-group">
          <label for="color-picker4">color 4</label>
          <input type="color" id="color-picker4" value="#000000"/>
        </div>
      </div>
      <div id="canvas-container"></div>
    </div>
  </div>

  <!-- p5.js library -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // State for layering control
    const layeringPermutations = [
      [1, 2, 3, 4],
      [4, 3, 2, 1],
      [1, 3, 2, 4],
      [2, 4, 1, 3],
      [3, 1, 4, 2]
    ];
    let layeringOrderIndex = 0;

    // Defaults for reset
    const defaultOpacity = 0.7;
    const defaultColors = ["#00ff00", "#0000ff", "#ff0000", "#000000"];

    // Utility: Hex to RGB
    function hexToRgb(hex) {
      let h = hex.replace('#', '');
      if (h.length === 3) {
        h = h.split('').map(ch => ch + ch).join('');
      }
      const num = parseInt(h, 16);
      return {
        r: (num >> 16) & 255,
        g: (num >> 8) & 255,
        b: num & 255
      };
    }

    function updateLayeringDisplay() {
      const display = document.getElementById('layering-display');
      display.textContent = layeringPermutations[layeringOrderIndex].join(', ');
    }

    // p5 instance
    let p5Instance = null;

    const sketch = (p) => {
      function getContainerSize() {
        const el = document.getElementById('canvas-container');
        const w = el.clientWidth || 600;
        const h = el.clientHeight || 500;
        return { w, h };
      }

      p.setup = function() {
        const { w, h } = getContainerSize();
        p.createCanvas(w, h);
        p.pixelDensity(1);
        p.noLoop();
        p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
        p.redraw(); // initial draw
      };

      p.windowResized = function() {
        const { w, h } = getContainerSize();
        p.resizeCanvas(w, h);
        p.redraw();
      };

      p.draw = function() {
        p.background('#f0f0f0');

        const cw = p.width;
        const ch = p.height;

        const circleData = {
          1: { cx: cw * 0.35, cy: ch * 0.4,  d: cw * 0.4 },
          2: { cx: cw * 0.65, cy: ch * 0.4,  d: cw * 0.4 },
          3: { cx: cw * 0.50, cy: ch * 0.65, d: cw * 0.4 },
          4: { cx: cw * 0.50, cy: ch * 0.45, d: cw * 0.3 }
        };

        const order = layeringPermutations[layeringOrderIndex] || [1, 2, 3, 4];

        p.noStroke();
        for (const idx of order) {
          const colorHex = document.getElementById('color-picker' + idx).value;
          const { r, g, b } = hexToRgb(colorHex);
          const opacity = parseFloat(document.getElementById('slider-opacity' + idx).value);
          const a = Math.round(opacity * 255);
          p.fill(r, g, b, a);
          const c = circleData[idx];
          p.ellipse(c.cx, c.cy, c.d, c.d);
        }

        // Labels atop everything for clarity
        p.fill(0);
        p.textAlign(p.CENTER, p.CENTER);
        const tSize = Math.max(12, Math.round(Math.min(cw, ch) * 0.06));
        p.textSize(tSize);
        for (let i = 1; i <= 4; i++) {
          const c = circleData[i];
          p.text(String(i), c.cx, c.cy);
        }
      };
    };

    document.addEventListener('DOMContentLoaded', () => {
      // Initialize UI display
      updateLayeringDisplay();

      // Init p5
      p5Instance = new p5(sketch, 'canvas-container');

      // Wire up opacity sliders
      for (let i = 1; i <= 4; i++) {
        const slider = document.getElementById('slider-opacity' + i);
        slider.addEventListener('input', () => p5Instance.redraw());
      }

      // Wire up color pickers
      for (let i = 1; i <= 4; i++) {
        const picker = document.getElementById('color-picker' + i);
        picker.addEventListener('input', () => p5Instance.redraw());
      }

      // Layering control buttons
      document.getElementById('btn-layer-next').addEventListener('click', () => {
        layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
        updateLayeringDisplay();
        p5Instance.redraw();
      });
      document.getElementById('btn-layer-prev').addEventListener('click', () => {
        layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
        updateLayeringDisplay();
        p5Instance.redraw();
      });

      // Reset button
      document.getElementById('btn-reset').addEventListener('click', () => {
        for (let i = 1; i <= 4; i++) {
          document.getElementById('slider-opacity' + i).value = String(defaultOpacity);
        }
        document.getElementById('color-picker1').value = defaultColors[0];
        document.getElementById('color-picker2').value = defaultColors[1];
        document.getElementById('color-picker3').value = defaultColors[2];
        document.getElementById('color-picker4').value = defaultColors[3];

        layeringOrderIndex = 0;
        updateLayeringDisplay();

        p5Instance.redraw();
      });
    });
  </script>
</body>
</html>
