  </style>
</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="eos-group">
        <span style="font-weight:600;">EoS:</span>
        <button id="btn-srk" class="selected">SRK</button>
        <button id="btn-pr">PR</button>
      </div>

      <div>
        <input type="checkbox" id="checkbox-aspen" checked />
        <label for="checkbox-aspen">comparison with Aspen Plus</label>
      </div>

      <div>
        <label for="slider-pressure">pressure P</label>
        <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10" />
        <span id="display-pressure">10</span>
        <span>bar</span>
      </div>
    </div>

    <div id="plot-container"></div>
  </div>

  <script>
    // Physical constants for propylene
    const R = 0.0831446; // L·bar/(mol·K)
    const Tc = 365.57;   // K
    const Pc = 46.65;    // bar
    const omega = 0.144; // acentric factor

    // UI elements
    const btnSRK = document.getElementById('btn-srk');
    const btnPR = document.getElementById('btn-pr');
    const checkboxAspen = document.getElementById('checkbox-aspen');
    const sliderPressure = document.getElementById('slider-pressure');
    const displayPressure = document.getElementById('display-pressure');
    const plotContainer = document.getElementById('plot-container');

    // State
    let selectedEoS = 'SRK'; // default SRK
    const Tmin = 300;
    const Tmax = 500;
    const Tstep = 2;
    const Tgrid = Array.from({length: Math.floor((Tmax - Tmin)/Tstep)+1}, (_, i) => Tmin + i*Tstep);

    // Aspen comparison dataset (fixed points)
    const aspenX = [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400];
    const aspenY = [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946];

    // Cubic root solver for x^3 + a x^2 + b x + c = 0 (returns all real roots)
    function cubicRealRoots(a, b, c) {
      const a2 = a * a;
      const q = (2 * a * a2) / 27 - (a * b) / 3 + c;     // q
      const p = b - a2 / 3;                              // p
      const D = (q * q) / 4 + (p * p * p) / 27;          // discriminant

      const eps = 1e-12;
      const roots = [];

      if (D > eps) {
        const sqrtD = Math.sqrt(D);
        const u = Math.cbrt(-q / 2 + sqrtD);
        const v = Math.cbrt(-q / 2 - sqrtD);
        const y = u + v;
        roots.push(y - a / 3);
      } else if (Math.abs(D) <= eps) {
        // Multiple real roots
        const u = Math.cbrt(-q / 2);
        const y1 = 2 * u;
        const y2 = -u;
        roots.push(y1 - a / 3);
        roots.push(y2 - a / 3);
        roots.push(y2 - a / 3);
      } else {
        // Three distinct real roots
        const r = Math.sqrt(-p * p * p / 27);
        // cos(theta) = -q/2 / r
        let cosTheta = -q / (2 * r);
        // Clamp due to numerical issues
        cosTheta = Math.max(-1, Math.min(1, cosTheta));
        const theta = Math.acos(cosTheta);
        const m = 2 * Math.sqrt(-p / 3);
        const y1 = m * Math.cos(theta / 3);
        const y2 = m * Math.cos((theta + 2 * Math.PI) / 3);
        const y3 = m * Math.cos((theta + 4 * Math.PI) / 3);
        roots.push(y1 - a / 3);
        roots.push(y2 - a / 3);
        roots.push(y3 - a / 3);
      }
      // Filter real finite roots
      return roots.filter(x => Number.isFinite(x));
    }

    // SRK parameters and phi calculation at given T (K) and P (bar)
    function phiSRK(T, P) {
      const Tr = T / Tc;
      const a = 0.42748 * R * R * Tc * Tc / Pc;
      const b = 0.08664 * R * Tc / Pc;
      const m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
      const alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
      const a_eff = a * alpha;

      const A = a_eff * P / (R * T * R * T);
      const B = b * P / (R * T);

      // Cubic: Z^3 - Z^2 + (A - B - B^2)Z - A B = 0
      const ca = -1;
      const cb = (A - B - B * B);
      const cc = -A * B;

      const roots = cubicRealRoots(ca, cb, cc);
      const Z = Math.max(...roots); // vapor root (largest real root)

      // Guarded ln(phi)
      if (!(Z > B) || !Number.isFinite(Z)) return NaN;

      const lnphi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
      return Math.exp(lnphi);
    }

    // PR parameters and phi calculation at given T (K) and P (bar)
    function phiPR(T, P) {
      const Tr = T / Tc;
      const a = 0.45724 * R * R * Tc * Tc / Pc;
      const b = 0.07780 * R * Tc / Pc;
      const m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
      const alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
      const a_eff = a * alpha;

      const A = a_eff * P / (R * T * R * T);
      const B = b * P / (R * T);

      // Cubic: Z^3 - (1-B)Z^2 + (A - 3B^2 - 2B)Z - (A B - B^2 - B^3) = 0
      const ca = (B - 1);
      const cb = (A - 3 * B * B - 2 * B);
      const cc = (-A * B + B * B + B * B * B);

      const roots = cubicRealRoots(ca, cb, cc);
      const Z = Math.max(...roots); // vapor root

      if (!(Z > B) || !Number.isFinite(Z)) return NaN;

      const sqrt2 = Math.SQRT2;
      const term1 = Z + (1 + sqrt2) * B;
      const term2 = Z + (1 - sqrt2) * B;
      if (!(term1 > 0 && term2 > 0 && Z - B > 0)) return NaN;

      const lnphi = Z - 1 - Math.log(Z - B) - (A / (2 * sqrt2 * B)) * Math.log(term1 / term2);
      return Math.exp(lnphi);
    }

    function computeCurve(eos, P) {
      const y = [];
      for (let i = 0; i < Tgrid.length; i++) {
        const T = Tgrid[i];
        let phi;
        if (eos === 'SRK') {
          phi = phiSRK(T, P);
        } else {
          phi = phiPR(T, P);
        }
        y.push(Number.isFinite(phi) ? phi : null);
      }
      return y;
    }

    function aspenVisible() {
      const P = Number(sliderPressure.value);
      return P === 10 && checkboxAspen.checked;
    }

    function applyEoSButtonStyles() {
      if (selectedEoS === 'SRK') {
        btnSRK.classList.add('selected');
        btnPR.classList.remove('selected');
      } else {
        btnPR.classList.add('selected');
        btnSRK.classList.remove('selected');
      }
    }

    function initPlot() {
      const P0 = Number(sliderPressure.value);

      const srkY = computeCurve('SRK', P0);
      const prY  = computeCurve('PR',  P0);

      const data = [
        {
          type: 'scatter',
          mode: 'lines',
          name: 'SRK',
          x: Tgrid,
          y: srkY,
          line: {color: 'red', width: 2},
          visible: true
        },
        {
          type: 'scatter',
          mode: 'lines',
          name: 'PR',
          x: Tgrid,
          y: prY,
          line: {color: 'blue', width: 2},
          visible: false
        },
        {
          type: 'scatter',
          mode: 'markers',
          name: 'Aspen Plus (P=10 bar)',
          x: aspenX,
          y: aspenY,
          marker: {color: 'green', symbol: 'square', size: 8},
          visible: aspenVisible()
        }
      ];

      const layout = {
        margin: {l: 70, r: 20, t: 30, b: 60},
        plot_bgcolor: '#f2f2f2',
        paper_bgcolor: '#f2f2f2',
        xaxis: {
          title: 'temperature (K)',
          range: [295, 505],
          zeroline: false,
          showgrid: true,
          gridcolor: '#ffffff',
          gridwidth: 2,
          dtick: 20,
          minor: {
            showgrid: true,
            gridcolor: '#ffffff',
            gridwidth: 1,
            dtick: 5
          }
        },
        yaxis: {
          title: 'gas-phase fugacity coefficient',
          range: [0.68, 1.02],
          zeroline: false,
          showgrid: true,
          gridcolor: '#ffffff',
          gridwidth: 2,
          dtick: 0.05,
          minor: {
            showgrid: true,
            gridcolor: '#ffffff',
            gridwidth: 1,
            dtick: 0.01
          }
        },
        legend: {orientation: 'h', x: 0.02, y: 1.08}
      };

      Plotly.newPlot(plotContainer, data, layout, {responsive: true, displayModeBar: true});
    }

    function updateCurvesForPressure() {
      const P = Number(sliderPressure.value);
      const newSRK = computeCurve('SRK', P);
      const newPR  = computeCurve('PR',  P);

      Plotly.restyle(plotContainer, {y: [newSRK]}, [0]);
      Plotly.restyle(plotContainer, {y: [newPR]},  [1]);
    }

    function updateEoSVisibility() {
      if (selectedEoS === 'SRK') {
        Plotly.restyle(plotContainer, {visible: true},  [0]);
        Plotly.restyle(plotContainer, {visible: false}, [1]);
      } else {
        Plotly.restyle(plotContainer, {visible: false}, [0]);
        Plotly.restyle(plotContainer, {visible: true},  [1]);
      }
    }

    function updateAspenTraceVisibility() {
      Plotly.restyle(plotContainer, {visible: aspenVisible()}, [2]);
    }

    // Event bindings
    btnSRK.addEventListener('click', () => {
      selectedEoS = 'SRK';
      applyEoSButtonStyles();
      updateEoSVisibility();
      updateAspenTraceVisibility();
    });

    btnPR.addEventListener('click', () => {
      selectedEoS = 'PR';
      applyEoSButtonStyles();
      updateEoSVisibility();
      updateAspenTraceVisibility();
    });

    sliderPressure.addEventListener('input', () => {
      displayPressure.textContent = sliderPressure.value;
      updateCurvesForPressure();
      updateAspenTraceVisibility();
    });

    checkboxAspen.addEventListener('change', () => {
      updateAspenTraceVisibility();
    });

    // Initialize
    (function start() {
      displayPressure.textContent = sliderPressure.value;
      applyEoSButtonStyles();
      initPlot();
    })();
  </script>
</body>
</html>
