<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        // -----------------------------
        // Global state and parameters
        // -----------------------------
        let currentStep = '1';

        // Geometry: main triangle ABC
        let A, B, C, M, centroid;
        let mainSide, mainAlt;

        // Draggable point P (logical, kept within triangle)
        let P;
        let dragging = false;
        const P_RADIUS = 9;

        // Cached foot points and distances
        let footAB, footBC, footAC;
        let dAB = 0, dBC = 0, dAC = 0;

        // Colors
        const COLOR_RED   = [255, 0, 0, 110];
        const COLOR_GREEN = [0, 180, 0, 110];
        const COLOR_BLUE  = [0, 90, 255, 110];

        // --------------------------------
        // Utility vector/geometry helpers
        // --------------------------------
        function vec(x, y) { return createVector(x, y); }

        function sub(a, b) { return p5.Vector.sub(a, b); }
        function add(a, b) { return p5.Vector.add(a, b); }
        function dot(a, b) { return a.x * b.x + a.y * b.y; }
        function crossZ(a, b) { return a.x * b.y - a.y * b.x; }
        function len2(v) { return v.x * v.x + v.y * v.y; }

        function clamp01(t) { return t < 0 ? 0 : (t > 1 ? 1 : t); }

        function projectPointOnSegment(p, a, b) {
            const ab = sub(b, a);
            const ap = sub(p, a);
            const ab2 = len2(ab);
            let t = ab2 > 0 ? dot(ap, ab) / ab2 : 0;
            t = clamp01(t);
            const foot = add(a, ab.mult(t));
            const dist = p5.Vector.dist(p, foot);
            return { point: foot, t, dist };
        }

        function pointInTriangle(p, a, b, c) {
            // Using barycentric technique via sign of cross products
            const v0 = sub(b, a);
            const v1 = sub(c, b);
            const v2 = sub(a, c);
            const ap = sub(p, a);
            const bp = sub(p, b);
            const cp = sub(p, c);
            const c1 = crossZ(v0, ap);
            const c2 = crossZ(v1, bp);
            const c3 = crossZ(v2, cp);
            const hasNeg = (c1 < 0) || (c2 < 0) || (c3 < 0);
            const hasPos = (c1 > 0) || (c2 > 0) || (c3 > 0);
            return !(hasNeg && hasPos); // inside or on boundary
        }

        function closestPointOnTriangle(p, a, b, c) {
            const pab = projectPointOnSegment(p, a, b);
            const pbc = projectPointOnSegment(p, b, c);
            const pca = projectPointOnSegment(p, c, a);
            let best = pab;
            if (pbc.dist < best.dist) best = pbc;
            if (pca.dist < best.dist) best = pca;
            return best.point.copy();
        }

        function clampPointToTriangle(p, a, b, c) {
            if (pointInTriangle(p, a, b, c)) return p.copy();
            return closestPointOnTriangle(p, a, b, c);
        }

        function unit(v) {
            const m = v.mag();
            if (m === 0) return createVector(0, 0);
            return p5.Vector.mult(v, 1 / m);
        }

        function perp(v) {
            // Rotate +90 degrees
            return createVector(-v.y, v.x);
        }

        function inwardNormalForSide(a, b, center) {
            const dir = sub(b, a);
            const n = unit(perp(dir)); // one of the two normals
            const toCenter = sub(center, a);
            // Choose the normal that points towards the triangle centroid
            if (dot(n, toCenter) < 0) return p5.Vector.mult(n, -1);
            return n;
        }

        function altitudeToSideAndFoot(P, a, b) {
            // perpendicular from P to segment AB (clamped)
            const proj = projectPointOnSegment(P, a, b);
            return { foot: proj.point, dist: proj.dist };
        }

        // --------------------------------
        // p5.js setup and drawing
        // --------------------------------
        function setup() {
            const cnv = createCanvas(600, 520);
            cnv.parent('canvas-container');
            pixelDensity(window.devicePixelRatio || 1);
            textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
            textSize(13);

            // Define main equilateral triangle
            const margin = 100;
            A = vec(100, 460);
            B = vec(500, 460);
            mainSide = p5.Vector.dist(A, B);
            mainAlt  = mainSide * Math.sqrt(3) / 2;
            M = vec((A.x + B.x) / 2, (A.y + B.y) / 2);
            C = vec(M.x, M.y - mainAlt);
            centroid = vec((A.x + B.x + C.x) / 3, (A.y + B.y + C.y) / 3);

            // Initial P (clamped to triangle)
            const initP = vec(width * 0.4, height * 0.6);
            P = clampPointToTriangle(initP, A, B, C);

            // Radio buttons: step selection
            document.querySelectorAll('input[name="step-selector"]').forEach(el => {
                el.addEventListener('change', (e) => {
                    currentStep = e.target.value;
                });
            });

            // Precompute first time
            recomputePerpendiculars();
        }

        function draw() {
            background(255);

            // Update feet/distances each frame (if P moved)
            recomputePerpendiculars();

            drawMainTriangle();
            drawMainAltitude();

            if (currentStep === '1') {
                drawPerpendicularsStep1();
                drawPointP(true);
            } else if (currentStep === '2') {
                drawSmallTrianglesStep2();
                drawPointP(true);
            } else if (currentStep === '3') {
                drawStackedTrianglesStep3();
                drawPointP(false); // ghosted
            }

            // Hover cursor feedback
            if (dist(mouseX, mouseY, P.x, P.y) <= P_RADIUS + 2) {
                if (!dragging) cursor('grab');
            } else {
                cursor('default');
            }
        }

        // --------------------------------
        // Drawing helpers
        // --------------------------------
        function drawMainTriangle() {
            stroke(0);
            strokeWeight(2);
            noFill();
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);

            // Vertex labels
            noStroke();
            fill(0);
            textAlign(CENTER, TOP);
            text('A', A.x, A.y + 6);
            text('B', B.x, B.y + 6);
            textAlign(CENTER, BOTTOM);
            text('C', C.x, C.y - 6);
        }

        function drawMainAltitude() {
            // Dashed light-blue altitude from C to midpoint of AB
            stroke(100, 180, 255);
            strokeWeight(2);
            drawingContext.setLineDash([7, 7]);
            line(C.x, C.y, M.x, M.y);
            drawingContext.setLineDash([]);
        }

        function drawPointP(active) {
            push();
            if (active) {
                // Solid marker
                fill(200);
                stroke(0);
                strokeWeight(1.5);
                circle(P.x, P.y, P_RADIUS * 2);
                // Crosshair
                line(P.x - 6, P.y, P.x + 6, P.y);
                line(P.x, P.y - 6, P.x, P.y + 6);
                // Label
                noStroke();
                fill(220, 20, 20);
                textAlign(LEFT, BOTTOM);
                text('P', P.x + 10, P.y - 8);
            } else {
                // Ghosted marker (Step 3)
                fill(180, 180, 180, 70);
                stroke(80, 80, 80, 140);
                strokeWeight(1);
                circle(P.x, P.y, P_RADIUS * 2);
                line(P.x - 6, P.y, P.x + 6, P.y);
                line(P.x, P.y - 6, P.x, P.y + 6);
                noStroke();
                fill(120, 120, 120, 160);
                textAlign(LEFT, BOTTOM);
                text('P', P.x + 10, P.y - 8);
            }
            pop();
        }

        function drawPerpendicularsStep1() {
            // Lines from P to feet on sides, with blue dots at feet
            stroke(0);
            strokeWeight(1.5);
            line(P.x, P.y, footAB.x, footAB.y);
            line(P.x, P.y, footBC.x, footBC.y);
            line(P.x, P.y, footAC.x, footAC.y);

            noStroke();
            fill(0, 110, 255);
            circle(footAB.x, footAB.y, 7);
            circle(footBC.x, footBC.y, 7);
            circle(footAC.x, footAC.y, 7);
        }

        function drawSmallTrianglesStep2() {
            // Compute unit directions along sides and normals
            const uAB = unit(sub(B, A));
            const uBC = unit(sub(C, B));
            const uAC = unit(sub(C, A));

            // For normals, use direction from P to foot when available; fallback to inward normal
            // Red: base parallel to AB (horizontal), apex at P, altitude dAB downward (towards AB)
            const sRed = (2 / Math.sqrt(3)) * dAB;
            const baseCenterRed = createVector(P.x, P.y + dAB); // base parallel to AB
            const baseR1 = createVector(baseCenterRed.x - sRed / 2, baseCenterRed.y);
            const baseR2 = createVector(baseCenterRed.x + sRed / 2, baseCenterRed.y);

            // Blue: base parallel to BC, apex at P, altitude dBC in direction towards footBC
            const nBC = (dBC > 0.0001) ? unit(sub(footBC, P)) : inwardNormalForSide(B, C, centroid);
            const sBlue = (2 / Math.sqrt(3)) * dBC;
            const baseCenterBlue = add(P, p5.Vector.mult(nBC, dBC));
            const baseB1 = add(baseCenterBlue, p5.Vector.mult(uBC, -sBlue / 2));
            const baseB2 = add(baseCenterBlue, p5.Vector.mult(uBC,  sBlue / 2));

            // Green: base parallel to AC, apex at P, altitude dAC in direction towards footAC
            const nAC = (dAC > 0.0001) ? unit(sub(footAC, P)) : inwardNormalForSide(A, C, centroid);
            const sGreen = (2 / Math.sqrt(3)) * dAC;
            const baseCenterGreen = add(P, p5.Vector.mult(nAC, dAC));
            const baseG1 = add(baseCenterGreen, p5.Vector.mult(uAC, -sGreen / 2));
            const baseG2 = add(baseCenterGreen, p5.Vector.mult(uAC,  sGreen / 2));

            // Draw filled triangles
            noStroke();
            // Red triangle (apex P, baseR1-baseR2)
            fill(...COLOR_RED);
            beginShape();
            vertex(P.x, P.y);
            vertex(baseR1.x, baseR1.y);
            vertex(baseR2.x, baseR2.y);
            endShape(CLOSE);

            // Blue triangle (apex P, baseB1-baseB2)
            fill(...COLOR_BLUE);
            beginShape();
            vertex(P.x, P.y);
            vertex(baseB1.x, baseB1.y);
            vertex(baseB2.x, baseB2.y);
            endShape(CLOSE);

            // Green triangle (apex P, baseG1-baseG2)
            fill(...COLOR_GREEN);
            beginShape();
            vertex(P.x, P.y);
            vertex(baseG1.x, baseG1.y);
            vertex(baseG2.x, baseG2.y);
            endShape(CLOSE);

            // Altitudes inside the small triangles (from P to each base center)
            stroke(0, 0, 0, 160);
            strokeWeight(1.2);
            line(P.x, P.y, baseCenterRed.x, baseCenterRed.y);
            line(P.x, P.y, baseCenterBlue.x, baseCenterBlue.y);
            line(P.x, P.y, baseCenterGreen.x, baseCenterGreen.y);

            // Label the feet on AC and BC as requested: 'x' (AC) and 'y' (BC)
            noStroke();
            fill(0);
            textAlign(CENTER, CENTER);
            // 'x' at foot on AC
            drawLabel('x', footAC.x, footAC.y, -10, -6);
            // 'y' at foot on BC
            drawLabel('y', footBC.x, footBC.y, -10, -6);
        }

        function drawStackedTrianglesStep3() {
            // Stacked along main altitude centered at x = M.x
            const x0 = M.x;
            const baseY = A.y;

            // Red at bottom with base on AB
            const sR = (2 / Math.sqrt(3)) * dAB;
            const apexR = createVector(x0, baseY - dAB);
            const baseR1 = createVector(x0 - sR / 2, baseY);
            const baseR2 = createVector(x0 + sR / 2, baseY);

            // Blue stacked on red
            const sB = (2 / Math.sqrt(3)) * dBC;
            const baseYB = apexR.y; // base on horizontal line through red apex
            const apexB = createVector(x0, baseYB - dBC);
            const baseB1 = createVector(x0 - sB / 2, baseYB);
            const baseB2 = createVector(x0 + sB / 2, baseYB);

            // Green stacked on blue; top should align with C if dAB + dBC + dAC = mainAlt
            const sG = (2 / Math.sqrt(3)) * dAC;
            const baseYG = apexB.y;
            let apexG = createVector(x0, baseYG - dAC);
            // Snap to C for visual exactness
            apexG = createVector(C.x, C.y);
            const baseG1 = createVector(x0 - sG / 2, baseYG);
            const baseG2 = createVector(x0 + sG / 2, baseYG);

            noStroke();
            fill(...COLOR_RED);
            beginShape();
            vertex(apexR.x, apexR.y);
            vertex(baseR1.x, baseR1.y);
            vertex(baseR2.x, baseR2.y);
            endShape(CLOSE);

            fill(...COLOR_BLUE);
            beginShape();
            vertex(apexB.x, apexB.y);
            vertex(baseB1.x, baseB1.y);
            vertex(baseB2.x, baseB2.y);
            endShape(CLOSE);

            fill(...COLOR_GREEN);
            beginShape();
            vertex(apexG.x, apexG.y);
            vertex(baseG1.x, baseG1.y);
            vertex(baseG2.x, baseG2.y);
            endShape(CLOSE);

            // Label top vertex of blue as 'y'
            noStroke();
            fill(0);
            textAlign(LEFT, BOTTOM);
            text('y', apexB.x + 8, apexB.y - 6);
        }

        function drawLabel(txt, x, y, dx = 0, dy = 0) {
            push();
            noStroke();
            fill(0);
            textAlign(CENTER, CENTER);
            text(txt, x + dx, y + dy);
            pop();
        }

        // --------------------------------
        // Perpendicular computation
        // --------------------------------
        function recomputePerpendiculars() {
            const resAB = altitudeToSideAndFoot(P, A, B);
            const resBC = altitudeToSideAndFoot(P, B, C);
            const resAC = altitudeToSideAndFoot(P, A, C);
            footAB = resAB.foot;
            footBC = resBC.foot;
            footAC = resAC.foot;
            dAB = resAB.dist;
            dBC = resBC.dist;
            dAC = resAC.dist;
        }

        // --------------------------------
        // Interaction: dragging P
        // --------------------------------
        function mousePressed() {
            if (dist(mouseX, mouseY, P.x, P.y) <= P_RADIUS + 4) {
                dragging = true;
                cursor('grabbing');
            }
        }

        function mouseDragged() {
            if (!dragging) return;
            const desired = vec(mouseX, mouseY);
            const clamped = clampPointToTriangle(desired, A, B, C);
            P.set(clamped.x, clamped.y);
        }

        function mouseReleased() {
            dragging = false;
        }

        function touchStarted() {
            // Enable dragging on touch devices
            if (dist(touchX, touchY, P.x, P.y) <= P_RADIUS + 8) {
                dragging = true;
            }
        }

        function touchMoved() {
            if (!dragging) return;
            const desired = vec(touchX, touchY);
            const clamped = clampPointToTriangle(desired, A, B, C);
            P.set(clamped.x, clamped.y);
            return false; // prevent default scrolling
        }

        function touchEnded() {
            dragging = false;
        }
    </script>
</body>
</html>
