  </div>

  <!-- three.js via CDN (module) -->
  <script type="module">
    import * as THREE from "https://unpkg.com/three@0.157.0/build/three.module.js";
    import { OrbitControls } from "https://unpkg.com/three@0.157.0/examples/jsm/controls/OrbitControls.js";

    // ---- DOM elements and initial states ----
    const canvas = document.getElementById("3d-canvas");
    const dateDisplay = document.getElementById("date-display");
    const timeSlider = document.getElementById("time-slider");
    const playPauseButton = document.getElementById("play-pause-button");
    const dwarfPlanetSelector = document.getElementById("dwarf-planet-selector");
    const vizContainer = document.getElementById("visualization-container");

    let selectedDwarfPlanet = "Pluto"; // initial state
    let isAnimating = false;
    let animationId = null;

    // ---- three.js setup ----
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf4f6f8);

    const camera = new THREE.PerspectiveCamera(45, 1, 0.01, 500);
    camera.position.set(0, 15, 30);

    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.target.set(0, 0, 0);
    controls.minDistance = 3;
    controls.maxDistance = 300;
    controls.addEventListener("change", renderScene);

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambient);

    const pointLight = new THREE.PointLight(0xffffff, 1.2, 0);
    pointLight.position.set(0, 0, 0);
    scene.add(pointLight);

    // Sun
    const sunGeo = new THREE.SphereGeometry(0.5, 32, 32);
    const sunMat = new THREE.MeshBasicMaterial({ color: 0xffd54f });
    const sun = new THREE.Mesh(sunGeo, sunMat);
    sun.position.set(0, 0, 0);
    scene.add(sun);

    // ---- Orbital elements (at J2000 epoch) ----
    const Elements = {
      Mercury: { a: 0.3871, e: 0.2056, i: 7.005,  Omega: 48.331,  omega: 29.124,  M0: 174.796 },
      Venus:   { a: 0.7233, e: 0.0068, i: 3.395,  Omega: 76.680,  omega: 54.884,  M0: 50.416  },
      Earth:   { a: 1.0000, e: 0.0167, i: 0.000,  Omega: -11.260, omega: 114.208, M0: 358.617 },
      Mars:    { a: 1.5237, e: 0.0934, i: 1.850,  Omega: 49.562,  omega: 286.502, M0: 19.412  },
      Ceres:   { a: 2.7691, e: 0.0760, i: 10.593, Omega: 80.393,  omega: 73.598,  M0: 77.372  },
      Pluto:   { a: 39.482, e: 0.2488, i: 17.160, Omega: 110.299, omega: 113.834, M0: 14.882  },
      Haumea:  { a: 43.132, e: 0.1912, i: 28.220, Omega: 122.155, omega: 239.544, M0: 204.03  },
      Makemake:{ a: 45.791, e: 0.1559, i: 29.006, Omega: 79.623,  omega: 294.821, M0: 95.895  },
      Eris:    { a: 67.668, e: 0.4418, i: 44.187, Omega: 35.951,  omega: 151.291, M0: 206.51  }
    };

    // ---- Utility: Date and time ----
    const MS_PER_DAY = 86400000;
    const baseStartDateUTC = Date.UTC(2000, 0, 1, 0, 0, 0);  // Jan 1, 2000 00:00:00 UTC (slider base)
    const J2000_UTC = Date.UTC(2000, 0, 1, 12, 0, 0);       // Jan 1, 2000 12:00:00 UTC

    function dateFromSlider() {
      const days = parseInt(timeSlider.value, 10) || 0;
      return new Date(baseStartDateUTC + days * MS_PER_DAY);
    }

    function formatUTCDate(d) {
      const days = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"];
      const months = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
      const day = days[d.getUTCDay()];
      const dd = d.getUTCDate();
      const mon = months[d.getUTCMonth()];
      const yyyy = d.getUTCFullYear();
      // Always show midnight per spec
      return `${day} ${dd} ${mon} ${yyyy} 00:00:00`;
    }

    function setDateDisplay(d) {
      dateDisplay.textContent = formatUTCDate(d);
    }

    // ---- Orbital Mechanics ----
    function degToRad(x) { return x * Math.PI / 180; }
    function radToDeg(x) { return x * 180 / Math.PI; }

    function orbitalPeriodDays(a_AU) {
      // Kepler's Third Law in Earth years; convert to days
      return 365.25 * Math.pow(a_AU, 1.5);
    }

    // Solve Kepler's equation M = E - e sin(E) for E (radians), Newton-Raphson
    function solveEccentricAnomaly(M, e) {
      // Normalize M to [-pi, pi] to improve convergence
      M = Math.atan2(Math.sin(M), Math.cos(M));
      let E = (e < 0.8) ? M : Math.PI; // initial guess
      const eps = 1e-6;
      let delta = 1;
      let iter = 0;
      while (Math.abs(delta) > eps && iter < 50) {
        const f = E - e * Math.sin(E) - M;
        const fprime = 1 - e * Math.cos(E);
        delta = f / fprime;
        E = E - delta;
        iter++;
      }
      return E;
    }

    // Calculate heliocentric ecliptic position (AU) for a given date and elements.
    function calculatePosition(date, elements) {
      const { a, e, i, Omega, omega, M0 } = elements;
      const d = (date.getTime() - J2000_UTC) / MS_PER_DAY; // days since J2000 (2000-01-01 12:00 UTC)
      const P = orbitalPeriodDays(a);                      // days
      // Mean anomaly at date, degrees -> radians
      let M = (M0 + (360 / P) * d) % 360;
      if (M < 0) M += 360;
      M = degToRad(M);

      const E = solveEccentricAnomaly(M, e);
      const cosE = Math.cos(E);
      const sinE = Math.sin(E);

      // True anomaly
      const nu = Math.atan2(Math.sqrt(1 - e*e) * sinE, cosE - e);

      // Distance
      const r = a * (1 - e * cosE);

      // Argument of latitude u = omega + nu
      const iRad = degToRad(i);
      const OmegaRad = degToRad(Omega);
      const omegaRad = degToRad(omega);
      const u = omegaRad + nu;

      const cosu = Math.cos(u), sinu = Math.sin(u);
      const cosO = Math.cos(OmegaRad), sinO = Math.sin(OmegaRad);
      const cosi = Math.cos(iRad),     sini = Math.sin(iRad);

      // Heliocentric ecliptic coordinates
      const X = r * (cosO * cosu - sinO * sinu * cosi);
      const Y = r * (sinO * cosu + cosO * sinu * cosi);
      const Z = r * (sinu * sini);

      return new THREE.Vector3(X, Z, Y); // Map ecliptic Y->Z to use three.js Y as "up" (optional)
    }

    // ---- Orbits (curves) ----
    function createOrbitObject(elements, color) {
      const { a, e, i, Omega, omega } = elements;
      const b = a * Math.sqrt(1 - e * e);

      // Ellipse centered at (-a*e, 0), focus at origin, perihelion on +x
      const curve = new THREE.EllipseCurve(
        -a * e, // aX (center x)
        0,      // aY
        a,      // xRadius
        b,      // yRadius
        0, 2 * Math.PI, // startAngle, endAngle
        false,   // clockwise
        0        // rotation
      );
      const points2D = curve.getPoints(200);
      const pts3D = points2D.map(p => new THREE.Vector3(p.x, 0, p.y));
      const geom = new THREE.BufferGeometry().setFromPoints(pts3D);
      const mat = new THREE.LineBasicMaterial({ color, transparent: true, opacity: 0.9 });
      const line = new THREE.Line(geom, mat);

      const orbitObj = new THREE.Group();
      orbitObj.add(line);

      // Apply orientation: Rz(Ω) Rx(i) Rz(ω)
      orbitObj.rotation.set(0, 0, 0);
      orbitObj.rotateZ(degToRad(Omega));
      orbitObj.rotateX(degToRad(i));
      orbitObj.rotateZ(degToRad(omega));

      return { group: orbitObj, line, geom, mat };
    }

    // ---- Bodies (meshes) ----
    function makeBody(radius, color) {
      const geo = new THREE.SphereGeometry(radius, 24, 24);
      const mat = new THREE.MeshLambertMaterial({ color });
      const mesh = new THREE.Mesh(geo, mat);
      return mesh;
    }

    // ---- Create inner planet system ----
    const planetColors = {
      Mercury: 0xa8a8a8,
      Venus:   0xe5c07b,
      Earth:   0x61afef,
      Mars:    0xe06c75
    };
    const planetSizes = {
      Mercury: 0.06,
      Venus:   0.08,
      Earth:   0.09,
      Mars:    0.08
    };

    const planetMeshes = {};
    const planetOrbitObjs = {};

    ["Mercury", "Venus", "Earth", "Mars"].forEach(name => {
      const color = planetColors[name];
      const size = planetSizes[name];
      const mesh = makeBody(size, color);
      planetMeshes[name] = mesh;
      scene.add(mesh);

      const { group } = createOrbitObject(Elements[name], 0x3b82f6);
      planetOrbitObjs[name] = group;
      scene.add(group);
    });

    // ---- Dwarf planet (selected) ----
    let dwarfMesh = null;
    let dwarfOrbit = null;

    function createSelectedDwarf(name) {
      // Remove old
      if (dwarfMesh) {
        scene.remove(dwarfMesh);
        dwarfMesh.geometry.dispose();
        dwarfMesh.material.dispose();
        dwarfMesh = null;
      }
      if (dwarfOrbit) {
        scene.remove(dwarfOrbit.group);
        dwarfOrbit.geom.dispose();
        dwarfOrbit.mat.dispose();
        dwarfOrbit = null;
      }

      // Orbit (red)
      dwarfOrbit = createOrbitObject(Elements[name], 0xef4444);
      scene.add(dwarfOrbit.group);

      // Body (dark purple)
      dwarfMesh = makeBody(0.12, 0x6a0dad);
      scene.add(dwarfMesh);

      // Position according to current date
      const d = dateFromSlider();
      const pos = calculatePosition(d, Elements[name]);
      dwarfMesh.position.copy(pos);
    }

    // ---- Update positions ----
    function updateAllPositions(date) {
      // Inner planets
      ["Mercury", "Venus", "Earth", "Mars"].forEach(name => {
        const pos = calculatePosition(date, Elements[name]);
        planetMeshes[name].position.copy(pos);
      });
      // Selected dwarf
      if (dwarfMesh) {
        const posD = calculatePosition(date, Elements[selectedDwarfPlanet]);
        dwarfMesh.position.copy(posD);
      }
    }

    // ---- Rendering and resize ----
    function onResize() {
      const w = vizContainer.clientWidth;
      const h = vizContainer.clientHeight;
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderScene();
    }

    function renderScene() {
      controls.update();
      renderer.render(scene, camera);
    }

    window.addEventListener("resize", onResize);

    // ---- Interactions ----

    // Dwarf planet selection
    dwarfPlanetSelector.addEventListener("click", (ev) => {
      const btn = ev.target.closest("button[data-planet]");
      if (!btn) return;
      const newName = btn.getAttribute("data-planet");
      if (newName === selectedDwarfPlanet) return;

      // Toggle active class
      [...dwarfPlanetSelector.querySelectorAll("button")].forEach(b => b.classList.remove("active"));
      btn.classList.add("active");

      // Update selection and rebuild orbit/body
      selectedDwarfPlanet = newName;
      createSelectedDwarf(selectedDwarfPlanet);
      // reposition for current date
      updateAllPositions(dateFromSlider());
      renderScene();
    });

    // Time slider
    function onTimeChange() {
      const d = dateFromSlider();
      setDateDisplay(d);
      updateAllPositions(d);
      renderScene();
    }
    timeSlider.addEventListener("input", onTimeChange);

    // Play/Pause
    function animate() {
      if (!isAnimating) return;
      // Advance time by ~2 days per frame
      let val = parseInt(timeSlider.value, 10) || 0;
      val += 2;
      if (val > parseInt(timeSlider.max, 10)) val = parseInt(timeSlider.min, 10);
      timeSlider.value = String(val);
      onTimeChange();

      animationId = requestAnimationFrame(animate);
    }

    playPauseButton.addEventListener("click", () => {
      isAnimating = !isAnimating;
      if (isAnimating) {
        playPauseButton.textContent = "⏸";
        if (animationId) cancelAnimationFrame(animationId);
        animationId = requestAnimationFrame(animate);
      } else {
        playPauseButton.textContent = "▶";
        if (animationId) cancelAnimationFrame(animationId);
        animationId = null;
      }
    });

    // ---- Initialization ----
    function init() {
      onResize(); // set renderer size and camera aspect
      // Set initial date
      const d = dateFromSlider();
      setDateDisplay(d);

      // Create initial dwarf selection (Pluto)
      createSelectedDwarf(selectedDwarfPlanet);

      // Position all bodies
      updateAllPositions(d);

      renderScene();
    }

    init();
  </script>
</body>
</html>
