                <span id="label-year" class="value">1950</span>
            </div>

            <label for="slider-time">time (s)</label>
            <div class="row">
                <input type="range" id="slider-time" min="0" max="30" step="1" value="1" style="flex:1;">
                <span id="label-time" class="value">1</span>
            </div>

            <div class="spacer"></div>
            <div class="hint">
                - Red dot shows selected year on active CFC.<br>
                - Ozone line length follows time (s).<br>
                - Ozone layer opacity decreases with higher CFC.
            </div>
        </div>

        <!-- Right Panel: Visualizations -->
        <div id="visualization-panel">
            <div id="plots-container">
                <div id="plot-ozone" class="plot"></div>
                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container"></div>
        </div>
    </div>

<script>
/* =============================
   Data
   ============================= */
const cfcData = {
  'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
  'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
  'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
  'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
};

/* =============================
   DOM references
   ============================= */
const selectCfc = document.getElementById('select-cfc');
const checkboxShowAll = document.getElementById('checkbox-show-all');
const sliderYear = document.getElementById('slider-year');
const labelYear = document.getElementById('label-year');
const sliderTime = document.getElementById('slider-time');
const labelTime = document.getElementById('label-time');

const plotOzoneEl = document.getElementById('plot-ozone');
const plotCfcEl = document.getElementById('plot-cfc');
const sceneContainer = document.getElementById('scene-container');

/* =============================
   Helper functions
   ============================= */
function interpolate(dataPairs, year) {
  // Linear interpolation over [ [x0,y0], [x1,y1], ... ]
  if (year <= dataPairs[0][0]) return dataPairs[0][1];
  if (year >= dataPairs[dataPairs.length - 1][0]) return dataPairs[dataPairs.length - 1][1];
  for (let i = 0; i < dataPairs.length - 1; i++) {
    const [x0, y0] = dataPairs[i];
    const [x1, y1] = dataPairs[i + 1];
    if (year >= x0 && year <= x1) {
      const t = (year - x0) / (x1 - x0);
      return y0 + t * (y1 - y0);
    }
  }
  return dataPairs[0][1];
}

function getActiveConcentration() {
  const cfc = selectCfc.value;
  const conc = interpolate(cfcData[cfc].data, parseInt(sliderYear.value, 10));
  return conc;
}

/* =============================
   Plotly: CFC concentration plot
   ============================= */
function buildCfcTraces() {
  const showAll = checkboxShowAll.checked;
  const active = selectCfc.value;
  const yr = parseInt(sliderYear.value, 10);

  const traces = [];

  const allKeys = Object.keys(cfcData);
  for (const key of allKeys) {
    const series = cfcData[key];
    const xs = series.data.map(d => d[0]);
    const ys = series.data.map(d => d[1]);

    if (!showAll && key !== active) continue; // only active trace

    traces.push({
      type: 'scatter',
      mode: 'lines',
      name: key,
      x: xs,
      y: ys,
      line: {
        color: series.color,
        width: key === active ? 4 : 2
      },
      hovertemplate: 'year: %{x}<br>conc: %{y}<extra>' + key + '</extra>'
    });
  }

  // Red marker on active CFC at current year
  const activeSeries = cfcData[active];
  const yAtYear = interpolate(activeSeries.data, yr);
  traces.push({
    type: 'scatter',
    mode: 'markers',
    name: 'selected year',
    x: [yr],
    y: [yAtYear],
    marker: { color: 'red', size: 10 },
    hoverinfo: 'skip',
    showlegend: false
  });

  return traces;
}

function renderCfcPlot() {
  const layout = {
    title: { text: 'CFC concentration', x: 0.5 },
    margin: { l: 50, r: 10, t: 40, b: 40 },
    xaxis: {
      title: 'year',
      range: [1920, 2020],
      tickmode: 'linear',
      dtick: 10
    },
    yaxis: {
      range: [0, 600]
    },
    legend: { orientation: 'h', y: -0.15 },
    paper_bgcolor: '#ffffff',
    plot_bgcolor: '#ffffff'
  };
  Plotly.react(plotCfcEl, buildCfcTraces(), layout, { displayModeBar: false, responsive: true });
}

/* =============================
   Plotly: Ozone plot
   ============================= */
const O3_INV_0 = 10;       // 1/[O3](0)
const K_FACTOR = 0.002;    // proportionality factor for pseudo-first-order: slope = k*[CFC]

function buildOzoneTrace() {
  const tMax = parseInt(sliderTime.value, 10);
  const conc = getActiveConcentration();
  const slope = K_FACTOR * conc; // k*[CFC]
  const xs = [];
  const ys = [];
  for (let t = 0; t <= tMax; t++) {
    xs.push(t);
    ys.push(O3_INV_0 + slope * t);
  }
  return [{
    type: 'scatter',
    mode: 'lines',
    name: '1/[O3]',
    x: xs,
    y: ys,
    line: { color: 'rgba(0,95,255,1.0)', width: 3 },
    hovertemplate: 't=%{x}s<br>1/[O3]=%{y:.2f}<extra></extra>'
  }];
}

function renderOzonePlot() {
  const layout = {
    title: { text: 'concentration of ozone', x: 0.5 },
    margin: { l: 60, r: 10, t: 40, b: 50 },
    xaxis: {
      title: 'time (s)',
      range: [0, 30],
      dtick: 5
    },
    yaxis: {
      title: '$1/[O_{3}]$',
      range: [0, 110],
      dtick: 10
    },
    showlegend: false,
    paper_bgcolor: '#ffffff',
    plot_bgcolor: '#ffffff'
  };
  Plotly.react(plotOzoneEl, buildOzoneTrace(), layout, { displayModeBar: false, responsive: true });
  if (window.MathJax && MathJax.typesetPromise) {
    MathJax.typesetPromise();
  }
}

/* =============================
   three.js scene
   ============================= */
let renderer, scene, camera;
let ozoneMesh;

function initThree() {
  const w = sceneContainer.clientWidth;
  const h = sceneContainer.clientHeight;

  renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
  renderer.setSize(w, h);
  renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
  sceneContainer.appendChild(renderer.domElement);

  scene = new THREE.Scene();
  camera = new THREE.PerspectiveCamera(45, w / h, 0.1, 100);
  camera.position.set(3.5, 2.6, 5.5);
  camera.lookAt(new THREE.Vector3(0, 0, 0));

  // Earth
  const earthGeo = new THREE.SphereGeometry(1, 32, 32);
  const earthMat = new THREE.MeshBasicMaterial({ color: 0x0000ff });
  const earth = new THREE.Mesh(earthGeo, earthMat);
  scene.add(earth);

  // Ozone layer (green, slightly larger radius)
  const ozoneGeo = new THREE.SphereGeometry(1.15, 32, 32);
  const ozoneMat = new THREE.MeshBasicMaterial({
    color: 0x00ff00,
    transparent: true,
    opacity: 0.6,
    depthWrite: false
  });
  ozoneMesh = new THREE.Mesh(ozoneGeo, ozoneMat);
  scene.add(ozoneMesh);

  // Atmosphere shell (reddish-orange, faint)
  const atmGeo = new THREE.SphereGeometry(1.45, 32, 32);
  const atmMat = new THREE.MeshBasicMaterial({
    color: 0xffaa88,
    transparent: true,
    opacity: 0.08,
    depthWrite: false
  });
  const atmosphere = new THREE.Mesh(atmGeo, atmMat);
  scene.add(atmosphere);

  // Sun (yellow) to the left
  const sunGeo = new THREE.SphereGeometry(0.3, 24, 24);
  const sunMat = new THREE.MeshBasicMaterial({ color: 0xffff00 });
  const sun = new THREE.Mesh(sunGeo, sunMat);
  sun.position.set(-5, 1.0, -1.0);
  scene.add(sun);

  // Radiation arrows, orange, towards Earth
  const arrowColor = 0xffa500;
  const arrowGroup = new THREE.Group();
  const origins = [
    new THREE.Vector3(-4.5, 1.0, -1.0),
    new THREE.Vector3(-4.8, 0.5, 0.2),
    new THREE.Vector3(-4.6, 1.5, 0.8),
    new THREE.Vector3(-4.9, -0.2, -0.3),
    new THREE.Vector3(-4.7, 0.8, 1.2)
  ];
  origins.forEach(o => {
    const toEarth = new THREE.Vector3().subVectors(new THREE.Vector3(0, 0, 0), o).normalize();
    const arrow = new THREE.ArrowHelper(toEarth, o, 3.5, arrowColor, 0.4, 0.25);
    arrowGroup.add(arrow);
  });
  scene.add(arrowGroup);

  // Render loop
  function animate() {
    requestAnimationFrame(animate);
    renderer.render(scene, camera);
  }
  animate();

  // Handle resize
  window.addEventListener('resize', () => {
    const w2 = sceneContainer.clientWidth;
    const h2 = sceneContainer.clientHeight;
    renderer.setSize(w2, h2);
    camera.aspect = w2 / h2;
    camera.updateProjectionMatrix();
    if (plotCfcEl && plotOzoneEl) {
      Plotly.Plots.resize(plotCfcEl);
      Plotly.Plots.resize(plotOzoneEl);
    }
  });
}

function updateOzoneOpacity() {
  if (!ozoneMesh) return;
  const conc = getActiveConcentration(); // 0..~540
  const minOpacity = 0.08;
  const maxOpacity = 0.75;
  // Higher CFC -> lower opacity
  const opacity = Math.max(minOpacity, Math.min(maxOpacity, maxOpacity - (conc / 600) * (maxOpacity - minOpacity)));
  ozoneMesh.material.opacity = opacity;
  ozoneMesh.material.needsUpdate = true;
}

/* =============================
   Update orchestrator
   ============================= */
function update() {
  // Sync labels
  labelYear.textContent = sliderYear.value;
  labelTime.textContent = sliderTime.value;

  // Plots
  renderCfcPlot();
  renderOzonePlot();

  // 3D opacity
  updateOzoneOpacity();
}

/* =============================
   Initialization
   ============================= */
document.addEventListener('DOMContentLoaded', () => {
  // Initial render
  renderCfcPlot();
  renderOzonePlot();
  initThree();
  updateOzoneOpacity();

  // Wire events
  selectCfc.addEventListener('change', update);
  checkboxShowAll.addEventListener('change', update);
  sliderYear.addEventListener('input', update);
  sliderTime.addEventListener('input', update);
});
</script>

</body>
</html>
