  </style>
</head>
<body>
  <main>
    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p class="description">
      Adjust the post angle to match the angular difference of the Sun’s rays observed between Syene and Alexandria.
      Toggle the zoom to focus on the central geometry. The dashed lines represent radii and local references, the yellow lines are parallel Sun rays,
      and the red arc shows the measured angle.
    </p>

    <div id="control-panel">
      <div class="group">
        <label for="slider-post-angle">post angle</label>
        <input type="range" id="slider-post-angle" min="1" max="60" step="0.1" value="7" />
        <span id="display-post-angle">7.0</span>
      </div>
      <div class="group">
        <label for="checkbox-zoom">zoom</label>
        <input type="checkbox" id="checkbox-zoom" />
      </div>
    </div>

    <div id="visualization-container">
      <canvas id="demo-canvas" width="600" height="450"></canvas>
    </div>
    <p class="hint">Tip: In the zoomed view, the sector angle at the center equals the measured post angle at Alexandria.</p>
  </main>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // Global state
    let angle = 7;       // in degrees
    let isZoomed = false;

    // DOM references
    const slider = document.getElementById('slider-post-angle');
    const angleDisplay = document.getElementById('display-post-angle');
    const zoomCheckbox = document.getElementById('checkbox-zoom');

    // Update display function
    function updateAngleDisplay() {
      angleDisplay.textContent = Number(angle).toFixed(1);
    }

    // Event listeners for controls
    slider.addEventListener('input', (e) => {
      angle = parseFloat(e.target.value);
      updateAngleDisplay();
    });
    zoomCheckbox.addEventListener('change', (e) => {
      isZoomed = !!e.target.checked;
    });

    // p5 sketch
    let cnv;

    function setup() {
      // Replace the placeholder canvas with a p5-managed one that keeps the required id.
      const container = document.getElementById('visualization-container');
      const old = document.getElementById('demo-canvas');
      if (old && old.parentNode) {
        old.parentNode.removeChild(old);
      }
      cnv = createCanvas(600, 450);
      cnv.id('demo-canvas');        // preserve required id
      cnv.parent(container);        // mount inside the container
      pixelDensity(1);
      angleMode(DEGREES);
      textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
      updateAngleDisplay();
    }

    function draw() {
      background('#F0F0F0');
      drawSunRays();

      if (isZoomed) {
        drawZoomView();
      } else {
        drawFullView();
      }
    }

    function drawSunRays() {
      // Parallel, horizontal yellow lines at right side with left-pointing arrowheads
      const xStart = width * 0.58;
      const xEnd = width - 18;
      const top = 40;
      const bottom = height - 40;
      const lines = 6;
      stroke('#DAA520');
      strokeWeight(3);
      noFill();
      for (let i = 0; i < lines; i++) {
        const y = map(i, 0, lines - 1, top, bottom);
        line(xStart, y, xEnd, y);
        // Arrowhead pointing left at xStart
        const ah = 8;
        const aw = 7;
        fill('#DAA520');
        noStroke();
        triangle(xStart, y,
                 xStart + ah, y - aw,
                 xStart + ah, y + aw);
        noFill();
        stroke('#DAA520');
        strokeWeight(3);
      }
    }

    // Utility to draw dashed line
    function dashedLine(x1, y1, x2, y2, dash = 8, gap = 5) {
      const ctx = drawingContext;
      ctx.save();
      ctx.setLineDash([dash, gap]);
      line(x1, y1, x2, y2);
      ctx.restore();
    }

    function drawFullView() {
      // Earth, center left-half
      const cx = width * 0.33;
      const cy = height * 0.5;
      const r = 160;

      // Earth disk
      noStroke();
      fill('#468296');
      circle(cx, cy, r * 2);

      // label "Earth"
      fill(255);
      noStroke();
      textAlign(CENTER, CENTER);
      textSize(18);
      text('Earth', cx, cy);

      // Syene radius (horizontal to the right) dashed
      stroke('#000');
      strokeWeight(2);
      dashedLine(cx, cy, cx + r, cy);
      // Syene label
      noStroke();
      fill('#000');
      textAlign(LEFT, CENTER);
      textSize(14);
      text('Syene', cx + r + 8, cy);

      // Alexandria point on circumference at angle (CCW from Syene), using math convention
      // Convert to screen coordinates: y goes downward, so subtract sin()
      const ax = cx + r * cos(angle);
      const ay = cy - r * sin(angle);

      // Alexandria radius dashed
      stroke('#000');
      strokeWeight(2);
      dashedLine(cx, cy, ax, ay);

      // Horizontal dashed line through Alexandria point (parallel to Syene line)
      dashedLine(ax - 50, ay, ax + 50, ay);

      // Alexandria label near the point, offset outward along radial
      const rx = ax - cx;
      const ry = ay - cy;
      const mag = Math.sqrt(rx * rx + ry * ry) || 1;
      const rux = rx / mag;
      const ruy = ry / mag;
      noStroke();
      fill('#000');
      textAlign(LEFT, BOTTOM);
      const labelAx = ax + rux * 14 + 6;
      const labelAy = ay + ruy * 14 - 6;
      text('Alexandria', labelAx, labelAy);

      // Angle visualization at Alexandria: arc between leftward horizontal (180°) and inward radial (180 - angle)
      noFill();
      stroke('#cc2f4a');
      strokeWeight(3);
      const small = 44; // arc diameter
      arc(ax, ay, small, small, 180, 180 - angle);

      // Angle label near arc
      const midAng = 180 - angle / 2;
      const tx = ax + (small * 0.7 / 2) * cos(midAng);
      const ty = ay + (small * 0.7 / 2) * sin(midAng);
      noStroke();
      fill('#cc2f4a');
      textAlign(CENTER, CENTER);
      textSize(14);
      text(`${angle.toFixed(1)}°`, tx, ty);

      // Small solid black triangle/arrowhead at Alexandria point along the tangent direction
      // Tangent is perpendicular to radius; choose direction of increasing angle (CCW)
      const tux = -ruy; // (-ry, rx) normalized -> using rux, ruy: tangent = (-ruy, rux)
      const tuy = rux;
      const tipX = ax;
      const tipY = ay;
      const baseCenterX = tipX - tux * 10;
      const baseCenterY = tipY - tuy * 10;
      const halfWidth = 4;
      const b1x = baseCenterX + rux * halfWidth;
      const b1y = baseCenterY + ruy * halfWidth;
      const b2x = baseCenterX - rux * halfWidth;
      const b2y = baseCenterY - ruy * halfWidth;
      fill('#000');
      noStroke();
      triangle(tipX, tipY, b1x, b1y, b2x, b2y);
    }

    function drawZoomView() {
      // Center of Earth sector near bottom-left
      const cx = 110;
      const cy = height - 20;
      const r = 360;

      // Earth sector (from 0 to -angle so it rises upward visually)
      noStroke();
      fill('#468296');
      arc(cx, cy, r * 2, r * 2, 0, -angle, PIE);

      // Radii: Syene (0°) and Alexandria (angle)
      const sx = cx + r; // 0 degrees
      const sy = cy;
      const ax = cx + r * cos(angle);
      const ay = cy - r * sin(angle);

      stroke('#000');
      strokeWeight(2);
      dashedLine(cx, cy, sx, sy);
      dashedLine(cx, cy, ax, ay);

      // Labels at arc ends
      noStroke();
      fill('#000');
      textSize(14);
      textAlign(CENTER, BOTTOM);
      text('Syene', sx, sy - 8);

      textAlign(LEFT, CENTER);
      // Offset label slightly outward along radius
      const rx = ax - cx;
      const ry = ay - cy;
      const mag = Math.sqrt(rx * rx + ry * ry) || 1;
      const rux = rx / mag;
      const ruy = ry / mag;
      const labelAx = ax + rux * 14 + 6;
      const labelAy = ay + ruy * 14;
      text('Alexandria', labelAx, labelAy);

      // Angle arc at center
      noFill();
      stroke('#cc2f4a');
      strokeWeight(4);
      const big = 140;
      arc(cx, cy, big, big, 0, -angle);

      // Angle label inside sector
      noStroke();
      fill('#cc2f4a');
      textAlign(CENTER, CENTER);
      textSize(18);
      const midAng = -angle / 2; // midpoint of sector in p5 screen angles
      const tx = cx + (big * 0.35) * cos(-midAng); // careful: cos/sin use current angleMode; adjust sign for placement
      const ty = cy + (big * 0.35) * sin(-midAng);
      // Using an alternative: place text at angle/2 above the x-axis
      const txx = cx + (big * 0.35) * cos(-angle / 2);
      const tyy = cy + (big * 0.35) * sin(-angle / 2);
      text(`${angle.toFixed(1)}°`, txx, tyy);

      // Post and Shadow triangle at Alexandria point:
      // One side aligned with Alexandria radius (the post), another side horizontal (incoming sun ray),
      // and the hypotenuse connects them.
      const postLen = 26;
      const sunLen = 28;

      // Unit vectors
      const ruX = rux; // outward radial unit
      const ruY = ruy;
      // Horizontal (sun ray) direction (to the right)
      const huX = 1;
      const huY = 0;

      // Base at the arc point
      const baseX = ax;
      const baseY = ay;

      // Post end (aligned with radius, outward from the surface)
      const postEndX = baseX + ruX * postLen;
      const postEndY = baseY + ruY * postLen;

      // Sun ray end (horizontal from base)
      const sunEndX = baseX + huX * sunLen;
      const sunEndY = baseY + huY * sunLen;

      // Draw the small triangle
      noStroke();
      fill('#d2b48c'); // tan
      triangle(baseX, baseY, postEndX, postEndY, sunEndX, sunEndY);

      // Outline for clarity
      stroke('#8b6b3e');
      strokeWeight(1);
      noFill();
      triangle(baseX, baseY, postEndX, postEndY, sunEndX, sunEndY);
    }
  </script>
</body>
</html>
