  <div id="app-container">
    <div id="control-panel">
      <button id="btn-reset">+</button>
      <h2>Controls</h2>

      <div class="control-row">
        <label for="slider-moon-pos">position of moon</label>
        <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144" />
      </div>

      <div class="control-row">
        <label for="slider-moon-pull">pull from moon</label>
        <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40" />
      </div>

      <div class="control-row">
        <label for="slider-sun-pos">position of sun</label>
        <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144" />
      </div>

      <div class="control-row">
        <label for="slider-sun-pull">pull from sun</label>
        <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20" />
      </div>

      <div class="hint">Move sliders to update tides. Click + to reset.</div>
    </div>

    <div id="viz-container"></div>
  </div>

  <script>
    // Defaults per specification
    const DEFAULTS = {
      moonAngle: 144,
      moonPull: 40,
      sunAngle: 144,
      sunPull: 20
    };

    // Grab DOM elements
    const elMoonPos  = document.getElementById('slider-moon-pos');
    const elMoonPull = document.getElementById('slider-moon-pull');
    const elSunPos   = document.getElementById('slider-sun-pos');
    const elSunPull  = document.getElementById('slider-sun-pull');
    const btnReset   = document.getElementById('btn-reset');

    // Attach input listeners: trigger redraw on any change
    [elMoonPos, elMoonPull, elSunPos, elSunPull].forEach(el => {
      el.addEventListener('input', () => {
        // p5's redraw (defined after setup)
        if (typeof window.redraw === 'function') {
          window.redraw();
        }
      });
    });

    // Reset button logic
    btnReset.addEventListener('click', () => {
      elMoonPos.value  = DEFAULTS.moonAngle;
      elMoonPull.value = DEFAULTS.moonPull;
      elSunPos.value   = DEFAULTS.sunAngle;
      elSunPull.value  = DEFAULTS.sunPull;
      if (typeof window.redraw === 'function') {
        window.redraw();
      }
    });

    // p5.js sketch (global mode as required)
    let earthRadius, baseWaterRadius, scalingFactor;

    function setup() {
      const canvas = createCanvas(500, 500);
      canvas.parent('viz-container');
      // Drawing setup
      noLoop(); // draw only when user interacts (we call redraw manually)
      angleMode(RADIANS);

      // Radii and scaling per spec
      earthRadius = 180;       // px
      baseWaterRadius = 190;   // slightly larger than Earth
      scalingFactor = 0.3;     // converts "pull" into pixel displacement

      // Initial render
      redraw();
    }

    function draw() {
      // Clear the canvas
      background(255);
      translate(width / 2, height / 2);

      // Read slider values
      const moonAngleDeg = parseFloat(elMoonPos.value);
      const moonPull     = parseFloat(elMoonPull.value);
      const sunAngleDeg  = parseFloat(elSunPos.value);
      const sunPull      = parseFloat(elSunPull.value);

      const moonAngleRad = radians(moonAngleDeg);
      const sunAngleRad  = radians(sunAngleDeg);

      // Draw water layer first
      stroke(0);
      strokeWeight(1);
      fill('#aed6f1');
      beginShape();
      for (let deg = 0; deg < 360; deg += 1) {
        const theta = radians(deg);
        const moonEffect = moonPull * Math.pow(Math.cos(theta - moonAngleRad), 2);
        const sunEffect  = sunPull  * Math.pow(Math.cos(theta - sunAngleRad), 2);
        const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

        const x = totalRadius * Math.cos(theta);
        const y = totalRadius * Math.sin(theta);
        vertex(x, y);
      }
      endShape(CLOSE);

      // Draw Earth on top
      noStroke();
      fill('#966F33');
      ellipse(0, 0, earthRadius * 2, earthRadius * 2);
    }

    // Utility: degrees to radians (p5 has radians(), but also keep vanilla)
    function radians(deg) {
      return deg * Math.PI / 180;
    }
  </script>
</body>
</html>
