        <div class="canvas-holder" style="background:#fff">
          <canvas id="orbit-canvas"></canvas>
        </div>
        <p>orbit diagram</p>
      </div>
    </div>
  </div>

<script>
(function() {
  // DOM elements
  const sliderEarth = document.getElementById('slider-earth-position');
  const sliderComet = document.getElementById('slider-comet-position');
  const sliderComa = document.getElementById('slider-coma');
  const sliderTail = document.getElementById('slider-tail');
  const sliderTailSpread = document.getElementById('slider-tail-spread');

  const orbitCanvas = document.getElementById('orbit-canvas');
  const telescopicCanvas = document.getElementById('telescopic-canvas');

  // Three.js: Orbit Diagram Scene
  const orbitScene = new THREE.Scene();
  const orbitCamera = new THREE.PerspectiveCamera(45, 1, 0.01, 2000);
  orbitCamera.position.set(0, 30, 30);
  orbitCamera.lookAt(0, 0, 0);

  const orbitRenderer = new THREE.WebGLRenderer({
    canvas: orbitCanvas,
    antialias: true,
    alpha: false
  });
  orbitRenderer.setClearColor(0xffffff, 1);

  // Lights for orbit scene
  const orbitAmbient = new THREE.AmbientLight(0xffffff, 0.6);
  orbitScene.add(orbitAmbient);
  const sunLight = new THREE.PointLight(0xffffff, 1.2, 0, 2);
  sunLight.position.set(0, 0, 0);
  orbitScene.add(sunLight);

  // Sun mesh at origin
  const sunGeom = new THREE.SphereGeometry(0.7, 32, 16);
  const sunMat = new THREE.MeshBasicMaterial({ color: 0xffcc00 });
  const sunMesh = new THREE.Mesh(sunGeom, sunMat);
  orbitScene.add(sunMesh);

  // Utility: make orbit line using EllipseCurve (center shifted so Sun ~ at focus)
  function makeOrbitLine(a, e, colorHex) {
    const c = a * e; // distance from center to focus
    const b = a * Math.sqrt(1 - e * e);

    const curve = new THREE.EllipseCurve(
      c,            // ax: shift center in +X so left focus near origin
      0,            // ay
      a,            // xRadius (semi-major)
      b,            // yRadius (semi-minor)
      0,            // startAngle
      Math.PI * 2,  // endAngle
      false,
      0
    );
    const points2 = curve.getPoints(512);
    const points3 = points2.map(p => new THREE.Vector3(p.x, 0, p.y)); // map Y->Z for XZ plane
    const geom = new THREE.BufferGeometry().setFromPoints(points3);
    const mat = new THREE.LineBasicMaterial({ color: colorHex, linewidth: 1 });
    const line = new THREE.LineLoop(geom, mat);
    return line;
  }

  // Orbit parameters
  const orbits = {
    earth:   { a: 4.0,  e: 0.0167, color: 0x2474ff },
    mars:    { a: 6.1,  e: 0.0934, color: 0xff4b4b },
    jupiter: { a: 20.8, e: 0.0489, color: 0xff9c33 },
    holmes:  { a: 14.4, e: 0.432,  color: 0xfff066 }
  };

  // Add orbit lines
  const earthOrbitLine   = makeOrbitLine(orbits.earth.a, orbits.earth.e, orbits.earth.color);
  const marsOrbitLine    = makeOrbitLine(orbits.mars.a, orbits.mars.e, orbits.mars.color);
  const jupiterOrbitLine = makeOrbitLine(orbits.jupiter.a, orbits.jupiter.e, orbits.jupiter.color);
  const holmesOrbitLine  = makeOrbitLine(orbits.holmes.a, orbits.holmes.e, orbits.holmes.color);
  orbitScene.add(earthOrbitLine, marsOrbitLine, jupiterOrbitLine, holmesOrbitLine);

  // Earth mesh
  const earthGeom = new THREE.SphereGeometry(0.25, 24, 16);
  const earthMat = new THREE.MeshStandardMaterial({ color: 0x2474ff, roughness: 0.6, metalness: 0.0 });
  const earthMesh = new THREE.Mesh(earthGeom, earthMat);
  orbitScene.add(earthMesh);

  // Function to compute position on ellipse (focus at origin, plane XZ)
  function positionOnOrbitXZ(a, e, thetaRad) {
    const c = a * e;
    const b = a * Math.sqrt(1 - e * e);
    const x = c + a * Math.cos(thetaRad);
    const z = b * Math.sin(thetaRad);
    return new THREE.Vector3(x, 0, z);
  }

  // Comet: shared group (will be re-parented between scenes before each render)
  const cometGroup = new THREE.Group();

  // Nucleus
  const nucleusGeom = new THREE.SphereGeometry(0.18, 24, 16);
  const nucleusMat = new THREE.MeshBasicMaterial({ color: 0xffffff });
  const nucleusMesh = new THREE.Mesh(nucleusGeom, nucleusMat);
  cometGroup.add(nucleusMesh);

  // Coma (semi-transparent, additive)
  const comaGeom = new THREE.SphereGeometry(1.0, 32, 24);
  const comaMat = new THREE.MeshBasicMaterial({
    color: 0xa8ff8a,
    transparent: true,
    opacity: 0.5,
    blending: THREE.AdditiveBlending,
    depthWrite: false
  });
  const comaMesh = new THREE.Mesh(comaGeom, comaMat);
  cometGroup.add(comaMesh);

  // Tail (semi-transparent cone pointing away from Sun)
  const baseTailHeight = 4.0;
  const baseTailRadius = 0.5;
  const tailGeom = new THREE.ConeGeometry(baseTailRadius, baseTailHeight, 48, 1, false);
  // Move geometry so apex is at the origin (where the nucleus is)
  tailGeom.translate(0, -baseTailHeight / 2, 0);
  // Tail material
  const tailMat = new THREE.MeshBasicMaterial({
    color: 0xc7ff80,
    transparent: true,
    opacity: 0.4,
    blending: THREE.AdditiveBlending,
    depthWrite: false
  });
  const tailMesh = new THREE.Mesh(tailGeom, tailMat);
  cometGroup.add(tailMesh);

  // Three.js: Telescopic View Scene
  const telescopicScene = new THREE.Scene();
  const telescopicCamera = new THREE.PerspectiveCamera(15, 1, 0.01, 5000);
  telescopicCamera.position.set(0, 0, 5);
  const telescopicRenderer = new THREE.WebGLRenderer({
    canvas: telescopicCanvas,
    antialias: true,
    alpha: false
  });
  telescopicRenderer.setClearColor(0x000000, 1);

  // Lighting for telescopic view (soft ambient so comet is visible)
  const telescopicAmbient = new THREE.AmbientLight(0xffffff, 0.9);
  telescopicScene.add(telescopicAmbient);

  // State
  const state = {
    earthAngleDeg: parseFloat(sliderEarth.value),
    cometAngleDeg: parseFloat(sliderComet.value),
    coma: parseFloat(sliderComa.value),
    tail: parseFloat(sliderTail.value),
    tailSpread: parseFloat(sliderTailSpread.value)
  };

  // Update function: apply all slider-driven updates
  function update() {
    state.earthAngleDeg = parseFloat(sliderEarth.value);
    state.cometAngleDeg = parseFloat(sliderComet.value);
    state.coma = parseFloat(sliderComa.value);
    state.tail = parseFloat(sliderTail.value);
    state.tailSpread = parseFloat(sliderTailSpread.value);

    // Update Earth position (orbit diagram)
    const earthTheta = THREE.MathUtils.degToRad(state.earthAngleDeg);
    const earthPos = positionOnOrbitXZ(orbits.earth.a, orbits.earth.e, earthTheta);
    earthMesh.position.copy(earthPos);

    // Update comet position (both views)
    const cometTheta = THREE.MathUtils.degToRad(state.cometAngleDeg);
    const cometPos = positionOnOrbitXZ(orbits.holmes.a, orbits.holmes.e, cometTheta);
    cometGroup.position.copy(cometPos);

    // Update tail orientation to point away from Sun (origin)
    // Direction vector from nucleus (at cometPos) away from Sun is simply normalize(cometPos)
    const dir = cometPos.clone().normalize();
    const up = new THREE.Vector3(0, 1, 0);
    const quat = new THREE.Quaternion().setFromUnitVectors(up, dir);
    tailMesh.quaternion.copy(quat);

    // Update coma scale (0 -> nearly invisible, 100 -> very large)
    const comaScale = 0.01 + (state.coma / 100) * 10.0; // 0.01 .. 10.01
    comaMesh.scale.setScalar(comaScale);

    // Update tail length (scale along height/Y)
    const tailLengthScale = (state.tail / 100) * 10.0; // 0 .. 10
    tailMesh.scale.y = tailLengthScale;

    // Update tail spread (scale along X and Z)
    const tailSpreadScale = 0.1 + (state.tailSpread / 100) * 4.0; // 0.1 .. 4.1
    tailMesh.scale.x = tailSpreadScale;
    tailMesh.scale.z = tailSpreadScale;

    // Update telescopic camera at Earth's current position, looking at comet
    telescopicCamera.position.copy(earthPos);
    telescopicCamera.lookAt(cometPos);
  }

  // Resize handlers
  function resizeRenderersToDisplaySize(renderer, canvas, camera) {
    const width = canvas.clientWidth;
    const height = canvas.clientHeight;
    const needResize = (canvas.width !== Math.floor(width * window.devicePixelRatio)) ||
                       (canvas.height !== Math.floor(height * window.devicePixelRatio));
    if (needResize) {
      renderer.setPixelRatio(window.devicePixelRatio);
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }
  }

  // Animation loop
  function animate() {
    requestAnimationFrame(animate);

    // Keep renderers sized to their canvases
    resizeRenderersToDisplaySize(orbitRenderer, orbitCanvas, orbitCamera);
    resizeRenderersToDisplaySize(telescopicRenderer, telescopicCanvas, telescopicCamera);

    // Render Orbit Diagram with comet group
    if (cometGroup.parent !== orbitScene) {
      orbitScene.add(cometGroup);
    }
    orbitRenderer.render(orbitScene, orbitCamera);

    // Render Telescopic View with the same comet group instance re-parented to telescopicScene
    if (cometGroup.parent !== telescopicScene) {
      telescopicScene.add(cometGroup);
    }
    telescopicRenderer.render(telescopicScene, telescopicCamera);
  }

  // Hook sliders
  [sliderEarth, sliderComet, sliderComa, sliderTail, sliderTailSpread].forEach(sl => {
    sl.addEventListener('input', update);
  });

  // Initial add: place the comet group into orbit scene first so it appears there on first render
  orbitScene.add(cometGroup);

  // Initial update and start
  update();
  animate();

  // Handle window resize
  window.addEventListener('resize', () => {
    // Sizes are recalculated in the animate loop per frame
    // Keeping this for completeness if any layout changes externally
    update();
  });
})();
</script>
</body>
