  const sliderY = document.getElementById('slider-y');

  const labelM = document.getElementById('label-m');
  const labelX = document.getElementById('label-x');
  const labelY = document.getElementById('label-y');

  // Update label helper with appropriate precision
  function updateLabels() {
    labelM.textContent = Number(sliderM.value).toFixed(3);
    labelX.textContent = Number(sliderX.value).toFixed(1);
    labelY.textContent = Number(sliderY.value).toFixed(3);
  }

  // Attach input listeners to keep labels in sync
  sliderM.addEventListener('input', updateLabels);
  sliderX.addEventListener('input', updateLabels);
  sliderY.addEventListener('input', updateLabels);

  // p5.js sketch
  let L = 10;        // logical distance between posts
  let g = 9.81;      // gravity (m/s^2)
  let canvasW = 800;
  let canvasH = 500;

  // Anchor points in pixels
  const leftAnchor = { x: 100, y: 150 };
  const rightAnchor = { x: 700, y: 150 };
  const scaleX = (rightAnchor.x - leftAnchor.x) / L; // px per logical unit for x
  const scaleY = 50;  // px per logical unit for y

  // Utility: draw dashed line
  function drawDashedLine(p, x1, y1, x2, y2, dash = 8, gap = 6) {
    const dx = x2 - x1;
    const dy = y2 - y1;
    const dist = Math.hypot(dx, dy);
    const ux = dx / dist;
    const uy = dy / dist;
    let drawn = 0;
    while (drawn < dist) {
      const sx = x1 + ux * drawn;
      const sy = y1 + uy * drawn;
      const ex = x1 + ux * Math.min(drawn + dash, dist);
      const ey = y1 + uy * Math.min(drawn + dash, dist);
      p.line(sx, sy, ex, ey);
      drawn += (dash + gap);
    }
  }

  // Compute a perpendicular offset point near a segment midpoint
  function offsetMidpoint(x1, y1, x2, y2, offset = 16) {
    const mx = 0.5 * (x1 + x2);
    const my = 0.5 * (y1 + y2);
    const dx = x2 - x1;
    const dy = y2 - y1;
    const len = Math.hypot(dx, dy) || 1;
    // perpendicular vector (-dy, dx)
    const nx = -dy / len;
    const ny = dx / len;
    return { x: mx + nx * offset, y: my + ny * offset };
  }

  // p5 setup and draw
  let sketch = (p) => {
    p.setup = function() {
      const c = p.createCanvas(canvasW, canvasH);
      c.parent('viz');
      p.pixelDensity(1);
      p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
      p.textSize(14);
      updateLabels(); // ensure labels match initial slider values
    };

