      // Read current values from sliders
      const m = Number(sliderM.value);
      const x = Number(sliderX.value);
      const y = Number(sliderY.value);

      // Compute physics
      const theta1 = Math.atan2(y, x);           // radians
      const theta2 = Math.atan2(y, (L - x));     // radians
      const Fg = m * g;                          // weight
      const denom = Math.sin(theta1 + theta2);
      const safeDenom = Math.abs(denom) < 1e-9 ? (denom >= 0 ? 1e-9 : -1e-9) : denom;
      const T1 = (Fg * Math.cos(theta2)) / safeDenom;
      const T2 = (Fg * Math.cos(theta1)) / safeDenom;

      // Map logical to pixel coordinates
      const massX = leftAnchor.x + x * scaleX;
      const massY = leftAnchor.y + y * scaleY;

      // Draw posts
      p.push();
      p.noStroke();
      p.fill(0);
      // Left post
      p.rect(leftAnchor.x - 15, 50, 30, 400, 6);
      // Right post
      p.rect(rightAnchor.x - 15, 50, 30, 400, 6);
      p.pop();

      // Draw anchor points
      p.push();
      p.noStroke();
      p.fill(20);
      p.circle(leftAnchor.x, leftAnchor.y, 8);
      p.circle(rightAnchor.x, rightAnchor.y, 8);
      p.pop();

      // Draw dashed horizontal reference between anchors
      p.push();
      p.stroke(180);
      p.strokeWeight(1.5);
      drawDashedLine(p, leftAnchor.x, leftAnchor.y, rightAnchor.x, rightAnchor.y, 10, 6);
      p.pop();

      // Draw ropes
      p.push();
      p.stroke(0);
      p.strokeWeight(3);
      p.line(leftAnchor.x, leftAnchor.y, massX, massY);
      p.line(rightAnchor.x, rightAnchor.y, massX, massY);
      p.pop();

      // Draw mass
      p.push();
      p.noStroke();
      p.fill(0);
      p.circle(massX, massY, 20 * 2); // radius ~20 px
      p.pop();

      // Draw angles using arcs
      const rArc = 60; // radius of the arc for angles

      // Angles in screen coordinates for arcs
      const alphaLeft = Math.atan2(massY - leftAnchor.y, massX - leftAnchor.x);   // ~ [0, PI/2]
      const alphaRight = Math.atan2(massY - rightAnchor.y, massX - rightAnchor.x); // ~ (PI/2, PI)

      p.push();
      p.noFill();
      p.stroke('#ff8800');
      p.strokeWeight(2);
      // Left angle θ₁: from horizontal (0) to alphaLeft
      p.arc(leftAnchor.x, leftAnchor.y, rArc, rArc, 0, alphaLeft);
      // Right angle θ₂: from alphaRight to PI (leftward horizontal)
      p.arc(rightAnchor.x, rightAnchor.y, rArc, rArc, alphaRight, Math.PI);
      p.pop();

      // Place θ labels near arcs
      p.push();
      p.noStroke();
      p.fill(0);
      p.textSize(14);
      p.textAlign(p.CENTER, p.CENTER);
      // θ₁ label at left
      const midLeft = alphaLeft / 2;
      const lx = leftAnchor.x + (rArc * 0.45) * Math.cos(midLeft);
      const ly = leftAnchor.y + (rArc * 0.45) * Math.sin(midLeft);
      p.text('θ₁', lx, ly);
      // θ₂ label at right
      const midRight = (alphaRight + Math.PI) / 2;
      const rx = rightAnchor.x + (rArc * 0.45) * Math.cos(midRight);
      const ry = rightAnchor.y + (rArc * 0.45) * Math.sin(midRight);
      p.text('θ₂', rx, ry);
      p.pop();

      // Label T₁ and T₂ near rope segments
      const off1 = offsetMidpoint(leftAnchor.x, leftAnchor.y, massX, massY, 18);
      const off2 = offsetMidpoint(rightAnchor.x, rightAnchor.y, massX, massY, 18);
      p.push();
      p.noStroke();
      p.fill(0);
      p.textSize(14);
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text('T₁', off1.x, off1.y);
      p.text('T₂', off2.x, off2.y);
      p.pop();

      // Display T₁ and T₂ values at top center
      p.push();
      p.textAlign(p.CENTER, p.TOP);
      p.textSize(16);
      p.fill(20);
      const t1Str = (isFinite(T1) ? T1.toFixed(4) : '—');
      const t2Str = (isFinite(T2) ? T2.toFixed(4) : '—');
      p.text(`T₁ = ${t1Str} N | T₂ = ${t2Str} N`, p.width / 2, 10);
      p.pop();

      // Optional: display numeric angles under the tensions line
      // p.textAlign(p.CENTER, p.TOP);
      // p.fill(100);
      // p.text(`θ₁ = ${(theta1 * 180/Math.PI).toFixed(2)}° | θ₂ = ${(theta2 * 180/Math.PI).toFixed(2)}°`, p.width/2, 32);
    };
  };

  new p5(sketch);
</script>
