  <script>
    // State variables
    let time = 0; // 0..10
    let animationFrameId = null;
    let isRunning = false;
    let lastTimestamp = null;

    // Simulation parameters and solution functions
    let params = {
      x0: 0.1,
      v0: 1.0,
      k: 1.0,
      m: 1.0,
      c: 0.1,
      Kp: 0.1,
      Kd: 0.1
    };
    let k_eff = 0, c_eff = 0, wn = 0, zeta = 0;
    let fx = (t)=>0, fv = (t)=>0; // x(t), v(t)

    // Time vector for plots
    const T_END = 10.0;
    const NPTS = 1001;
    const tArray = Array.from({length: NPTS}, (_,i)=> i*(T_END/(NPTS-1)));

    // UI elements
    const el = {
      disp: document.getElementById('slider-displacement'),
      vel: document.getElementById('slider-velocity'),
      k: document.getElementById('slider-stiffness'),
      m: document.getElementById('slider-mass'),
      c: document.getElementById('slider-damping'),
      Kp: document.getElementById('slider-proportional-gain'),
      Kd: document.getElementById('slider-derivative-gain'),
      vdisp: document.getElementById('value-displacement'),
      vvel: document.getElementById('value-velocity'),
      vk: document.getElementById('value-stiffness'),
      vm: document.getElementById('value-mass'),
      vc: document.getElementById('value-damping'),
      vKp: document.getElementById('value-proportional-gain'),
      vKd: document.getElementById('value-derivative-gain'),
      btnPlay: document.getElementById('btn-play'),
      btnPause: document.getElementById('btn-pause'),
      btnReset: document.getElementById('btn-reset')
    };

    function fmt(v) {
      const n = Math.abs(v) < 1 ? 3 : 2;
      return Number(v).toFixed(n);
    }

    function readParamsFromUI() {
      params.x0 = parseFloat(el.disp.value);
      params.v0 = parseFloat(el.vel.value);
      params.k  = parseFloat(el.k.value);
      params.m  = parseFloat(el.m.value);
      params.c  = parseFloat(el.c.value);
      params.Kp = parseFloat(el.Kp.value);
      params.Kd = parseFloat(el.Kd.value);
    }

    function updateValueSpans() {
      el.vdisp.textContent = fmt(params.x0);
      el.vvel.textContent  = fmt(params.v0);
      el.vk.textContent    = fmt(params.k);
      el.vm.textContent    = fmt(params.m);
      el.vc.textContent    = fmt(params.c);
      el.vKp.textContent   = fmt(params.Kp);
      el.vKd.textContent   = fmt(params.Kd);
    }

    function computeSolution() {
      // Effective parameters
      k_eff = params.k + params.Kp;
      c_eff = params.c + params.Kd;

      // Handle degenerate cases robustly
      if (k_eff <= 1e-10) {
        // No stiffness: m x'' + c_eff x' = 0
        if (c_eff > 1e-10) {
          const gamma = c_eff / params.m;
          fx = (t) => params.x0 + (params.v0 / gamma) * (1 - Math.exp(-gamma * t));
          fv = (t) => params.v0 * Math.exp(-gamma * t);
        } else {
          // Free particle: x = x0 + v0 t
          fx = (t) => params.x0 + params.v0 * t;
          fv = (t) => params.v0;
        }
        return;
      }

      wn = Math.sqrt(k_eff / params.m);
      const denom = 2 * Math.sqrt(k_eff * params.m);
      zeta = c_eff / denom;

      if (zeta < 1 - 1e-9) {
        const wd = wn * Math.sqrt(1 - zeta * zeta);
        const A = params.x0;
        const B = (params.v0 + zeta * wn * params.x0) / wd;
        fx = (t) => {
          const E = Math.exp(-zeta * wn * t);
          const c = Math.cos(wd * t);
          const s = Math.sin(wd * t);
          return E * (A * c + B * s);
        };
        fv = (t) => {
          const E = Math.exp(-zeta * wn * t);
          const c = Math.cos(wd * t);
          const s = Math.sin(wd * t);
          // derivative of x(t)
          return E * (
              -zeta * wn * (A * c + B * s) + (-A * wd * s + B * wd * c)
          );
        };
      } else if (Math.abs(zeta - 1) <= 1e-9) {
        // Critically damped
        const C2 = params.v0 + wn * params.x0;
        fx = (t) => Math.exp(-wn * t) * (params.x0 + C2 * t);
        fv = (t) => {
          const E = Math.exp(-wn * t);
          return E * (-wn * (params.x0 + C2 * t) + C2);
        };
      } else {
        // Overdamped
        const alpha = Math.sqrt(zeta * zeta - 1);
        const r1 = -wn * (zeta - alpha);
        const r2 = -wn * (zeta + alpha);
        const C1 = (params.v0 - r2 * params.x0) / (r1 - r2);
        const C2 = params.x0 - C1;
        fx = (t) => C1 * Math.exp(r1 * t) + C2 * Math.exp(r2 * t);
        fv = (t) => C1 * r1 * Math.exp(r1 * t) + C2 * r2 * Math.exp(r2 * t);
      }
    }

    // Precompute arrays for plots
    function computeTrajectories() {
      const xArr = tArray.map(fx);
      const vArr = tArray.map(fv);
      return { xArr, vArr };
