      };

      const dispLayout = JSON.parse(JSON.stringify(commonLayout));
      dispLayout.yaxis.title = { text: 'Displacement (m)', font: { color: '#c9d1d9' } };

      const velLayout = JSON.parse(JSON.stringify(commonLayout));
      velLayout.yaxis.title = { text: 'Velocity (m/s)', font: { color: '#c9d1d9' } };

      Plotly.react('plot-displacement', dispData, dispLayout, {displayModeBar: false, responsive: true});
      Plotly.react('plot-velocity', velData, velLayout, {displayModeBar: false, responsive: true});
    }

    function updateMarkers(t) {
      const x = fx(t);
      const v = fv(t);
      Plotly.restyle('plot-displacement', {x: [[t]], y: [[x]]}, [1]);
      Plotly.restyle('plot-velocity',     {x: [[t]], y: [[v]]}, [1]);
