    // THREE.js scene
    let scene, camera, renderer;
    let massMesh, springMesh, damperHousing, damperRod, baseMesh, wallMesh, enclosure;
    const restLength = 2.5; // nominal spring/damper rest length
    const springRadius = 0.18;
    const springTurns = 10;

    class HelixCurve extends THREE.Curve {
      constructor(length, radius, coils) {
        super();
        this.length = length;
        this.radius = radius;
        this.coils = coils;
      }
      getPoint(t) {
        const x = t * this.length;
        const angle = 2 * Math.PI * this.coils * t;
        const y = this.radius * Math.cos(angle);
        const z = this.radius * Math.sin(angle);
        return new THREE.Vector3(x, y, z);
      }
    }

    function setupThree() {
      scene = new THREE.Scene();
      scene.background = new THREE.Color(0x0d1117);

