      renderer.setSize(width, height);
      renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
      document.getElementById('scene-container').appendChild(renderer.domElement);

      // Lights
      const amb = new THREE.AmbientLight(0xffffff, 0.5);
      scene.add(amb);
      const dir = new THREE.DirectionalLight(0xffffff, 0.8);
      dir.position.set(4, 6, 3);
      scene.add(dir);

      // Base (green)
      const baseGeom = new THREE.BoxGeometry(10, 0.2, 3.5);
      const baseMat = new THREE.MeshStandardMaterial({ color: 0x2e8b57, roughness: 0.8, metalness: 0.2 });
      baseMesh = new THREE.Mesh(baseGeom, baseMat);
      baseMesh.position.set(5, -0.7, 0);
      scene.add(baseMesh);

      // Wall/anchor at x = 0
      const wallGeom = new THREE.BoxGeometry(0.2, 2.0, 2.5);
      const wallMat = new THREE.MeshStandardMaterial({ color: 0x2e8b57, roughness: 0.8, metalness: 0.2 });
      wallMesh = new THREE.Mesh(wallGeom, wallMat);
      wallMesh.position.set(-0.1, 0.3, 0);
      scene.add(wallMesh);

      // Spring (red), initially length = restLength
      const springCurve = new HelixCurve(restLength, springRadius, springTurns);
      const springGeom = new THREE.TubeGeometry(springCurve, 280, 0.05, 10, false);
      const springMat = new THREE.MeshStandardMaterial({ color: 0xff3b30, roughness: 0.6, metalness: 0.6 });
      springMesh = new THREE.Mesh(springGeom, springMat);
      springMesh.position.set(0, 0, 0);
      scene.add(springMesh);

      // Damper (blue): housing + piston (two cylinders)
      const blueMat = new THREE.MeshStandardMaterial({ color: 0x1f6feb, roughness: 0.4, metalness: 0.6 });

      // We'll use cylinders with base length 1 and scale.y to desired height, rotated to align with x-axis
      damperHousing = new THREE.Mesh(new THREE.CylinderGeometry(0.16, 0.16, 1, 20), blueMat);
      damperHousing.rotation.z = Math.PI / 2;
      scene.add(damperHousing);

      damperRod = new THREE.Mesh(new THREE.CylinderGeometry(0.07, 0.07, 1, 20), blueMat);
      damperRod.rotation.z = Math.PI / 2;
      scene.add(damperRod);

      // Mass block (yellow)
      const massGeom = new THREE.BoxGeometry(0.6, 0.6, 0.6);
      const massMat = new THREE.MeshStandardMaterial({ color: 0xffd60a, roughness: 0.5, metalness: 0.4 });
      massMesh = new THREE.Mesh(massGeom, massMat);
      massMesh.position.set(restLength, 0, 0);
      scene.add(massMesh);

      // Enclosure wireframe box
      const encGeom = new THREE.BoxGeometry(12, 4, 4);
      const encMat = new THREE.MeshBasicMaterial({ color: 0x58a6ff, wireframe: true, transparent: true, opacity: 0.25 });
      enclosure = new THREE.Mesh(encGeom, encMat);
      enclosure.position.set(5.5, 0.6, 0);
      scene.add(enclosure);

      renderer.render(scene, camera);
    }

    function updateSpringDamperAndMass(x) {
      // Map physical displacement to 3D
      const massX = restLength + x; // center position of mass along x
      massMesh.position.x = massX;

      // Spring scaling along x (avoid negative scale if overly compressed)
      const scaleX = Math.max(0.15, (massX) / restLength);
      springMesh.scale.set(scaleX, 1, 1);

      // Damper: housing fixed near the wall, piston extends to mass
      const housingLen = 0.80 * restLength;
      damperHousing.scale.y = housingLen; // y scale becomes x after rotation
      damperHousing.position.x = housingLen / 2;

      let rodLen = Math.max(0.15, (massX - housingLen));
      damperRod.scale.y = rodLen;
      damperRod.position.x = housingLen + rodLen / 2;

      renderer.render(scene, camera);
    }

    // Animation loop
    function animate(now) {
      if (!isRunning) return;
      if (lastTimestamp == null) lastTimestamp = now;
      const dt = Math.min(0.05, (now - lastTimestamp) / 1000.0);
      lastTimestamp = now;

      time += dt;
      if (time >= T_END) {
        time = T_END;
        isRunning = false;
      }

      const x = fx(time);
      updateSpringDamperAndMass(x);
      updateMarkers(time);

      if (isRunning) {
        animationFrameId = requestAnimationFrame(animate);
      }
    }

    function play() {
      if (isRunning) return;
      isRunning = true;
      lastTimestamp = null;
      animationFrameId = requestAnimationFrame(animate);
    }

    function pause() {
      isRunning = false;
      if (animationFrameId) cancelAnimationFrame(animationFrameId);
      animationFrameId = null;
      lastTimestamp = null;
    }

    function reset() {
      pause();
      time = 0;
      updateMarkers(0);
      updateSpringDamperAndMass(fx(0));
    }

    // Slider interactions: pause, reset, recompute solution, redraw plots, update 3D
    function onAnySliderChange() {
      pause();
      time = 0;
      readParamsFromUI();
      updateValueSpans();
      computeSolution();
      drawPlots();
      updateSpringDamperAndMass(fx(0));
    }

    // Initialize everything
    function init() {
      // THREE
      setupThree();

      // Parameters from UI and initial spans
      readParamsFromUI();
      updateValueSpans();

      // Solution and plots
      computeSolution();
      drawPlots();
      updateSpringDamperAndMass(fx(0));
      updateMarkers(0);

      // Listeners for sliders
      el.disp.addEventListener('input', onAnySliderChange);
      el.vel.addEventListener('input', onAnySliderChange);
      el.k.addEventListener('input', onAnySliderChange);
      el.m.addEventListener('input', onAnySliderChange);
      el.c.addEventListener('input', onAnySliderChange);
      el.Kp.addEventListener('input', onAnySliderChange);
      el.Kd.addEventListener('input', onAnySliderChange);

      // Buttons
      el.btnPlay.addEventListener('click', play);
      el.btnPause.addEventListener('click', pause);
      el.btnReset.addEventListener('click', reset);

      // Responsive behavior
      window.addEventListener('resize', () => {
        const sc = document.getElementById('scene-container');
        const w = sc.clientWidth;
        const h = sc.clientHeight;
        if (w > 0 && h > 0) {
          renderer.setSize(w, h);
          camera.aspect = w / h;
          camera.updateProjectionMatrix();
          renderer.render(scene, camera);
        }
        if (document.getElementById('plot-displacement')) {
          Plotly.Plots.resize('plot-displacement');
        }
        if (document.getElementById('plot-velocity')) {
          Plotly.Plots.resize('plot-velocity');
        }
      });
    }

    // Start
    init();
  </script>
</body>
</html>
