    margin: 0;
    line-height: 1.4;
  }
</style>
</head>
<body>
  <div id="app-container">
    <!-- Header Controls Section -->
    <div id="header-controls">
      <button id="btn-move" class="active">move the points</button>
      <button id="btn-start-algo">start the algorithm</button>
    </div>

    <!-- Algorithm Step Controls Section -->
    <div id="step-controls">
      <!-- Step 1 -->
      <div class="step-group">
        <button disabled>step 1</button>
        <button id="btn-step1-explain" disabled>explain</button>
        <button id="btn-step1-do" disabled>do it</button>
      </div>
      <!-- Step 2 -->
      <div class="step-group">
        <button disabled>step 2</button>
        <button id="btn-step2-explain" disabled>explain</button>
        <button id="btn-step2-do" disabled>do it</button>
      </div>
      <!-- Step 3 -->
      <div class="step-group" id="step3-controls-container">
        <button disabled>step 3</button>
        <button id="btn-step3-explain" disabled>explain</button>
        <div id="step3-actions" style="display: inline-block;"></div>
      </div>
    </div>

    <!-- Visualization Canvas Section -->
    <div id="canvas-container"></div>

    <!-- Explanation Panel Section -->
    <div id="explanation-panel">
      <p id="explanation-text"></p>
    </div>
  </div>

  <!-- p5.js dependency -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

  <script>
    // ====== Global State Variables (as specified) ======
    let mode = 'move'; // 'move' or 'algorithm'
    let points = [];   // array of p5.Vector
    let draggedPoint = null;

    const algorithmStateDefault = () => ({
      step: 0,
      hullPointsIndices: [],
      fullHull: [],
      leftmostPointIndex: null,
      currentPointIndex: null,
      candidateLines: [],
      explanationSteps: []
    });
    let algorithmState = algorithmStateDefault();

    // additional helper state
    let isClosed = false; // whether final closing edge back to leftmost has been drawn

    // Canvas constants
    const CANVAS_W = 700;
    const CANVAS_H = 500;

    // Initial points (to be converted to p5.Vector in setup)
    const initialPointPositions = [
      {x: 150, y: 350},
      {x: 250, y: 150},
      {x: 350, y: 250},
      {x: 550, y: 180},
      {x: 480, y: 320},
      {x: 420, y: 400}
    ];

    // ====== Utility Functions for UI ======
    function setHeaderActive(btnId) {
      document.getElementById('btn-move').classList.remove('active');
      document.getElementById('btn-start-algo').classList.remove('active');
      document.getElementById(btnId).classList.add('active');
    }

    function resetAlgorithmStateUI() {
      // Disable all step buttons
      ['btn-step1-explain','btn-step1-do','btn-step2-explain','btn-step2-do','btn-step3-explain'].forEach(id => {
        const el = document.getElementById(id);
        if (el) el.disabled = true;
      });
      // Clear step3 actions
      document.getElementById('step3-actions').innerHTML = '';
      // Hide step controls
      document.getElementById('step-controls').style.display = 'none';
      // Clear explanation
      document.getElementById('explanation-text').innerHTML = '';
    }

    function enableStep1Buttons() {
      document.getElementById('btn-step1-explain').disabled = false;
      document.getElementById('btn-step1-do').disabled = false;
    }

    function disableStep1Buttons() {
      document.getElementById('btn-step1-explain').disabled = true;
      document.getElementById('btn-step1-do').disabled = true;
    }

    function enableStep2Buttons() {
      document.getElementById('btn-step2-explain').disabled = false;
      document.getElementById('btn-step2-do').disabled = false;
    }

    function disableStep2Buttons() {
      document.getElementById('btn-step2-explain').disabled = true;
      document.getElementById('btn-step2-do').disabled = true;
    }

    function enableStep3Explain(enable) {
      document.getElementById('btn-step3-explain').disabled = !enable;
    }

    function populateStep3Actions() {
      const container = document.getElementById('step3-actions');
      container.innerHTML = '';

      // Number of remaining hull points (excluding the first two already found in steps 1 and 2)
      const n = algorithmState.fullHull.length;

      // As specified: add one "do it" button, followed by (fullHull.length - 2) "repeat" buttons.
      // Note: This yields (n-1) total buttons for step 3. We'll gracefully handle completion before any extra clicks.
      const doBtn = document.createElement('button');
      doBtn.id = 'btn-step3-do';
      doBtn.textContent = 'do it';
      doBtn.disabled = true; // will be enabled after step 2 is done
      doBtn.addEventListener('click', () => step3Action(doBtn));
      container.appendChild(doBtn);

      const repeatCount = Math.max(0, n - 2);
      for (let i = 1; i <= repeatCount; i++) {
        const rep = document.createElement('button');
        rep.id = `btn-step3-repeat-${i}`;
        rep.textContent = 'repeat';
        rep.disabled = true;
        rep.addEventListener('click', () => step3Action(rep));
        container.appendChild(rep);
      }
    }

    function setExplanationTextFromState() {
      const txt = algorithmState.explanationSteps.map((s, i) => `${s}`).join('<br>');
      document.getElementById('explanation-text').innerHTML = txt;
    }

    // ====== Geometry / Algorithm Helpers ======
    function orientationIndex(i, j, k) {
      const a = points[i];
      const b = points[j];
      const c = points[k];
      return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
    }

    function distSqIndex(i, j) {
      const dx = points[i].x - points[j].x;
      const dy = points[i].y - points[j].y;
      return dx*dx + dy*dy;
    }

    function findLeftmostIndex() {
      let minIdx = 0;
      for (let i = 1; i < points.length; i++) {
        if (points[i].x < points[minIdx].x || (points[i].x === points[minIdx].x && points[i].y < points[minIdx].y)) {
          minIdx = i;
        }
      }
      return minIdx;
    }

    function computeJarvisHullIndices() {
      const n = points.length;
      if (n < 3) {
        return Array.from({length: n}, (_, idx) => idx);
      }
      const left = findLeftmostIndex();
      const hull = [left];
      let current = left;

      while (true) {
        // pick a candidate that is not current
        let candidate = (current + 1) % n;
        if (candidate === current) candidate = (candidate + 1) % n;

        for (let j = 0; j < n; j++) {
          if (j === current || j === candidate) continue;
          const orient = orientationIndex(current, candidate, j);
          // Select the most clockwise point: orientation < 0 means 'j' is more to the right
          if (orient < 0) {
            candidate = j;
          } else if (orient === 0) {
            // If collinear, pick the farther point
            if (distSqIndex(current, j) > distSqIndex(current, candidate)) {
              candidate = j;
            }
          }
        }

        if (candidate === left) {
          break;
        } else {
          hull.push(candidate);
          current = candidate;
          // safety
          if (hull.length > n + 5) break;
        }
      }
      return hull;
    }

    function resetAlgorithmState() {
      algorithmState = algorithmStateDefault();
      isClosed = false;
      algorithmState.step = 0;
      algorithmState.candidateLines = [];
      algorithmState.explanationSteps = [];
    }

    function clearCandidateLines() {
      algorithmState.candidateLines = [];
    }

    function setCandidateLinesFromCurrent(excludeSet = new Set()) {
      const from = algorithmState.currentPointIndex;
      const pairs = [];
      for (let i = 0; i < points.length; i++) {
        if (i === from) continue;
        if (excludeSet.has(i)) continue;
        pairs.push([from, i]);
      }
      algorithmState.candidateLines = pairs;
    }

    // ====== Step Actions ======
    function startMoveMode() {
      mode = 'move';
      setHeaderActive('btn-move');
      resetAlgorithmState();
      resetAlgorithmStateUI();
      // redraw handled by draw()
    }

    function startAlgorithmMode() {
      mode = 'algorithm';
      setHeaderActive('btn-start-algo');
      resetAlgorithmState();

      // Show step controls and enable step 1 only
      document.getElementById('step-controls').style.display = 'flex';
      enableStep1Buttons();
      disableStep2Buttons();
      enableStep3Explain(false);

      // Pre-calculate full hull silently
      algorithmState.fullHull = computeJarvisHullIndices();

      // Prepare dynamic Step 3 buttons
      populateStep3Actions();

      // Clear explanation text
      document.getElementById('explanation-text').innerHTML = '';
    }

    function step1Explain() {
      algorithmState.explanationSteps.push('Step 1. Find the leftmost point.');
      setExplanationTextFromState();
    }

    function step1Do() {
      const leftIdx = findLeftmostIndex();
      algorithmState.leftmostPointIndex = leftIdx;
      algorithmState.currentPointIndex = leftIdx;
      algorithmState.hullPointsIndices = [leftIdx];
      algorithmState.step = 1;

      // Update UI: disable step 1, enable step 2
      disableStep1Buttons();
      enableStep2Buttons();
    }

    function step2Explain() {
      algorithmState.explanationSteps.push('Step 2. Find the point clockwise furthest to the right relative to the leftmost point.');
      setExplanationTextFromState();
    }

    function step2Do() {
      // Show candidate lines
      setCandidateLinesFromCurrent(new Set(algorithmState.hullPointsIndices));

      // Use precomputed hull to select the second point
      const secondHullIdx = algorithmState.fullHull[1];
      const prevCurrent = algorithmState.currentPointIndex;

      // After a short delay, add the second point and clear candidates
      setTimeout(() => {
        // Add hull edge and point
        algorithmState.hullPointsIndices.push(secondHullIdx);
        algorithmState.currentPointIndex = secondHullIdx;
        clearCandidateLines();
        algorithmState.step = 2;

        // UI: disable step 2, enable step 3 explain and first action
        disableStep2Buttons();
        enableStep3Explain(true);
        const btnDo = document.getElementById('btn-step3-do');
        if (btnDo) btnDo.disabled = false;

      }, 500);
    }

    function step3Explain() {
      algorithmState.explanationSteps.push('Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.');
      setExplanationTextFromState();
    }

    function step3Action(clickedBtn) {
      // Determine the next index in fullHull to add
      // We've already added first (step1) and second (step2). Current to add is at position k = hullPointsIndices.length
      const k = algorithmState.hullPointsIndices.length;

      // If we already have all hull vertices, just close if not closed
      if (k >= algorithmState.fullHull.length) {
        finalizeClosure();
        disableStep3ActionButton(clickedBtn);
        return;
      }

      // Set candidate lines excluding already-on-hull points
      const exclude = new Set(algorithmState.hullPointsIndices);
      setCandidateLinesFromCurrent(exclude);

      // Next hull point index from precomputed list
      const nextIdx = algorithmState.fullHull[k];

      setTimeout(() => {
        // Add the next point
        algorithmState.hullPointsIndices.push(nextIdx);
        algorithmState.currentPointIndex = nextIdx;
        clearCandidateLines();

        // If this was the last hull point, draw the closing edge
        if (algorithmState.hullPointsIndices.length >= algorithmState.fullHull.length) {
          finalizeClosure();
          // Disable all remaining Step 3 action buttons
          disableAllStep3ActionButtons();
        } else {
          // Enable the next Step 3 action button if any
          disableStep3ActionButton(clickedBtn);
          enableNextStep3ActionButton(clickedBtn);
        }
      }, 500);
    }

    function finalizeClosure() {
      if (!isClosed && algorithmState.leftmostPointIndex != null && algorithmState.hullPointsIndices.length > 0) {
        isClosed = true; // draw() will render the closing edge
      }
    }

    function disableAllStep3ActionButtons() {
      const container = document.getElementById('step3-actions');
      Array.from(container.querySelectorAll('button')).forEach(btn => btn.disabled = true);
    }

    function disableStep3ActionButton(btn) {
      if (btn) btn.disabled = true;
    }

    function enableNextStep3ActionButton(btn) {
      // Enable the next sibling button if it exists
      const next = btn.nextElementSibling;
      if (next && next.tagName === 'BUTTON') {
        next.disabled = false;
      }
    }

    // ====== p5.js Sketch ======
    let canvasRef = null;

    function setup() {
      canvasRef = createCanvas(CANVAS_W, CANVAS_H);
      canvasRef.parent('canvas-container');

      // Init points as p5.Vector
      points = initialPointPositions.map(p => createVector(p.x, p.y));

      // Smooth drawing
      pixelDensity(1);
      frameRate(60);
    }

    function draw() {
      background(255);

      // Draw light grey border rectangle
      push();
      stroke(220);
      noFill();
      strokeWeight(1);
      rect(0.5, 0.5, CANVAS_W - 1, CANVAS_H - 1);
      pop();

      // Draw candidate lines (if any)
      if (mode === 'algorithm' && algorithmState.candidateLines && algorithmState.candidateLines.length > 0) {
        push();
        stroke(60, 150, 140);
        strokeWeight(2);
        for (const [i, j] of algorithmState.candidateLines) {
          const a = points[i];
          const b = points[j];
          line(a.x, a.y, b.x, b.y);
        }
        pop();
      }

      // Draw hull edges (dark blue)
      if (mode === 'algorithm' && algorithmState.hullPointsIndices.length > 1) {
        push();
        stroke(45, 60, 110);
        strokeWeight(3);
        for (let i = 1; i < algorithmState.hullPointsIndices.length; i++) {
          const i0 = algorithmState.hullPointsIndices[i-1];
          const i1 = algorithmState.hullPointsIndices[i];
          const a = points[i0];
          const b = points[i1];
          line(a.x, a.y, b.x, b.y);
        }
        // Closing edge back to leftmost if closed
        if (isClosed && algorithmState.leftmostPointIndex != null) {
          const lastIdx = algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 1];
          const a = points[lastIdx];
          const b = points[algorithmState.leftmostPointIndex];
          line(a.x, a.y, b.x, b.y);
        }
        pop();
      }

      // Draw points
      for (let i = 0; i < points.length; i++) {
        const p = points[i];
        const isHull = (mode === 'algorithm') ? algorithmState.hullPointsIndices.includes(i) : false;

        push();
        stroke(0);
        strokeWeight(1);
        if (isHull) {
          fill(45, 60, 110); // dark blue
        } else {
          fill(200, 220, 100); // light green/yellow-green
        }
        ellipse(p.x, p.y, 12, 12);

        // crosshair
        stroke(0);
        strokeWeight(1);
        line(p.x - 3, p.y, p.x + 3, p.y);
        line(p.x, p.y - 3, p.x, p.y + 3);
        pop();
      }
    }

    function mousePressed() {
      if (mode !== 'move') return;
      const mx = mouseX, my = mouseY;
      if (mx < 0 || mx > CANVAS_W || my < 0 || my > CANVAS_H) return;
      const radius = 10;
      for (let i = 0; i < points.length; i++) {
        const d = dist(mx, my, points[i].x, points[i].y);
        if (d <= radius) {
          draggedPoint = i;
          break;
        }
      }
    }

    function mouseDragged() {
      if (mode !== 'move') return;
      if (draggedPoint == null) return;
      const nx = constrain(mouseX, 0, CANVAS_W);
      const ny = constrain(mouseY, 0, CANVAS_H);
      points[draggedPoint].set(nx, ny);
    }

    function mouseReleased() {
      if (mode !== 'move') return;
      draggedPoint = null;
    }

    // ====== Wire up DOM events ======
    window.addEventListener('DOMContentLoaded', () => {
      // Header controls
      document.getElementById('btn-move').addEventListener('click', () => {
        startMoveMode();
      });

      document.getElementById('btn-start-algo').addEventListener('click', () => {
        startAlgorithmMode();
      });

      // Step 1
      document.getElementById('btn-step1-explain').addEventListener('click', step1Explain);
      document.getElementById('btn-step1-do').addEventListener('click', step1Do);

      // Step 2
      document.getElementById('btn-step2-explain').addEventListener('click', step2Explain);
      document.getElementById('btn-step2-do').addEventListener('click', step2Do);

      // Step 3
      document.getElementById('btn-step3-explain').addEventListener('click', step3Explain);
    });
  </script>
</body>
</html>
