</style>
</head>
<body>
<div class="container">
  <div id="controls">
    <h2 class="section-title">Control Panel</h2>

    <details>
      <summary>initial conditions</summary>
      <div style="margin-top:8px;font-size:13px;color:#333;line-height:1.5;">
        The simulation starts at t = 0 h with:
        <ul style="margin:6px 0 0 18px;padding:0;">
          <li>Biomass X(0) = 0.1 mol/m³</li>
          <li>Glucose Glu(0) = 60 mol/m³</li>
          <li>Ethanol Eta(0) = 0 mol/m³</li>
          <li>Enzymes: E₀(0) = 1.0, E₁(0) = 0.0, E₂(0) = 0.0 mol/m³</li>
          <li>Volume V(0) = 1.0 m³</li>
        </ul>
      </div>
    </details>

    <div class="control">
      <label for="slider-alpha-max">maximum fraction of respiring biomass</label>
      <div class="slider-row">
        <input id="slider-alpha-max" type="range" min="0.1" max="1.0" step="0.01" value="0.3"/>
        <span class="value-span" id="value-alpha-max">0.30</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-t-min">run time minimum (h)</label>
      <div class="slider-row">
        <input id="slider-t-min" type="range" min="0" max="0" step="1" value="0" disabled/>
        <span class="value-span" id="value-t-min">0</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-t-max">run time maximum (h)</label>
      <div class="slider-row">
        <input id="slider-t-max" type="range" min="10" max="100" step="1" value="60"/>
        <span class="value-span" id="value-t-max">60</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-enzyme-control">intermediate enzyme control [mol/m³]</label>
      <div class="slider-row">
        <input id="slider-enzyme-control" type="range" min="0.1" max="5.0" step="0.01" value="1"/>
        <span class="value-span" id="value-enzyme-control">1.00</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-feed-rate">feed flow rate (m³/h)</label>
      <div class="slider-row">
        <input id="slider-feed-rate" type="range" min="0.0" max="2.0" step="0.01" value="0.2"/>
        <span class="value-span" id="value-feed-rate">0.20</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
      <div class="slider-row">
        <input id="slider-substrate-feed-conc" type="range" min="10" max="200" step="1" value="100"/>
        <span class="value-span" id="value-substrate-feed-conc">100</span>
      </div>
    </div>

    <div class="control">
      <label for="slider-feed-time">feed time (h)</label>
      <div class="slider-row">
        <input id="slider-feed-time" type="range" min="0" max="60" step="1" value="35"/>
        <span class="value-span" id="value-feed-time">35</span>
      </div>
    </div>

    <div class="control">
      <label for="select-plot">plot</label>
      <div class="select-row">
        <select id="select-plot">
          <option value="fed batch" selected>fed batch</option>
          <option value="α">α</option>
        </select>
      </div>
    </div>

    <div class="footer-note">Move sliders to update the simulation. The feed time is automatically capped by the run time maximum.</div>
  </div>

  <div id="visualization">
    <div id="plot-top"></div>
    <div id="plot-bottom"></div>
  </div>
</div>

<script>
  // ---------- UI Elements ----------
  const el = {
    alphaMax: document.getElementById('slider-alpha-max'),
    tMin: document.getElementById('slider-t-min'),
    tMax: document.getElementById('slider-t-max'),
    enzCtl: document.getElementById('slider-enzyme-control'),
    feedRate: document.getElementById('slider-feed-rate'),
    feedConc: document.getElementById('slider-substrate-feed-conc'),
    feedTime: document.getElementById('slider-feed-time'),
    selectPlot: document.getElementById('select-plot'),

    vAlphaMax: document.getElementById('value-alpha-max'),
    vTMin: document.getElementById('value-t-min'),
    vTMax: document.getElementById('value-t-max'),
    vEnzCtl: document.getElementById('value-enzyme-control'),
    vFeedRate: document.getElementById('value-feed-rate'),
    vFeedConc: document.getElementById('value-substrate-feed-conc'),
    vFeedTime: document.getElementById('value-feed-time'),

    plotTop: document.getElementById('plot-top'),
    plotBottom: document.getElementById('plot-bottom')
  };

  // ---------- Simulation Defaults ----------
  const IC = {
    X: 0.1,   // mol/m^3
    G: 60.0,  // mol/m^3
    Eta: 0.0, // mol/m^3
    E0: 1.0,  // mol/m^3
    E1: 0.0,  // mol/m^3
    E2: 0.0,  // mol/m^3
    V: 1.0    // m^3
  };

  // Model constants (dimensionally consistent for an educational demo)
  const CONST = {
    mu_f_max: 0.6,   // h^-1, fermentative max growth
    mu_r_max: 0.35,  // h^-1, respirative max growth
    K_s: 1.0,        // mol/m^3, Monod constant for glucose (fermentative)
    K_sr: 0.5,       // mol/m^3, Monod constant for glucose (respirative)
    c_gf: 1.6,       // glucose req. per fermentative growth
    c_gr: 0.9,       // glucose req. per respirative growth
    k_ep: 0.8,       // ethanol production coupling
    k_ec: 0.25,      // ethanol consumption rate
    k_syn0: 0.08,    // baseline enzyme synthesis E0
    k_syn1: 0.45,    // synthesis rate E1 (fermentative enzyme)
    k_syn2: 0.4,     // synthesis rate E2 (respirative enzyme)
    k_deg: 0.1,      // degradation for enzymes
    K_E: 30.0,       // mol/m^3, enzyme induction half-sat
    nHill: 4         // Hill coefficient (sharp switch)
  };

  let lastData = null; // stores the most recent simulation results

  // ---------- Utility ----------
  function formatNumber(val, step) {
    const decimals = (step.toString().split('.')[1] || '').length;
    return Number(val).toFixed(Math.min(decimals, 3));
  }

  function updateValueDisplays() {
    el.vAlphaMax.textContent = formatNumber(el.alphaMax.value, el.alphaMax.step);
    el.vTMin.textContent = formatNumber(el.tMin.value, el.tMin.step);
    el.vTMax.textContent = formatNumber(el.tMax.value, el.tMax.step);
    el.vEnzCtl.textContent = formatNumber(el.enzCtl.value, el.enzCtl.step);
    el.vFeedRate.textContent = formatNumber(el.feedRate.value, el.feedRate.step);
    el.vFeedConc.textContent = formatNumber(el.feedConc.value, el.feedConc.step);
    el.vFeedTime.textContent = formatNumber(el.feedTime.value, el.feedTime.step);
  }

  function getParamsFromUI() {
    const t_max = parseFloat(el.tMax.value);
    // Ensure feed slider respects t_max
    el.feedTime.max = String(t_max);
    if (parseFloat(el.feedTime.value) > t_max) {
      el.feedTime.value = String(t_max);
    }

    // Update displayed numbers after any potential capping
    updateValueDisplays();

    return {
      alpha_max: parseFloat(el.alphaMax.value),
      t_min: parseFloat(el.tMin.value), // always 0 (disabled)
      t_max,
      K_I: parseFloat(el.enzCtl.value),
      F0: parseFloat(el.feedRate.value),
      G_in: parseFloat(el.feedConc.value),
      t_feed: parseFloat(el.feedTime.value)
    };
  }

  // ---------- ODE System (Fed-batch diauxic behavior) ----------
  // state y = [X, G, Eta, E0, E1, E2, V]
  function derivatives(t, y, p) {
    const X = Math.max(0, y[0]);
    const G = Math.max(0, y[1]);
    const Eta = Math.max(0, y[2]);
    const E0 = Math.max(0, y[3]);
    const E1 = Math.max(0, y[4]);
    const E2 = Math.max(0, y[5]);
    const V = Math.max(1e-9, y[6]); // avoid division by zero

    const n = CONST.nHill;

    // Fraction of respiring biomass (alpha); inverse function of glucose
    const alpha = p.alpha_max * Math.pow(p.K_I, n) / (Math.pow(p.K_I, n) + Math.pow(G, n) + 1e-12);

    // Feed and dilution
    const F = (t <= p.t_feed) ? p.F0 : 0;
    const D = F / V;

    // Saturations
    const satG_f = G / (CONST.K_s + G);
    const satG_r = G / (CONST.K_sr + G);

    // Growth contributions (coupled to enzymes and alpha)
    const mu_f = CONST.mu_f_max * satG_f * (E1 / (1 + E1)) * (1 - alpha);
    const mu_r = CONST.mu_r_max * satG_r * (E2 / (1 + E2)) * alpha;
    const mu = Math.max(0, mu_f + mu_r);

    // Glucose consumption proportional to growth modes
    const qG = CONST.c_gf * mu_f + CONST.c_gr * mu_r;

    // Ethanol dynamics: produced with fermentation; consumed during respiration
    const r_eta_prod = CONST.k_ep * mu_f * X;
    const r_eta_cons = CONST.k_ec * alpha * E2 * Eta;

    // Enzyme regulation by glucose (E1 induced, E2 repressed)
    const hillG = Math.pow(G, n) / (Math.pow(CONST.K_E, n) + Math.pow(G, n) + 1e-12);

    const dX = mu * X - D * X;
    const dG = -qG * X + D * (p.G_in - G);
    const dEta = r_eta_prod - r_eta_cons - D * Eta;
    const dE0 = CONST.k_syn0 - CONST.k_deg * E0 - D * E0;
    const dE1 = CONST.k_syn1 * hillG - CONST.k_deg * E1 - D * E1;
    const dE2 = CONST.k_syn2 * (1 - hillG) - CONST.k_deg * E2 - D * E2;
    const dV = F;

    return {
      dydt: [dX, dG, dEta, dE0, dE1, dE2, dV],
      alpha
    };
  }

  // 4th-order Runge-Kutta integrator
  function rk4Step(t, y, dt, p) {
    const f = (tt, yy) => derivatives(tt, yy, p).dydt;

    const k1 = f(t, y);
    const y2 = y.map((yi, i) => yi + 0.5 * dt * k1[i]);
    const k2 = f(t + 0.5 * dt, y2);
    const y3 = y.map((yi, i) => yi + 0.5 * dt * k2[i]);
    const k3 = f(t + 0.5 * dt, y3);
    const y4 = y.map((yi, i) => yi + dt * k3[i]);
    const k4 = f(t + dt, y4);

    const yNext = y.map((yi, i) => yi + (dt / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
    // Clamp to non-negative where appropriate
    for (let i = 0; i < yNext.length; i++) {
      if (i !== 6) yNext[i] = Math.max(0, yNext[i]); // all but volume
    }
    yNext[6] = Math.max(1e-9, yNext[6]);
    return yNext;
  }

  function simulate(params) {
    const t0 = 0;
    const t1 = params.t_max;

    // Choose resolution ~ 20 points per hour, min 400, max 4000
    const steps = Math.min(4000, Math.max(400, Math.ceil((t1 - t0) * 20)));
    const dt = (t1 - t0) / steps;

    // Initial state vector
    let y = [IC.X, IC.G, IC.Eta, IC.E0, IC.E1, IC.E2, IC.V];

    const tArr = new Array(steps + 1);
    const XArr = new Array(steps + 1);
    const GArr = new Array(steps + 1);
    const EtaArr = new Array(steps + 1);
    const E0Arr = new Array(steps + 1);
    const E1Arr = new Array(steps + 1);
    const E2Arr = new Array(steps + 1);
    const VArr = new Array(steps + 1);
    const alphaArr = new Array(steps + 1);

    let t = t0;
    for (let i = 0; i <= steps; i++) {
      const { alpha } = derivatives(t, y, params);

      tArr[i] = t;
      XArr[i] = y[0];
      GArr[i] = y[1];
      EtaArr[i] = y[2];
      E0Arr[i] = y[3];
      E1Arr[i] = y[4];
      E2Arr[i] = y[5];
      VArr[i] = y[6];
      alphaArr[i] = Math.max(0, Math.min(params.alpha_max, alpha));

      if (i < steps) {
        y = rk4Step(t, y, dt, params);
        t += dt;
      }
    }

    return {
      t: tArr,
      X: XArr,
      Glu: GArr,
      Eta: EtaArr,
      E0: E0Arr,
      E1: E1Arr,
      E2: E2Arr,
      V: VArr,
      alpha: alphaArr,
      meta: { params }
    };
  }

  // ---------- Plotting ----------
  function layoutBase({ title, xTitle, yTitle }) {
    return {
      title: { text: title, font: { size: 16 } },
      margin: { l: 60, r: 20, t: 40, b: 50 },
      xaxis: {
        title: { text: xTitle },
        gridcolor: '#eaecef',
        zeroline: false
      },
      yaxis: {
        title: { text: yTitle },
        gridcolor: '#eaecef',
        zeroline: false
      },
      legend: { orientation: 'h', x: 0, y: 1.12 },
      paper_bgcolor: '#ffffff',
      plot_bgcolor: '#ffffff'
    };
  }

  function drawPlots() {
    if (!lastData) return;

    const t = lastData.t;
    const plotChoice = el.selectPlot.value;

    if (plotChoice === 'fed batch') {
      // Top: X, Glu, Eta
      const topData = [
        { x: t, y: lastData.X, mode: 'lines', name: 'X(t)', line: { color: '#1f77b4', width: 2 } },
        { x: t, y: lastData.Glu, mode: 'lines', name: 'Glu(t)', line: { color: '#d62728', width: 2 } },
        { x: t, y: lastData.Eta, mode: 'lines', name: 'Eta(t)', line: { color: '#2ca02c', width: 2 } }
      ];
      const topLayout = layoutBase({
        title: 'diauxic behavior',
        xTitle: 'time (h)',
        yTitle: 'concentration [mol/m³]'
      });
      Plotly.react(el.plotTop, topData, topLayout, { responsive: true });

      // Bottom: Enzymes E2, E1, E0
      const bottomData = [
        { x: t, y: lastData.E2, mode: 'lines', name: 'E₂(t)', line: { color: '#000000', width: 2 } },
        { x: t, y: lastData.E1, mode: 'lines', name: 'E₁(t)', line: { color: '#e377c2', width: 2 } },
        { x: t, y: lastData.E0, mode: 'lines', name: 'E₀(t)', line: { color: '#ff7f0e', width: 2 } }
      ];
      const bottomLayout = layoutBase({
        title: 'enzyme concentration',
        xTitle: 'time (h)',
        yTitle: 'enzyme concentration [mol/m³]'
      });
      Plotly.react(el.plotBottom, bottomData, bottomLayout, { responsive: true });

    } else {
      // "α" selected
      const topData = [
        { x: t, y: lastData.Glu, mode: 'lines', name: 'Glu(t)', line: { color: '#d62728', width: 2 } }
      ];
      const topLayout = layoutBase({
        title: 'glucose concentration',
        xTitle: 'time (h)',
        yTitle: 'glucose concentration [mol/m³]'
      });
      Plotly.react(el.plotTop, topData, topLayout, { responsive: true });

      const bottomData = [
        { x: t, y: lastData.alpha, mode: 'lines', name: 'α', line: { color: '#1f77b4', width: 2 } }
      ];
      const bottomLayout = layoutBase({
        title: 'fraction of respiring biomass',
        xTitle: 'time (h)',
        yTitle: 'α'
      });
      Plotly.react(el.plotBottom, bottomData, bottomLayout, { responsive: true });
    }
  }

  // ---------- Wiring ----------
  function updateAndSimulate() {
    const params = getParamsFromUI();
    lastData = simulate(params);
    drawPlots();
  }

  // Attach events
  [
    el.alphaMax,
    el.tMax,
    el.enzCtl,
    el.feedRate,
    el.feedConc,
    el.feedTime
  ].forEach(inp => {
    inp.addEventListener('input', updateAndSimulate);
  });

  el.selectPlot.addEventListener('change', drawPlots);

  // Initialize UI text
  updateValueDisplays();

  // Initial run
  updateAndSimulate();
</script>
</body>
</html>
