  <script type="module">
    import * as THREE from 'https://unpkg.com/three@0.160.0/build/three.module.js';
    import { OrbitControls } from 'https://unpkg.com/three@0.160.0/examples/jsm/controls/OrbitControls.js';

    // DOM references
    const elDate = document.getElementById('slider-date');
    const elLat = document.getElementById('slider-latitude');
    const elTime = document.getElementById('slider-time');
    const elLabelDate = document.getElementById('label-date');
    const elLabelLat = document.getElementById('label-latitude');
    const elLabelTime = document.getElementById('label-time');
    const elView = document.getElementById('select-view');

    const elReadDay = document.getElementById('readout-day-length');
    const elReadNight = document.getElementById('readout-night-length');
    const elReadSunriseTime = document.getElementById('readout-sunrise-time');
    const elReadSunriseDir = document.getElementById('readout-sunrise-direction');
    const elReadSunsetTime = document.getElementById('readout-sunset-time');
    const elReadSunsetDir = document.getElementById('readout-sunset-direction');

    const elOptGround = document.getElementById('options-ground');
    const elOptSpace = document.getElementById('options-space');

    const chkSunPath = document.getElementById('check-sun-path');
    const chkConstDay = document.getElementById('check-constellations-day');

    const chkStationary = document.getElementById('check-stationary-earth');
    const chkConst = document.getElementById('check-constellations');
    const chkTerminator = document.getElementById('check-day-night-division');
    const chkParallelDay = document.getElementById('check-day-night-parallel');

    // Renderer / Scene / Camera
    const container = document.getElementById('visualization-container');
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.setSize(container.clientWidth, container.clientHeight);
    renderer.domElement.id = 'main-canvas';
    container.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f4ff);

    const camera = new THREE.PerspectiveCamera(55, container.clientWidth / container.clientHeight, 0.1, 2000);
    camera.position.set(0, 2, 8);
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.08;

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.4);
    scene.add(ambient);

    // Shared Sun (mesh + light)
    const sunGroup = new THREE.Group();
    const sunGeom = new THREE.SphereGeometry(0.7, 32, 16);
    const sunMat = new THREE.MeshBasicMaterial({ color: 0xffdd66, emissive: 0xffcc22, emissiveIntensity: 0.8 });
    const sunMesh = new THREE.Mesh(sunGeom, sunMat);
    sunGroup.add(sunMesh);
    const sunLight = new THREE.DirectionalLight(0xffffff, 1.0);
    sunLight.position.set(0, 0, 0);
    sunLight.castShadow = false;
    sunGroup.add(sunLight);
    scene.add(sunGroup);

    // Groups for the two views
    const groundGroup = new THREE.Group();
    const spaceGroup = new THREE.Group();
    scene.add(groundGroup);
    scene.add(spaceGroup);

    // ======================
    // Ground View Objects
    // ======================
    const groundRadius = 10;
    const groundGeom = new THREE.CylinderGeometry(groundRadius, groundRadius, 0.5, 64, 1, false);
    const groundMatTop = new THREE.MeshPhongMaterial({ color: 0x5ba860, shininess: 10 });
    const groundMatSide = new THREE.MeshPhongMaterial({ color: 0x7db7f0, shininess: 10 });
    // Split top/side materials
    const groundMesh = new THREE.Mesh(groundGeom, [groundMatTop, groundMatSide, groundMatTop]);
    groundMesh.position.y = -0.25;
    groundMesh.receiveShadow = true;
    groundGroup.add(groundMesh);

    // Celestial dome
    const domeRadius = 15;
    const domeGeom = new THREE.SphereGeometry(domeRadius, 48, 32, 0, Math.PI*2, 0, Math.PI/2);
    const domeMat = new THREE.MeshPhongMaterial({
      color: 0x88aaff,
      opacity: 0.35,
      transparent: true,
      side: THREE.BackSide
    });
    const dome = new THREE.Mesh(domeGeom, domeMat);
    groundGroup.add(dome);

    // Cardinal sprites N E S W
    function makeLabelSprite(text, color='#263238', bg='rgba(255,255,255,0.8)') {
      const size = 256;
      const canvas = document.createElement('canvas');
      canvas.width = canvas.height = size;
      const ctx = canvas.getContext('2d');
      ctx.fillStyle = bg;
      ctx.beginPath();
      ctx.arc(size/2, size/2, size*0.44, 0, Math.PI*2);
      ctx.fill();
      ctx.fillStyle = color;
      ctx.font = 'bold 140px system-ui, Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(text, size/2, size/2);
      const tex = new THREE.CanvasTexture(canvas);
      const mat = new THREE.SpriteMaterial({ map: tex, transparent: true });
      const sprite = new THREE.Sprite(mat);
      sprite.scale.set(1.2, 1.2, 1.2);
      return sprite;
    }
    const spriteN = makeLabelSprite('N');
    const spriteE = makeLabelSprite('E');
    const spriteS = makeLabelSprite('S');
    const spriteW = makeLabelSprite('W');
    spriteN.position.set(0, 0.02, groundRadius);
    spriteS.position.set(0, 0.02, -groundRadius);
    spriteE.position.set(groundRadius, 0.02, 0);
    spriteW.position.set(-groundRadius, 0.02, 0);
    groundGroup.add(spriteN, spriteE, spriteS, spriteW);

    // Sun path line (dynamic)
    const sunPathMat = new THREE.LineBasicMaterial({ color: 0xff8c00, linewidth: 2 });
    let sunPathLine = new THREE.Line(new THREE.BufferGeometry(), sunPathMat);
    groundGroup.add(sunPathLine);

    // Ground-view constellations (on the dome)
    const constellationsGround = new THREE.Group();
    groundGroup.add(constellationsGround);

    // ======================
    // Space View Objects
    // ======================
    const earthGroup = new THREE.Group();
    spaceGroup.add(earthGroup);

    const earthRadius = 5;
    const earthGeom = new THREE.SphereGeometry(earthRadius, 64, 48);
    const earthMat = new THREE.MeshPhongMaterial({ color: 0x4a90e2, shininess: 25, specular: 0x88aaff });
    const earthMesh = new THREE.Mesh(earthGeom, earthMat);
    earthMesh.castShadow = false;
    earthMesh.receiveShadow = false;
    earthGroup.add(earthMesh);

    // Earth's axis as a line
    const axisGeom = new THREE.BufferGeometry().setFromPoints([
      new THREE.Vector3(0, -earthRadius*1.2, 0),
      new THREE.Vector3(0,  earthRadius*1.2, 0)
    ]);
    const axisMat = new THREE.LineBasicMaterial({ color: 0xffffff });
    const axisLine = new THREE.Line(axisGeom, axisMat);
    earthGroup.add(axisLine);

    // Tilt Earth by obliquity (around X-axis)
    const OBLIQUITY = THREE.MathUtils.degToRad(23.44);
    earthGroup.rotation.x = OBLIQUITY;

    // Terminator plane (day/night division)
    const planeGeom = new THREE.PlaneGeometry(20, 20, 1, 1);
    const planeMat = new THREE.MeshBasicMaterial({ color: 0x000000, opacity: 0.25, transparent: true, side: THREE.DoubleSide });
    const terminatorPlane = new THREE.Mesh(planeGeom, planeMat);
    spaceGroup.add(terminatorPlane);

    // Latitude parallel (thin torus)
    let latitudeTorus = null;
    function updateLatitudeTorus(latDeg) {
      if (latitudeTorus) {
        earthGroup.remove(latitudeTorus);
        latitudeTorus.geometry.dispose();
        latitudeTorus.material.dispose();
        latitudeTorus = null;
      }
      const phi = THREE.MathUtils.degToRad(latDeg);
      const Rmajor = earthRadius * Math.max(0.0001, Math.cos(phi));
      const torusGeom = new THREE.TorusGeometry(Rmajor, 0.035, 8, 256);
      latitudeTorus = new THREE.Mesh(torusGeom, new THREE.MeshBasicMaterial({ color: 0xffff66, transparent: true, opacity: 0.9 }));
      latitudeTorus.position.y = earthRadius * Math.sin(phi);
      latitudeTorus.rotation.x = Math.PI/2; // align major circle around Y-axis
      earthGroup.add(latitudeTorus);
    }

    // Daylit portion on latitude parallel (highlight line)
    let parallelDayLine = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({ color: 0xff4444, linewidth: 3 }));
    earthGroup.add(parallelDayLine);

    // Space-view constellations (celestial sphere far away)
    const constellationsSpace = new THREE.Group();
    spaceGroup.add(constellationsSpace);

    // ======================
    // Constellations (shared generator)
    // ======================
    function addConstellationLines(group, radius) {
      // Few simple polylines approximating well-known outlines (rough RA/Dec in degrees)
      // Using Equatorial coordinates: RA (hours->deg), Dec (deg)
      // We'll define in degrees directly for simplicity.
      const sets = [
        // Orion belt and sword (very rough)
        [
          { ra: 83, dec: -1 }, { ra: 86, dec: -1 }, { ra: 88, dec: -1 }, // Belt
          { ra: 88, dec: -1 }, { ra: 84, dec: -8 }, { ra: 83, dec: -1 }  // Sword link
        ],
        // Big Dipper (Ursa Major) outline rough
        [
          { ra: 165, dec: 56 }, { ra: 168, dec: 49 }, { ra: 178, dec: 54 }, { ra: 183, dec: 57 },
          { ra: 194, dec: 62 }, { ra: 200, dec: 54 }, { ra: 210, dec: 49 }
        ],
        // Cassiopeia W
        [
          { ra: 10, dec: 60 }, { ra: 20, dec: 60 }, { ra: 28, dec: 56 }, { ra: 35, dec: 60 }, { ra: 45, dec: 60 }
        ],
        // Southern Cross
        [
          { ra: 187, dec: -62 }, { ra: 186, dec: -56 }, { ra: 191, dec: -60 }, { ra: 187, dec: -62 }, { ra: 189, dec: -64 }
        ]
      ];

      const mat = new THREE.LineBasicMaterial({ color: 0xffffff, transparent: true, opacity: 0.6 });
      for (const poly of sets) {
        const pts = [];
        for (const star of poly) {
          const raRad = THREE.MathUtils.degToRad(star.ra);
          const decRad = THREE.MathUtils.degToRad(star.dec);
          // Unit vector in equatorial coordinates: x=cosδ cosα, y=sinδ, z=cosδ sinα
          const x = Math.cos(decRad) * Math.cos(raRad);
          const y = Math.sin(decRad);
          const z = Math.cos(decRad) * Math.sin(raRad);
          pts.push(new THREE.Vector3(x*radius, y*radius, z*radius));
        }
        const geom = new THREE.BufferGeometry().setFromPoints(pts);
        const line = new THREE.Line(geom, mat);
        group.add(line);
      }
    }

    addConstellationLines(constellationsGround, 100);
    addConstellationLines(constellationsSpace, 200);

    // ======================
    // Utility functions
    // ======================
    function clamp(x, a, b) { return Math.max(a, Math.min(b, x)); }
    function timeToString(hoursFloat) {
      let h = Math.floor(hoursFloat + 1e-6);
      let m = Math.floor((hoursFloat - h) * 60 + 1e-6);
      if (m >= 60) { m -= 60; h += 1; }
      if (h < 0) h += 24;
      if (h >= 24) h -= 24;
      return `${String(h).padStart(2,'0')}:${String(m).padStart(2,'0')}`;
    }
    function doyToMonthDay(N) {
      // Non-leap year
      const mdays = [31,28,31,30,31,30,31,31,30,31,30,31];
      let day = N;
      let month = 0;
      while (month < 12 && day > mdays[month]) {
        day -= mdays[month];
        month++;
      }
      const monthNames = ["January","February","March","April","May","June","July","August","September","October","November","December"];
      const label = `${monthNames[month]} ${day}`;
      return label;
    }
    function deg(x) { return x * 180/Math.PI; }
    function rad(x) { return x * Math.PI/180; }
    function formatAngleDeg(d) {
      const s = d.toFixed(0) + '°';
      return s;
    }
    function azimuthToCardinal(adeg) {
      // 16-wind compass
      const dirs = ['N','NNE','NE','ENE','E','ESE','SE','SSE','S','SSW','SW','WSW','W','WNW','NW','NNW','N'];
      const idx = Math.round(((adeg % 360) / 22.5));
      const dir = dirs[idx];
      return `${dir} (${Math.round(adeg)%360}°)`;
    }

    // Solar position calculations (simplified, solar time, approximate)
    // Ecliptic longitude λ ~ 2π*(N - 80)/365. Declination and RA from obliquity transformation.
    function sunEquatorialFromDay(N) {
      const eps = OBLIQUITY; // obliquity
      const lambda = 2*Math.PI * ((N - 80) / 365.0); // vernal equinox near N=80
      const sinlam = Math.sin(lambda), coslam = Math.cos(lambda);
      const sinDec = Math.sin(eps) * sinlam;
      const dec = Math.asin(sinDec);
      const y = Math.cos(eps) * sinlam; // component for atan2
      const x = coslam;
      const ra = Math.atan2(y, x); // in [-pi,pi]
      return { ra, dec, lambda };
    }

    // Given latitude φ and declination δ -> hour angle at sunrise/sunset
    function hourAngleSunrise(lat, dec) {
      // cos H0 = -tan φ tan δ
      const cosH0 = -Math.tan(lat) * Math.tan(dec);
      if (cosH0 >= 1) return { exists:false, polar:'night', H0:0 };
      if (cosH0 <= -1) return { exists:false, polar:'day', H0:Math.PI }; // sun circumpolar
      const H0 = Math.acos(cosH0);
      return { exists:true, H0 };
    }

    function horizontalFromEq(lat, dec, H) {
      // h: altitude, A: azimuth from North (clockwise, degrees)
      const sinH = Math.sin(H), cosH = Math.cos(H);
      const sinphi = Math.sin(lat), cosphi = Math.cos(lat);
      const sind = Math.sin(dec), cosd = Math.cos(dec);
      const sinAlt = sind * sinphi + cosd * cosphi * cosH;
      const alt = Math.asin(sinAlt);
      const cosAlt = Math.cos(alt);
      let sinAz = -cosd * sinH / Math.max(1e-6, cosAlt);
      let cosAz = (sind - sinphi * sinAlt) / (Math.max(1e-6, cosAlt) * cosphi);
      let A = Math.atan2(sinAz, cosAz); // [-pi,pi], where 0=N, +pi/2=E
      if (A < 0) A += 2*Math.PI;
      return { alt, az: A };
    }

    function computeReadouts(N, latDeg) {
      const { dec } = sunEquatorialFromDay(N);
      const lat = rad(latDeg);
      const sr = hourAngleSunrise(lat, dec);
      let dayLen, nightLen, sunriseTime, sunsetTime, sunriseAz, sunsetAz;
      if (!sr.exists) {
        if (sr.polar === 'day') {
          dayLen = 24; nightLen = 0;
          sunriseTime = 'none'; sunsetTime = 'none';
          sunriseAz = '—'; sunsetAz = '—';
        } else {
          dayLen = 0; nightLen = 24;
          sunriseTime = 'none'; sunsetTime = 'none';
          sunriseAz = '—'; sunsetAz = '—';
        }
      } else {
        const H0 = sr.H0; // radians
        const dayHours = (2 * deg(H0)) / 15.0;
        dayLen = dayHours;
        nightLen = 24 - dayHours;
        sunriseTime = timeToString(12 - dayHours/2);
        sunsetTime = timeToString(12 + dayHours/2);

        // Azimuth at rise/set using H = -H0 (sunrise) and +H0 (sunset)
        const { az: Arise } = horizontalFromEq(lat, dec, -H0);
        const { az: Aset } = horizontalFromEq(lat, dec, +H0);
        sunriseAz = azimuthToCardinal(deg(Arise));
        sunsetAz = azimuthToCardinal(deg(Aset));
      }
      return {
        decDeg: deg(dec),
        dayLen, nightLen,
        sunriseTime, sunsetTime,
        sunriseAz, sunsetAz
      };
    }

    // ======================
    // Ground view update helpers
    // ======================
    function updateGroundSunAndDome(N, latDeg, timeHours) {
      const { dec } = sunEquatorialFromDay(N);
      const lat = rad(latDeg);
      const H = rad((timeHours - 12) * 15); // hour angle in radians
      const hor = horizontalFromEq(lat, dec, H);

      // Position sun on dome (radius slightly less than dome)
      const R = domeRadius * 0.85;
      const alt = hor.alt;
      const A = hor.az;
      const x = Math.cos(alt) * Math.sin(A);
      const z = Math.cos(alt) * Math.cos(A);
      const y = Math.sin(alt);
      const sunPos = new THREE.Vector3(x*R, y*R, z*R);

      sunGroup.position.copy(sunPos);
      sunLight.position.set(-x, -y, -z).normalize(); // light shining from sun to scene

      // Dome opacity darker at night: based on sun altitude
      const dayOpacity = 0.28, nightOpacity = 0.6;
      const t = clamp((deg(alt) + 5) / 20, 0, 1); // blend around horizon
      dome.material.opacity = nightOpacity * (1 - t) + dayOpacity * t;
    }

    function regenerateSunPath(N, latDeg) {
      const { dec } = sunEquatorialFromDay(N);
      const lat = rad(latDeg);
      const sr = hourAngleSunrise(lat, dec);

      const points = [];
      const R = domeRadius * 0.82;
      let times = [];
      if (!sr.exists) {
        // polar day or night: draw full 24h path
        const steps = 240;
        for (let i=0;i<=steps;i++) {
          times.push(i * 24/steps);
        }
      } else {
        const dayHours = (2 * deg(sr.H0)) / 15.0;
        const tStart = 12 - dayHours/2;
        const tEnd = 12 + dayHours/2;
        const steps = 160;
        for (let i=0;i<=steps;i++) {
          times.push(tStart + (tEnd - tStart) * (i/steps));
        }
      }
      for (const t of times) {
        const H = rad((t - 12) * 15);
        const hor = horizontalFromEq(lat, dec, H);
        const alt = hor.alt, A = hor.az;
        const x = Math.cos(alt) * Math.sin(A);
        const z = Math.cos(alt) * Math.cos(A);
        const y = Math.sin(alt);
        points.push(new THREE.Vector3(x*R, y*R, z*R));
      }
      const geom = new THREE.BufferGeometry().setFromPoints(points);
      sunPathLine.geometry.dispose();
      sunPathLine.geometry = geom;
      sunPathLine.visible = chkSunPath.checked;
    }

    // ======================
    // Space view update helpers
    // ======================
    function sunDirectionWorld(N) {
      // In equatorial frame: s = [cosδ cosα, sinδ, cosδ sinα]
      const { ra, dec } = sunEquatorialFromDay(N);
      const cosd = Math.cos(dec), sind = Math.sin(dec);
      const cosa = Math.cos(ra), sina = Math.sin(ra);
      const s = new THREE.Vector3(cosd*cosa, sind, cosd*sina); // unit
      return s.normalize();
    }

    function updateSpaceSunAndTerminator(N) {
      const s = sunDirectionWorld(N); // from Earth to Sun
      const sunDist = 50;
      sunGroup.position.copy(s.clone().multiplyScalar(sunDist));
      sunLight.position.copy(s.clone().multiplyScalar(-1)); // light shining towards Earth

      // Terminator plane: normal along s, passing through origin (Earth center)
      const zAxis = new THREE.Vector3(0,0,1);
      const quat = new THREE.Quaternion().setFromUnitVectors(zAxis, s);
      terminatorPlane.setRotationFromQuaternion(quat);
      terminatorPlane.position.set(0,0,0);

      // Apply checkbox visibility
      terminatorPlane.visible = chkTerminator.checked;
    }

    function updateEarthRotationFromTime(timeHours) {
      // Rotate around Earth's own axis (local Y in earthGroup) so that at 12:00, prime meridian faces Sun (rotation = 0)
      const angle = - (timeHours - 12) * (Math.PI * 2 / 24);
      earthMesh.rotation.y = angle;
      axisLine.rotation.y = angle;
      if (latitudeTorus) latitudeTorus.rotation.y = angle; // rotate torus with Earth surface
      // The highlighted day segment is recomputed separately; rotation of the highlight geometry is not necessary if we recompute points in world space.
    }

    function updateParallelDaySegment(N, latDeg) {
      // Sample points along the latitude circle and keep those lit by sun
      const phi = rad(latDeg);
      const Rmaj = earthRadius * Math.cos(phi);
      const y = earthRadius * Math.sin(phi);

      const s = sunDirectionWorld(N); // world direction
      const pts = [];
      const steps = 720; // high resolution
      for (let i=0;i<=steps;i++) {
        const theta = i/steps * Math.PI*2; // longitude angle around Y' (Earth local axis)
        // Point on sphere in Earth local coordinates (before tilt and rotation)
        const x_local = Rmaj * Math.cos(theta);
        const z_local = Rmaj * Math.sin(theta);
        const local = new THREE.Vector3(x_local, y, z_local);

        // Normal vector at that point in Earth local coords is just normalized position
        const n_local = local.clone().normalize();

        // Transform normal to world: apply earthGroup (tilt) and Earth's daily rotation (we applied via earthMesh/axisLine/latitudeTorus rotation.y).
        // We need a container that includes the rotation about Y; we didn't rotate earthGroup but earthMesh/axisLine/torus.
        // For normal transform including daily rotation, we can build a quaternion: Q = earthGroup.rotationX + rotationY(angle from time)
        const qTilt = new THREE.Quaternion().setFromEuler(earthGroup.rotation.clone()); // tilt around X
        const qDaily = new THREE.Quaternion().setFromEuler(new THREE.Euler(0, earthMesh.rotation.y, 0, 'XYZ'));
        const qTotal = qTilt.clone().multiply(qDaily);

        const n_world = n_local.clone().applyQuaternion(qTotal).normalize();
        const lit = n_world.dot(s) > 0; // day side if facing the Sun

        if (lit) {
          pts.push(n_world.clone().multiplyScalar(earthRadius)); // position on surface
        } else {
          // optionally insert gaps; lines can't break easily in a single Line, so we simply skip dark points
        }
      }
      const geom = new THREE.BufferGeometry().setFromPoints(pts);
      if (parallelDayLine.geometry) parallelDayLine.geometry.dispose();
      parallelDayLine.geometry = geom;
      parallelDayLine.visible = chkParallelDay.checked;
    }

    // ======================
    // Options visibility sync
    // ======================
    function syncOptionsUI() {
      const view = elView.value;
      elOptGround.style.display = (view === 'ground') ? 'block' : 'none';
      elOptSpace.style.display = (view === 'space') ? 'block' : 'none';

      groundGroup.visible = (view === 'ground');
      spaceGroup.visible = (view === 'space');

      // Apply checkboxes to scene elements
      sunPathLine.visible = (view === 'ground') ? chkSunPath.checked : false;
      constellationsGround.visible = (view === 'ground') ? chkConstDay.checked : false;

      constellationsSpace.visible = (view === 'space') ? chkConst.checked : false;
      terminatorPlane.visible = (view === 'space') ? chkTerminator.checked : false;
      parallelDayLine.visible = (view === 'space') ? chkParallelDay.checked : false;

      // Camera presets
      if (view === 'ground') {
        camera.position.set(0, 1.8, 8);
        controls.target.set(0, 0.8, 0);
      } else {
        camera.position.set(0, 0, 30);
        controls.target.set(0, 0, 0);
      }
      controls.update();
    }

    // ======================
    // Readouts update
    // ======================
    function updateReadouts() {
      const N = parseInt(elDate.value, 10);
      const latDeg = parseFloat(elLat.value);
      const res = computeReadouts(N, latDeg);

      elReadDay.textContent = `${timeToString(res.dayLen)} (${res.dayLen.toFixed(2)} h)`;
      elReadNight.textContent = `${timeToString(res.nightLen)} (${res.nightLen.toFixed(2)} h)`;

      elReadSunriseTime.textContent = res.sunriseTime;
      elReadSunriseDirection.textContent = res.sunriseAz;
      elReadSunsetTime.textContent = res.sunsetTime;
      elReadSunsetDirection.textContent = res.sunsetAz;
    }

    // ======================
    // Labels update
    // ======================
    function updateLabels() {
      elLabelDate.textContent = doyToMonthDay(parseInt(elDate.value, 10));
      const lat = parseFloat(elLat.value);
      elLabelLat.textContent = `${lat >= 0 ? lat : Math.abs(lat)}°${lat>=0?'':'S'}`.replace('0°S','0°');
      const t = parseFloat(elTime.value);
      elLabelTime.textContent = timeToString(t);
    }

    // ======================
    // Controls handlers
    // ======================
    let dirty = true;

    function onDateChange() {
      updateLabels();
      updateReadouts();
      const N = parseInt(elDate.value, 10);
      const latDeg = parseFloat(elLat.value);
      const timeHours = parseFloat(elTime.value);

      // Update both views appropriately
      regenerateSunPath(N, latDeg);
      updateGroundSunAndDome(N, latDeg, timeHours);

      updateSpaceSunAndTerminator(N);
      // Parallel depends on time (due to daily rotation)
      if (!chkStationary.checked) updateEarthRotationFromTime(timeHours);
      updateParallelDaySegment(N, latDeg);

      dirty = true;
    }

    function onLatitudeChange() {
      updateLabels();
      updateReadouts();
      const N = parseInt(elDate.value, 10);
      const latDeg = parseFloat(elLat.value);
      const timeHours = parseFloat(elTime.value);

      regenerateSunPath(N, latDeg);
      updateGroundSunAndDome(N, latDeg, timeHours);

      updateLatitudeTorus(latDeg);
      if (!chkStationary.checked) updateEarthRotationFromTime(timeHours);
      updateParallelDaySegment(N, latDeg);

      dirty = true;
    }

    function onTimeChange() {
      updateLabels();
      const N = parseInt(elDate.value, 10);
      const latDeg = parseFloat(elLat.value);
      const timeHours = parseFloat(elTime.value);

      updateGroundSunAndDome(N, latDeg, timeHours);

      if (!chkStationary.checked) updateEarthRotationFromTime(timeHours);
      updateParallelDaySegment(N, latDeg);

      dirty = true;
    }

    function onViewChange() {
      syncOptionsUI();
      dirty = true;
    }

    function onCheckboxChange() {
      // Refresh visibilities quickly
      syncOptionsUI();
      dirty = true;
    }

    // Event listeners
    elDate.addEventListener('input', onDateChange);
    elLat.addEventListener('input', onLatitudeChange);
    elTime.addEventListener('input', onTimeChange);
    elView.addEventListener('change', onViewChange);

    chkSunPath.addEventListener('change', onCheckboxChange);
    chkConstDay.addEventListener('change', onCheckboxChange);

    chkStationary.addEventListener('change', onCheckboxChange);
    chkConst.addEventListener('change', onCheckboxChange);
    chkTerminator.addEventListener('change', onCheckboxChange);
    chkParallelDay.addEventListener('change', onCheckboxChange);

    // Resize handling
    window.addEventListener('resize', () => {
      renderer.setSize(container.clientWidth, container.clientHeight);
      camera.aspect = container.clientWidth / container.clientHeight;
      camera.updateProjectionMatrix();
      dirty = true;
    });

    // ======================
    // Initial setup
    // ======================
    function init() {
      // Defaults already set in HTML
      syncOptionsUI();
      updateLabels();
      updateReadouts();

      // Initialize objects for current latitude/date/time
      const N = parseInt(elDate.value, 10);
      const latDeg = parseFloat(elLat.value);
      const timeHours = parseFloat(elTime.value);

      regenerateSunPath(N, latDeg);
      updateGroundSunAndDome(N, latDeg, timeHours);

      updateLatitudeTorus(latDeg);
      updateSpaceSunAndTerminator(N);
      updateEarthRotationFromTime(timeHours);
      updateParallelDaySegment(N, latDeg);

      // Constellations initial visibility
      constellationsGround.visible = chkConstDay.checked && (elView.value === 'ground');
      constellationsSpace.visible = chkConst.checked && (elView.value === 'space');
    }

    // ======================
    // Animation loop with "dirty" render strategy
    // ======================
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      if (dirty) {
        renderer.render(scene, camera);
        dirty = false;
      }
    }

    init();
    animate();

  </script>
</body>
</html>
