<body>
  <div id="app-container">
    <div id="controls-container">
      <div id="function-controls">
        <span>function</span>
        <button class="function-btn active" data-function="x">x</button>
        <button class="function-btn" data-function="(x-3)²">(x-3)²</button>
        <button class="function-btn" data-function="(x-3)³+27">(x-3)³+27</button>
        <button class="function-btn" data-function="√x">√x</button>
        <button class="function-btn" data-function="sin(x)+1">sin(x)+1</button>
        <button class="function-btn" data-function="eˣ/³">eˣ/³</button>
      </div>
      <div id="type-controls">
        <span>type</span>
        <button class="type-btn" data-type="left">left</button>
        <button class="type-btn active" data-type="right">right</button>
        <button class="type-btn" data-type="midpoint">midpoint</button>
        <button class="type-btn" data-type="overestimate">overestimate</button>
        <button class="type-btn" data-type="underestimate">underestimate</button>
        <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
      </div>
      <div id="slider-a-controls">
        <label for="slider-a">upper limit a</label>
        <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5" />
        <span id="value-a">5</span>
      </div>
      <div id="slider-n-controls">
        <label for="slider-n">number of quadrilaterals</label>
        <input type="range" id="slider-n" min="1" max="50" step="1" value="3" />
        <span id="value-n">3</span>
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

<script>
  // Global state
  let state = { funcKey: 'x', type: 'right', a: 5, n: 3 };

  // Function definitions and antiderivatives
  const FUNCTIONS = {
    'x': {
      f: (x) => x,
      F: (x) => 0.5 * x * x
    },
    '(x-3)²': {
      f: (x) => (x - 3) * (x - 3),
      F: (x) => Math.pow(x - 3, 3) / 3
    },
    '(x-3)³+27': {
      f: (x) => Math.pow(x - 3, 3) + 27,
      F: (x) => Math.pow(x - 3, 4) / 4 + 27 * x
    },
    '√x': {
      f: (x) => x >= 0 ? Math.sqrt(x) : NaN,
      F: (x) => x >= 0 ? (2/3) * Math.pow(x, 1.5) : NaN
    },
    'sin(x)+1': {
      f: (x) => Math.sin(x) + 1,
      F: (x) => -Math.cos(x) + x
    },
    'eˣ/³': { // interpreted as e^(x/3)
      f: (x) => Math.exp(x / 3),
      F: (x) => 3 * Math.exp(x / 3)
    }
  };

  // DOM hookup
  document.addEventListener('DOMContentLoaded', () => {
    // Function buttons
    document.querySelectorAll('.function-btn').forEach(btn => {
      btn.addEventListener('click', () => {
        state.funcKey = btn.dataset.function;
        document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
        p5Instance.redraw();
      });
    });

    // Type buttons
    document.querySelectorAll('.type-btn').forEach(btn => {
      btn.addEventListener('click', () => {
        state.type = btn.dataset.type;
        document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
        p5Instance.redraw();
      });
    });

    // Sliders
    const sliderA = document.getElementById('slider-a');
    const valueA = document.getElementById('value-a');
    sliderA.addEventListener('input', () => {
      state.a = parseFloat(sliderA.value);
      valueA.textContent = state.a.toFixed(2);
      p5Instance.redraw();
    });

    const sliderN = document.getElementById('slider-n');
    const valueN = document.getElementById('value-n');
    sliderN.addEventListener('input', () => {
      state.n = parseInt(sliderN.value, 10);
      valueN.textContent = String(state.n);
      p5Instance.redraw();
    });
  });

  // p5 sketch
  let p5Instance = new p5((p) => {
    // Plot ranges
    const xMin = -1;
    const xMax = 8.5;
    let yMin = -1;
    let yMax = 5;
    const padding = 50;

    // Helpers: coordinate transforms
    const xToPx = (x) => p.map(x, xMin, xMax, padding, p.width - padding);
    const yToPx = (y) => p.map(y, yMin, yMax, p.height - padding, padding);

    // Determine "nice" tick step for y axis
    function niceStep(range) {
      const rough = range / 8;
      const pow10 = Math.pow(10, Math.floor(Math.log10(rough)));
      const candidates = [1, 2, 5, 10];
      let best = candidates[0] * pow10;
      for (const c of candidates) {
        const val = c * pow10;
        if (Math.abs(val - rough) < Math.abs(best - rough)) best = val;
      }
      return best;
    }

    function computeYScale() {
      const fn = FUNCTIONS[state.funcKey].f;
      let minY = Infinity, maxY = -Infinity;

      // Sample across the full x-range primarily where function is defined
      const samples = 800;
      for (let i = 0; i <= samples; i++) {
        const x = p.map(i, 0, samples, xMin, xMax);
        const y = fn(x);
        if (Number.isFinite(y)) {
          if (y < minY) minY = y;
          if (y > maxY) maxY = y;
        }
      }

      // Ensure baseline y=0 visible
      minY = Math.min(minY, 0);
      maxY = Math.max(maxY, 0);

      // Ensure function values within [0, a] are considered more densely
      const dense = 300;
      for (let i = 0; i <= dense; i++) {
        const x = p.map(i, 0, dense, 0, Math.min(state.a, 8));
        const y = fn(x);
        if (Number.isFinite(y)) {
          if (y < minY) minY = y;
          if (y > maxY) maxY = y;
        }
      }

      if (!Number.isFinite(minY) || !Number.isFinite(maxY) || minY === Infinity || maxY === -Infinity) {
        minY = -1; maxY = 1;
      }
      if (Math.abs(maxY - minY) < 1e-6) {
        maxY += 1;
        minY -= 1;
      }

      // Add padding
      const pad = 0.1 * (maxY - minY);
      yMin = minY - pad;
      yMax = maxY + pad;
    }

    function drawAxes() {
      p.push();
      p.stroke('#cccccc');
      p.strokeWeight(1);

      // x-axis at y=0
      if (yMin < 0 && yMax > 0) {
        const y0 = yToPx(0);
        p.line(xToPx(xMin), y0, xToPx(xMax), y0);
      }

      // y-axis at x=0
      if (xMin < 0 && xMax > 0) {
        const x0 = xToPx(0);
        p.line(x0, yToPx(yMin), x0, yToPx(yMax));
      }

      p.fill('#777');
      p.noStroke();
      p.textSize(11);
      p.textAlign(p.CENTER, p.TOP);

      // x ticks every 1
      p.textAlign(p.CENTER, p.TOP);
      for (let xi = Math.ceil(xMin); xi <= Math.floor(xMax); xi++) {
        const px = xToPx(xi);
        p.stroke('#e0e0e0');
        p.line(px, yToPx(yMin), px, yToPx(yMax));
        p.noStroke();
        p.fill('#888');
        const labelY = (yMin < 0 && yMax > 0) ? yToPx(0) + 4 : p.height - padding + 4;
        p.text(xi, px, labelY);
      }

      // y ticks with nice step
      const stepY = niceStep(yMax - yMin);
      p.textAlign(p.RIGHT, p.CENTER);
      for (let yi = Math.ceil(yMin / stepY) * stepY; yi <= yMax; yi += stepY) {
        const py = yToPx(yi);
        p.stroke('#e0e0e0');
        p.line(xToPx(xMin), py, xToPx(xMax), py);
        p.noStroke();
        p.fill('#888');
        p.text(yi.toFixed(Math.abs(stepY) < 1 ? 1 : 0), xToPx(xMin) - 6, py);
      }
      p.pop();
    }

    function drawFunctionCurve() {
      const fn = FUNCTIONS[state.funcKey].f;
      p.push();
      p.stroke(0);
      p.strokeWeight(2.2);
      p.noFill();

      const samples = Math.max(600, Math.floor((p.width - 2*padding)));
      let prevValid = false;
      p.beginShape();
      for (let i = 0; i <= samples; i++) {
        const x = p.map(i, 0, samples, xMin, xMax);
        const y = fn(x);
        if (Number.isFinite(y)) {
          const px = xToPx(x);
          const py = yToPx(y);
          if (!prevValid) {
            p.endShape();
            p.beginShape();
          }
          p.vertex(px, py);
          prevValid = true;
        } else {
          prevValid = false;
        }
      }
      p.endShape();
      p.pop();
    }

    function subintervalTrueIntegral(F, a, b) {
      const Fa = F(a);
      const Fb = F(b);
      const val = (Number.isFinite(Fb) ? Fb : 0) - (Number.isFinite(Fa) ? Fa : 0);
      return val;
    }

    function sampleExtremaOnInterval(f, a, b, samples = 50) {
      let minV = Infinity, maxV = -Infinity;
      for (let i = 0; i <= samples; i++) {
        const x = a + (i / samples) * (b - a);
        const y = f(x);
        if (Number.isFinite(y)) {
          if (y < minV) minV = y;
          if (y > maxV) maxV = y;
        }
      }
      if (minV === Infinity) { minV = 0; }
      if (maxV === -Infinity) { maxV = 0; }
      return { min: minV, max: maxV };
    }

    function drawEstimationAndComputeAreas() {
      const f = FUNCTIONS[state.funcKey].f;
      const F = FUNCTIONS[state.funcKey].F;

      const n = Math.max(1, state.n|0);
      const a = Math.max(0.1, Math.min(state.a, 8));
      const deltaX = a / n;

      let estimatedArea = 0;

      p.stroke(80);
      p.strokeWeight(1);

      for (let i = 0; i < n; i++) {
        const x0 = i * deltaX;
        const x1 = x0 + deltaX;

        // Heights
        let h = 0, h0 = 0, h1 = 0;
        switch (state.type) {
          case 'left':
            h = f(x0);
            h = Number.isFinite(h) ? h : 0;
            break;
          case 'right':
            h = f(x1);
            h = Number.isFinite(h) ? h : 0;
            break;
          case 'midpoint':
            h = f((x0 + x1) / 2);
            h = Number.isFinite(h) ? h : 0;
            break;
          case 'overestimate': {
            const { max } = sampleExtremaOnInterval(f, x0, x1, 80);
            h = Number.isFinite(max) ? max : 0;
            break;
          }
          case 'underestimate': {
            const { min } = sampleExtremaOnInterval(f, x0, x1, 80);
            h = Number.isFinite(min) ? min : 0;
            break;
          }
          case 'trapezoidal':
            h0 = f(x0); if (!Number.isFinite(h0)) h0 = 0;
            h1 = f(x1); if (!Number.isFinite(h1)) h1 = 0;
            break;
        }

        // Shape area
        let shapeArea = 0;
        if (state.type === 'trapezoidal') {
          shapeArea = (h0 + h1) * 0.5 * deltaX;
        } else {
          shapeArea = h * deltaX;
        }

        // True integral over sub-interval
        const trueInt = subintervalTrueIntegral(F, x0, x1);

        // Color by over/under
        const over = shapeArea > trueInt;
        const fillColor = over ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
        p.fill(fillColor);

        // Draw shape
        p.beginShape();
        if (state.type === 'trapezoidal') {
          const verts = [
            [x0, 0],
            [x1, 0],
            [x1, h1],
            [x0, h0]
          ];
          verts.forEach(([vx, vy]) => p.vertex(xToPx(vx), yToPx(vy)));
        } else {
          const verts = [
            [x0, 0],
            [x1, 0],
            [x1, h],
            [x0, h]
          ];
          verts.forEach(([vx, vy]) => p.vertex(xToPx(vx), yToPx(vy)));
        }
        p.endShape(p.CLOSE);

        estimatedArea += shapeArea;
      }

      return estimatedArea;
    }

    function drawUpperLimitMarker() {
      const a = state.a;
      p.push();
      p.stroke('#ff5252');
      p.strokeWeight(2);
      const px = xToPx(a);
      p.line(px, yToPx(yMin), px, yToPx(yMax));
      p.pop();
    }

    function drawOverlayText(estimatedArea, trueIntegral) {
      p.push();
      p.fill(20);
      p.noStroke();
      p.textAlign(p.CENTER, p.TOP);
      p.textSize(14);
      const txt1 = `estimated area: ${estimatedArea.toFixed(5)}`;
      const txt2 = `integral: ${trueIntegral.toFixed(5)}`;
      p.text(`${txt1}   |   ${txt2}`, p.width/2, 12);
      p.pop();
    }

    function computeTrueIntegralTotal() {
      const F = FUNCTIONS[state.funcKey].F;
      const a = state.a;
      const v = subintervalTrueIntegral(F, 0, a);
      return v;
    }

    p.setup = function() {
      const parent = document.getElementById('canvas-container');
      const w = parent.clientWidth - 16;
      const h = Math.max(420, Math.floor(window.innerHeight * 0.55));
      p.createCanvas(w, h).parent('canvas-container');
      p.noLoop();
      p.pixelDensity(Math.min(2, window.devicePixelRatio || 1));
    };

    p.windowResized = function() {
      const parent = document.getElementById('canvas-container');
      const w = parent.clientWidth - 16;
      const h = Math.max(420, Math.floor(window.innerHeight * 0.55));
      p.resizeCanvas(w, h);
      p.redraw();
    };

    p.draw = function() {
      computeYScale();

      p.background(248, 248, 248);

      drawAxes();
      drawFunctionCurve();

      const estimatedArea = drawEstimationAndComputeAreas();
      drawUpperLimitMarker();

      const trueIntegral = computeTrueIntegralTotal();
      drawOverlayText(estimatedArea, trueIntegral);
    };
  });
</script>
</body>
</html>
