  const initial = {
    current: 0.431,
    earth: 0.0000194,
    radius: 0.5,
    turns: 25
  };

  function fmtCurrent(v) { return Number(v).toFixed(3); }
  function fmtRadius(v) { return Number(v).toFixed(2); }
  function fmtTurns(v) { return String(Math.round(Number(v))); }
  function fmtEarth(v) {
    const x = Number(v);
    if (x === 0) return "0";
    if (Math.abs(x) < 1e-5) return x.toExponential(2); // e.g., 9.4e-6
    return x.toFixed(7).replace(/0+$/,'').replace(/\.$/,''); // keep initial precision nicely
  }

  function updateValueDisplays() {
    values.current.textContent = fmtCurrent(sliders.current.value);
    values.earth.textContent   = fmtEarth(sliders.earth.value);
    values.radius.textContent  = fmtRadius(sliders.radius.value);
    values.turns.textContent   = fmtTurns(sliders.turns.value);
  }

  function resetAll() {
    sliders.current.value = initial.current;
    sliders.earth.value   = initial.earth;
    sliders.radius.value  = initial.radius;
    sliders.turns.value   = initial.turns;
    updateValueDisplays();
    triggerRedraw();
  }

  document.getElementById('btn-reset').addEventListener('click', resetAll);

  // Update on any slider movement
  Object.values(sliders).forEach(el => {
    el.addEventListener('input', () => {
      updateValueDisplays();
      triggerRedraw();
    });
  });

  // -------------------------------
  // Physics and visualization
  // -------------------------------
  const mu0 = 4 * Math.PI * 1e-7; // T·m/A
  // World bounds (Y,Z) in meters shown on canvas
  const world = {
    yMin: -0.5, yMax: 0.5,
    zMin: -0.5, zMax: 0.5
  };

  // p5 variables
  let cnv;
  let canvasSize = 640; // will adjust on setup/resize
  let gridCount = 25;   // 25x25 grid
  let maxStreamSteps = 100;
  let stepWorld = 0.01; // streamline integration step (world units)
  let arrowEvery = 10;  // draw arrowhead every N steps

  function getParams() {
    return {
      I: Number(sliders.current.value),
      BzEarth: Number(sliders.earth.value),
      R: Number(sliders.radius.value),
      N: Number(sliders.turns.value)
    };
  }

  function triggerRedraw() {
    if (window._p5_ready) {
      redraw();
    }
  }

  // Map world (y,z) to screen (x,y)
  function toScreen(y, z) {
    const x = mapValue(y, world.yMin, world.yMax, 0, canvasSize);
    const yy = mapValue(z, world.zMax, world.zMin, 0, canvasSize); // invert z so up is positive
    return { x, y: yy };
  }

  function mapValue(v, a1, a2, b1, b2) {
    return b1 + (b2 - b1) * ((v - a1) / (a2 - a1));
  }

  function insideWorld(y, z, margin = 0) {
    return (y >= world.yMin - margin && y <= world.yMax + margin &&
            z >= world.zMin - margin && z <= world.zMax + margin);
  }

  // -------------------------------
  // Elliptic integrals (complete) via 16-point Gauss-Legendre quadrature
  // K(m) = ∫_0^{π/2} dθ / sqrt(1 - m sin^2 θ)
  // E(m) = ∫_0^{π/2} sqrt(1 - m sin^2 θ) dθ
  // -------------------------------
  const gl16_x = [
    -0.989400934991649932596154173450,
    -0.944575023073232576077988415535,
    -0.865631202387831743880467897712,
    -0.755404408355003033895101194847,
    -0.617876244402643748446671764049,
    -0.458016777657227386342419442984,
    -0.281603550779258913230460501460,
    -0.095012509837637440185319335424,
     0.095012509837637440185319335424,
     0.281603550779258913230460501460,
     0.458016777657227386342419442984,
     0.617876244402643748446671764049,
     0.755404408355003033895101194847,
     0.865631202387831743880467897712,
     0.944575023073232576077988415535,
     0.989400934991649932596154173450
  ];
  const gl16_w = [
    0.027152459411754094851780572456,
    0.062253523938647892862843836994,
    0.095158511682492784809925107602,
    0.124628971255533872052476282192,
    0.149595988816576732081501730547,
    0.169156519395002538189312079030,
    0.182603415044923588866763667969,
    0.189450610455068496285396723208,
    0.189450610455068496285396723208,
    0.182603415044923588866763667969,
    0.169156519395002538189312079030,
    0.149595988816576732081501730547,
    0.124628971255533872052476282192,
    0.095158511682492784809925107602,
    0.062253523938647892862843836994,
    0.027152459411754094851780572456
  ];
  function ellipticK(m) {
    // Clamp m to [0, 1-ε] for numerical stability
    if (m <= 0) return Math.PI / 2;
    if (m >= 1) m = 1 - 1e-12;
    const a = Math.PI / 4;
    let s = 0;
    for (let i = 0; i < 16; i++) {
      const t = a * (gl16_x[i] + 1); // map from [-1,1] -> [0, π/2]
      const sinT = Math.sin(t);
      const denom = Math.sqrt(1 - m * sinT * sinT);
      s += gl16_w[i] * (1 / denom);
    }
    return a * s;
  }
  function ellipticE(m) {
    if (m <= 0) return Math.PI / 2;
    if (m >= 1) m = 1 - 1e-12;
    const a = Math.PI / 4;
    let s = 0;
    for (let i = 0; i < 16; i++) {
      const t = a * (gl16_x[i] + 1);
      const sinT = Math.sin(t);
      const val = Math.sqrt(1 - m * sinT * sinT);
      s += gl16_w[i] * val;
    }
    return a * s;
  }

  // -------------------------------
  // Magnetic field of a circular loop (radius R, current I, N turns)
  // at (y, z) in the Y-Z plane, loop centered at z_offset along Z.
  //
  // Uses standard expressions with complete elliptic integrals.
  // We handle y<0 by symmetry: use rho=|y| in formulas; By sign = sign(y).
  // For rho ~ 0, use on-axis formula for stability.
  // -------------------------------
  function loopField(y, z, R, I, N, z_offset) {
    const zp = z - z_offset;
    const rho = Math.abs(y);
    const sgn = (y >= 0) ? 1 : -1;
    const eps = 1e-12;

    const common1 = (mu0 * I * N) / (2 * Math.PI);

    if (rho < 1e-9) {
      // On-axis limit: By = 0; Bz matches classical on-axis expression
      const denom = Math.pow(R*R + zp*zp, 1.5);
      const Bz = mu0 * I * N * R*R / (2 * denom);
      return { By: 0, Bz };
    }

    const rp = R + rho;
    const rm = R - rho;
    const Q = Math.sqrt(rp*rp + zp*zp);
    const m = (4 * R * rho) / (Q*Q); // parameter m in [0,1)

    const K = ellipticK(m);
    const E = ellipticE(m);
    const common2 = 1 / Q;
    const D = (rm*rm + zp*zp);

    // From standard expressions for B_rho and B_z (here rho ≡ |y|)
    // By is B_rho with sign(y)
    let By_mag = common1 * (zp / rho) * common2 * ( ((R*R + rho*rho + zp*zp)/D) * E - K );
    let Bz = common1 * common2 * ( K - ((R*R - rho*rho - zp*zp)/D) * E );

    if (!isFinite(By_mag)) By_mag = 0;

    const By = sgn * By_mag;
    return { By, Bz };
  }

  function coilsField(y, z, R, I, N) {
    // Helmholtz pair centered at z = ± R/2
    const f1 = loopField(y, z, R, I, N, -R/2);
    const f2 = loopField(y, z, R, I, N, +R/2);
    return { By: f1.By + f2.By, Bz: f1.Bz + f2.Bz };
  }

  function totalField(y, z, params) {
    const coils = coilsField(y, z, params.R, params.I, params.N);
    return { By: coils.By, Bz: coils.Bz + params.BzEarth };
  }

  function fieldMagnitude(y, z, params) {
    const f = totalField(y, z, params);
    return Math.hypot(f.By, f.Bz);
  }

  function computeMagnitudeRange(params) {
    let minMag = Infinity;
    let maxMag = 0;
    const n = gridCount;
    for (let i = 0; i < n; i++) {
      const y = world.yMin + (i / (n - 1)) * (world.yMax - world.yMin);
      for (let j = 0; j < n; j++) {
        const z = world.zMin + (j / (n - 1)) * (world.zMax - world.zMin);
        const mag = fieldMagnitude(y, z, params);
        if (isFinite(mag)) {
          if (mag < minMag) minMag = mag;
          if (mag > maxMag) maxMag = mag;
        }
      }
    }
    if (!isFinite(minMag) || minMag <= 0) minMag = 1e-12;
    if (!isFinite(maxMag) || maxMag <= minMag) maxMag = minMag * 10;
    return { minMag, maxMag };
  }

  // -------------------------------
  // Streamline drawing
  // -------------------------------
  function drawStreamline(y0, z0, params, minMag, maxMag) {
    function stepOnce(y, z, dir) {
      const f = totalField(y, z, params);
      const mag = Math.max(1e-16, Math.hypot(f.By, f.Bz));
      const dy = (f.By / mag) * stepWorld * dir;
      const dz = (f.Bz / mag) * stepWorld * dir;
      return { yn: y + dy, zn: z + dz, mag };
    }

    // Forward direction
    let y = y0, z = z0;
    let prev = toScreen(y, z);
    for (let s = 1; s <= maxStreamSteps; s++) {
      const { yn, zn, mag } = stepOnce(y, z, +1);
      if (!insideWorld(yn, zn)) break;

      const scr = toScreen(yn, zn);
      const hue = mapValue(mag, minMag, maxMag, 60, 270); // low -> yellow (60), high -> purple/blue (270)
      stroke(hue, 100, 100);
      line(prev.x, prev.y, scr.x, scr.y);

      if (s % arrowEvery === 0) {
        drawArrowHead(prev.x, prev.y, scr.x, scr.y, hue);
      }

      y = yn; z = zn; prev = scr;
    }

    // Backward direction
    y = y0; z = z0; prev = toScreen(y, z);
    for (let s = 1; s <= maxStreamSteps; s++) {
      const { yn, zn, mag } = stepOnce(y, z, -1);
      if (!insideWorld(yn, zn)) break;

      const scr = toScreen(yn, zn);
      const hue = mapValue(mag, minMag, maxMag, 60, 270);
      stroke(hue, 100, 100);
      line(prev.x, prev.y, scr.x, scr.y);

      if (s % arrowEvery === 0) {
        drawArrowHead(prev.x, prev.y, scr.x, scr.y, hue);
      }

      y = yn; z = zn; prev = scr;
    }
  }

  function drawArrowHead(x1, y1, x2, y2, hue) {
    const dx = x2 - x1;
    const dy = y2 - y1;
    const L = Math.hypot(dx, dy) || 1;
    const ux = dx / L, uy = dy / L;

    const size = 6; // pixels
    const angle = Math.PI / 7;

    // Rotate backwards to draw two small lines
    const rx1 =  Math.cos(angle) * ux - Math.sin(angle) * uy;
    const ry1 =  Math.sin(angle) * ux + Math.cos(angle) * uy;

    const rx2 =  Math.cos(-angle) * ux - Math.sin(-angle) * uy;
    const ry2 =  Math.sin(-angle) * ux + Math.cos(-angle) * uy;

    stroke(hue, 100, 100);
    line(x2, y2, x2 - size * rx1, y2 - size * ry1);
    line(x2, y2, x2 - size * rx2, y2 - size * ry2);
  }

  // -------------------------------
  // Axes (Y horizontal, Z vertical) with ticks and labels (red)
  // -------------------------------
  function drawAxes() {
    stroke('#ff0000');
    fill('#ff0000');
    strokeWeight(1);

    // Axes lines
    const y0 = 0, z0 = 0;
    const scrYmin = toScreen(world.yMin, z0);
    const scrYmax = toScreen(world.yMax, z0);
    const scrZmin = toScreen(y0, world.zMin);
    const scrZmax = toScreen(y0, world.zMax);

    // Y axis (horizontal line at z=0)
    line(scrYmin.x, scrYmin.y, scrYmax.x, scrYmax.y);
    // Z axis (vertical line at y=0)
    line(scrZmin.x, scrZmin.y, scrZmax.x, scrZmax.y);

    // Ticks and labels every 0.2 units
    textSize(11);
    textAlign(CENTER, TOP);
    const tick = 0.2;

    for (let v = Math.ceil(world.yMin / tick) * tick; v <= world.yMax + 1e-9; v += tick) {
      const p = toScreen(v, 0);
      line(p.x, p.y - 4, p.x, p.y + 4);
      if (Math.abs(v) > 1e-10) {
        text(v.toFixed(1), p.x, p.y + 6);
      }
    }

    textAlign(RIGHT, CENTER);
    for (let v = Math.ceil(world.zMin / tick) * tick; v <= world.zMax + 1e-9; v += tick) {
      const p = toScreen(0, v);
      line(p.x - 4, p.y, p.x + 4, p.y);
      if (Math.abs(v) > 1e-10) {
        text(v.toFixed(1), p.x - 6, p.y);
      }
    }

    // Axis labels
    textSize(14);
    textAlign(RIGHT, BOTTOM);
    text('Y', scrYmax.x - 4, scrYmax.y - 4);

    textAlign(LEFT, TOP);
    text('Z', scrZmax.x + 4, scrZmax.y + 4);
  }

  // -------------------------------
  // p5.js sketch
  // -------------------------------
  function setup() {
    const parent = document.getElementById('viz-container');
    canvasSize = Math.min(700, Math.max(400, parent.clientWidth - 32));
    cnv = createCanvas(canvasSize, canvasSize);
    cnv.parent('viz-container');
    cnv.id('p5-canvas');

    colorMode(HSB, 360, 100, 100, 100);
    strokeWeight(1);
    noLoop();

    window._p5_ready = true;
    updateValueDisplays();
    redraw();
  }

  function windowResized() {
    const parent = document.getElementById('viz-container');
    const newSize = Math.min(700, Math.max(400, parent.clientWidth - 32));
    if (newSize !== canvasSize) {
      canvasSize = newSize;
      resizeCanvas(canvasSize, canvasSize);
      redraw();
    }
  }

  function draw() {
    background(255);

    const params = getParams();

    // Compute magnitude range for color mapping
    const range = computeMagnitudeRange(params);
    const minMag = range.minMag;
    const maxMag = range.maxMag;

    // Draw axes
    push();
    drawAxes();
    pop();

    // Draw streamlines
    push();
    // Use full brightness/saturation, hues mapped in drawStreamline
    for (let i = 0; i < gridCount; i++) {
      const y = world.yMin + (i / (gridCount - 1)) * (world.yMax - world.yMin);
      for (let j = 0; j < gridCount; j++) {
        const z = world.zMin + (j / (gridCount - 1)) * (world.zMax - world.zMin);
        drawStreamline(y, z, params, minMag, maxMag);
      }
    }
    pop();
  }
</script>
</body>
