  <script>
    // Shared State
    let currentMessage = "";
    let currentCiphertext = "";

    // Word list (sorted by length descending)
    const WORD_LIST = ['THE', 'IDES', 'OF', 'MARCH', 'ARE', 'COME', 'EVEN', 'YOU', 'BRUTUS', 'CAESAR', 'CIPHER', 'HELLO', 'WORLD', 'SECRET', 'MESSAGE', 'ATTACK', 'DAWN', 'I', 'A']
      .sort((a,b) => b.length - a.length);

    // Utilities
    const A_CODE = 65;
    const Z_CODE = 90;

    function mod(n, m) {
      return ((n % m) + m) % m;
    }

    function standardAlphabetArray() {
      return Array.from({length: 26}, (_, i) => String.fromCharCode(A_CODE + i));
    }

    function alphabetStringFromArray(arr) {
      return arr.join(' ');
    }

    function shiftAlphabet(shift) {
      const arr = [];
      for (let i = 0; i < 26; i++) {
        const code = A_CODE + mod(i + shift, 26);
        arr.push(String.fromCharCode(code));
      }
      return alphabetStringFromArray(arr);
    }

    function sanitizePlaintext(message) {
      return message.toUpperCase().replace(/ /g, 'X');
    }

    function encryptChar(ch, offset) {
      const code = ch.charCodeAt(0);
      if (code >= A_CODE && code <= Z_CODE) {
        return String.fromCharCode(A_CODE + mod((code - A_CODE) + offset, 26));
      }
      return ch;
    }

    function decryptChar(ch, offset) {
      // Per spec: char((original_char_code - 65 - d + 26) % 26 + 65)
      const code = ch.charCodeAt(0);
      if (code >= A_CODE && code <= Z_CODE) {
        return String.fromCharCode(A_CODE + mod((code - A_CODE) - offset, 26));
      }
      return ch;
    }

    function encryptString(plaintext, offset) {
      let out = '';
      for (let i = 0; i < plaintext.length; i++) {
        out += encryptChar(plaintext[i], offset);
      }
      return out;
    }

    function decryptString(cipher, offset) {
      let out = '';
      for (let i = 0; i < cipher.length; i++) {
        out += decryptChar(cipher[i], offset);
      }
      return out;
    }

    function highlightWords(s) {
      let i = 0;
      let parts = [];
      const N = s.length;
      while (i < N) {
        let matched = false;
        for (const w of WORD_LIST) {
          if (i + w.length <= N && s.startsWith(w, i)) {
            parts.push('<span class="highlighted">' + w + '</span>');
            i += w.length;
            matched = true;
            break;
          }
        }
        if (!matched) {
          parts.push(s[i]);
          i += 1;
        }
      }
      return parts.join('');
    }

    // DOM Elements
    const btnEncoding = document.getElementById('btn-toggle-encoding');
    const btnDecoding = document.getElementById('btn-toggle-decoding');
    const panelEncoding = document.getElementById('panel-encoding');
    const panelDecoding = document.getElementById('panel-decoding');

    // Encoding elements
    const selectMessage = document.getElementById('select-message');
    const btnClearEncoding = document.getElementById('btn-clear-encoding');
    const sliderEncoding = document.getElementById('slider-encoding-offset');
    const spanEncOffset = document.getElementById('span-encoding-offset-value');
    const keyboardEncoding = document.getElementById('keyboard-encoding');
    const displayMessage = document.getElementById('display-message');
    const displayPlaintext = document.getElementById('display-plaintext');
    const preEncAlphaFrom = document.getElementById('display-encoding-alphabet-from');
    const preEncAlphaTo = document.getElementById('display-encoding-alphabet-to');
    const displayCipher = document.getElementById('display-ciphertext');

    // Decoding elements
    const btnIntercept = document.getElementById('btn-intercept');
    const btnClearDecoding = document.getElementById('btn-clear-decoding');
    const sliderDecoding = document.getElementById('slider-decoding-offset');
    const spanDecOffset = document.getElementById('span-decoding-offset-value');
    const keyboardDecoding = document.getElementById('keyboard-decoding');
    const displayIntercept = document.getElementById('display-intercept');
    const preDecAlphaFrom = document.getElementById('display-decoding-alphabet-from');
    const preDecAlphaTo = document.getElementById('display-decoding-alphabet-to');
    const tableDecoding = document.getElementById('table-decoding-output');
    const tbodyDecoding = tableDecoding.querySelector('tbody');

    // Generate keyboards
    function generateKeyboard(container, includeSpace) {
      container.innerHTML = '';
      // A-Z
      for (let i = 0; i < 26; i++) {
        const btn = document.createElement('button');
        btn.type = 'button';
        const char = String.fromCharCode(A_CODE + i);
        btn.textContent = char;
        btn.dataset.key = char;
        container.appendChild(btn);
      }
      // SPACE (only in encoding)
      if (includeSpace) {
        const spaceBtn = document.createElement('button');
        spaceBtn.type = 'button';
        spaceBtn.classList.add('special');
        spaceBtn.textContent = 'SPACE';
        spaceBtn.dataset.key = 'SPACE';
        container.appendChild(spaceBtn);
      }
      // BACKSPACE
      const backBtn = document.createElement('button');
      backBtn.type = 'button';
      backBtn.classList.add('special', 'backspace');
      backBtn.textContent = 'BACKSPACE';
      backBtn.dataset.key = 'BACKSPACE';
      container.appendChild(backBtn);
    }

    // Tab controls
    btnEncoding.addEventListener('click', () => {
      panelEncoding.style.display = '';
      panelDecoding.style.display = 'none';
      btnEncoding.classList.add('active');
      btnDecoding.classList.remove('active');
    });
    btnDecoding.addEventListener('click', () => {
      panelEncoding.style.display = 'none';
      panelDecoding.style.display = '';
      btnDecoding.classList.add('active');
      btnEncoding.classList.remove('active');
    });

    // Encoding logic
    function updateEncoding() {
      const offset = parseInt(sliderEncoding.value, 10) || 0;
      spanEncOffset.textContent = String(offset);

      // Update message display
      displayMessage.textContent = currentMessage;

      // Plaintext generation (uppercase, spaces -> X)
      const plaintext = sanitizePlaintext(currentMessage);
      displayPlaintext.textContent = plaintext;

      // Update alphabet mapping
      preEncAlphaFrom.textContent = alphabetStringFromArray(standardAlphabetArray());
      preEncAlphaTo.textContent = shiftAlphabet(offset);

      // Ciphertext generation
      const ciphertext = encryptString(plaintext, offset);
      displayCipher.textContent = ciphertext;
      currentCiphertext = ciphertext;
    }

    selectMessage.addEventListener('change', () => {
      currentMessage = selectMessage.value || "";
      displayMessage.textContent = currentMessage;
      updateEncoding();
    });

    sliderEncoding.addEventListener('input', () => {
      spanEncOffset.textContent = sliderEncoding.value;
      updateEncoding();
    });

    keyboardEncoding.addEventListener('click', (e) => {
      const t = e.target;
      if (!(t instanceof HTMLElement)) return;
      const key = t.dataset.key;
      if (!key) return;

      if (key === 'BACKSPACE') {
        currentMessage = currentMessage.slice(0, -1);
      } else if (key === 'SPACE') {
        if (currentMessage.length < 32) currentMessage += ' ';
      } else {
        if (currentMessage.length < 32) currentMessage += key;
      }
      displayMessage.textContent = currentMessage;
      updateEncoding();
    });

    btnClearEncoding.addEventListener('click', () => {
      currentMessage = "";
      currentCiphertext = "";
      selectMessage.value = "";
      sliderEncoding.value = "0";
      spanEncOffset.textContent = "0";
      displayMessage.textContent = "";
      displayPlaintext.textContent = "";
      displayCipher.textContent = "";
      updateEncoding();
    });

    // Decoding logic
    function updateDecoding() {
      const sliderVal = parseInt(sliderDecoding.value, 10) || 0;
      spanDecOffset.textContent = String(sliderVal);

      const intercept = displayIntercept.textContent || "";

      // Update alphabet mapping for decoding:
      // "from" shifted by negative of slider offset, "to" is standard
      preDecAlphaFrom.textContent = shiftAlphabet(-sliderVal);
      preDecAlphaTo.textContent = alphabetStringFromArray(standardAlphabetArray());

      // Build decoding table
      tbodyDecoding.innerHTML = "";
      const offsets = [sliderVal - 2, sliderVal - 1, sliderVal, sliderVal + 1, sliderVal + 2];

      offsets.forEach((d) => {
        const tr = document.createElement('tr');

        const tdOffset = document.createElement('td');
        tdOffset.textContent = String(d);
        tr.appendChild(tdOffset);

        const tdText = document.createElement('td');
        const dec = decryptString(intercept, d);
        tdText.innerHTML = highlightWords(dec);
        tr.appendChild(tdText);

        tbodyDecoding.appendChild(tr);
      });
    }

    btnIntercept.addEventListener('click', () => {
      displayIntercept.textContent = currentCiphertext || "";
      updateDecoding();
    });

    sliderDecoding.addEventListener('input', () => {
      spanDecOffset.textContent = sliderDecoding.value;
      updateDecoding();
    });

    keyboardDecoding.addEventListener('click', (e) => {
      const t = e.target;
      if (!(t instanceof HTMLElement)) return;
      const key = t.dataset.key;
      if (!key) return;

      let curr = displayIntercept.textContent || "";
      if (key === 'BACKSPACE') {
        curr = curr.slice(0, -1);
      } else {
        if (curr.length < 32) curr += key;
      }
      displayIntercept.textContent = curr;
      updateDecoding();
    });

    btnClearDecoding.addEventListener('click', () => {
      displayIntercept.textContent = "";
      sliderDecoding.value = "0";
      spanDecOffset.textContent = "0";
      tbodyDecoding.innerHTML = "";
      updateDecoding();
    });

    // Initialize
    function init() {
      generateKeyboard(keyboard-encoding, true);
    }
    // Avoid hyphen issue in variable name, correct initialization:
    generateKeyboard(keyboardEncoding, true);
    generateKeyboard(keyboardDecoding, false);

    // Initial updates
    updateEncoding();
    updateDecoding();
  </script>
</body>
</html>
