<script>
  // Global state
  let p5Instance = null;
  let beforeGrid = [];
  let afterGrid = [];
  let currentN = 24;
  let currentK = 2;
  let currentFilter = 'shadow';
  let currentLetter = 'a';

  const perImageSize = 200; // fixed size per image in pixels
  const gapBetween = 20;    // gap between before/after on the canvas

  // Helper: create and normalize kernels
  function createKernel(k, filterType) {
    const D = 2 * k + 1;
    const kernel = Array.from({ length: D }, () => Array(D).fill(0));

    if (filterType === 'blur') {
      const v = 1 / (D * D);
      for (let y = 0; y < D; y++) for (let x = 0; x < D; x++) kernel[y][x] = v;
    } else {
      // "shadow" filter: a k x k block of ones offset by (1,1) from the center
      const c = k; // center index
      const start = c + 1; // offset by (1,1)
      for (let y = 0; y < k; y++) {
        for (let x = 0; x < k; x++) {
          const ky = start + y;
          const kx = start + x;
          if (ky >= 0 && ky < D && kx >= 0 && kx < D) {
            kernel[ky][kx] = 1;
          }
        }
      }
      // Normalize by k*k if k>0 (k is at least 1 by UI constraints)
      const norm = k * k;
      for (let y = 0; y < D; y++) for (let x = 0; x < D; x++) kernel[y][x] /= norm;
    }
    return kernel;
  }

  // Compute "before" grid using a p5.Graphics buffer
  function generateBeforeGrid(p, N, letter) {
    const g = p.createGraphics(N, N);
    g.pixelDensity(1);
    g.background(255);
    g.fill(0);
    g.noStroke();
    g.textAlign(p.CENTER, p.CENTER);
    g.textSize(N * 0.8);
    g.text(letter, N / 2, N / 2);

    g.loadPixels();
    const grid = Array.from({ length: N }, () => Array(N).fill(0));
    // Read pixels (RGBA)
    for (let y = 0; y < N; y++) {
      for (let x = 0; x < N; x++) {
        const idx = 4 * (y * N + x);
        const r = g.pixels[idx]; // grayscale since we used black/white
        // Threshold to binary: black ink becomes 1, white background 0
        grid[y][x] = (r < 128) ? 1 : 0;
      }
    }
    g.remove(); // free graphics buffer
    return grid;
  }

  // Apply convolution with zero-padding at edges
  function convolveGrid(src, N, kernel, k) {
    const D = 2 * k + 1;
    const mid = k;
    const out = Array.from({ length: N }, () => Array(N).fill(0));
    for (let y = 0; y < N; y++) {
      for (let x = 0; x < N; x++) {
        let sum = 0;
        for (let ky = 0; ky < D; ky++) {
          for (let kx = 0; kx < D; kx++) {
            const yy = y + (ky - mid);
            const xx = x + (kx - mid);
            if (yy >= 0 && yy < N && xx >= 0 && xx < N) {
              sum += kernel[ky][kx] * src[yy][xx];
            }
          }
        }
        out[y][x] = sum; // already in [0,1]
      }
    }
    return out;
  }

  // Update visualization according to current UI state
  function updateVisualization() {
    // Read controls
    const sConv = document.getElementById('slider-convolver');
    const sSize = document.getElementById('slider-image-size');
    const inLetter = document.getElementById('input-letter');
    const selected = document.querySelector('input[name="filter"]:checked');

    currentK = Math.max(1, Math.min(5, parseInt(sConv.value || '2', 10)));
    currentN = Math.max(10, Math.min(40, parseInt(sSize.value || '24', 10)));
    currentFilter = selected ? selected.value : 'shadow';
    currentLetter = (inLetter.value || 'a').slice(0, 1);

    if (!p5Instance) return;

    // Generate "before" binary grid
    beforeGrid = generateBeforeGrid(p5Instance, currentN, currentLetter);

    // Generate kernel and convolve
    const kernel = createKernel(currentK, currentFilter);
    afterGrid = convolveGrid(beforeGrid, currentN, kernel, currentK);

    // Trigger redraw
    p5Instance.redraw();
  }

  // p5 sketch
  const sketch = (p) => {
    p5Instance = p;

    p.setup = function() {
      const canvasWidth = perImageSize * 2 + gapBetween;
      const canvasHeight = perImageSize;
      const cnv = p.createCanvas(canvasWidth, canvasHeight);
      cnv.parent('visualization-area');
      p.pixelDensity(1);
      p.noLoop(); // redraw only on updates
      updateVisualization(); // initial render with defaults
    };

    function drawGrid(grid, x0, y0, N, binary = false) {
      const cell = perImageSize / N;
      p.noStroke();
      for (let y = 0; y < N; y++) {
        for (let x = 0; x < N; x++) {
          const v = grid[y][x];
          const shade = 255 * (1 - (binary ? (v ? 1 : 0) : v));
          p.fill(shade);
          p.rect(x0 + x * cell, y0 + y * cell, cell, cell);
        }
      }
    }

    p.draw = function() {
      p.background(248);
      // Left: before
      drawGrid(beforeGrid, 0, 0, currentN, true);
      // Right: after
      drawGrid(afterGrid, perImageSize + gapBetween, 0, currentN, false);
    };
  };

  // Initialize p5
  new p5(sketch);

  // UI wiring
  function clampSlider(el) {
    const min = Number(el.min), max = Number(el.max);
    let v = Number(el.value);
    if (Number.isNaN(v)) v = min;
    if (v < min) v = min;
    if (v > max) v = max;
    el.value = String(v);
  }

  function adjustSlider(id, delta) {
    const el = document.getElementById(id);
    const step = Number(el.step) || 1;
    const min = Number(el.min), max = Number(el.max);
    let v = Number(el.value) + delta * step;
    v = Math.max(min, Math.min(max, v));
    el.value = String(v);
    el.dispatchEvent(new Event('input', { bubbles: true }));
  }

  // Sliders
  document.getElementById('slider-convolver').addEventListener('input', () => {
    clampSlider(document.getElementById('slider-convolver'));
    updateVisualization();
  });
  document.getElementById('slider-image-size').addEventListener('input', () => {
    clampSlider(document.getElementById('slider-image-size'));
    updateVisualization();
  });

  // Plus buttons
  document.getElementById('convolver-plus-btn').addEventListener('click', () => adjustSlider('slider-convolver', +1));
  document.getElementById('image-size-plus-btn').addEventListener('click', () => adjustSlider('slider-image-size', +1));

  // Optional minus buttons (wired via data-target)
  document.querySelectorAll('.step-btn-minus').forEach(btn => {
    btn.addEventListener('click', () => {
      const target = btn.getAttribute('data-target');
      if (target) adjustSlider(target, -1);
    });
  });

  // Letter input: sanitize and update on input
  const letterInput = document.getElementById('input-letter');
  letterInput.addEventListener('input', () => {
    let v = letterInput.value || '';
    if (v.length > 1) v = v.slice(0, 1);
    letterInput.value = v;
    updateVisualization();
  });

  // Arrow up/down cycles through alphabet
  letterInput.addEventListener('keydown', (e) => {
    if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
      e.preventDefault();
      let ch = (letterInput.value || 'a').toLowerCase();
      let code = ch.charCodeAt(0);
      if (code < 97 || code > 122) code = 97; // ensure a..z
      if (e.key === 'ArrowUp') {
        code = (code === 122) ? 97 : code + 1;
      } else {
        code = (code === 97) ? 122 : code - 1;
      }
      letterInput.value = String.fromCharCode(code);
      letterInput.dispatchEvent(new Event('input', { bubbles: true }));
    }
  });

  // Filter radios
  document.getElementById('radio-shadow').addEventListener('input', updateVisualization);
  document.getElementById('radio-blur').addEventListener('input', updateVisualization);

  // Resize handling (keep canvas content centered; no dynamic resize needed)
  window.addEventListener('resize', () => {
    // No action needed: canvas size is fixed by design.
  });
</script>
</body>
</html>
