<script>
(function() {
  // Amino acid mapping
  const AA_CODES = ['A','C','D','E','F','G','H','I','K','L','M','N','P','Q','R','S','T','V','W','Y'];
  const AA_MAP = {
    A: 'ALA', C: 'CYS', D: 'ASP', E: 'GLU', F: 'PHE',
    G: 'GLY', H: 'HIS', I: 'ILE', K: 'LYS', L: 'LEU',
    M: 'MET', N: 'ASN', P: 'PRO', Q: 'GLN', R: 'ARG',
    S: 'SER', T: 'THR', V: 'VAL', W: 'TRP', Y: 'TYR'
  };

  // DOM elements
  const btn3D = document.getElementById('btn-crystallography');
  const btn2D = document.getElementById('btn-contact-map');
  const checkboxLateral = document.getElementById('checkbox-lateral-chains');
  const sliderGray = document.getElementById('slider-gray-level');
  const spanGray = document.getElementById('gray-level-value');
  const sliderBrowse = document.getElementById('slider-browse-sequence');
  const spanBrowse = document.getElementById('browse-sequence-value');
  const selectResidue = document.getElementById('select-residue');

  const sliderX = document.getElementById('slider-residue-x');
  const spanX = document.getElementById('residue-x-value');
  const sliderY = document.getElementById('slider-residue-y');
  const spanY = document.getElementById('residue-y-value');
  const sliderRMSD = document.getElementById('slider-rmsd');
  const spanRMSD = document.getElementById('rmsd-value');
  const sliderPlotSize = document.getElementById('slider-plot-size');
  const spanPlotSize = document.getElementById('plot-size-value');

  const btnReset = document.getElementById('btn-reset');
  const infoDisplay = document.getElementById('info-display');
  const canvasContainer = document.getElementById('canvas-container');

  // Global state
  let currentView = '3d'; // '3d' or '2d'
  let protein = [];
  let caPositions = []; // Array of THREE.Vector3 for 'CA'
  let distMatrix = null; // Float32Array N*N
  const N = 400;

  // 3D scene variables
  let THREE_state = {
    renderer: null,
    scene: null,
    camera: null,
    controls: null,
    meshes: [], // all atom meshes
    boxHelper: null,
    rafId: null,
    initialCam: { position: new THREE.Vector3(0,0,0), target: new THREE.Vector3(0,0,0) },
  };

  // 2D p5 variables
  let p5Instance = null;

  // Utility formatting
  function setSpanValue(span, input) {
    const step = parseFloat(input.step || '1');
    const val = parseFloat(input.value);
    if (step < 1) {
      span.textContent = val.toFixed(String(step).split('.')[1]?.length || 2);
    } else {
      span.textContent = Math.round(val).toString();
    }
  }

  function clamp(v, lo, hi) { return Math.max(lo, Math.min(hi, v)); }

  // Data generation
  function generateProteinData(numResidues) {
    const residues = [];
    caPositions = [];
    const start = new THREE.Vector3(0, 0, 0);
    let dir = new THREE.Vector3(1, 0.2, 0.1).normalize();
    let pos = start.clone();
    const stepLen = 3.8; // approximate CA-CA distance
    for (let i = 1; i <= numResidues; i++) {
      // Slowly vary the direction to create a contorted chain
      const angle = 0.15 + Math.sin(i * 0.1) * 0.05;
      const axis = new THREE.Vector3(
        Math.sin(i * 0.07) * 0.3,
        Math.cos(i * 0.05) * 0.3,
        Math.sin(i * 0.09) * 0.3
      ).normalize();
      dir.applyAxisAngle(axis, angle).normalize();
      // Small random jitter
      dir.add(new THREE.Vector3(
        (Math.random()-0.5)*0.2,
        (Math.random()-0.5)*0.2,
        (Math.random()-0.5)*0.2
      )).normalize();
      pos = pos.clone().add(dir.clone().multiplyScalar(stepLen));

      const code = AA_CODES[Math.floor(Math.random()*AA_CODES.length)];
      const resName = AA_MAP[code];

      // Atom positions relative to CA
      const ca = pos.clone();
      // local frame
      const t = dir.clone().normalize();
      const up = new THREE.Vector3(0,1,0);
      if (Math.abs(t.dot(up)) > 0.9) up.set(1,0,0);
      const n1 = new THREE.Vector3().crossVectors(t, up).normalize();
      const n2 = new THREE.Vector3().crossVectors(t, n1).normalize();

      const atoms = [
        { atomName: 'CA', x: ca.x, y: ca.y, z: ca.z },
        { atomName: 'N',  x: ca.x - t.x*1.3 + n1.x*0.5, y: ca.y - t.y*1.3 + n1.y*0.5, z: ca.z - t.z*1.3 + n1.z*0.5 },
        { atomName: 'C',  x: ca.x + t.x*1.5, y: ca.y + t.y*1.5, z: ca.z + t.z*1.5 },
        { atomName: 'O',  x: ca.x + t.x*2.1 + n2.x*0.4, y: ca.y + t.y*2.1 + n2.y*0.4, z: ca.z + t.z*2.1 + n2.z*0.4 },
        { atomName: 'CB', x: ca.x + n1.x*1.6 + n2.x*0.6, y: ca.y + n1.y*1.6 + n2.y*0.6, z: ca.z + n1.z*1.6 + n2.z*0.6 },
      ];

      residues.push({
        resName, resCode: code, resSeq: i, atoms
      });
      caPositions.push(ca);
    }
    return residues;
  }

  function precomputeDistances() {
    distMatrix = new Float32Array(N * N);
    for (let i = 0; i < N; i++) {
      const pi = caPositions[i];
      for (let j = 0; j < N; j++) {
        const pj = caPositions[j];
        const dx = pi.x - pj.x, dy = pi.y - pj.y, dz = pi.z - pj.z;
        distMatrix[i*N + j] = Math.sqrt(dx*dx + dy*dy + dz*dz);
      }
    }
  }

  // 3D view
  function initThree() {
    disposeP5(); // ensure only one canvas exists
    clearCanvasContainer();

    const plotSize = parseInt(sliderPlotSize.value, 10);
    canvasContainer.style.width = plotSize + 'px';
    canvasContainer.style.height = plotSize + 'px';

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setSize(plotSize, plotSize);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
    renderer.domElement.style.display = 'block';
    canvasContainer.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    const camera = new THREE.PerspectiveCamera(45, plotSize/plotSize, 0.1, 2000);
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Position camera to view the whole protein
    const box3 = new THREE.Box3().setFromPoints(caPositions);
    const size = new THREE.Vector3();
    const center = new THREE.Vector3();
    box3.getSize(size);
    box3.getCenter(center);

    const maxDim = Math.max(size.x, size.y, size.z);
    const camZ = maxDim * 1.8;
    camera.position.set(center.x + camZ, center.y + camZ * 0.5, center.z + camZ);
    camera.lookAt(center);
    controls.target.copy(center);

    THREE_state.initialCam.position = camera.position.clone();
    THREE_state.initialCam.target = controls.target.clone();

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.6);
    const dir = new THREE.DirectionalLight(0xffffff, 0.8);
    dir.position.set(1, 1, 1);
    scene.add(ambient, dir);

    // Build atom meshes
    const meshes = [];
    const sphereGeoCA = new THREE.SphereGeometry(0.55, 16, 16);
    const sphereGeoOther = new THREE.SphereGeometry(0.38, 12, 12);

    for (let r = 0; r < protein.length; r++) {
      const residue = protein[r];
      for (let a = 0; a < residue.atoms.length; a++) {
        const atom = residue.atoms[a];
        const isCA = atom.atomName === 'CA';
        const geo = isCA ? sphereGeoCA : sphereGeoOther;
        const mat = new THREE.MeshLambertMaterial({ color: 0x0000ff });
        const mesh = new THREE.Mesh(geo, mat);
        mesh.position.set(atom.x, atom.y, atom.z);
        mesh.userData = { residueIndex: r+1, atomName: atom.atomName, resCode: residue.resCode, resName: residue.resName };
        meshes.push(mesh);
        scene.add(mesh);
      }
    }

    // Bounding box helper (gray)
    const boxHelper = new THREE.Box3Helper(new THREE.Box3().setFromPoints(caPositions), 0x888888);
    scene.add(boxHelper);

    // Save state
    THREE_state.renderer = renderer;
    THREE_state.scene = scene;
    THREE_state.camera = camera;
    THREE_state.controls = controls;
    THREE_state.meshes = meshes;
    THREE_state.boxHelper = boxHelper;

    applyLateralChainsVisibility();
    update3DColors();
    animate3D();
  }

  function animate3D() {
    if (currentView !== '3d' || !THREE_state.renderer) return;
    const { renderer, scene, camera, controls } = THREE_state;
    controls.update();
    renderer.render(scene, camera);
    THREE_state.rafId = requestAnimationFrame(animate3D);
  }

  function disposeThree() {
    if (THREE_state.rafId) {
      cancelAnimationFrame(THREE_state.rafId);
      THREE_state.rafId = null;
    }
    const { renderer, scene } = THREE_state;
    if (scene) {
      // Dispose geometries and materials
      THREE_state.meshes.forEach(m => {
        if (m.geometry) m.geometry.dispose();
        if (m.material) m.material.dispose();
      });
      THREE_state.meshes = [];
    }
    if (renderer) {
      renderer.dispose();
    }
    clearCanvasContainer();
    // Reset state
    THREE_state.renderer = null;
    THREE_state.scene = null;
    THREE_state.camera = null;
    THREE_state.controls = null;
    THREE_state.boxHelper = null;
  }

  function clearCanvasContainer() {
    while (canvasContainer.firstChild) {
      canvasContainer.removeChild(canvasContainer.firstChild);
    }
  }

  function applyLateralChainsVisibility() {
    if (!THREE_state.meshes) return;
    const showAll = checkboxLateral.checked;
    for (const m of THREE_state.meshes) {
      if (m.userData.atomName !== 'CA') {
        m.visible = showAll;
      }
    }
  }

  function update3DColors() {
    if (!THREE_state.meshes) return;
    const baseVal = parseFloat(sliderGray.value);
    // blue from black (0,0,0) to bright blue (0,0,1)
    const baseColor = new THREE.Color(0, 0, baseVal);

    const resA = parseInt(sliderBrowse.value, 10);
    const resX = parseInt(sliderX.value, 10);
    const resY = parseInt(sliderY.value, 10);

    for (const m of THREE_state.meshes) {
      // Base color
      m.material.color.copy(baseColor);
    }
    // Highlight Residue_A (all atoms) yellow
    for (const m of THREE_state.meshes) {
      if (m.userData.residueIndex === resA) {
        m.material.color.setHex(0xffff00);
      }
    }
    // Highlight CA of X and Y red
    for (const m of THREE_state.meshes) {
      if (m.userData.atomName === 'CA' && (m.userData.residueIndex === resX || m.userData.residueIndex === resY)) {
        m.material.color.setHex(0xff0000);
      }
    }
  }

  function reset3DCamera() {
    if (!THREE_state.camera || !THREE_state.controls) return;
    const { camera, controls, initialCam } = THREE_state;
    camera.position.copy(initialCam.position);
    controls.target.copy(initialCam.target);
    controls.update();
  }

  function resizeThree() {
    if (!THREE_state.renderer || !THREE_state.camera) return;
    const size = parseInt(sliderPlotSize.value, 10);
    canvasContainer.style.width = size + 'px';
    canvasContainer.style.height = size + 'px';
    THREE_state.renderer.setSize(size, size);
    THREE_state.camera.aspect = 1;
    THREE_state.camera.updateProjectionMatrix();
  }

  // 2D p5 view
  function initP5() {
    disposeThree(); // ensure only one canvas exists
    clearCanvasContainer();

    const plotSize = parseInt(sliderPlotSize.value, 10);
    canvasContainer.style.width = plotSize + 'px';
    canvasContainer.style.height = plotSize + 'px';

    const sketch = (p) => {
      p.setup = function() {
        const ps = parseInt(sliderPlotSize.value, 10);
        const cnv = p.createCanvas(ps, ps);
        cnv.parent('canvas-container');
        p.noLoop();
        p.pixelDensity(1);
      };

      p.drawAxes = function(margin) {
        p.push();
        p.stroke(180);
        p.strokeWeight(1);
        p.fill(80);
        p.textSize(10);
        // axes
        p.line(margin, margin, margin, p.height - margin);
        p.line(margin, p.height - margin, p.width - margin, p.height - margin);
        p.line(margin, margin, p.width - margin, margin);
        p.line(p.width - margin, margin, p.width - margin, p.height - margin);

        const ticks = [1, 100, 200, 300, 400];
        const scale = (p.width - 2*margin) / (N - 1);
        p.textAlign(p.CENTER, p.TOP);
        for (const t of ticks) {
          const x = margin + (t - 1) * scale;
          // bottom
          p.line(x, p.height - margin, x, p.height - margin + 4);
          p.text(t, x, p.height - margin + 6);
          // top
          p.line(x, margin, x, margin - 4);
          p.text(t, x, margin - 16);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for (const t of ticks) {
          const y = margin + (t - 1) * scale;
          // left
          p.line(margin, y, margin - 4, y);
          p.text(t, margin - 6, y);
          // right
          p.line(p.width - margin, y, p.width - margin + 4, y);
          p.text(t, p.width - margin + 30, y);
        }
        p.pop();
      };

      p.draw = function() {
        const ps = parseInt(sliderPlotSize.value, 10);
        if (p.width !== ps || p.height !== ps) {
          p.resizeCanvas(ps, ps, false);
        }
        p.background(255);
        const margin = 40;
        p.drawAxes(margin);

        const rmsd = parseFloat(sliderRMSD.value);
        const scale = (p.width - 2 * margin) / (N - 1);

        p.push();
        p.translate(margin, margin);
        // contact points
        p.stroke(0);
        p.strokeWeight(Math.max(1, Math.floor(scale*0.35)));
        for (let i = 0; i < N; i++) {
          const xi = i * scale;
          for (let j = 0; j < N; j++) {
            if (distMatrix[i*N + j] <= rmsd) {
              const yj = j * scale;
              p.point(xi, yj);
            }
          }
        }
        // red lines for X and Y
        const idxX = parseInt(sliderX.value, 10) - 1;
        const idxY = parseInt(sliderY.value, 10) - 1;
        p.stroke(255, 0, 0);
        p.strokeWeight(1.5);
        p.line(idxX * scale, 0, idxX * scale, (N - 1) * scale);
        p.line(0, idxY * scale, (N - 1) * scale, idxY * scale);
        p.pop();
      };
    };

    p5Instance = new p5(sketch);
    // Redraw once
    p5Instance.redraw();
  }

  function disposeP5() {
    if (p5Instance) {
      p5Instance.remove();
      p5Instance = null;
    }
  }

  function resizeP5() {
    if (!p5Instance) return;
    const ps = parseInt(sliderPlotSize.value, 10);
    canvasContainer.style.width = ps + 'px';
    canvasContainer.style.height = ps + 'px';
    p5Instance.resizeCanvas(ps, ps, false);
    p5Instance.redraw();
  }

  // Shared helpers
  function updateInfoDisplay() {
    const idxA = parseInt(sliderBrowse.value, 10);
    const idxX = parseInt(sliderX.value, 10);
    const idxY = parseInt(sliderY.value, 10);
    const resA = protein[idxA - 1];
    const resX = protein[idxX - 1];
    const resY = protein[idxY - 1];
    const str = `Res A: ${resA.resName}${idxA} | Res X: ${resX.resName}${idxX} | Res Y: ${resY.resName}${idxY}`;
    infoDisplay.textContent = str;
  }

  function switchView(view) {
    if (view === currentView) return;
    currentView = view;
    if (view === '3d') {
      btn3D.classList.add('active');
      btn2D.classList.remove('active');
      initThree();
    } else {
      btn2D.classList.add('active');
      btn3D.classList.remove('active');
      initP5();
    }
  }

  function populateSelectResidue() {
    selectResidue.innerHTML = '';
    AA_CODES.forEach(code => {
      const opt = document.createElement('option');
      opt.value = code;
      opt.textContent = code;
      selectResidue.appendChild(opt);
    });
    selectResidue.value = 'A'; // default
  }

  function findNextResidueIndexByCode(code, startIndex) {
    // startIndex is 1-based; search (startIndex+1) ... N, then wrap to 1 ... startIndex
    const start = clamp(startIndex, 1, N);
    for (let i = start; i <= N; i++) {
      if (protein[i - 1].resCode === code && i !== start) return i;
    }
    for (let i = 1; i <= start; i++) {
      if (protein[i - 1].resCode === code && i !== start) return i;
    }
    // If the very same index matches and no other found, return same to at least trigger
    if (protein[start - 1].resCode === code) return start;
    return start; // fallback
  }

  // Event bindings
  btn3D.addEventListener('click', () => switchView('3d'));
  btn2D.addEventListener('click', () => switchView('2d'));
  btnReset.addEventListener('click', () => {
    if (currentView === '3d') reset3DCamera();
  });

  checkboxLateral.addEventListener('change', () => {
    if (currentView === '3d') applyLateralChainsVisibility();
  });

  sliderGray.addEventListener('input', () => {
    setSpanValue(spanGray, sliderGray);
    if (currentView === '3d') update3DColors();
  });

  sliderBrowse.addEventListener('input', () => {
    setSpanValue(spanBrowse, sliderBrowse);
    updateInfoDisplay();
    if (currentView === '3d') update3DColors();
  });

  selectResidue.addEventListener('change', () => {
    const code = selectResidue.value;
    const currentIdx = parseInt(sliderBrowse.value, 10);
    const nextIdx = findNextResidueIndexByCode(code, currentIdx);
    sliderBrowse.value = nextIdx;
    setSpanValue(spanBrowse, sliderBrowse);
    updateInfoDisplay();
    if (currentView === '3d') update3DColors();
  });

  sliderX.addEventListener('input', () => {
    setSpanValue(spanX, sliderX);
    updateInfoDisplay();
    if (currentView === '3d') update3DColors();
    if (currentView === '2d' && p5Instance) p5Instance.redraw();
  });

  sliderY.addEventListener('input', () => {
    setSpanValue(spanY, sliderY);
    updateInfoDisplay();
    if (currentView === '3d') update3DColors();
    if (currentView === '2d' && p5Instance) p5Instance.redraw();
  });

  sliderRMSD.addEventListener('input', () => {
    setSpanValue(spanRMSD, sliderRMSD);
    if (currentView === '2d' && p5Instance) p5Instance.redraw();
  });

  sliderPlotSize.addEventListener('input', () => {
    setSpanValue(spanPlotSize, sliderPlotSize);
    if (currentView === '3d') resizeThree();
    if (currentView === '2d') resizeP5();
  });

  // Initialize
  function init() {
    populateSelectResidue();

    setSpanValue(spanGray, sliderGray);
    setSpanValue(spanBrowse, sliderBrowse);
    setSpanValue(spanX, sliderX);
    setSpanValue(spanY, sliderY);
    setSpanValue(spanRMSD, sliderRMSD);
    setSpanValue(spanPlotSize, sliderPlotSize);

    protein = generateProteinData(N);
    precomputeDistances();

    // Start in 3D view
    initThree();
    updateInfoDisplay();
  }

  // Kick off
  init();

})();
</script>
</body>
