<script>
  // State and helpers
  const el = {
    infoToggle: document.getElementById('info-toggle'),
    infoContent: document.getElementById('info-content'),
    checkboxMode: document.getElementById('checkbox-mode'),
    checkboxDisplay: document.getElementById('checkbox-display'),
    inputA: document.getElementById('input-a'),
    inputB: document.getElementById('input-b'),
    sliderC: document.getElementById('slider-c'),
    displayC: document.getElementById('display-c'),
    eqText: document.getElementById('equation-text')
  };

  const state = {
    a: 7,
    b: 8,
    c: 24,
    modeDots: true,
    showFrobenius: false
  };

  function gcd(a, b) {
    a = Math.abs(a); b = Math.abs(b);
    while (b !== 0) {
      const t = b;
      b = a % b;
      a = t;
    }
    return a;
  }
  function isCoprime(a, b) {
    return gcd(a, b) === 1;
  }
  function frobeniusNumber(a, b) {
    return a * b - a - b;
  }
  function clamp(val, min, max) {
    return Math.max(min, Math.min(max, val));
  }
  function updateSliderMaxIfCoprime() {
    if (!isCoprime(state.a, state.b)) return;
    const maxC = state.a * state.b * 2;
    el.sliderC.max = String(maxC);
    // clamp current c
    state.c = clamp(state.c, parseInt(el.sliderC.min, 10), maxC);
    el.sliderC.value = String(state.c);
    el.displayC.textContent = String(state.c);
  }
  function setSliderEnabled(enabled) {
    el.sliderC.disabled = !enabled;
    el.displayC.classList.toggle('disabled', !enabled);
  }
  function updateEquationText() {
    if (!isCoprime(state.a, state.b)) {
      el.eqText.textContent = 'Numbers a and b should be coprime!';
      return;
    }
    if (state.showFrobenius) {
      const F = frobeniusNumber(state.a, state.b);
      el.eqText.textContent = `F = a*b - a - b = ${state.a}*${state.b} - ${state.a} - ${state.b} = ${F}`;
    } else {
      el.eqText.textContent = `Current equation: ${state.a} x + ${state.b} y = ${state.c}`;
    }
  }

  // Info toggle
  el.infoToggle.addEventListener('click', () => {
    const isOpen = el.infoContent.style.display === 'block';
    el.infoContent.style.display = isOpen ? 'none' : 'block';
    el.infoToggle.textContent = isOpen ? '+' : '-';
  });

  // Controls events
  el.inputA.addEventListener('change', () => {
    let v = parseInt(el.inputA.value, 10);
    if (isNaN(v)) v = state.a;
    v = clamp(v, parseInt(el.inputA.min, 10), parseInt(el.inputA.max, 10));
    state.a = v;
    if (isCoprime(state.a, state.b)) {
      updateSliderMaxIfCoprime();
    }
    updateEquationText();
    p5Redraw();
  });
  el.inputB.addEventListener('change', () => {
    let v = parseInt(el.inputB.value, 10);
    if (isNaN(v)) v = state.b;
    v = clamp(v, parseInt(el.inputB.min, 10), parseInt(el.inputB.max, 10));
    state.b = v;
    if (isCoprime(state.a, state.b)) {
      updateSliderMaxIfCoprime();
    }
    updateEquationText();
    p5Redraw();
  });
  el.sliderC.addEventListener('input', () => {
    state.c = parseInt(el.sliderC.value, 10);
    el.displayC.textContent = String(state.c);
    if (isCoprime(state.a, state.b) && !state.showFrobenius) {
      updateEquationText();
      p5Redraw();
    }
  });
  el.checkboxMode.addEventListener('change', () => {
    state.modeDots = el.checkboxMode.checked;
    p5Redraw();
