  el.checkboxDisplay.addEventListener('change', () => {
    state.showFrobenius = el.checkboxDisplay.checked;
    setSliderEnabled(!state.showFrobenius);
    updateEquationText();
    p5Redraw();
  });

  // Initialize slider defaults
  (function init() {
    state.modeDots = true;
    state.showFrobenius = false;
    el.checkboxMode.checked = true;
    el.checkboxDisplay.checked = false;

    // slider c
    el.sliderC.min = '1';
    el.sliderC.step = '1';
    state.c = 24;
    updateSliderMaxIfCoprime(); // sets max and clamps value
    el.sliderC.value = String(state.c);
    el.displayC.textContent = String(state.c);
    setSliderEnabled(true);
    updateEquationText();
  })();

  // p5.js sketch (global mode)
  let cnv;
  const CANVAS_W = 550, CANVAS_H = 550;
  const MARGIN = 48;

  function setup() {
    cnv = createCanvas(CANVAS_W, CANVAS_H);
    cnv.parent('canvas-container');
    textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial');
    noLoop();
    p5Redraw();
  }

  function p5Redraw() {
    if (typeof redraw === 'function') {
      redraw();
    }
  }

  function drawAxesAndLabels(sx, sy, a, b) {
    // Axes (in transformed coordinate system)
    push();
    translate(MARGIN, height - MARGIN);
    scale(1, -1);
    stroke(0);
    strokeWeight(2);
    // x-axis
    line(0, 0, b * sx, 0);
    // y-axis
    line(0, 0, 0, a * sy);
    // tick marks
    strokeWeight(1.5);
    for (let i = 0; i <= b; i++) {
      const x = i * sx;
      line(x, 0, x, -5); // small tick downward (remember flipped)
    }
    for (let j = 0; j <= a; j++) {
      const y = j * sy;
      line(0, y, -5, y); // small tick to the left
    }
    pop();

    // Numeric labels (screen coordinates; blue)
    fill('#1d4ed8');
    noStroke();
    textSize(12);
    textAlign(CENTER, TOP);
    for (let i = 0; i <= b; i++) {
      const px = MARGIN + i * sx;
      const py = height - MARGIN + 6;
      text(String(i), px, py);
    }
    textAlign(RIGHT, CENTER);
    for (let j = 0; j <= a; j++) {
      const px = MARGIN - 6;
      const py = height - MARGIN - j * sy;
      text(String(j), px, py);
    }
  }

  function computeLineSegmentIntersections(a, b, c, maxX, maxY) {
    // Computes intersection points of ax + by = c with rectangle [0,maxX]x[0,maxY]
    const pts = [];
    function within(x, y) {
      return x >= 0 && x <= maxX && y >= 0 && y <= maxY;
    }
    // x = 0 => by = c => y = c/b
    const y0 = c / b;
    if (isFinite(y0) && within(0, y0)) pts.push({x: 0, y: y0});
    // y = 0 => ax = c => x = c/a
    const x0 = c / a;
    if (isFinite(x0) && within(x0, 0)) pts.push({x: x0, y: 0});
    // x = maxX => a*maxX + b*y = c => y = (c - a*maxX)/b
    const yb = (c - a * maxX) / b;
    if (isFinite(yb) && within(maxX, yb)) pts.push({x: maxX, y: yb});
    // y = maxY => a*x + b*maxY = c => x = (c - b*maxY)/a
    const xa = (c - b * maxY) / a;
    if (isFinite(xa) && within(xa, maxY)) pts.push({x: xa, y: maxY});

    // Deduplicate nearly-equal points
    const uniq = [];
    for (const p of pts) {
      if (!uniq.some(q => Math.hypot(q.x - p.x, q.y - p.y) < 1e-6)) {
        uniq.push(p);
      }
    }
    if (uniq.length >= 2) {
      return [uniq[0], uniq[1]];
    }
    return null;
  }

  function representableSetUpTo(a, b, limit) {
    const rep = new Array(limit + 1).fill(false);
    const maxX = Math.floor(limit / a);
    for (let x = 0; x <= maxX; x++) {
      const remaining = limit - a * x;
      const maxY = Math.floor(remaining / b);
      for (let y = 0; y <= maxY; y++) {
        rep[a * x + b * y] = true;
      }
    }
    return rep;
  }

  function draw() {
    clear();
    background(255);

    const a = state.a;
    const b = state.b;
    const cop = isCoprime(a, b);

    const plotW = width - 2 * MARGIN;
    const plotH = height - 2 * MARGIN;
    const sx = plotW / Math.max(1, b);
    const sy = plotH / Math.max(1, a);

    if (!cop) {
      // Show only message (already in equation text); blank canvas as per plan.
      return;
    }

    const showingF = state.showFrobenius;
    const cLine = showingF ? frobeniusNumber(a, b) : state.c;

    // Axes and labels
    drawAxesAndLabels(sx, sy, a, b);

    // "Dots" mode
    if (state.modeDots) {
      // Draw dots and line in transformed coordinate system
      push();
      translate(MARGIN, height - MARGIN);
      scale(1, -1);

      // Draw all dots
      stroke(0);
      fill(0);
      const rSmall = 4;
      for (let xi = 0; xi <= b; xi++) {
        for (let yi = 0; yi <= a; yi++) {
          circle(xi * sx, yi * sy, rSmall);
        }
      }

      // Draw line ax + by = c in red
      const seg = computeLineSegmentIntersections(a, b, cLine, b, a);
      if (seg) {
        stroke('#ef4444');
        strokeWeight(2);
        line(seg[0].x * sx, seg[0].y * sy, seg[1].x * sx, seg[1].y * sy);
      }

      // Highlight integer solutions (green)
      noStroke();
      fill('#16a34a');
      const rBig = 8;
      for (let xi = 0; xi <= b; xi++) {
        for (let yi = 0; yi <= a; yi++) {
          if (a * xi + b * yi === cLine) {
            circle(xi * sx, yi * sy, rBig);
          }
        }
      }
      pop();

      // If showing Frobenius in dots mode, nothing extra required (line already drawn at c = F).
    } else {
      // "Numbers" mode
      // Draw the numbers at each lattice point
      const baseSize = Math.floor(Math.min(sx, sy) * 0.35);
      const txtSize = Math.max(10, Math.min(18, baseSize));
      textSize(txtSize);
      textAlign(CENTER, CENTER);
      for (let xi = 0; xi <= b; xi++) {
        for (let yi = 0; yi <= a; yi++) {
          const v = a * xi + b * yi;
          const px = MARGIN + xi * sx;
          const py = height - MARGIN - yi * sy;
          if (!showingF && v === cLine) {
            fill('#16a34a'); // green highlight for current c
          } else {
            fill(20, 20, 20);
          }
          noStroke();
          text(String(v), px, py);
        }
      }

      // If showing Frobenius, highlight all numbers not representable by ax+by up to F (in a compact strip)
      if (showingF) {
        const F = cLine; // same as frobeniusNumber(a, b)
        const rep = representableSetUpTo(a, b, F);
        const stripW = width - 2 * MARGIN;
        const cellW = Math.max(1, Math.floor(stripW / (F + 1)));
        const stripH = 10;
        const x0 = MARGIN;
        const y0 = 10; // top strip
        noStroke();
        for (let n = 0; n <= F; n++) {
          if (rep[n]) {
            fill(200); // representable -> light gray
          } else {
            fill('#ef4444'); // not representable -> red
          }
          rect(x0 + n * cellW, y0, cellW, stripH);
        }
        // thin border around strip
        noFill();
        stroke(0);
        rect(x0, y0, (F + 1) * cellW, stripH);

        // legend text
        noStroke();
        fill('#64748b');
        textSize(12);
        textAlign(LEFT, TOP);
        text('Unsolvable numbers up to F (red)', x0, y0 + stripH + 4);
      }
    }
  }
</script>
</body>
</html>
