  });
  checkShowSome.addEventListener('change', () => {
    // p5 draw loop will reflect this automatically
  });

  // p5 instance mode
  const sketch = (p) => {
    let worldImg;
    let canvas;
    let hintSelection = [];
    let lastHintUpdateFrame = 0;

    p.preload = function() {
      const url = 'https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg';
      worldImg = p.loadImage(url);
    };

    function canvasSize() {
      const parent = document.getElementById('canvas-container');
      const w = parent.clientWidth || 800;
      const h = Math.round(w * 0.5); // equirectangular aspect ~2:1
      return {w, h};
    }

    p.setup = function() {
      const {w, h} = canvasSize();
      canvas = p.createCanvas(w, h);
      canvas.parent('canvas-container');
      p.noLoop(); // We'll use redraw on demand AND also requestAnimationFrame-like via updates
      p.frameRate(60);
      p.loop(); // continuous draw to keep cursor position indicator responsive
    };

    p.windowResized = function() {
      const {w, h} = canvasSize();
      p.resizeCanvas(w, h);
    };

    function mapToPixels(lon, lat) {
      const x = p.map(lon, -180, 180, 0, p.width);
      const y = p.map(lat, 90, -90, 0, p.height);
      return {x, y};
    }

    function mapToGeo(x, y) {
      const lon = p.map(x, 0, p.width, -180, 180);
      const lat = p.map(y, 0, p.height, 90, -90);
      return {lon, lat};
    }

    // Expose helpers for other parts if needed (not strictly necessary)
    window._mapToPixels = mapToPixels;
    window._mapToGeo = mapToGeo;

    function updatePositionDisplay() {
      if (p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height) {
        const geo = mapToGeo(p.mouseX, p.mouseY);
        const lon = Math.round(geo.lon);
        const lat = Math.round(geo.lat);
        pPosition.textContent = `Current position {${lon}, ${lat}}`;
      }
    }

    function chooseHints() {
      // Pick up to 3 random unfound items
      const unfound = currentData.filter(d => !d.found);
      const count = Math.min(3, unfound.length);
      // Simple shuffle copy
      const arr = unfound.slice();
      for (let i = arr.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
      }
      hintSelection = arr.slice(0, count);
    }

    function drawMarkers() {
      if (!checkShowLocations.checked) return;
      p.noStroke();
      p.fill('red');
      currentData.forEach(item => {
        if (!item.found) {
          const pt = mapToPixels(item.lon, item.lat);
          p.circle(pt.x, pt.y, 10);
        }
      });
    }

    function drawHints() {
      if (!checkShowSome.checked) return;
      // Refresh hints every 30 frames (~0.5s) to avoid flicker and keep variety
      if (p.frameCount - lastHintUpdateFrame > 30) {
        chooseHints();
        lastHintUpdateFrame = p.frameCount;
      }
      p.textSize(12);
      p.noStroke();
      p.fill('white');
      hintSelection.forEach(item => {
        const pt = mapToPixels(item.lon, item.lat);
        p.text(item.name, pt.x + 8, pt.y - 8);
      });
    }

    function drawCursor() {
      if (p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height) {
        p.noStroke();
        p.fill(173, 216, 230, 150);
        p.circle(p.mouseX, p.mouseY, 30);
      }
    }

    p.draw = function() {
      // Background image
      if (worldImg) {
        p.image(worldImg, 0, 0, p.width, p.height);
      } else {
        p.background(0);
      }

      // Markers and hints
      drawMarkers();
      drawHints();

      // Cursor marker
      drawCursor();

      // Update position display
      updatePositionDisplay();
    };

    p.mouseClicked = function() {
      // Check clicks only if inside canvas
      if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;

      const tolerance = 15;
      for (let i = 0; i < currentData.length; i++) {
        const item = currentData[i];
        if (item.found) continue;
        const pt = mapToPixels(item.lon, item.lat);
        const dx = p.mouseX - pt.x;
        const dy = p.mouseY - pt.y;
        const dist = Math.sqrt(dx*dx + dy*dy);
        if (dist <= tolerance) {
          item.found = true;
          // Update table cell
          const td = statusCells.get(item.name);
          if (td) {
            td.textContent = 'True';
            td.classList.add('found');
          }
          break; // prevent multiple finds per click
        }
      }
    };
  };

  // Initialize quiz and p5 after DOM is ready
  window.addEventListener('DOMContentLoaded', () => {
    // Default quiz type: rivers
    setupQuiz('rivers');

    // Initialize p5 instance in the specified container
    p5Instance = new p5(sketch, 'canvas-container');
  });
  </script>
</body>
