  // Insert encoding table initially (tier 5 default => visible)
  const encodingTableDiv = document.getElementById("encoding-table");
  encodingTableDiv.innerHTML = tableHTML;

  // Nodes data
  const nodes = [
    { id: 'I',  label: 'I',  freq: 5,  tier: 1, pos: {x: 100, y: 450}, children: [] },
    { id: 'S',  label: 'S',  freq: 4,  tier: 1, pos: {x: 180, y: 450}, children: [] },
